INSERT INTO [t29779] VALUES (1,'ليس لي مال، ولي يتيم. قال: كل من مال يتيمك غير مسرف , ولا مبذر , ولا متأثل، من غير أن تقي','2','0','0');
INSERT INTO [t29779] VALUES (2,'قال يوم الفتح: كفوا السلاح إلا خزاعة عن بني بكر. فقتلوهم إلى صلاة العصر , ثم قال: كفوا','3','0','0');
INSERT INTO [t29779] VALUES (3,'إن فلانا ابني , فقال: ذهب أمر الجاهلية، لا دعوى في الإسلام، الولد للفراش، وللعاهر','4','0','0');
INSERT INTO [t29779] VALUES (4,'في المواضح خمس خمس من الإبل، وفي الأصابع عشر عشر من الإبل','5','0','0');
INSERT INTO [t29779] VALUES (5,'صلاة بعد الصبح حتى تشرق الشمس , وعن صلاة بعد العصر حتى تغرب الشمس','6','0','0');
INSERT INTO [t29779] VALUES (6,'أن تنكح المرأة على عمتها , أو على خالتها','7','0','0');
INSERT INTO [t29779] VALUES (7,'لا يجوز لامرأة عطية إلا بإذن زوجها , وأوفوا بحلف الجاهلية , فإن الإسلام لم يزده إلا شدة ,','8','0','0');
INSERT INTO [t29779] VALUES (8,'إذا جاءكم المصدق , فلا يصدر عنكم إلا وهو راض','9','0','0');
INSERT INTO [t29779] VALUES (9,'لا نورث','10','0','0');
INSERT INTO [t29779] VALUES (10,'سيأتي على الناس زمان تحل فيه العزوبة , فلا يسلم لذي دين دينه إلا من فر بدينه من شاهق إلى','11','0','0');
INSERT INTO [t29779] VALUES (11,'تفتي الناس بركعتين بعد العصر؟ قال: إن عائشة , قالت: إن رسول الله صلى الله عليه وسلم','12','0','0');
INSERT INTO [t29779] VALUES (12,'آخر من يخرج من النار فيدخل الجنة رجل يحبو , فيقال له: ادخل الجنة، فيخيل إليه أنها ملأى ,','13','0','0');
INSERT INTO [t29779] VALUES (13,'اجعلوا من صلاتكم في بيوتكم ولا تتخذوها قبورا أو تجعلوها قبورا.','14','0','0');
INSERT INTO [t29779] VALUES (14,'رجلا ممن كان قبلكم أخذته قرحة في يده، فأخذ حديدة، فحز بها في يده حتى قطعها، فما رقأ دمها','15','0','0');
INSERT INTO [t29779] VALUES (15,'كانت تركز له الحربة، ويصلي إليها','16','0','0');
INSERT INTO [t29779] VALUES (16,'أحشر يوم القيامة أنا وأبو بكر وعمر هكذا. قال: وأراه ذكر فيه: ونحن مشرفون على','17','0','0');
INSERT INTO [t29779] VALUES (17,'أهدى جملا','18','0','0');
INSERT INTO [t29779] VALUES (18,'قطع في مجن، أو جحفة بثمن ثلاثة دراهم.','19','0','0');
INSERT INTO [t29779] VALUES (19,'نضر الله عبدا سمع مقالتي فعمل بها، حتى يحدث بها أخاه , ثلاث لا يغل عليهن قلب امرئ مسلم:','20','0','0');
INSERT INTO [t29779] VALUES (20,'يوتر ب سبح اسم ربك الأعلى وقل يا أيها الكافرون وقل هو الله أحد','21','0','0');
INSERT INTO [t29779] VALUES (21,'العبد إذا أحسن عبادة ربه ونصح لسيده يؤتى أجره مرتين','22','0','0');
INSERT INTO [t29779] VALUES (22,'ليس من البر الصيام في السفر','23','0','0');
INSERT INTO [t29779] VALUES (23,'إذا قام أحدكم يصلي، فلا يتنخم في قبلته؛ فإن العبد إذا قام يصلي فإنما يناجي ربه عز','24','0','0');
INSERT INTO [t29779] VALUES (24,'بئس أخو العشيرة , وبئس الرجل.','25','0','0');
INSERT INTO [t29779] VALUES (25,'بالمغرب بابا مفتوحا للتوبة لا يغلق حتى تطلع الشمس من نحوه , فذلك يوم {لا ينفع نفسا','26','0','0');
INSERT INTO [t29779] VALUES (26,'يمسح على الموقين والخمار','27','0','0');
INSERT INTO [t29779] VALUES (27,'هل عندك من أدم؟ . قالت: نعم، خل.','28','0','0');
INSERT INTO [t29779] VALUES (28,'أن تؤتى النساء في أعجازهن.','29','0','0');
INSERT INTO [t29779] VALUES (29,'محاش النساء عليكم حرام','30','0','0');
INSERT INTO [t29779] VALUES (30,'شارب الخمر كعابد الوثن، شارب الخمر كعابد اللات والعزى','31','0','0');
INSERT INTO [t29779] VALUES (31,'كيف أنت إذا بقيت في حثالة من الناس، قد مرجت عهودهم، وأماناتهم الحديث','32','0','0');
INSERT INTO [t29779] VALUES (32,'أصلحوا مثاويكم , واجعلوا الرأس رأسين , وأخيفوا الهوام قبل أن تخيفكم','33','0','0');
INSERT INTO [t29779] VALUES (33,'أن نستقبل القبلة بفروجنا , أو نستنجي بأيماننا , أو نستنجي بعظم , أو برجيع','34','0','0');
INSERT INTO [t29779] VALUES (34,'عن العقيقة؟ فقال: عن الغلام شاتان مكافأتان , وعن الجارية شاة','35','0','0');
INSERT INTO [t29779] VALUES (35,'الرجل يلاعب أهله فيخرج منه المذي من غير ماء الحيوة؟ قال: يغسل فرجه ويتوضأ وضوءه','36','0','0');
INSERT INTO [t29779] VALUES (36,'فرض الزكاة في الذهب والفضة، والإبل والبقر والغنم، والحنطة والشعير، والزبيب','37','0','0');
INSERT INTO [t29779] VALUES (37,'بدأ بالطواف بالبيت، حين دخل المسجد قبل الصلاة','38','0','0');
INSERT INTO [t29779] VALUES (38,'لحوم الحمر الأهلية , فأكفأنا القدور','39','0','0');
INSERT INTO [t29779] VALUES (39,'عفوت لكم عن صدقة الخيل والرقيق، فأدوا ربع العشر','40','0','0');
INSERT INTO [t29779] VALUES (40,'الشهر هكذا وهكذا وهكذا.','41','0','0');
INSERT INTO [t29779] VALUES (41,'إذا ولي أحدكم أخاه، فليحسن كفنه','42','0','0');
INSERT INTO [t29779] VALUES (42,'إن أمه توفيت، أفينفعها إن تصدقت عنها؟ قال: نعم.','43','0','0');
INSERT INTO [t29779] VALUES (43,'إذا قامت الصلاة فلا صلاة إلا المكتوبة','44','0','0');
INSERT INTO [t29779] VALUES (44,'سبحان الله العظيم وبحمده غرست له نخلة في الجنة','45','0','0');
INSERT INTO [t29779] VALUES (45,'كان يصوم ثلاثة أيام غرة كل هلال، وقل ما كان يفطر يوم الجمعة','46','0','0');
INSERT INTO [t29779] VALUES (46,'تسمعون ويسمع ممن يسمع منكم','47','0','0');
INSERT INTO [t29779] VALUES (47,'اسمعوا من قول قريش ودعوا فعلهم.','48','0','0');
INSERT INTO [t29779] VALUES (48,'كنا نمسح على عهد رسول الله في الحضر يوما وليلة، وفي السفر ثلاثة أيام','49','0','0');
INSERT INTO [t29779] VALUES (49,'أما رأيت العارض الذي عرض لي؟ قلت: بلى , قال: ذلك ملك لم يهبط إلى الأرض قبل الساعة،','50','0','0');
INSERT INTO [t29779] VALUES (50,'إذا راح أحدكم إلى الجمعة فليغتسل.','51','0','0');
INSERT INTO [t29779] VALUES (51,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله , فمن قالها فقد عصم مني ماله ودمه، إلا','52','0','0');
INSERT INTO [t29779] VALUES (52,'الأنبياء كلهم كانت لهم عصي يتخصرون بها تواضعا لله , والمتخصرون يوم القيامة','53','0','0');
INSERT INTO [t29779] VALUES (53,'من أحب أن يحيى حياتي، ويموت موتي، ويتمسك بالقصبة الياقوت التي خلقها الله , وقال:','54','0','0');
INSERT INTO [t29779] VALUES (54,'قد تكفر النعمة، وتقطع الرحم، ولم ير كتواصل القلوب','55','0','0');
INSERT INTO [t29779] VALUES (55,'بعث سرية، فاستعمل عليهم رجلا يقال له سارية , فبينما عمر يخطب في يوم الجمعة , فقال: يا','56','0','0');
INSERT INTO [t29779] VALUES (56,'يصلي في النعلين','57','0','0');
INSERT INTO [t29779] VALUES (57,'أثقل شيء في الميزان خلق حسن','58','0','0');
INSERT INTO [t29779] VALUES (58,'ما عاب رسول الله صلى الله عليه وسلم طعاما قط','59','0','0');
INSERT INTO [t29779] VALUES (59,'عن الرجل يدنو من أهله، فيمذي؟ قال: يغسل ذكره ويتوضأ.','60','0','0');
INSERT INTO [t29779] VALUES (60,'ما أسكر كثيره فقليله حرام','61','0','0');
INSERT INTO [t29779] VALUES (61,'ثلاث ترفع عنهم الأقلام: المصاب حتى يعقل , والنائم حتى يستيقظ , والصغير حتى','62','0','0');
INSERT INTO [t29779] VALUES (62,'إذا قدم من سفره، فأبصر جدارات المدينة أوضع ناقته، وإن كان على دابة','63','0','0');
INSERT INTO [t29779] VALUES (63,'اسكب لي ماء.','64','0','0');
INSERT INTO [t29779] VALUES (64,'من كذب علي متعمدا فليتبوأ مقعده من النار','65','0','0');
INSERT INTO [t29779] VALUES (65,'إذا أردت أن تصدق بصدقة، فاجعلها عن أبويك يكن لهما أجرها، ولا ينقص من أجرها','66','0','0');
INSERT INTO [t29779] VALUES (66,'ما من رجل يتكلم بكلمة، أو كلمتين، أو ثلاث، أو أربع، أو خمس، مما فرض الله ورسوله،','67','0','0');
INSERT INTO [t29779] VALUES (67,'من لعب بالنرد فقد عصى الله ورسوله','68','0','0');
INSERT INTO [t29779] VALUES (68,'يكره عشر خصال: الواشرة، والنتف، والخاتم إلا لذي سلطان، ومكامعة الرجل الرجل، والمرأة','69','0','0');
INSERT INTO [t29779] VALUES (69,'من ابتاع طعاما، فلا يبعه حتى يستوفيه.','70','0','0');
INSERT INTO [t29779] VALUES (70,'يخفف ركعتي الفجر حتى أني لأتمارى، أقرأ فيها بأم القرآن أم لا','71','0','0');
INSERT INTO [t29779] VALUES (71,'من سره أن يقرأ القرآن رطبا كما أنزل فليقرأه بقراءة ابن مسعود','72','0','0');
INSERT INTO [t29779] VALUES (72,'إني تارك فيكم الثقلين، كتاب الله حبل ممدود من السماء إلى الأرض , وعترتي أهل بيتي، وإنهما','73','0','0');
INSERT INTO [t29779] VALUES (73,'برأ الله هذه الجزيرة من الشرك، ولكن إنما أخاف عليهم الضلالة بالنجوم','74','0','0');
INSERT INTO [t29779] VALUES (74,'حزرنا، ونحن خلف رسول الله صلى الله عليه وسلم في صلاة الظهر قراءته بنحو من سورة الم تنزيل،','75','0','0');
INSERT INTO [t29779] VALUES (75,'عن الرجل يصلي، فيسوي الحصا؟ قال: إن كان لا بد، فمرة واحدة','76','0','0');
INSERT INTO [t29779] VALUES (76,'لا تسبوا تبعا، فإنه قد أسلم','77','0','0');
INSERT INTO [t29779] VALUES (77,'رجلا زوج ابنته بكرا أو ثيبا، فأنكرت، فرد النبي صلى الله عليه وسلم','78','0','0');
INSERT INTO [t29779] VALUES (78,'كانوا يكرهون أن يسترضعوا أولادهم من المشركين، فسألوا النبي صلى الله عليه وسلم , فنزلت هذه','79','0','0');
INSERT INTO [t29779] VALUES (79,'قتل أبي يوم أحد وبلغني ذلك. فأقبلت وإذا هو بين يدي رسول الله صلى الله عليه وسلم مسجى ,','80','0','0');
INSERT INTO [t29779] VALUES (80,'إذا أتاكم كريم قوم فأكرموه','81','0','0');
INSERT INTO [t29779] VALUES (81,'نهى عن كل ذي ناب من السباع، وكل ذي مخلب من الطير','82','0','0');
INSERT INTO [t29779] VALUES (82,'أحب المؤمنين إلى الله من نصب في طاعة الله، ونصح لعباده، وكمل عقله','83','0','0');
INSERT INTO [t29779] VALUES (83,'ما كنا نرى على من لم ينزل غسلا , حتى قالت عائشة: فعل ذلك رسول الله صلى الله عليه وسلم،','84','0','0');
INSERT INTO [t29779] VALUES (84,'عن لحم الصيد يصيده الحلال أيأكله المحرم؟ فقال رسول الله صلى الله عليه وسلم:','85','0','0');
INSERT INTO [t29779] VALUES (85,'كتب إلى هرقل: سلام على من اتبع الهدى','86','0','0');
INSERT INTO [t29779] VALUES (86,'سألت ربي ثلاث خصال لأمتي , فأعطاني اثنتين , ومنعني واحدة قال: قلت: يا رب، لا تهلك أمتي','87','0','0');
INSERT INTO [t29779] VALUES (87,'يصلي صلاة الضحى في بيته، ولا يصليها في المسجد، مخافة أن يراه الناس فيتخذونها','88','0','0');
INSERT INTO [t29779] VALUES (88,'رجل على عهد رسول الله صلى الله عليه وسلم كثيرا ما يسأل الله الشهادة في عافية , قال: وكان','89','0','0');
INSERT INTO [t29779] VALUES (89,'لأغزون قريشا.','90','0','0');
INSERT INTO [t29779] VALUES (90,'خرج رسول الله صلى الله عليه وسلم، وهم يتذاكرون القدر , فقال: أبهذا أمرتم؟ إنكم قد اتخذتم','91','0','0');
INSERT INTO [t29779] VALUES (91,'الله عز وجل يربي لأحدكم اللقمة كما يربي أحدكم فصيله، حتى يجعلها له مثل','92','0','0');
INSERT INTO [t29779] VALUES (92,'إنما الصدقة عن ظهر غنى، واليد العليا خير من اليد السفلى، وابدأ بمن','93','0','0');
INSERT INTO [t29779] VALUES (93,'ليس الغنى عن كثرة العرض، إنما الغنى غنى النفس','94','0','0');
INSERT INTO [t29779] VALUES (94,'من فارق الروح الجسد وهو برئ من ثلاث دخل الجنة: الكبر , والدين ,','95','0','0');
INSERT INTO [t29779] VALUES (95,'إن الرفق لم يكن في شيء قط إلا زانه، ولم يكن الخرق في شيء إلا شانه','96','0','0');
INSERT INTO [t29779] VALUES (96,'عفوت لكم عن الخيل والرقيق','97','0','0');
INSERT INTO [t29779] VALUES (97,'من استعف أو من استغنى أغناه الله، ومن سألنا لم ندخر عنه شيئا وجدناه. فقلت في نفسي: أفلا','98','0','0');
INSERT INTO [t29779] VALUES (98,'ما من قوم يجتمعون في بيت من بيوت الله يتعلمون القرآن ويتدارسونه إلا حفتهم الملائكة،','99','0','0');
INSERT INTO [t29779] VALUES (99,'انظروا إلى من أسفل منكم؛ فإنه أجدر أن لا تزدروا نعمة الله','100','0','0');
INSERT INTO [t29779] VALUES (100,'الله لا يقبض العلم انتزاعا ينتزعه من صدور الرجال , ولكن يقبض العلماء , حتى إذا لم يبق','101','0','0');
INSERT INTO [t29779] VALUES (101,'أهل الجنة يأكلون ويشربون، ولا يتغوطون، ولا يبولون، ولا يتفلون، ولا يمتخطون، يلهمون','102','0','0');
INSERT INTO [t29779] VALUES (102,'أكثر عذاب القبر في البول','103','0','0');
INSERT INTO [t29779] VALUES (103,'ونحن بمكة , فقال: انشق القمر رسول الله صلى الله عليه وسلم: اللهم اشهد','104','0','0');
INSERT INTO [t29779] VALUES (104,'سمى المدينة طابة','105','0','0');
INSERT INTO [t29779] VALUES (105,'من ادعى أبا في الإسلام وهو غير أبيه فالجنة عليه حرام.','106','0','0');
INSERT INTO [t29779] VALUES (106,'لا تحاسدوا، ولا تباغضوا، وكونوا عباد الله إخوانا , ولا يحل لمسلم أن يهجر أخاه فوق ثلاثة','107','0','0');
INSERT INTO [t29779] VALUES (107,'صلى رسول الله صلى الله عليه وسلم في ثوب واحد متوشحا به','108','0','0');
INSERT INTO [t29779] VALUES (108,'توضأ ومسح على خفيه','109','0','0');
INSERT INTO [t29779] VALUES (109,'إنكم ستعرضون على سبي , فسبوني، وإن عرضتم على البراءة مني , فلا تتبرءوا مني، فإني على دين','110','0','0');
INSERT INTO [t29779] VALUES (110,'نزلت هذه الآية {إنما يريد الله ليذهب عنكم الرجس أهل البيت} [الأحزاب: ٣٣] ، في خمس: في','111','0','0');
INSERT INTO [t29779] VALUES (111,'أكان أصحاب النبي صلى الله عليه وسلم يتصافحون؟ قال: نعم، ويتعانقون','112','0','0');
INSERT INTO [t29779] VALUES (112,'اغتسل رسول الله صلى الله عليه وسلم من جنابة، فلما اغتسل فإذا لمعة في جسده لم يصبها الماء،','113','0','0');
INSERT INTO [t29779] VALUES (113,'ما من عبد يشاك بشوكة فما فوقها إلا رفعه الله بها درجة وحط عنه بها','114','0','0');
INSERT INTO [t29779] VALUES (114,'إذا التقى المسلمان بسيفيهما , فالقاتل والمقتول في النار','115','0','0');
INSERT INTO [t29779] VALUES (115,'لا صام من صام الأبد','116','0','0');
INSERT INTO [t29779] VALUES (116,'من أحبهما أحببته، ومن أبغضهما أبغضته , ومن أبغضته أبغضه الله , وأدخله نار جهنم، وله عذاب','117','0','0');
INSERT INTO [t29779] VALUES (117,'من الإيمان أن يحب الرجل أخاه لا يحبه إلا لله، من غير مال يعطيه، فذلك من','118','0','0');
INSERT INTO [t29779] VALUES (118,'لقد أنزلت هذه الآية التي ذكرت في سورة النساء {ومن يقتل مؤمنا متعمدا فجزاؤه جهنم} [النساء:','119','0','0');
INSERT INTO [t29779] VALUES (119,'لا يسترعي عبدا رعية قلت أو كثرت إلا سأله عنها أقام فيهم أمر الله أم أضاعه؟ حتى يسأله عن','120','0','0');
INSERT INTO [t29779] VALUES (120,'أي مسجد وضع للناس أولا؟ قال: المسجد الحرام. قلت: ثم أي؟ قال: مسجد بيت','121','0','0');
INSERT INTO [t29779] VALUES (121,'الأرواح جنود مجندة , فما كان في الله ائتلف , وما كان في غير الله اختلف','122','0','0');
INSERT INTO [t29779] VALUES (122,'الرجل يتوضأ فيمس ذكره قال: هو منه','123','0','0');
INSERT INTO [t29779] VALUES (123,'ليس شيء أغير من الله','124','0','0');
INSERT INTO [t29779] VALUES (124,'خير مال المرء مهرة مأمورة، أو سكة مأبورة','125','0','0');
INSERT INTO [t29779] VALUES (125,'خذوا من العمل ما تطيقون، فوالله لا يسأم الله حتى تسأموا','126','0','0');
INSERT INTO [t29779] VALUES (126,'نسمة المسلم طير تعلق في شجر الجنة، حتى يرجعه الله في جسده يوم يبعثه','127','0','0');
INSERT INTO [t29779] VALUES (127,'خرج رسول الله صلى الله عليه وسلم لحاجته فتلقيته بماء , فقال: من أمرك بهذا؟ فقلت: ما أمرني','128','0','0');
INSERT INTO [t29779] VALUES (128,'لأقتلن العمالقة في كتيبة.','129','0','0');
INSERT INTO [t29779] VALUES (129,'سيأتي على الناس زمان يأكلون فيه كلهم الربا.','130','0','0');
INSERT INTO [t29779] VALUES (130,'الشيب نور المؤمن , فمن شاء أن يطفئه , فليطفئه','131','0','0');
INSERT INTO [t29779] VALUES (131,'صحبت رسول الله صلى الله عليه وسلم , فكنت أعرف له حق النبوة وحق الولاية , ثم صحبت أبا بكر','132','0','0');
INSERT INTO [t29779] VALUES (132,'رب قني عذابك يوم تبعث عبادك أو يوم تجمع عبادك','133','0','0');
INSERT INTO [t29779] VALUES (133,'عيخيل إليه أنها ملأى , فيقول: أي رب، إنها ملأى , فيقال له: ادخل الجنة , فيقول: إنها ملأى','134','0','0');
INSERT INTO [t29779] VALUES (134,'عن المحاقلة والمزابنة، ورخص في العرية','135','0','0');
INSERT INTO [t29779] VALUES (135,'أول زمرة تدخل الجنة من أمتي سبعون ألفا ليس عليهم حساب ولا عذاب، صورة وجه كل رجل منهم على','136','0','0');
INSERT INTO [t29779] VALUES (136,'كان لا يرى أن ينزل بالأبطح , ويقول: إنما أقام به رسول الله صلى الله عليه وسلم على','137','0','0');
INSERT INTO [t29779] VALUES (137,'أتاني جبريل بالحمى والطاعون , فأمسكت الحمى بالمدينة , وأرسلت الطاعون إلى الشام , فالطاعون','138','0','0');
INSERT INTO [t29779] VALUES (138,'ائتزروا , وارتدوا , وانتعلوا , وألقوا الخفاف , وألقوا السراويلات , وألقوا الركب , وأنزوا','139','0','0');
INSERT INTO [t29779] VALUES (139,'أول زمرة من أمتي تدخل الجنة على ضوء القمر ليلة البدر , ثم الذين يلونهم على أشد كوكب في','140','0','0');
INSERT INTO [t29779] VALUES (140,'نحن الآخرون السابقون يوم القيامة , بيد أنهم أوتوا الكتاب قبلنا وأوتيناه من بعدهم , وهذا','141','0','0');
INSERT INTO [t29779] VALUES (141,'لا تقوم الساعة حتى يقبض العلم , ويظهر الجهل , وتظهر الفتن , ويفيض المال , ويكثر','142','0','0');
INSERT INTO [t29779] VALUES (142,'تكثر الصواعق عند اقتراب الساعة حتى يأتي الرجل القوم، فيقول: من صعق منكم الغداة؟ فيقولون:','143','0','0');
INSERT INTO [t29779] VALUES (143,'عن الضب؟ فقال: لا آكله ولا أحرمه','144','0','0');
INSERT INTO [t29779] VALUES (144,'كفر بالله من ادعى إلى نسب لا يعرف , وكفر بالله تبرؤ من نسب وإن دق','145','0','0');
INSERT INTO [t29779] VALUES (145,'لما أسري بي انتهى بي جبريل فأتى على البراق إلى السماء السادسة , فاستفتح , فقالوا: من هذا','146','0','0');
INSERT INTO [t29779] VALUES (146,'أتاني جبريل فقال: ارفع صوتك بالإهلال فإنه شعار الحج','147','0','0');
INSERT INTO [t29779] VALUES (147,'يسلم الراكب على الماشي , والماشي على القاعد , والقليل على الكثير','148','0','0');
INSERT INTO [t29779] VALUES (148,'من أتى عرافا أو كاهنا فصدقه بما يقول فقد برئ مما أنزل على محمد صلى الله عليه','149','0','0');
INSERT INTO [t29779] VALUES (149,'من كذب علي متعمدا فليتبوأ مقعده من النار','150','0','0');
