INSERT INTO [t29779] VALUES (151,'عن قول الله عز وجل: {وتأتون في ناديكم المنكر} [العنكبوت: ٢٩] , فقال رسول الله صلى الله','152','0','0');
INSERT INTO [t29779] VALUES (152,'سرنا مع رسول الله صلى الله عليه وسلم في غزوة , أو قال: سرية فلما كان من آخر السحر عرسنا،','153','0','0');
INSERT INTO [t29779] VALUES (153,'يبدأ فيغسل يديه من الجنابة , ثم يتوضأ وضوءه للصلاة , ثم يخلل شعره بالماء , ثم يفيض عليه','154','0','0');
INSERT INTO [t29779] VALUES (154,'إني أستحاض , فلا أطهر , أفأدع الصلاة؟ قال: إن ذلك ليس بالحيض , ولكنه عرق , فإذا أقبلت','155','0','0');
INSERT INTO [t29779] VALUES (155,'إذا حضرت العشاء، وأراد الرجل الخلاء فابدأ بالخلاء','156','0','0');
INSERT INTO [t29779] VALUES (156,'خير نسائها مريم بنت عمران , وخير نسائها خديجة','157','0','0');
INSERT INTO [t29779] VALUES (157,'من كذب علي متعمدا فليتبوأ مقعده من النار','158','0','0');
INSERT INTO [t29779] VALUES (158,'إذا انصرف من الصلاة , قال: لا إله إلا الله وحده لا شريك له , له الملك وله الحمد , وهو على','159','0','0');
INSERT INTO [t29779] VALUES (159,'إذا شرب الخمر فاجلدوه , فإن عاد فاجلدوه، فإن عاد فاجلدوه، فإن عاد','160','0','0');
