INSERT INTO [t29779] VALUES (168,'انطلق إلى أهل الله، انههم عن أربع خصال: عن بيع ما لم يقبضوا , وعن ربح ما لم يضمنوا , وعن','169','0','0');
INSERT INTO [t29779] VALUES (169,'صلى صلاة الصبح ذات يوم , ثم أقبل علينا بوجهه , فقال: أشاهد فلان؟ قالوا: لا. قال: فلان؟','170','0','0');
