INSERT INTO [t29779] VALUES (204,'عن القزع','205','0','0');
INSERT INTO [t29779] VALUES (205,'ما من مؤمن إلا يأتي يوم القيامة بيهودي أو نصراني، فيقول: هذا فدائي من','206','0','0');
