INSERT INTO [t29779] VALUES (216,'نحن الآخرون السابقون يوم القيامة , بيد أنهم أوتوا الكتاب من قبلنا , وأوتيناه من بعدهم ,','217','0','0');
INSERT INTO [t29779] VALUES (217,'من نسي صلاة أو نام عنها فليصلها إذا ذكرها، إن الله يقول: {وأقم الصلاة لذكري} [طه:','218','0','0');
