INSERT INTO [t29780] VALUES (1,'كان عمر آدم ألف سنة فجعل لداود أربعين سنة والأقلام رطبة تجري','2','0','0');
INSERT INTO [t29780] VALUES (2,'خلق الله الإنسان من ثلاثة: من طين لازب , وصلصال , وحمأ مسنون , فالطين اللازب اللازق الجيد','3','0','0');
INSERT INTO [t29780] VALUES (3,'الله تعالى خمر طينة آدم عليه السلام أربعين ليلة أو أربعين يوما , ثم ضرب بيديه ففرقهما.','4','0','0');
INSERT INTO [t29780] VALUES (4,'{من طين لازب} [الصافات: ١١] .','5','0','0');
INSERT INTO [t29780] VALUES (5,'إنما سمي آدم لأنه خلق من أديم الأرض كلها , من أحمرها وأسودها وأبيضها وسهلها وجبلها وسبخها','6','0','0');
INSERT INTO [t29780] VALUES (6,'إنما سمي الإنسان لأنه عهد إليه فنسي , وإنما سمي آدم لأنه خلق من أديم','7','0','0');
INSERT INTO [t29780] VALUES (7,'بعث رب العزة إبليس، فأمره أن يكبش من أديم الأرض من عذبها ومالحها، ففعل فخلق منه آدم، فمن','8','0','0');
INSERT INTO [t29780] VALUES (8,'إنما سمي آدم لأنه خلق من أديم الأرض','9','0','0');
INSERT INTO [t29780] VALUES (9,'خير يوم طلعت عليه الشمس يوم الجمعة , خلق الله آدم فيه , وفيه أدخل الجنة وفيه يعاد','10','0','0');
INSERT INTO [t29780] VALUES (10,'خير يوم طلعت فيه الشمس يوم الجمعة , فيه خلق آدم وفيه أسكن الجنة وفيه','11','0','0');
INSERT INTO [t29780] VALUES (11,'خير يوم طلعت فيه الشمس يوم الجمعة , فيها خلق آدم وفيها أدخل الجنة وفيها أخرج منها وفيها','12','0','0');
INSERT INTO [t29780] VALUES (12,'تدري ما يوم الجمعة؟ قال: قلت: الله ورسوله أعلم.','13','0','0');
INSERT INTO [t29780] VALUES (13,'من أفضل أيامكم يوم الجمعة فيه خلق الله آدم , وفيه النفخة , وفيه الصعقة','14','0','0');
INSERT INTO [t29780] VALUES (14,'ما أسكن آدم الجنة إلا ما بين صلاة العصر إلى غروب الشمس','15','0','0');
INSERT INTO [t29780] VALUES (15,'ما مكث آدم في الجنة إلا ما بين الظهر إلى العصر','16','0','0');
INSERT INTO [t29780] VALUES (16,'خلق الله آدم عليه السلام لسبع ساعات من يوم الجمعة، بمحلوف عبد الله بن سلام فما جاءت إحدى','17','0','0');
INSERT INTO [t29780] VALUES (17,'إن أباكم آدم عليه السلام كان رجلا طوالا مثل النخلة السحوق , كثير الشعر يواري العورة ,','18','0','0');
INSERT INTO [t29780] VALUES (18,'كان آدم صلوات الله عليه رجلا طويلا كثير شعر الصدر كأنه نخلة جوفاء , فلما أصاب الخطيئة سقط','19','0','0');
INSERT INTO [t29780] VALUES (19,'لما ثقل آدم أمر بنيه أن يجيئوه من ثمار الجنة فجاءوا , فلقيتهم الملائكة , فقالت: ارجعوا','20','0','0');
INSERT INTO [t29780] VALUES (20,'الملائكة صلت على آدم عليه السلام وكبرت عليه أربعا , وقالوا: هذه سنتكم يا بني آدم حدثنا','21','0','0');
INSERT INTO [t29780] VALUES (21,'كان لآدم عليه السلام بنون؛ ود , وسواع , ويغوث , ويعوق , ونسر , فكان أكبرهم يغوث , فقال','22','0','0');
INSERT INTO [t29780] VALUES (22,'الله لما أخرج آدم من الجنة زوده من ثمار الجنة وعلمه صنعة كل شيء , فثماركم من ثمار الجنة','23','0','0');
INSERT INTO [t29780] VALUES (23,'كان لباس آدم عليه السلام وحواء كالظفر , فلما أكلا الشجرة لم يبق منه شيء إلا مثل الظفر','24','0','0');
INSERT INTO [t29780] VALUES (24,'أهبط الله آدم من الجنة وهو يأكل منها رغدا حيث شاء إلى غير طعام، فعلمه العمل كيف يعمله ,','25','0','0');
INSERT INTO [t29780] VALUES (25,'لم يحزن حزن آدم أحد , بكى أربعين عاما وسجد أربعين عاما حتى قبل الله','26','0','0');
INSERT INTO [t29780] VALUES (26,'{فتلقى آدم من ربه كلمات} [البقرة: ٣٧] .','27','0','0');
INSERT INTO [t29780] VALUES (27,'لما خلق الله آدم نفخ الروح في رأسه قبل جسده فعطس , فقال: الحمد لله لرب خلقني. فقال له','28','0','0');
INSERT INTO [t29780] VALUES (28,'أي الأنبياء كان أولا؟ قال: آدم.','29','0','0');
INSERT INTO [t29780] VALUES (29,'كم كانوا الأنبياء؟ قال: كانوا مائة ألف وعشرين ألفا. قال: قلت: يا رسول الله وكلهم كان','30','0','0');
INSERT INTO [t29780] VALUES (30,'{فلا يخرجنكما من الجنة} [طه: ١١٧] . قال: شقاؤه عمله في الدنيا بعد نعمة كانت في','31','0','0');
INSERT INTO [t29780] VALUES (31,'لما صور الله عز وجل آدم تركه ما شاء أن يتركه , فجعل إبليس يطيف به فلما رآه أجوف عرف أنه','32','0','0');
INSERT INTO [t29780] VALUES (32,'الكعبة خلقت قبل الأرض بألفي سنة وهي من الأرض. قال: إنها كانت حشفة على الماء، يعني زبدا','33','0','0');
INSERT INTO [t29780] VALUES (33,'متى كتبت نبيا؟ قال: وآدم بين الروح والجسد','34','0','0');
INSERT INTO [t29780] VALUES (34,'كتبت نبيا وآدم بين الروح والجسد','35','0','0');
INSERT INTO [t29780] VALUES (35,'متى وجبت لك النبوة؟ قال: من خلق آدم ونفخ الروح فيه','36','0','0');
INSERT INTO [t29780] VALUES (36,'إني لعبد الله وخاتم النبيين وإن آدم لمنجدل في طينته','37','0','0');
INSERT INTO [t29780] VALUES (37,'{وتقلبك في الساجدين} [الشعراء: ٢١٩] .','38','0','0');
INSERT INTO [t29780] VALUES (38,'لم يزل الله عز وجل يتقدم في النبي صلى الله عليه وسلم إلى آدم فمن بعده , ولم تزل الأمم','39','0','0');
INSERT INTO [t29780] VALUES (39,'لنجد بعث النبي صلى الله عليه وسلم في سطرين من كتاب الله عز وجل , نجده في سطر محمد رسول','40','0','0');
INSERT INTO [t29780] VALUES (40,'أن بني إسرائيل لما أصابهم ما أصابهم من ظهور بختنصر عليهم وفرقتهم وذلتهم , تفرقوا فكانوا','41','0','0');
INSERT INTO [t29780] VALUES (41,'أخبرنا عن نفسك.','42','0','0');
INSERT INTO [t29780] VALUES (42,'{وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح} [الأحزاب: ٧] .','43','0','0');
INSERT INTO [t29780] VALUES (43,'هل تجدون محمدا في كتبكم؟ قالوا: نعم','44','0','0');
INSERT INTO [t29780] VALUES (44,'وجد في البيت كتاب في صخر منقور في الهرمة الأولى , فدعا رجلا فقرأه فإذا فيه: عبدي المتحبب','45','0','0');
INSERT INTO [t29780] VALUES (45,'كنا مع النبي صلى الله عليه وسلم جلوسا على جبل من جبال تهامة إذ أقبل شيخ بيده عصا فسلم ,','46','0','0');
INSERT INTO [t29780] VALUES (46,'ولد النبي صلى الله عليه وسلم يوم الفيل','47','0','0');
INSERT INTO [t29780] VALUES (47,'ولدت أنا ورسول الله صلى الله عليه وسلم عام الفيل','48','0','0');
INSERT INTO [t29780] VALUES (48,'أنت أكبر أو رسول الله صلى الله عليه وسلم؟ فقال: رسول الله صلى الله عليه وسلم أكبر مني','49','0','0');
INSERT INTO [t29780] VALUES (49,'خرج عبد الله بن عبد المطلب حتى أتى عيضا فوقف في أصل صومعته , ثم نادى: يا عيضا. فناداه: من','50','0','0');
