INSERT INTO [t29794] VALUES (1,'يخرج الدجال فيتوجه قبله رجل من المسلمين فتلقاه مسالح الدجال , فيقولون: أين تريد؟ فيقول:','2','0','0');
INSERT INTO [t29794] VALUES (2,'شاور يعني النبي صلى الله عليه وسلم الناس يوم بدر , فتكلم أبو بكر فضاف عنه , وتكلم عمر','3','0','0');
INSERT INTO [t29794] VALUES (3,'صلاة القاعد على النصف من صلاة القائم.','4','0','0');
INSERT INTO [t29794] VALUES (4,'من ظلم قيد شبر من الأرض طوقه من سبع أرضين يوم القيامة.','5','0','0');
INSERT INTO [t29794] VALUES (5,'فما بال أقوام إذا غزونا تخلف أحدهم في عيالنا بنبيب كنبيب التيس , أما إن علي أن أوتي بأحد','6','0','0');
INSERT INTO [t29794] VALUES (6,'رجلا ممن كان قبلكم بينما هو يمشي في حلة إذ خسف الله به الأرض فهو يتجلجل فيها حتى تقوم','7','0','0');
INSERT INTO [t29794] VALUES (7,'ليفرن الناس من الدجال في الجبال. قالت أم شريك: فأين العرب يومئذ؟ قال: هم','8','0','0');
INSERT INTO [t29794] VALUES (8,'طعام الواحد يكفي الاثنين , وطعام الاثنين يكفي الأربعة , وطعام الأربعة يكفي','9','0','0');
INSERT INTO [t29794] VALUES (9,'تفئ الأرض أمثال الأساطين من الذهب والفضة , فيقوم السارق فيقول: لهذا قطعت , ويقول القاطع','10','0','0');
INSERT INTO [t29794] VALUES (10,'حال الشيطان بيني وبين صلاتي وقراءتي، فقال له رسول الله صلى الله عليه وسلم: ذاك شيطان يقال','11','0','0');
INSERT INTO [t29794] VALUES (11,'الخمر من هاتين الشجرتين؛ من النخلة والكرمة.','12','0','0');
INSERT INTO [t29794] VALUES (12,'ما أعلم النبي صلى الله عليه وسلم ترك أحدا أعلم بكتاب الله من هذا','13','0','0');
INSERT INTO [t29794] VALUES (13,'من أراد بها سوءا أذابه الله كما يذوب الملح في الماء.','14','0','0');
INSERT INTO [t29794] VALUES (14,'إذا قام أحدكم من الليل فاستعجم القرآن على لسانه فلم يدر ما يقول فلينصرف','15','0','0');
INSERT INTO [t29794] VALUES (15,'طعام الواحد يكفي الاثنين , وطعام الاثنين يكفي الأربعة , وطعام الأربعة يكفي الثمانية إذا','16','0','0');
INSERT INTO [t29794] VALUES (16,'اقرءوا القرآن؛ فإنه يأتي يوم القيامة شفيعا لصاحبه , اقرءوا الزهراوين: سورة البقرة , وسورة','17','0','0');
INSERT INTO [t29794] VALUES (17,'يقرأ في صلاة الصبح يوم الجمعة الم {١} تنزيل السجدة وهل أتى على','18','0','0');
INSERT INTO [t29794] VALUES (18,'زوجني ابنتك. قال: نعم ونعمة عين. قال: إني لست لنفسي أريدها. قال: فلمن؟ قال: لجليبيب. قال:','19','0','0');
INSERT INTO [t29794] VALUES (19,'إذا طبخت فأكثر المرقة , وتعاهد جيرانك أو أقسم في جيرانك.','20','0','0');
INSERT INTO [t29794] VALUES (20,'أسمع وأطيع ولو لعبد مجدع الأطرف , وإذا صنعت مرقة أن أكثر ماءها ثم أنظر إلى أهل بيت قريب','21','0','0');
INSERT INTO [t29794] VALUES (21,'الرجل يعمل عمل الصالح يحمده الناس ويثنون عليه. قال: ذلك عاجل بشرى','22','0','0');
INSERT INTO [t29794] VALUES (22,'صليت مع رسول الله صلى الله عليه وسلم الأولى , ثم خرج إلى أهله وخرجت معه , واستقبله ولدان','23','0','0');
INSERT INTO [t29794] VALUES (23,'ما من مسلم يغرس غرسا إلا كان ما أكل منه صدقة , وما يشرب منه صدقة , وما أكل منه الطير صدقة','24','0','0');
INSERT INTO [t29794] VALUES (24,'رجلا زار أخا له في قرية أخرى , فأرصد الله تعالى على مدرجته ملكا , فلما أتى الملك، قال:','25','0','0');
INSERT INTO [t29794] VALUES (25,'خلق أحدكم يجمع في بطن أمه خمسة وأربعين يوما , ثم يكون علقة مثل ذلك , ثم يكون مضغة مثل ذلك','26','0','0');
INSERT INTO [t29794] VALUES (26,'تقع النطفة في الرحم أربعين ليلة , ثم يتصور عليها الملك حسبته قال: الذي يخلقها فيقول: يا','27','0','0');
INSERT INTO [t29794] VALUES (27,'سألت ربي عز وجل ثلاثا فأعطاني اثنين ومنعني واحدة , سألت ربي أن لا يهلك أمتي بالغرق ولا','28','0','0');
INSERT INTO [t29794] VALUES (28,'رجل غريب جاء يسأل عن دينه لا يدري ما دينه. قال: فجاء رسول الله صلى الله عليه وسلم وترك','29','0','0');
INSERT INTO [t29794] VALUES (29,'لا حلف في الإسلام , وأيما حلف كان في الجاهلية لم يزده الإسلام إلا شدة.','30','0','0');
INSERT INTO [t29794] VALUES (30,'نهى أن يأكل الرجل بشماله , وأن يمشي في نعل واحد , وأن يشتمل الصماء , وأن يحتبي في ثوب','31','0','0');
INSERT INTO [t29794] VALUES (31,'لا يزال هذا الدين قائما تقاتل عليه عصابة من المسلمين حتى تقوم الساعة.','32','0','0');
INSERT INTO [t29794] VALUES (32,'بين يدي الساعة كذابين.','33','0','0');
INSERT INTO [t29794] VALUES (33,'من حلف على يمين فرأى ما هو أنقى منها فلينظر ما هو أنقى فليأخذ به , وليكفر يمينه ما','34','0','0');
INSERT INTO [t29794] VALUES (34,'وعليه ثوبان معصفران , فقال: هذه ثياب الكفار فلا تلبسها.','35','0','0');
INSERT INTO [t29794] VALUES (35,'{وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل} [الأنفال: ٦٠] . ألا إن القوة الرمي , ألا إن','36','0','0');
INSERT INTO [t29794] VALUES (36,'الملك، قال لي: إن أمتك لا يزالون يتساءلون فيما بينهم حتى يقولوا: هذا الله عز وجل خلق','37','0','0');
INSERT INTO [t29794] VALUES (37,'أرواح الشهداء، فقيل: جعلت في أجواف طير خضر تأوي إلى قناديل تحت العرش , تسرح من الجنة حيث','38','0','0');
INSERT INTO [t29794] VALUES (38,'إذا أوى إلى فراشه , قال: الحمد لله الذي أطعمنا وسقانا وكفانا وآوانا , فكم ممن لا كافي له','39','0','0');
INSERT INTO [t29794] VALUES (39,'كنت لأوتى الإناء وأنا حائض فأشرب منه , ثم يأخذه النبي صلى الله عليه وسلم فيضع فاه على','40','0','0');
INSERT INTO [t29794] VALUES (40,'{ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض} [البقرة: ٢٢٢] حتى فرغ من الآية ,','41','0','0');
INSERT INTO [t29794] VALUES (41,'يفتتح الصلاة بالتكبير والقراءة ب {الحمد لله رب العالمين} [الفاتحة: ٢] , وكان إذا ركع لم','42','0','0');
INSERT INTO [t29794] VALUES (42,'فصل ما بين صيامنا وصيام أهل الكتاب أكلة السحر.','43','0','0');
INSERT INTO [t29794] VALUES (43,'لا تبدءوا اليهود والنصارى بالسلام , وإذا لقيتموهم في طريق فاضطروهم إلى','44','0','0');
INSERT INTO [t29794] VALUES (44,'التختم بالذهب , وعن لباس القسي , وعن القراءة في الركوع والسجود , وعن لباس','45','0','0');
INSERT INTO [t29794] VALUES (45,'العين حق , لو كان شيء سابق القدر سبقته العين فإذا استغسلتم فاغسلوا.','46','0','0');
INSERT INTO [t29794] VALUES (46,'إذا رأيتم المداحين فاحثوا في وجوههم التراب.','47','0','0');
INSERT INTO [t29794] VALUES (47,'خرجنا مع رسول الله صلى الله عليه وسلم حين فتح مكة لسبع عشرة أو تسع عشرة خلت من رمضان ,','48','0','0');
INSERT INTO [t29794] VALUES (48,'ثلاث أعطنيهن. قال: نعم. قال: عندي أحسن نساء العرب وأجملهن أم حبيبة بنت أبي سفيان أزوجكها.','49','0','0');
INSERT INTO [t29794] VALUES (49,'من دل على خير فله مثل أجر فاعله.','50','0','0');
INSERT INTO [t29794] VALUES (50,'الشمس والقمر آيتان من آيات الله عز وجل , وإنهما لا ينكسفان بموت بشر , فإذا رأيتم شيئا من','51','0','0');
INSERT INTO [t29794] VALUES (51,'ائتموا بأئمتكم إن صلى قائما فصلوا قياما , وإن صلى قاعدا فصلوا قعودا.','52','0','0');
INSERT INTO [t29794] VALUES (52,'يخلط البسر والزبيب , والبسر والتمر , وقال: انبذوا كل واحد منها على حدة. رواه عن زهير،','53','0','0');
INSERT INTO [t29794] VALUES (53,'من حمل علينا السلاح فليس منا.','54','0','0');
INSERT INTO [t29794] VALUES (54,'لا ينكح المحرم ولا يخطب ولا ينكح','55','0','0');
INSERT INTO [t29794] VALUES (55,'لا ينكح المحرم ولا ينكح ولا يخطب.','56','0','0');
INSERT INTO [t29794] VALUES (56,'امسح بيمينك سبع مرات , وقل: أعوذ بعزة الله وقدرته من شر ما أجد.','57','0','0');
INSERT INTO [t29794] VALUES (57,'من نفس عن مسلم كربة من كرب الدنيا , نفس الله عنه كربة من كرب يوم القيامة , ومن ستر مسلما','58','0','0');
INSERT INTO [t29794] VALUES (58,'ما تجدون في كتابكم حد الزاني؟ فقالوا: نجد التحميم والجلد. فناشدهم عن ذلك , فقالوا: نجد','59','0','0');
INSERT INTO [t29794] VALUES (59,'استقيموا ولن تحصوا , واعلموا أن خير أعمالكم الصلاة , ولا يحافظ على الوضوء إلا','60','0','0');
INSERT INTO [t29794] VALUES (60,'حافظوا على الصلوات وصلاة العصر. قال: فقرأناها على عهد رسول الله صلى الله عليه وسلم ما شاء','61','0','0');
INSERT INTO [t29794] VALUES (61,'عليك السمع والطاعة في عسرك ويسرك ومنشطك ومكرهك وأثرة عليك. وقال قتيبة: الطاعة ولم يقل','62','0','0');
INSERT INTO [t29794] VALUES (62,'رأى عليه ثوبين معصفرين , قال: هذه ثياب الكفار فلا تلبسهما.','63','0','0');
INSERT INTO [t29794] VALUES (63,'علي ثوبين معصفرين , فقال: أمك أمرتك بهذا. قال: قلت: أغسلهما؟ قال: بل','64','0','0');
INSERT INTO [t29794] VALUES (64,'أراك ضعيفا , وإني أحب لك ما أحب لنفسي , لا تأمرن على اثنين , ولا تولين مال','65','0','0');
INSERT INTO [t29794] VALUES (65,'أيتكن خرجت إلى المسجد فلا تقربن طيبا.','66','0','0');
