INSERT INTO [t297] VALUES (2,'صور من النسخ الخطية','5','1','0');
INSERT INTO [t297] VALUES (3,'مقدمة المصنف','9','0','0');
INSERT INTO [t297] VALUES (4,'المسألة الأولى: في رجل مسبوق اقتدى بمسبوق آخر، فهل يصح اقتداؤه به أم لا؟','10','0','0');
INSERT INTO [t297] VALUES (5,'الثانية: في رجل تزوج امرأة حرة وشرط أنه حر فظهر عبدا فهل يصح نكاحه أم لا؟','11','0','0');
INSERT INTO [t297] VALUES (6,'الثالثة: رجل حر تزوج امرأة وشرط أنها حرة فظهرت أمة، وهو ممن يحل له نكاح الأمة فهل له الخيار أم لا؟','11','0','0');
INSERT INTO [t297] VALUES (7,'الرابعة: إذا أحبلها والحالة هذه فوضعت ولدا وغرمه السيد قيمة الولد هل يرجع بها على من غره أم لا؟','11','0','0');
INSERT INTO [t297] VALUES (8,'الخامسة: مهر المثل إذا غرمه وفسخ النكاح هل يرجع به على من غره؟','12','0','0');
INSERT INTO [t297] VALUES (9,'السادسة: رجل قال لزوجته: إن أبرأتني من صداقك علي وهو خمسة دنانير فأنت طالق طلقة. فقالت: أبرأتك منه. فهل يقع طلاق أم لا؟ وإذا وقع فهل يقع بائنا أم رجعيا؟','12','0','0');
INSERT INTO [t297] VALUES (10,'السابعة: في رجل قال لزوجته: إن أعطيتني ألفا فأنت طالق. فوضعته بين يديه، فهل يقع طلاق أم لا؟ وإذا وقع فهل يقع بائنا أم لا؟','12','0','0');
INSERT INTO [t297] VALUES (11,'الثامنة: قال لزوجته: إن أقبضتني ألفا فأنت طالق. فوضعته بين يديه فهل يقع عليه طلاق أم لا؟','13','0','0');
INSERT INTO [t297] VALUES (12,'التاسعة: في امرأة قالت لزوجها: إن طلقتني فقد أبرأتك من صداقي عليك. أو: فأنت برئ منه. وقدره عشرة دنانير. فقال لها: طلقتك على ذلك. فهل يقع الطلاق بائنا أم لا؟','13','0','0');
INSERT INTO [t297] VALUES (13,'العاشرة: رجل تزوج امرأة حاملا من الزنا، فهل يصح نكاحه أم لا؟ وإذا صح فهل له وطئها قبل الوضع أم لا؟','13','0','0');
INSERT INTO [t297] VALUES (14,'الحادية عشرة: إذا طلقها الزوج والحالة هذه قبل وضعها، وهي ممن تحيض على الحمل فبماذا تعتد؟','13','0','0');
INSERT INTO [t297] VALUES (15,'الثانية عشرة: رجل وطئ أمة يظنها زوجته الحرة فبما تعتد منه؟','14','0','0');
INSERT INTO [t297] VALUES (16,'الثالثة عشرة: لو وطئ أمة يظنها زوجته الأمة فهل تعتد بقرء أو بقرأين؟','14','0','0');
INSERT INTO [t297] VALUES (17,'الرابعة عشرة: رجل طلق زوجته طلاقا رجعيا ثم راجعها ثم طلقها قبل الوطء فهل تبني على العدة أم تستأنف؟','14','0','0');
INSERT INTO [t297] VALUES (18,'الخامسة عشرة: لو طلقها طلاقا رجعيا ثم خالعها فهل تبني على العدة أم تستأنف عدة؟','14','0','0');
INSERT INTO [t297] VALUES (19,'السادسة عشرة: لو طلقها طلاقا رجعيا ثم مات عنها في العدة فهل تبني أم تستأنف عدة وفاة إذا لم تكن حاملا؟','14','0','0');
INSERT INTO [t297] VALUES (20,'السابعة عشرة: إذا تزوج أمة ثم طلقها ثلاثا فهل له نكاحها بملك اليمين قبل أن تنكح زوجا غيره أم لا؟','15','0','0');
INSERT INTO [t297] VALUES (21,'الثامنة عشرة: إذا تزوج أمة ثم طلقها طلاقا رجعيا ثم عتقت في العدة فهل تعتد عدة حرة أو أمة؟','15','0','0');
INSERT INTO [t297] VALUES (22,'التاسعة عشرة: المكاتبة من يزوجها؟','15','0','0');
INSERT INTO [t297] VALUES (23,'المسألة العشرون: المبعضة من يزوجها؟','15','0','0');
INSERT INTO [t297] VALUES (24,'الحادية والعشرون: الموقوفة من يزوجها؟','16','0','0');
INSERT INTO [t297] VALUES (25,'الثانية والعشرون: أمة الصغيرة من يزوجها إذا لم يكن لها أب ولا جد؟','16','0','0');
INSERT INTO [t297] VALUES (26,'الثالثة والعشرون: أمة الصغيرة الثيب من يزوجها إذا لم يكن أب ولا جد؟','17','0','0');
INSERT INTO [t297] VALUES (27,'الرابعة والعشرون: رجل قال لزوجته: إن حضت فأنت طالق. فقالت: حضت. فكذبها فأحضرت بينة تشهد بذلك فهل يقع الطلاق أم لا؟ وإذا لم يقع الطلاق فكيف التوصل إلى وقوعه؟','17','0','0');
INSERT INTO [t297] VALUES (28,'الخامسة والعشرون: شهادة النسوة في الوقف هل يقبل أم لا؟','17','0','0');
INSERT INTO [t297] VALUES (29,'السادسة والعشرون: شهادة النسوة أيضا على رجل أنه أوصى لمرتد بمال هل تقبل أم لا؟','18','0','0');
INSERT INTO [t297] VALUES (30,'السابعة والعشرون: شهادة النسوة على السارق أنه سرق مال فلان من حرز، هل يثبت بها المال ولا يثبت القطع أم لا؟','18','0','0');
INSERT INTO [t297] VALUES (31,'الثامنة والعشرون: الوصي على المال هل يجوز له أن يزوج ما يعد مالا أم لا؟','18','0','0');
INSERT INTO [t297] VALUES (32,'التاسعة والعشرون: امرأة حضرت ولها جد معتقها وأخو معتقها فمن يزوجها منهما؟','19','0','0');
INSERT INTO [t297] VALUES (33,'المسألة الثلاثون: الأعمى هل يجوز أن يكون وليا في النكاح أم لا؟','19','0','0');
INSERT INTO [t297] VALUES (34,'الحادية والثلاثون: الأعمى أيضا هل يجوز أن يكون وكيلا في البيع والشراء أم لا؟','19','0','0');
INSERT INTO [t297] VALUES (35,'الثانية والثلاثون: في الأعمى أيضا هل يجوز أن يكون وصيا أم لا؟','20','0','0');
INSERT INTO [t297] VALUES (36,'الثالثة والثلاثون: في العمياء هل يجوز أن تكون حاضنة أم لا؟','20','0','0');
INSERT INTO [t297] VALUES (37,'الرابعة والثلاثون: إذا أرادت الأم إرضاع الولد بأجرة، ووجد الأب متبرعة، فهل ينزع من الأم ويسلم إلى المتبرعة؟','21','0','0');
INSERT INTO [t297] VALUES (38,'الخامسة والثلاثون: رجل قال لزوجته: إن دخلت الدار فأنت طالق. ثم قال: دخلت، فكذبها فهل القول قوله أو قولها؟، وإذا شهد نسوة أنها دخلت الدار، فهل يثبت ذلك أم لا؟ وإذا ثبت فهل يقع الطلاق أم لا؟','21','0','0');
INSERT INTO [t297] VALUES (39,'السادسة والثلاثون: إذا قال: إن دخلت دار عمك عمرو فأنت طالق. فدخلتها وقد صارت ملك زيد أو صارت حماما فهل يقع طلاق أم لا؟','21','0','0');
INSERT INTO [t297] VALUES (40,'السابعة والثلاثون: رجل استعار فرسا ليحمل عليها شيئا معلوما إلى مكان معلوم فتلفت الفرس بالاستعمال في القدر المأذون فيه فهل يضمنها المستعير أم لا؟','22','0','0');
INSERT INTO [t297] VALUES (41,'الثامنة والثلاثون: في رجل أسلم إلى آخر فرسا في شيئ معلوم إلى أجل معلوم فحملت الفرس المذكورة عند المسلم إليه ونتجت أولادا ثم انقطع المسلم فيه عند المحل فرجع المسلم في رأس ماله فهل يرجع فيه بزوائده المنفصلة أم لا؟','22','0','0');
INSERT INTO [t297] VALUES (42,'التاسعة والثلاثون: إذا وجد لقطة فعرفها سنة ثم تملكها فنتجت عنده أولادا وجاء صاحبها بعد ذلك فهل يأخذها والزوائد المنفصلة أو يأخذها وحدها؟','22','0','0');
INSERT INTO [t297] VALUES (43,'المسألة الأربعون: إذا ملك ولده فرسا فنتجت عنده أولادا ثم رجع الوالد في العين المرهونة فهل يرجع فيها بزوائدها المنفصلة أو يرجع فيها وحده؟','23','0','0');
INSERT INTO [t297] VALUES (44,'الحادية والأربعون: إذا باع فرسا لشخص بثمن إلى أجل معلوم ثم نتجت الفرس أولادا قبل حلول الثمن ثم أفلس المشتري ورجع البائع في الفرس فهل يرجع في الزوائد المذكورة أم لا؟','23','0','0');
INSERT INTO [t297] VALUES (45,'الثانية والأربعون: إذا غصب رجل فرسا من شخص ثم نتجت عند الغاصب أولادا ثم أخذها المغصوب منه فهل يأخذها بزوائدها وفوائدها أم يأخذها وحدها؟ وإذا سمنت ثم هزلت فأخذها وهي هازلة فهل يضمن السمن أم لا؟','23','0','0');
INSERT INTO [t297] VALUES (46,'الثالثة والأربعون: رجل أوصى لمنافع عبد لرجل فنفقة العبد على من تكون؟','24','0','0');
INSERT INTO [t297] VALUES (47,'الرابعة والأربعون: إذا أراد مالك الرقبة بيعه من غير الموصي له فهل يصح البيع أم لا؟','24','0','0');
INSERT INTO [t297] VALUES (48,'الخامسة والأربعون: رجل وطئ امرأة مزوجة بشبهة فاعتدت منه فنفقة العدة منه تكون على زوجها أو على الواطئ بشبهة أو ليس لها عليهما نفقة؟','24','0','0');
INSERT INTO [t297] VALUES (49,'السادسة والأربعون: طلق زوجته وهي حامل منه فالنفقة تجب لها أو للحمل؟ وهل يجب لها كسوة تبعا للنفقة أم لا؟','24','0','0');
INSERT INTO [t297] VALUES (50,'السابعة والأربعون: إذا باع قصيلا بشرط القطع فأخلف خلفة بعد قطعه فالخلفة تكون للبائع أو للمشتري؟','25','0','0');
INSERT INTO [t297] VALUES (51,'الثامنة والأربعون: إذا اشترى شجرة فهبت ريح فقلعها ثم بعد ذلك أخلفت خلفة فالخلفة للبائع أو للمشتري؟','25','0','0');
INSERT INTO [t297] VALUES (52,'التاسعة والأربعون: إذا باع ضفة من حمام البرج وهو طائر فهل يصح البيع أم لا؟','25','0','0');
INSERT INTO [t297] VALUES (53,'المسألة الخمسون: إذا باع شيئا من النحل وهو غائب فهل يصح البيع أم لا؟','25','0','0');
INSERT INTO [t297] VALUES (54,'الحادية والخمسون: إذا باع العبد المغصوب ممن يقدر على انتزاعه حالة البيع ثم عجز (٨/و) المشتري عن انتزاعه بعد ذلك فهل له الخيار وأخذ الثمن من البائع أم لا؟','25','0','0');
INSERT INTO [t297] VALUES (55,'الثانية والخمسون: إذا أسلم في القز وفيه الدود فهل يصح المسلم فيه أم لا؟','26','0','0');
INSERT INTO [t297] VALUES (56,'الثالثة والخمسون: إذا أسلم في شئ يصح السلم فيه وأطلق فهل ينعقد حالا أم لا؟','26','0','0');
INSERT INTO [t297] VALUES (57,'الرابعة والخمسون: إذا أسلم بعيرا في العرين إلى أجل معلوم ثم انقطع المسلم فيه عند المحل واختار الفسخ ورأس المال بألف فهل يرجع بمثله أو بقيمته، وإذا رجع بقيمته فهل يرجع بقيمته يوم تسليمه في مجلس العقد أو بقيمة يوم التلف؟','26','0','0');
INSERT INTO [t297] VALUES (58,'الخامسة والخمسون: رجل اشترى فرسا من رجل إلى أجل معلوم ثم إنه باعها لرجل آخر بثمن مقبوض ثم اشتراها (٨/ظ) منه بثمن معلوم إلى أجل معلوم ثم حل الثمنان وأفلس المشتري فهل يتقدم البائع الأول بأخذ الفرس أو البائع الثاني؟','26','0','0');
INSERT INTO [t297] VALUES (59,'السادسة والخمسون: رجل أجر عبده مدة معلومة ثم أعتقه في أثناء المدة فهل تنفسخ الإجارة أم لا؟ وإذا لم تنفسخ فنفقة المعتق بقية المدة هل تكون على سيده أو في بيت المال؟','27','0','0');
INSERT INTO [t297] VALUES (60,'السابعة والخمسون: رجل مات وترك ثلاث أخوات متفرقات وجدا، فمن كم تصح المسألة ومن يرث من الورثة المذكورين؟','27','0','0');
INSERT INTO [t297] VALUES (61,'الثامنة والخمسون: في امرأة ماتت وتركت جدا من قبل الأب وأختا شقيقة وأخا لأب فكيف تقسم؟','28','0','0');
INSERT INTO [t297] VALUES (62,'التاسعة والخمسون: رجل مات وترك جدة من قبل أبيه وأختين شقيقتين وأخا لأب، فمن كم تقسم المسألة ومن يرث من الورثة؟','28','0','0');
INSERT INTO [t297] VALUES (63,'المسالة الستون: رجل مات وترك جدة لأبيه وأختين شقيقتين وأخا وأختا لأب فمن كم تصح المسألة؟','28','0','0');
INSERT INTO [t297] VALUES (64,'الحادية والستون: رجل مات وترك جدة من قبل أبيه وأختا شقيقة وأخا وأختا لأب فمن كم تصح المسألة؟','28','0','0');
INSERT INTO [t297] VALUES (65,'الثانية والستون: رجل مات وترك جدة من قبل أبيه وأختا شقيقة وأختين لأب فمن كم تصح المسألة؟','29','0','0');
INSERT INTO [t297] VALUES (66,'الثالثة والستون: رجل مات وترك جدة من قبل أبيه وزوجة وأختين شقيقتين وأما، فمن كم تصح المسألة؟','29','0','0');
INSERT INTO [t297] VALUES (67,'الرابعة والستون: امرأة ماتت وتركت زوجا وبنتين وأما وجدا من قبل أبيها وأخا وأختا شقيقين، فمن كم تصح المسالة؟','29','0','0');
INSERT INTO [t297] VALUES (68,'الخامسة والستون: امرأة ماتت وخلفت زوجا وجدة وأخوين لأم وأخا شقيقا، فكيف تقسم التركة؟','29','0','0');
INSERT INTO [t297] VALUES (69,'السادسة والستون: رجل (لم) يحج عنه حجة الإسلام من رأس ماله بمبلغ معلوم ثم توفي وعليه ديون تستغرق ماله، فهل تقدم حجة الإسلام على الديون، أو الديون عليها، أو تقسم بينهما؟','30','0','0');
INSERT INTO [t297] VALUES (70,'السابعة والستون: إذا تجمد عليه زكاة ودين آدمي ثم مات فهل تقدم الزكاة أو دين الآدمي؟','31','0','0');
INSERT INTO [t297] VALUES (71,'الثامنة والستون: إذا أبرأت المرأة منهن زوجها من صداقها وقدره عشرة دنانير في مرض الموت فهل تصح البراءة من الثلث؟ وإذا صحت فهل هي وصية لوارث أم لا؟','31','0','0');
INSERT INTO [t297] VALUES (72,'التاسعة والستون: أوصى رجل مسلم أن يصرف ثلث ماله على الفقراء والمساكين بمدينة كذا وأطلق فهل يدخل فقراء أهل الذمة ومساكينهم أم لا؟','31','0','0');
INSERT INTO [t297] VALUES (73,'المسالة السبعون: إذا وقف على أولاده النصاري هل يصح الوقف أم لا؟','32','0','0');
INSERT INTO [t297] VALUES (74,'الحادية والسبعون: إذا باع شريك وقف حصته فهل للموقوف عليه الشفعة أم لا؟','32','0','0');
INSERT INTO [t297] VALUES (75,'الثانية والسبعون: هل تجري الشفعة في الاشجار القائمة في الأرض المحتكرة أو الخراجية؟','32','0','0');
INSERT INTO [t297] VALUES (76,'الثالثة والسبعون: رجل له على أخر دينان، أحدهما برهن والآخر بغير رهن، فإذا أخذ الدينين أو بعضه وقال: دفعته عن دين الرهن. وقال رب الدين: بل عن الآخر. فالقول قول من؟','32','0','0');
INSERT INTO [t297] VALUES (77,'الرابعة والسبعون: رجل استعار عينا لرهنها على دين فهل يصير المعير ضامنا للمستعير في الدين أم لا؟ وإذا صار ضامنا فباع المرتهن العين المرهونة فهل يرجع المعير على المستعير بما بيعت به','32','0','0');
INSERT INTO [t297] VALUES (78,'الخامسة والسبعون: رجل قال لرجل: ألك زوجة؟ قال: لا. فهل يقع عليه طلاق أم لا؟','33','0','0');
INSERT INTO [t297] VALUES (79,'السادسة والسبعون: رجل قيل له: أطلقت زوجتك؟ فقال: نعم. فهل يقع عليه طلاق أم لا؟','33','0','0');
INSERT INTO [t297] VALUES (80,'السابعة والسبعون: رجل قال لآخر: أنا حالف بالطلاق أن فلانة لا تدخل داري. ولم يكن حلف، وإنما أخبر بذلك لتزدجر المرأة عن الدخول ثم إنها دخلت، فهل يقع عليه طلاق أم لا؟','33','0','0');
INSERT INTO [t297] VALUES (81,'الثامنة والسبعون: رجل قال: الطلاق يلزمني ما أدخل دار فلان. ثم إنه دخل فهل يقع عليه طلاق أم لا؟','34','0','0');
INSERT INTO [t297] VALUES (82,'التاسعة والسبعون: رجل قال: الطلاق لازم لي , فهل يقع عليه طلاق أم لا؟','34','0','0');
INSERT INTO [t297] VALUES (83,'المسالة الثمانون: رجل خطب امرأة فقالت له: لا أتزوجك حتى تطلق زوجاتك فقال لها: طلقت. ولم ينو واحدة منهن، فهل يقع عليه طلاق أم لا؟','34','0','0');
INSERT INTO [t297] VALUES (84,'الحادية والثمانون: رجل قال لزوجته كلما ولدت ولدا فأنت طالق , فولدت ولدين , فكم تطلق وبكم تعتد؟ وإن ولدت ثلاثة أولاد فكم تطلق ومتى تنقضي عدتها؟ وإن ولدت أربعة فكم تطلق وأي وقت تنقضي عدتها؟','35','0','0');
INSERT INTO [t297] VALUES (85,'الثانية والثمانون: لو قال: إن ولدت ذكرا فأنت طالق طلقة، أو ولدت أنثى فأنت طالق طلقتين. فولدتهما فكم تطلق؟ وكيف التفصيل؟','35','0','0');
INSERT INTO [t297] VALUES (86,'الثالثة والثمانون: رجل قال لزوجته: كلما حلفت بطلاقك فأنت طالق. فهل يقع عليه طلاق في الحال أم لا؟','37','0','0');
INSERT INTO [t297] VALUES (87,'الرابعة والثمانون: رجل قال لزوجته: إن قدم زيد فأنت طالق قبل قدومه بشهر. فقدم قبل مضي شهر فهل تطلق أم لا؟ وإن قدم بعد شهر فهل تطلق أم لا؟','37','0','0');
INSERT INTO [t297] VALUES (88,'الخامسة والثمانون: رجل قال لزوجته: إن لم أطلقك اليوم فأنت طالق اليوم. فمضى اليوم ولم يطلقها، فهل تطلق أم لا؟','37','0','0');
INSERT INTO [t297] VALUES (89,'السادسة والثمانون: الصلاة متى فرضت، وفي أي مكان فرضت؟','38','0','0');
INSERT INTO [t297] VALUES (90,'السابعة الثمانون: الصوم متى فرض؟','38','0','0');
INSERT INTO [t297] VALUES (91,'الثامنة والثمانون: الزكاة هل فرضت قبل الصوم أو بعده؟','38','0','0');
INSERT INTO [t297] VALUES (92,'التاسعة والثمانون: هل فرض الحج سنة خمس أو ست أو تسع؟','39','0','0');
INSERT INTO [t297] VALUES (93,'المسألة التسعون: أي وقت بنى رسول الله صلى الله عليه وسلم مسجده؟ ومتى شرع الأذان؟','39','0','0');
INSERT INTO [t297] VALUES (94,'الحادية والتسعون: أي وقت حولت القبلة إلى الكعبة؟ وفي أي صلاة من الخمس؟ وفي أي يوم، وأي شهر؟','39','0','0');
INSERT INTO [t297] VALUES (95,'الثانية والتسعون: أي وقت فرضت صدقة الفطر؟','39','0','0');
INSERT INTO [t297] VALUES (96,'الثالثة والتسعون: رجلان حكما رجلا أن يحكم بينهما في شيئ (١٤/ظ) فحكم بينهما وألزم أحدهما بالحكم عليه فهل يلزمه أم لا؟ وهل له أن يحكم وبالمدينة قاض أم لا؟ وهل له أن يحكم في النكاح والقصاص وحد القذف أم لا؟','40','0','0');
INSERT INTO [t297] VALUES (97,'الرابعة والتسعون: رجل ضرب رجلا فرمى أسنانه كلها فهل يلزمه دية أو أكثر من دية؟','40','0','0');
INSERT INTO [t297] VALUES (98,'الخامسة والتسعون: رجل أقر أن في ذمته دينا لآخر مقداره ألف درهم، ثم في تاريخ الإقرار أقر له أيضا بخمس مئة درهم، فهل يلزمه الجميع أم الألف فقط؟','40','0','0');
INSERT INTO [t297] VALUES (99,'السادسة والتسعون: رجل أقر أنه طلق زوجته من مدة متقدمة على يوم الإقرار بشهر فهل يكون الطلاق من حين (١٥ /و) الإقرار أو من قبله بشهر؟','40','0','0');
INSERT INTO [t297] VALUES (100,'السابعة والتسعون: رجل رهن عند آخر جلد قصب سكري رهنا معادا، ثم إن الراهن زرع الجلد المذكورة واشتغل منها مغلا فهل يكون المغل مرهونا أم لا؟','41','0','0');
INSERT INTO [t297] VALUES (101,'الثامنة والتسعون: صلاته صلى الله عليه وسلم إلى بيت المقدس هل كانت بوحي أو باجتهاد؟','41','0','0');
INSERT INTO [t297] VALUES (102,'التاسعة والتسعون: صوم عاشوراء هل كان بوحي أو اجتهاد؟','41','0','0');
INSERT INTO [t297] VALUES (103,'المسألة الموفية مائة: هل كان تبيينه صلى الله عليه وسلم لمقادير الزكاة بوحي أو اجتهاد؟','42','0','0');
INSERT INTO [t297] VALUES (104,'الأولى بعد المائة: قوله صلى الله عليه وسلم في سبايا أوطاس: لا توطأ حائل حتى تحيض ولا حامل حتى تضع. هل كان بالوحى أو بالاجتهاد؟','43','0','0');
INSERT INTO [t297] VALUES (105,'الثانية بعد المائة: هل يجوز نسخ السنة بالكتاب، والكتاب بالسنة أم لا؟','44','0','0');
INSERT INTO [t297] VALUES (106,'الثالثة بعد المائة: هل يجوز النسخ بغير بدل، وبأثقل منه، وما مثاله؟','45','0','0');
INSERT INTO [t297] VALUES (107,'الرابعة بعد المائة: هل يجوز تخصيص السنة بالسنة أم لا؟','45','0','0');
INSERT INTO [t297] VALUES (108,'الخامسة بعد المائة: ما الفرق بين النسخ والتخصيص؟','46','0','0');
INSERT INTO [t297] VALUES (109,'السادسة بعد المائة: هل يقدم الأصل على الظاهر؟','46','0','0');
INSERT INTO [t297] VALUES (110,'السابعة بعد المائة: هل يقدم القياس على خبر الواحد أم لا؟','46','0','0');
INSERT INTO [t297] VALUES (111,'الثامنة بعد المائة: هل يقدم الشرط على المشروط مطلقا أو يحتاج إلى تفصيل؟','47','0','0');
INSERT INTO [t297] VALUES (112,'التاسعة بعد المائة: هل يقدم الاستثناء على المستثنى منه؟ وهل شرط أن يكون من جنسه، وأن يكون المستثنى ذاكرا للاستثناء من أول الكلام إلى آخره؟','47','0','0');
INSERT INTO [t297] VALUES (113,'العاشرة بعد المائة: أطفال المشركين هل يدخلون الجنة أم لا؟','49','0','0');
INSERT INTO [t297] VALUES (114,'الحادية عشرة بعد المائة: الشياطين - نعوذ بالله تعالى منهم - هل يلدون أو يبيضون ثم يفرخون؟','50','0','0');
INSERT INTO [t297] VALUES (115,'الثانية عشرة بعد المائة: كرامات الأولياء هل هي جائزة، وهل تقدح في معجزات الأنبياء، وما الفرق بين المعجزة والكرامة؟','51','0','0');
INSERT INTO [t297] VALUES (116,'الثالثة عشرة بعد المائة: الفتوى هل يسلك بها مسلك الحكم حتى لا يجوز فتوى العبد والمرأة، أو الفتوى أوسع من الحكم؟','56','0','0');
INSERT INTO [t297] VALUES (117,'الرابعة عشرة بعد المائة: رجل رهن عند آخر حلة قصب سكري على مبلغ دراهم، وشرط الراهن والمرتهن أن يزرع الراهن الحلة عند أوان الزرع ومهما يحصل منها من زرع وقند وسكر يكون رهنا مكانها فهل يصح هذا الرهن وهل يقاس على مسالة ما إذا رهن ما يسرع فساده حتى يباع ويجعل عنه رهنا مكانه أم لا؟','56','0','0');
