INSERT INTO [t29847] VALUES (1,'يقبل وهو صائم','2','0','0');
INSERT INTO [t29847] VALUES (2,'أن الذين هبطوا على رسول الله صلى الله عليه ببطن نخلة منهم زوبعة من جن نصيبين وهو يقرأ،','3','0','0');
INSERT INTO [t29847] VALUES (3,'لأن يمتلئ جوف أحدكم قيحا خير له من أن يمتلئ شعرا','4','0','0');
INSERT INTO [t29847] VALUES (4,'يوم حنين: اللهم إن تشأ لا تعبد بعد اليوم.','5','0','0');
INSERT INTO [t29847] VALUES (5,'من أريد ماله بغير حق فقاتل دونه فقتل فهو شهيد','6','0','0');
INSERT INTO [t29847] VALUES (6,'إذا أدى حق الله عز وجل، ونصح لسيده فإن له أجران','7','0','0');
INSERT INTO [t29847] VALUES (7,'علينا أمراء يأخذونا بحق الله عز وجل، ويمنعونا الذي جعل الله عز وجل لنا، نقاتلهم أو','8','0','0');
INSERT INTO [t29847] VALUES (8,'أيرقد أحدنا وهو جنب؟ فقال: نعم، ويتوضأ وضوءه للصلاة','9','0','0');
INSERT INTO [t29847] VALUES (9,'أقنت رسول الله؟ قال: نعم.','10','0','0');
INSERT INTO [t29847] VALUES (10,'لما نزل الله عذرها قام أبو بكر فقبل رأسها، فقالت: بحمد الله، لا بحمد صاحبك. تعني النبي','11','0','0');
INSERT INTO [t29847] VALUES (11,'صدقة رمضان على الصغير والكبير، والذكر والأنثى , صاعا من تمر أو صاعا من شعير. . . . . .','12','0','0');
INSERT INTO [t29847] VALUES (12,'ليس منا من حلق وسلق وشق','13','0','0');
INSERT INTO [t29847] VALUES (13,'خيركم من قرأ القرآن وأقرأه','14','0','0');
INSERT INTO [t29847] VALUES (14,'ما ساق رسول الله إلى أحد من أزواجه ولا بناته أكثر من اثنتي عشرة أوقية','15','0','0');
INSERT INTO [t29847] VALUES (15,'أرقاءكم أرقاءكم، أطعموهم مما تأكلون، وألبسوهم مما تلبسون، فإن جاءوا بذنب فبيعوا عباد الله','16','0','0');
INSERT INTO [t29847] VALUES (16,'بيع المحفلات خلابة، ولا تحل خلابة مسلم بيع المحفلات','17','0','0');
INSERT INTO [t29847] VALUES (17,'من حسن إسلام المرء تركه ما لا يعنيه','18','0','0');
INSERT INTO [t29847] VALUES (18,'لا ضرر ولا ضرورة، ولا يمنع أحدكم أخاه أن يضع خشبة على حائطه','19','0','0');
INSERT INTO [t29847] VALUES (19,'من ابتاع عبدا وله مال فماله للبائع إلا أن يشترط المبتاع','20','0','0');
INSERT INTO [t29847] VALUES (20,'يوم الخميس وما يوم الخميس؟ فبكى حتى بل دمعه الحصى، فقلنا: يا أبا عباس وما يوم الخميس؟','21','0','0');
INSERT INTO [t29847] VALUES (21,'حين يخرج من بيته: اللهم إني أعوذ بك أن أزل، أو أضل، أو أذل، أو أظلم أو أظلم، أو أجهل أو','22','0','0');
INSERT INTO [t29847] VALUES (22,'كأين تقرأ سورة الأحزاب؟ قلت: ثلاثا وسبعين آية، قال: إن كانت لتضاهي سورة البقرة وقد قرأت','23','0','0');
INSERT INTO [t29847] VALUES (23,'إذا رأى ريحا أو غيما يفزع، فإذا أمطرت قال: اللهم صيبا نافعا','24','0','0');
INSERT INTO [t29847] VALUES (24,'لا سمر إلا لأحد رجلين مصل ومسافر','25','0','0');
INSERT INTO [t29847] VALUES (25,'ليلة القدر، فقال: كنت علمتها ثم اختلست مني، فأرى أنها في رمضان، فاطلبوها في تسع بقين، أو','26','0','0');
INSERT INTO [t29847] VALUES (26,'أحرمت وكثر قمل رأسي، فبلغ ذلك رسول الله، فأتاني وأنا أطبخ قدرا لأصحابي، فمس رأسي بأصبعه،','27','0','0');
INSERT INTO [t29847] VALUES (27,'صلى بمكة سجدتين','28','0','0');
INSERT INTO [t29847] VALUES (28,'إذا أوتي بالمريض، قال: أذهب البأس رب الناس، واشف أنت الشافي، لا شفاء إلا شفاؤك , شفاء لا','29','0','0');
INSERT INTO [t29847] VALUES (29,'بعث رسول الله صلى الله عليه عمرو بن العاص على جيش ومعه أبو بكر وعمر وسراة أصحابه،','30','0','0');
INSERT INTO [t29847] VALUES (30,'لما قالوا في الإفك ما قالوا هممت آتي إلى قليب فأرمي بنفسي فيه','31','0','0');
INSERT INTO [t29847] VALUES (31,'رجل طلق امرأته وهي حائض؟ فقال: أتعرف ابن عمر؟ فإنه طلق امرأته وهي حائض، فسأل عمر النبي','32','0','0');
INSERT INTO [t29847] VALUES (32,'ولا سأل النبي من بناته أكثر من اثني عشر أوقية، وهي أربع مائة وثمانون درهما، فإن الرجل قد','33','0','0');
INSERT INTO [t29847] VALUES (33,'يقنت في الفجر يدعو على قوم من بني سليم','34','0','0');
INSERT INTO [t29847] VALUES (34,'أتى حاجة، ثم جاء فتوضأ ومسح على خفيه.','35','0','0');
INSERT INTO [t29847] VALUES (35,'من أجنب من الليل فلم يغتسل حتى يصبح فلا صوم له، فقالت عائشة: لا تقل شيئا، قد كان رسول','36','0','0');
INSERT INTO [t29847] VALUES (36,'يسجد على حصير ونسجد عليه','37','0','0');
INSERT INTO [t29847] VALUES (37,'خرجنا مع رسول الله صلى الله عليه من منى منا مكبر ومنا ملب','38','0','0');
INSERT INTO [t29847] VALUES (38,'لا تغالوا بصدق النساء، فإنها لو كانت مكرمة في الدنيا، أو تقوى عند الله عز وجل، كان أولاكم','39','0','0');
INSERT INTO [t29847] VALUES (39,'أفطر بعرفة أتته بلبن فشرب','40','0','0');
INSERT INTO [t29847] VALUES (40,'ما خصنا رسول الله صلى الله عليه بشيء دون الناس إلا بثلاث: أمرنا أن نسبغ الوضوء، وأن لا','41','0','0');
INSERT INTO [t29847] VALUES (41,'الدال على الخير كفاعله','42','0','0');
INSERT INTO [t29847] VALUES (42,'من سره أن ينجيه الله من كرب يوم القيامة فلينظر معسرا أو يضع عنه','43','0','0');
INSERT INTO [t29847] VALUES (43,'فنظر فإذا حية منطوية على فراشه فانتظمها برمحه، ثم وكز الرمح، وانتفضت الحية وانتفض الرجل','44','0','0');
INSERT INTO [t29847] VALUES (44,'لحوم الحمر وأذن في لحوم الخيل','45','0','0');
INSERT INTO [t29847] VALUES (45,'ووقع الناس في لحوم الحمر، فنصبوا القدور، ونصبت قدري، فبلغ ذلك رسول الله صلى الله عليه،','46','0','0');
INSERT INTO [t29847] VALUES (46,'سبعة يظلهم الله في يوم لا ظل إلا ظله: رجلان تحابا في الله عز وجل على ذلك، واجتمعا على','47','0','0');
INSERT INTO [t29847] VALUES (47,'أردف أسامة من عرفة إلى جمع، وأردفني من جمع إلى منى، ولم يزل يلبي حتى رمى','48','0','0');
INSERT INTO [t29847] VALUES (48,'الإسلام بني على خمس: شهادة أن لا إله إلا الله وأن محمدا رسول الله، وإقام الصلاة، وإيتاء','49','0','0');
INSERT INTO [t29847] VALUES (49,'يا أم ملدم عليك ببني عصية، فإنهم عصوا الله عز وجل ورسوله','50','0','0');
INSERT INTO [t29847] VALUES (50,'يصبح جنبا من غير احتلام , فيغتسل ويصوم','51','0','0');
INSERT INTO [t29847] VALUES (51,'أشهد الله على مسلم كان الله عز وجل عليه حق، وكان لي عليه حق لما لم يهريق دما، ولم يهراق','52','0','0');
INSERT INTO [t29847] VALUES (52,'يأخذ المصحف فيدعه على وجهه، فيقول: كتاب ربي عز وجل، وكلام ربي عز وجل','53','0','0');
INSERT INTO [t29847] VALUES (53,'إنكم تخلطون بيوعكم بلغو، فشوبوها بشيء من الصدقة.','54','0','0');
INSERT INTO [t29847] VALUES (54,'استلام الحجر، فقال: رأيت رسول الله صلى الله عليه يقبله، فقال رجل: أرأيت إن زحمت أو غلبت؟','55','0','0');
INSERT INTO [t29847] VALUES (55,'لسمرة بن جندب فحل من نخل في حائط رجل من الأنصار، قال: وكان يجيء سمرة إلى نخله، وكان مع','56','0','0');
INSERT INTO [t29847] VALUES (56,'حين قال لها أهل الإفك ما قالوا، فبرأها الله عز وجل، وكلهم حدثني طائفة من حديثها، فذكر','57','0','0');
INSERT INTO [t29847] VALUES (57,'من يأتينا بخبر القوم؟ فذهب الزبير فجاء بخبرهم','58','0','0');
INSERT INTO [t29847] VALUES (58,'بعثني عمر في نعم من نعم الصدقة إلى الربذة، فوضعت رجلي على ناقة منها عشراء، فخرج عمر آخذا','59','0','0');
INSERT INTO [t29847] VALUES (59,'اللهم بعلمك الغيب وبقدرتك على الخلق أحيني ما كانت الحياة خيرا لي، وتوفني إذا كانت الوفاة','60','0','0');
INSERT INTO [t29847] VALUES (60,'دعا غلاما من بني ثمامة فحجمه وأمر له بصاع وكلم مواليه فخففوا عنه من','61','0','0');
INSERT INTO [t29847] VALUES (61,'أقنت عمر؟ قال: نعم، ومن هو خير من عمر رضي الله عنه','62','0','0');
INSERT INTO [t29847] VALUES (62,'لا حلف في الإسلام، وما كان في الجاهلية فتمسكوا به.','63','0','0');
INSERT INTO [t29847] VALUES (63,'لا ترجعوا بعدي كفارا يضرب بعضكم رقاب بعض','64','0','0');
INSERT INTO [t29847] VALUES (64,'أصابوا حمرا فطبخوا، فنادى منادي رسول الله صلى الله عليه: أكفئوا','65','0','0');
INSERT INTO [t29847] VALUES (65,'قد رآني أبي رحمه الله أكتب فمحاه','66','0','0');
INSERT INTO [t29847] VALUES (66,'الله عز وجل لا يستحيي من الحق، لا تأتوا النساء في أعجازهن','67','0','0');
INSERT INTO [t29847] VALUES (67,'إذا أقيمت الصلاة فلا صلاة إلا المكتوبة','68','0','0');
INSERT INTO [t29847] VALUES (68,'من كان له جارية فأدبها فأحسن تأديبها، ثم أعتقها وتزوجها فله أجران','69','0','0');
INSERT INTO [t29847] VALUES (69,'استقيموا لقريش ما استقاموا لكم، فإن لم يستقيموا فضعوا سيوفكم على عواتقكم وأبيدوا','70','0','0');
INSERT INTO [t29847] VALUES (70,'صلى النبي صلى الله عليه على ابن الدحداح، فلما رجع أتي بفرس، قال: والفرس عري، فركبه، قال:','71','0','0');
INSERT INTO [t29847] VALUES (71,'باب من أبواب الجنة؟ قلت: بلى يا رسول الله.','72','0','0');
INSERT INTO [t29847] VALUES (72,'عن نبيذ الجر؟ قال: نعم، وعن الدباء','73','0','0');
INSERT INTO [t29847] VALUES (73,'{وأتموا الحج والعمرة لله} [البقرة: ١٩٦] .','74','0','0');
INSERT INTO [t29847] VALUES (74,'سجد في إذا السماء انشقت','75','0','0');
INSERT INTO [t29847] VALUES (75,'من شرب الخمر في الدنيا لم يشربها في الآخرة','76','0','0');
INSERT INTO [t29847] VALUES (76,'إذا أصبح حين يسلم: اللهم إني أسألك علما نافعا، ورزقا طيبا، وعملا','77','0','0');
INSERT INTO [t29847] VALUES (77,'رضا الله في رضا الوالدين، وسخط الله في سخط الوالدين','78','0','0');
INSERT INTO [t29847] VALUES (78,'أتي بجارية من الأنصار، وقد رضها يهودي بين حجرين فقتلها، وانتزع حليها، فقال لهم رسول الله','79','0','0');
INSERT INTO [t29847] VALUES (79,'من قتل دون ماله فهو شهيد','80','0','0');
INSERT INTO [t29847] VALUES (80,'يصلي على الجنازة؟ فقال: اللهم أنت خلقتها أو خلقته وأنت أعلم بسرها وعلانيتها فاغفر','81','0','0');
INSERT INTO [t29847] VALUES (81,'جمع القرآن على عهد رسول الله صلى الله عليه أربعة: أبي بن كعب، ومعاذ بن جبل، وزيد بن ثابت،','82','0','0');
INSERT INTO [t29847] VALUES (82,'أنا عند ظن عبدي بي، وأنا معه حين يذكرني','83','0','0');
INSERT INTO [t29847] VALUES (83,'ردوا القتلى إلى مضاجعها أو مصارعها','84','0','0');
INSERT INTO [t29847] VALUES (84,'نهى عن لحوم الحمر الأهلية، ونهى عن كل ذي ناب من السباع','85','0','0');
INSERT INTO [t29847] VALUES (85,'يسروا ولا تعسروا، وسكنوا ولا تنفروا','86','0','0');
INSERT INTO [t29847] VALUES (86,'اتقوا النار ولو بشق تمرة','87','0','0');
INSERT INTO [t29847] VALUES (87,'صمت اليوم؟ قال: لا، قد أكلنا، قال: فصوموا بقية يومكم.','88','0','0');
INSERT INTO [t29847] VALUES (88,'ثلاثة يؤتون أجرهم مرتين: رجل كانت له أمة فأدبها فأحسن أدبها، ثم أعتقها فتزوج بها، ورجل من','89','0','0');
INSERT INTO [t29847] VALUES (89,'أطيب رسول الله صلى الله عليه عند إحرامه وقبل أن ينفر، ويوم النحر','90','0','0');
INSERT INTO [t29847] VALUES (90,'لو سلك الأنصار واديا وأحسب عفان قال: شعبا، لسلكت شعب الأنصار، ولولا الهجرة لكنت امرءا من','91','0','0');
INSERT INTO [t29847] VALUES (91,'من مات يعلم أن لا إله إلا الله دخل الجنة','92','0','0');
INSERT INTO [t29847] VALUES (92,'لم يكن الذين كفروا. قال رسول الله صلى الله عليه لأبي بن كعب: إن ربي عز وجل أمرني أن','93','0','0');
INSERT INTO [t29847] VALUES (93,'توضأ فأخذ كفا من ماء فنضح به.','94','0','0');
INSERT INTO [t29847] VALUES (94,'لا تقوم الساعة إلا على شرار الناس','95','0','0');
INSERT INTO [t29847] VALUES (95,'أطيب ما أكل الرجل من كسبه، وولده من كسبه','96','0','0');
INSERT INTO [t29847] VALUES (96,'صلى خلف أبي بكر','97','0','0');
INSERT INTO [t29847] VALUES (97,'الذهب بالذهب والورق بالورق، وسواء الآخذ والمعطي سواء','98','0','0');
INSERT INTO [t29847] VALUES (98,'لا تقل: أنا خير من يونس بن متى','99','0','0');
INSERT INTO [t29847] VALUES (99,'لا ينبغي لعبد أن يقول: إني خير من يونس بن متى، سبح الله في الظلمات','100','0','0');
INSERT INTO [t29847] VALUES (100,'لا ينبغي لنبي أن يقول: إني خير من يونس بن متى','101','0','0');
INSERT INTO [t29847] VALUES (101,'يهلك فينا رجلان: محب مفرط، وعدو مفتر','102','0','0');
INSERT INTO [t29847] VALUES (102,'إذا طبخت قدرا فأكثر مرقتها واغرف لجيرانك منها','103','0','0');
INSERT INTO [t29847] VALUES (103,'إذا قام من الليل يشوص فاه بالسواك','104','0','0');
INSERT INTO [t29847] VALUES (104,'يسافر بالقرآن مخافة أن يناله العدو','105','0','0');
INSERT INTO [t29847] VALUES (105,'يقرأ في الظهر والعصر بفاتحة الكتاب وسورة، وفي الركعتين الأخيرتين بفاتحة','106','0','0');
INSERT INTO [t29847] VALUES (106,'حسب الرجل دينه، وأصله عقله، ومروءته خلقه','107','0','0');
INSERT INTO [t29847] VALUES (107,'إذا فاتته أربعا قبل الظهر قضاها','108','0','0');
INSERT INTO [t29847] VALUES (108,'إذا فاتته أربعا قبل الظهر قضاها','109','0','0');
INSERT INTO [t29847] VALUES (109,'في الجمعة لساعة لا يوافقها عبد مسلم يسأل الله فيها شيئا إلا أعطاه.','110','0','0');
INSERT INTO [t29847] VALUES (110,'إذا توضأت فاستنثر، وإذا استجمرت فأوتر.','111','0','0');
INSERT INTO [t29847] VALUES (111,'ما يسرني أن جبل أحد ذهبا، أموت يوم أموت وعندي منه دينار أو نصف دينار إلا لغريم مع','112','0','0');
INSERT INTO [t29847] VALUES (112,'إذا قال الرجل للرجل: أي عدو الله، فقد كفر أحدهما','113','0','0');
INSERT INTO [t29847] VALUES (113,'إذا قال الرجل للرجل: يا كافر، أو: أنت كافر، فقد باء به أحدهما، وإن كان كما قال وإلا رجعت','114','0','0');
INSERT INTO [t29847] VALUES (114,'اتقوا النار ولو بشق تمرة، فإن لم تجدوا فبكلمة طيبة','115','0','0');
INSERT INTO [t29847] VALUES (115,'رأيتنا مع رسول الله صلى الله عليه سابع سبعة ما لنا طعام إلا ورق الحبلة حتى إن أحدنا ليضع','116','0','0');
INSERT INTO [t29847] VALUES (116,'أوفاهم بالأمانة، وأعلمهم بالله عز وجل','117','0','0');
INSERT INTO [t29847] VALUES (117,'وأتته امرأة بابن لها مجنون، فدعا له النبي صلى الله عليه وسلم، أو قرأ عليه فاتحة','118','0','0');
INSERT INTO [t29847] VALUES (118,'إياكم والظلم؛ فإن الظلم ظلمات يوم القيامة، وإياكم والفحش؛ فإن الله لا يحب الفحش، وإياكم','119','0','0');
INSERT INTO [t29847] VALUES (119,'لا تدخل الملائكة بيتا فيه كلب ولا صورة ولا جنب','120','0','0');
INSERT INTO [t29847] VALUES (120,'أمر بقتل الكلاب، ثم قال: ما بال الكلاب؟ وقال: إذا ولغ الكلب في الإناء فاغسله سبع مرار،','121','0','0');
INSERT INTO [t29847] VALUES (121,'عن عثمان بن عفان، فقالت: شكا الناس منه ظلامة، فاستتابوه، فتاب وأناب، حتى إذا صيروه كالثوب','122','0','0');
INSERT INTO [t29847] VALUES (122,'أكانت المصافحة في أصحاب رسول الله؟ قال: نعم','123','0','0');
