INSERT INTO [t29852] VALUES (1,'من لعب بالنرد فقد عصى الله ورسوله.','2','0','0');
INSERT INTO [t29852] VALUES (2,'فنهى عن الدباء والمزفت','3','0','0');
INSERT INTO [t29852] VALUES (3,'عن الدباء، والحنتم','4','0','0');
INSERT INTO [t29852] VALUES (4,'دببت إلى قدر لأهلي، فانكفت على يدي، فأحرقتها، فتوركت بي أمي، فأتت بي رجلا بالبطحاء، فجعل','5','0','0');
INSERT INTO [t29852] VALUES (5,'لا صاعي تمر بصاع، ولا صاعي حنطة بصاع، ولا درهمين بدرهم','6','0','0');
INSERT INTO [t29852] VALUES (6,'المزات حرام، ألا إن المزات خليط البسر والتمر','7','0','0');
INSERT INTO [t29852] VALUES (7,'كانت امرأة تأتي قوما فتستعير منهم الحلي، ثم تمسكه، قال: فرفع ذلك إلى النبي صلى الله عليه','8','0','0');
INSERT INTO [t29852] VALUES (8,'توفي والدي، وترك علي وعليه عشرين وسقا من تمر، ولنا تمر يسير العجوة، لا تفي بما علينا من','9','0','0');
INSERT INTO [t29852] VALUES (9,'لو أفاء الله عليكم مثل سمر تهامة نعما لقسمته بينكم، ولا تجدوني بخيلا، ولا','10','0','0');
INSERT INTO [t29852] VALUES (10,'الملائكة تلعن أحدكم إذا أشار إلى أخيه بحديدة، وإن كان أخاه لأبيه وأمه','11','0','0');
INSERT INTO [t29852] VALUES (11,'لا تشد الرحال إلا إلى ثلاثة مساجد: المسجد الحرام، والمسجد الأقصى، ومسجدي هذا، ولا تسافر','12','0','0');
INSERT INTO [t29852] VALUES (12,'إني لأبغض المرأة تخرج من بيتها، تجر ذيلها، تشكو زوجها','13','0','0');
INSERT INTO [t29852] VALUES (13,'كانت ترقي برقية لها في الجاهلية، فلما جاء الإسلام، قالت: لا أرقي بها حتى أستأمر النبي صلى','14','0','0');
INSERT INTO [t29852] VALUES (14,'أشرف على المدينة، فقال: ويل أمها من قرية يخرج عنها أهلها أحسن ما كانت، فيأتيها الدجال،','15','0','0');
INSERT INTO [t29852] VALUES (15,'اللهم اغفر للأنصار، ولذراريهم، ولذراري ذراريهم','16','0','0');
INSERT INTO [t29852] VALUES (16,'بينما رسول الله صلى الله عليه وسلم يقسم شيئا أقبل رجل فأكب عليه، فطعنه رسول الله صلى الله','17','0','0');
INSERT INTO [t29852] VALUES (17,'ترفع زينة الدنيا سنة خمس وعشرين ومائة','18','0','0');
INSERT INTO [t29852] VALUES (18,'عثمان بن أبي سليمان لمن زينة الدنيا.','19','0','0');
INSERT INTO [t29852] VALUES (19,'ترفع زينة الدنيا سنة أربع وعشرين ومائة، يعني بالزينة الدجال','20','0','0');
INSERT INTO [t29852] VALUES (20,'من خرج مجاهدا فمات كتب الله عز وجل له أجره إلى يوم القيامة، ومن خرج حاجا فمات كتب الله له','21','0','0');
INSERT INTO [t29852] VALUES (21,'الرجل يتزوج المرأة، فيطلقها، فتتزوج زوجا غيره، فيطلقها، ولم يمسها، قال: لا تحل لزوجها','22','0','0');
INSERT INTO [t29852] VALUES (22,'أصبحتا صائمتين، فأهدي لهما طعام، والطعام إذ ذاك محروص عليه، فأفطرنا، قالت عائشة رضي الله','23','0','0');
INSERT INTO [t29852] VALUES (23,'أجد في صدري الشيء، لأن أكون حمما أحب إلي من أن أتكلم به.','24','0','0');
INSERT INTO [t29852] VALUES (24,'طاف رسول الله صلى الله عليه وسلم طواف الصدر ليلا، أو بليل، قال محمد بن الحسين: وكان','25','0','0');
INSERT INTO [t29852] VALUES (25,'ينحرون البدنة معقولة اليد اليسرى، قائمة على ما بقي من قوائمها','26','0','0');
INSERT INTO [t29852] VALUES (26,'يبال في الماء المقيم.','27','0','0');
INSERT INTO [t29852] VALUES (27,'أتي بأبي قحافة يوم فتح مكة، ورأسه، ولحيته كالثغامة بياضا، فقال رسول الله صلى الله عليه','28','0','0');
INSERT INTO [t29852] VALUES (28,'فأقام سبعة عشر، وقد قيل: تسعة عشر يقصر الصلاة.','29','0','0');
INSERT INTO [t29852] VALUES (29,'الشرك الخفي أن يعمل الرجل لمكان الرجل','30','0','0');
INSERT INTO [t29852] VALUES (30,'ينهى عن القران في التمر حتى يستأذن صاحبه.','31','0','0');
INSERT INTO [t29852] VALUES (31,'إذا كان يوم القيامة لم يبق مسلم، إلا أتي بيهودي أو نصراني، حتى يدفع إليه، فيقال له: هذا','32','0','0');
INSERT INTO [t29852] VALUES (32,'بؤسا لك يا ابن سمية، تقتلك الفئة الباغية','33','0','0');
INSERT INTO [t29852] VALUES (33,'يا ويلك يا ابن سمية، يقتلك الفئة الباغية','34','0','0');
INSERT INTO [t29852] VALUES (34,'عن الغلام شاتان مكافئتان، وعن الجارية شاة، لا يضركم أنثى كان أو ذكر','35','0','0');
INSERT INTO [t29852] VALUES (35,'لو أن رصاصة مثل هذه. وأشار إلى مثل الجمجمة. أرسلت من السماء إلى الأرض، وهي مسيرة خمسمائة','36','0','0');
INSERT INTO [t29852] VALUES (36,'{وهم فيها كالحون} [المؤمنون: ١٠٤] ، قال: تشويه النار، فتقلص شفته العليا حتى تبلغ وسط','37','0','0');
INSERT INTO [t29852] VALUES (37,'نعم الإدام الخل','38','0','0');
INSERT INTO [t29852] VALUES (38,'لا يبولن أحدكم في الماء الدائم، ثم يغتسل منه. حدثنا يحيى، نا يعقوب بن إبراهيم، قال: سألت','39','0','0');
INSERT INTO [t29852] VALUES (39,'إن إبليس ما بين قدميه إلى كعبه مسيرة كذا وكذا، وإن عرشه لعلى البحر، ولو ظهر للناس لعبد،','40','0','0');
INSERT INTO [t29852] VALUES (40,'نقول إذا جلسنا في الركعتين: التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة','41','0','0');
INSERT INTO [t29852] VALUES (41,'أعطيت فواتح الكلم، ونصرت بالرعب، وبينا أنا نائم البارحة أتيت بمفاتيح خزائن الأرض حتى وضعت','42','0','0');
INSERT INTO [t29852] VALUES (42,'الله عز وجل يبغض الفاحش المتفحش.','43','0','0');
INSERT INTO [t29852] VALUES (43,'حاج آدم موسى، فقال موسى: يا آدم، أنت الذي أخرجت الناس من الجنة بذنبك، وأشقيتهم، قال آدم:','44','0','0');
INSERT INTO [t29852] VALUES (44,'لتأمرن بالمعروف، ولتنهون عن المنكر، أو ليسلطن الله عز وجل شراركم على خياركم، فيدعوا','45','0','0');
INSERT INTO [t29852] VALUES (45,'ثلاثة لا يكلمهم الله عز وجل يوم القيامة: الشيخ الزاني، والعائل','46','0','0');
INSERT INTO [t29852] VALUES (46,'ما توضأ من لم يذكر اسم الله عليه، وما صلى من لم يتوضأ، وما آمن من لم يحبني، وما أحبني من','47','0','0');
INSERT INTO [t29852] VALUES (47,'اللهم آتني في الدنيا حسنة، وفي الآخرة حسنة، وقني عذاب النار','48','0','0');
INSERT INTO [t29852] VALUES (48,'ما أحب أن أطلع في بيت النبي صلى الله عليه وسلم.','49','0','0');
INSERT INTO [t29852] VALUES (49,'أنا عند ظن عبدي بي، وأنا معه حين يذكرني','50','0','0');
INSERT INTO [t29852] VALUES (50,'لما خرج رسول الله صلى الله عليه وسلم من مكة إلى المدينة مهاجرا، جعلت قريش لمن رده مائة','51','0','0');
INSERT INTO [t29852] VALUES (51,'من قرأ ( {تبارك الذي بيده الملك} [الملك: ١] ) كل ليلة، منعه الله بها من عذاب','52','0','0');
INSERT INTO [t29852] VALUES (52,'هذان أبنائي من أحبهما فقد أحبني. قال يوسف: هكذا وقع عندي، عن أبي بكر متصل مرفوع. قال أبو','53','0','0');
INSERT INTO [t29852] VALUES (53,'كان النبي صلى الله عليه وسلم يصلي فإذا سجد وثب الحسن والحسين على ظهره، فإذا أرادوا أن','54','0','0');
