INSERT INTO [t29863] VALUES (1,'إذا توضأ العبد فأحسن الوضوء ثم خرج إلى الصلاة خرجت ذنوبه من سمعه وبصره ويديه ورجليه، فإن','2','0','0');
INSERT INTO [t29863] VALUES (2,'لا يبلغني أحد منكم عن أحد من أصحابي شيئا، فإني أحب أن أخرج إليكم وأنا سليم','3','0','0');
INSERT INTO [t29863] VALUES (3,'أتيت النبي صلى الله عليه وسلم وهو في الأبطح وهو في قبة حمراء، فخرج إلينا بلال بفضل وضوء','4','0','0');
INSERT INTO [t29863] VALUES (4,'صلى رسول الله صلى الله عليه وسلم بالأبطح بين يديه عنزة أو شبيهة بالعنزة والطريق من ورائها','5','0','0');
INSERT INTO [t29863] VALUES (5,'رأيت النبي صلى الله عليه وسلم في قبة أدم حمراء فخرج بلال بوضوء النبي صلى الله عليه،','6','0','0');
INSERT INTO [t29863] VALUES (6,'من نام عن صلاة أو نسيها فليصلها إذا استيقظ، وليصلها إذا ذكر','7','0','0');
INSERT INTO [t29863] VALUES (7,'قدم علينا مصدق النبي صلى الله عليه فأخذ الصدقة من أموال أغنيائنا فوضعها في فقرائنا، قال:','8','0','0');
INSERT INTO [t29863] VALUES (8,'رأى ناس نارا في المقبرة، فأتوها فإذا رسول الله صلى الله عليه في القبر وإذا هو يقول:','9','0','0');
INSERT INTO [t29863] VALUES (9,'من مات وهو يعبد الله لا يشرك به شيئا دخل الجنة','10','0','0');
INSERT INTO [t29863] VALUES (10,'من قال لا إله إلا الله مخلصا دخل الجنة','11','0','0');
INSERT INTO [t29863] VALUES (11,'كان معاذ رحمه الله يصلي مع النبي صلى الله عليه وسلم ثم يرجع فيصلي','12','0','0');
INSERT INTO [t29863] VALUES (12,'من كان له عند رسول الله صلى الله عليه عدة فليأتني، فقلت: إن رسول الله صلى الله عليه وعدني','13','0','0');
INSERT INTO [t29863] VALUES (13,'يقبض الصالحون الأول فالأول ويبقى حثالة كحثالة التمر لا يبالي الله عنهم','14','0','0');
INSERT INTO [t29863] VALUES (14,'من راح إلى الجمعة اغتسل كما يغتسل من الجنابة','15','0','0');
INSERT INTO [t29863] VALUES (15,'من توضأ يوم الجمعة فبها ونعمت ومن اغتسل فالغسل أفضل','16','0','0');
INSERT INTO [t29863] VALUES (16,'ما نلبس إذا أحرمنا؟ قال: البس الإزار والرداء والنعلين فإن لم يكن إزار فسراويل، فإن لم يكن','17','0','0');
INSERT INTO [t29863] VALUES (17,'إني لأسير على فرس لي في الجاهلية إذا أنا بطرفة بن العبد، قال: فقال: يا أبا يحيى احملني','18','0','0');
INSERT INTO [t29863] VALUES (18,'من أم قوما وفيهم من هو أقرأ لكتاب الله عز وجل منه وأعلم، لم يزل في سفال إلى يوم','19','0','0');
INSERT INTO [t29863] VALUES (19,'لما أهبط آدم عليه السلام وإبليس ناح إبليس حين بكى آدم، ثم حدا حين ضحك','20','0','0');
INSERT INTO [t29863] VALUES (20,'{يأيها الرسل كلوا من الطيبات} .','21','0','0');
INSERT INTO [t29863] VALUES (21,'هل عندكم غذاء؟ . فنقول: لا فيقول: أنا صائم. فأتانا يوما وقد أهدي لنا حيس، فقال: عندكم','22','0','0');
INSERT INTO [t29863] VALUES (22,'يعوذ الحسن والحسين عليهما السلام، يقول: أعيذكما بكلمات الله التامة من كل شيطان وهامة ومن','23','0','0');
INSERT INTO [t29863] VALUES (23,'إبليس قد أيس أن يعبده المصلون ولكنه في التحريش بينهم','24','0','0');
INSERT INTO [t29863] VALUES (24,'أمسكوا عليكم أموالكم لا تعطوها أحدا، فمن أعمر أحدا شيئا فهو له','25','0','0');
INSERT INTO [t29863] VALUES (25,'من صلى الفجر في جماعة كان كقيام ليلة، ومن صلى العشاء في جماعة كان كقيام نصف','26','0','0');
INSERT INTO [t29863] VALUES (26,'من سره أن يمثل له الرجال قياما فليتبوأ بيته أو مقعده من النار','27','0','0');
INSERT INTO [t29863] VALUES (27,'حق الله على العباد أن يعبدوه لا يشركوا به شيئا. قال: فما حق العباد على الله عز وجل إذا','28','0','0');
INSERT INTO [t29863] VALUES (28,'خيرنا رسول الله صلى الله عليه وسلم فاخترناه فلم نعده طلاقا','29','0','0');
INSERT INTO [t29863] VALUES (29,'لما ولد الحسن رضي الله عنه أذن رسول الله صلى الله عليه في أذنه','30','0','0');
INSERT INTO [t29863] VALUES (30,'صلاة الرجل قاعدا على النصف من صلاته قائما','31','0','0');
INSERT INTO [t29863] VALUES (31,'لا صام من صام الأبد','32','0','0');
INSERT INTO [t29863] VALUES (32,'فجرت خادم لآل رسول الله صلى الله عليه وسلم فأمرني أن أقيم عليها الحد فوجدتها لم تجف من','33','0','0');
INSERT INTO [t29863] VALUES (33,'لا تسبوا الدهر فإن الله هو الدهر','34','0','0');
INSERT INTO [t29863] VALUES (34,'من بات وفي يده غمر فأصابه شيء فلا يلومن إلا نفسه','35','0','0');
INSERT INTO [t29863] VALUES (35,'إذا راعه شيء يقول: هو الله الله الله لا شريك له','36','0','0');
INSERT INTO [t29863] VALUES (36,'القدرية مجوس هذه الأمة لا تعودوا مرضاهم ولا تتبعوا جنائزهم ولا','37','0','0');
INSERT INTO [t29863] VALUES (37,'ما من شيء إلا وهو أطوع لله عز وجل من ابن آدم','38','0','0');
INSERT INTO [t29863] VALUES (38,'الزبير بن العوام كانت تحته أم كلثوم بنت عقبة، فقالت وهي حامل: طيب نفسي بتطليقة، فطلقها','39','0','0');
INSERT INTO [t29863] VALUES (39,'لا يبغض الأنصار رجل يؤمن بالله واليوم الآخر','40','0','0');
INSERT INTO [t29863] VALUES (40,'امرأة اعترفت أنها قد زنت، فجلدها، ثم رجمها، فقال: جلدتك للكتاب، ورجمتك','41','0','0');
INSERT INTO [t29863] VALUES (41,'الذي تولى كبره منهم له عذاب أليم عبد الله بن أبي','42','0','0');
INSERT INTO [t29863] VALUES (42,'ما أحد أصبر على أذى يسمعه من الله عز وجل وهو يعافيهم ويرزقهم','43','0','0');
INSERT INTO [t29863] VALUES (43,'أيما عبد أبق فقد برئت منه الذمة','44','0','0');
INSERT INTO [t29863] VALUES (44,'من تعلق شيئا وكل إليه','45','0','0');
INSERT INTO [t29863] VALUES (45,'ما مررت بملإ من الملائكة. يعني في الحجامة، وحديث كان النبي صلى الله عليه وسلم يكتحل','46','0','0');
INSERT INTO [t29863] VALUES (46,'كل أمر ذي بال لا يبدأ فيه بالحمد لله فهو أقطع','47','0','0');
INSERT INTO [t29863] VALUES (47,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فمن قال: لا إله إلا الله عصم مني ماله','48','0','0');
INSERT INTO [t29863] VALUES (48,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فمن قال: لا إله إلا الله، عصم مني نفسه','49','0','0');
INSERT INTO [t29863] VALUES (49,'أهديت إلى رسول الله صلى الله عليه وسلم رجل حمار وحشي وهو على قديد، فرده علي، قال: فلما','50','0','0');
INSERT INTO [t29863] VALUES (50,'نهى عن اشتمال الصماء وأن يحتبي الرجل في ثوب واحد ليس على فرجه منه شيء','51','0','0');
INSERT INTO [t29863] VALUES (51,'الرجل ليعمل الحسنة فيتكل عليها ويعمل المحقرات حتى يأتي الله عز وجل وقد أحيط به، وإنه','52','0','0');
INSERT INTO [t29863] VALUES (52,'عن العزل، فقال: أفتفعلون ذلك؟ فلا عليكم ألا تفعلوا، فإنه ليس نسمة قضى الله عز وجل أن تكون','53','0','0');
INSERT INTO [t29863] VALUES (53,'لقد ذكرتني بقراءتك المرسلات، إن آخر ما سمعت رسول الله صلى الله عليه وسلم يقرأ في المغرب','54','0','0');
INSERT INTO [t29863] VALUES (54,'طاف النبي صلى الله عليه وسلم بالبيت ثلاثة أسباع جميعا ثم أتى المقام فصلى خلفه ست ركعات','55','0','0');
INSERT INTO [t29863] VALUES (55,'شهدت وأنا غلام مع عمومتي حلف المطيبين فما أحب أن أنكثه وإن لي حمر','56','0','0');
INSERT INTO [t29863] VALUES (56,'امرأة من اليهود أهدت لرسول الله صلى الله عليه وسلم شاة مسمومة فقال لأصحابه: أمسكوا فإنها','57','0','0');
INSERT INTO [t29863] VALUES (57,'نفقتك على أهلك لك صدقة، وإن نفقتك على عيالك لك صدقة , حتى اللقمة تضعها في في امرأتك، وإنك','58','0','0');
INSERT INTO [t29863] VALUES (58,'إذا قرب العشاء وأحدكم صائم فليبدأ به قبل الصلاة صلاة المغرب ولا تعجلوا عن','59','0','0');
INSERT INTO [t29863] VALUES (59,'إذا حضر الطعام والصلاة فابدءوا بالطعام حتى تأتوا الصلاة وأنتم تعرفون','60','0','0');
INSERT INTO [t29863] VALUES (60,'إذا علم أحدكم من أخيه خيرا فليعلمه ذلك ليزداد فيه رغبة','61','0','0');
INSERT INTO [t29863] VALUES (61,'رأيت النبي صلى الله عليه وسلم توضأ ثلاثا ثلاثا، وتوضأت ثلاثا ثلاثا','62','0','0');
INSERT INTO [t29863] VALUES (62,'ورث امرأة أشيم في عقل زوجها','63','0','0');
INSERT INTO [t29863] VALUES (63,'لما جاء البشير إلى يعقوب عليه السلام، قال: ما وجدت عندنا شيئا وما اختبزنا منذ سبعة أيام','64','0','0');
INSERT INTO [t29863] VALUES (64,'دية اليهودي والنصراني أربعة ألاف، والمجوسي بمائة','65','0','0');
INSERT INTO [t29863] VALUES (65,'تصدقن ولو من حليكن وأكثر حطب جهنم أنتن. فجعلت المرأة ترمي بالقلب والخرص وبالخاتم، فضمه','66','0','0');
INSERT INTO [t29863] VALUES (66,'أيما امرأة نكحت بغير إذن وليها فنكاحها باطل ولها الذي أعطاها بما أصاب منها، فإن اشتجروا','67','0','0');
INSERT INTO [t29863] VALUES (67,'إذا أراد سفرا أقرع بين نسائه، فأيهن خرج سهمها أخرج بها معه','68','0','0');
INSERT INTO [t29863] VALUES (68,'من الشعر حكمة','69','0','0');
INSERT INTO [t29863] VALUES (69,'لا يقولن أحدكم خبثت نفسي، ولكن ليقل لقست نفسي','70','0','0');
INSERT INTO [t29863] VALUES (70,'توضئي مما مست النار','71','0','0');
INSERT INTO [t29863] VALUES (71,'توفي رسول الله صلى الله عليه وسلم وهو ابن ثلاث وستين','72','0','0');
INSERT INTO [t29863] VALUES (72,'دخل قائف ورسول الله صلى الله عليه وسلم شاهد، وأسامة بن زيد، وزيد بن حارثة مضجعان، فقال:','73','0','0');
INSERT INTO [t29863] VALUES (73,'لا تخيروني على موسى فإن الناس يصعقون فأكون أول من يفيق، فإذا موسى عليه السلام باطش بجانب','74','0','0');
INSERT INTO [t29863] VALUES (74,'نهى رسول الله صلى الله عليه وسلم عن المحاقلة والمزابنة.','75','0','0');
INSERT INTO [t29863] VALUES (75,'أولكلكم ثوبان','76','0','0');
INSERT INTO [t29863] VALUES (76,'صلى على النجاشي ملك الحبشة فكبر عليه أربعا','77','0','0');
INSERT INTO [t29863] VALUES (77,'العجماء جرحها جبار، والمعدن جبار والبئر جبار، وفي الركاز الخمس','78','0','0');
INSERT INTO [t29863] VALUES (78,'نهى عن اختناث الأسقية','79','0','0');
INSERT INTO [t29863] VALUES (79,'مستلقيا في المسجد واضعا إحدى رجليه على الأخرى','80','0','0');
INSERT INTO [t29863] VALUES (80,'منضجعا في المسجد واضعا إحدى رجليه على الأخرى.','81','0','0');
INSERT INTO [t29863] VALUES (81,'وهو في المسجد مستلق رافعا إحدى رجليه على الأخرى','82','0','0');
INSERT INTO [t29863] VALUES (82,'يقرأ: {واتخذوا من مقام إبراهيم مصلى}','83','0','0');
INSERT INTO [t29863] VALUES (83,'القلب يحزن والعين تدمع، ولا نقول إلا ما يرضي الرب عز وجل يا إبراهيم إنا بك','84','0','0');
INSERT INTO [t29863] VALUES (84,'ناركم هذه جزء من سبعين جزءا من نار جهنم ولولا أنه ضرب بها البحر عشر مرات ما انتفعتم منها','85','0','0');
INSERT INTO [t29863] VALUES (85,'من أفتى الناس بكل ما يسألونه فهو مجنون','86','0','0');
INSERT INTO [t29863] VALUES (86,'تعلموا العلم فإن أحدكم لا يدري متى يحتاج إليه؟','87','0','0');
INSERT INTO [t29863] VALUES (87,'إذا كنا مع رسول الله صلى الله عليه وسلم سفرا يأمرنا أن لا ننزع خفافنا ثلاثا، وإذا كنا','88','0','0');
INSERT INTO [t29863] VALUES (88,'لأهل الجنة يا أهل الجنة. فيقولون: لبيك وسعديك والخير لديك، فيقول: هل رضيتم؟ . فيقولون: ما','89','0','0');
INSERT INTO [t29863] VALUES (89,'كنا مع رسول الله صلى الله عليه بعسفان، فحضرت صلاة الظهر وخالد بن الوليد على المشركين،','90','0','0');
INSERT INTO [t29863] VALUES (90,'المسلم من سلم المسلمون من لسانه ويده، والمهاجر من هجر ما نهى الله عز وجل','91','0','0');
INSERT INTO [t29863] VALUES (91,'اشتد غضب الله عز وجل على امرأة تدخل في قوم من ليس منهم يشركهم في أموالهم ويطلع على','92','0','0');
INSERT INTO [t29863] VALUES (92,'من كان على ديني ودين داود وسليمان وإبراهيم عليهم السلام، فليتزوج إن وجد إلى النساء سبيلا،','93','0','0');
INSERT INTO [t29863] VALUES (93,'بعث رسول الله صلى الله عليه رهطا واستعمل عليهم عبيدة بن الحارث، قال: فلما انطلق ليتوجه،','94','0','0');
INSERT INTO [t29863] VALUES (94,'فجاء رجل، فصلى فلم يتم ركوعها ولا سجودها، فقال حذيفة: ردوا الرجل، فردوه عليه، فقال له:','95','0','0');
INSERT INTO [t29863] VALUES (95,'صلوا الصلاة في المساجد فإنها من أمر الهدى وسنة نبيكم صلى الله عليه','96','0','0');
INSERT INTO [t29863] VALUES (96,'الرجل من أهل الكفر يسلم على يدي الرجل من المسلمين، ما السنة فيه؟ قال: هو أولى الناس','97','0','0');
INSERT INTO [t29863] VALUES (97,'اندقت ثنيتي يوم أحد فأتيت النبي صلى الله عليه، فأمرني أن أتخذ ثنية من','98','0','0');
INSERT INTO [t29863] VALUES (98,'{سحران} . قال: كذب العبد، قرأتها على ابن عباس في الطواف: ٠ ساحران ٠.','99','0','0');
INSERT INTO [t29863] VALUES (99,'لن تكون عالما حتى تكون متعلما، ولن تكون متعلما حتى تكون بها عاملا','100','0','0');
