INSERT INTO [t29867] VALUES (1,'إذا كبر للصلاة رفع يديه حذو منكبيه، فإذا رفع فعل مثل ذلك، ولا يفعل ذلك بين','2','0','0');
INSERT INTO [t29867] VALUES (2,'البزاق في المسجد خطيئة وكفارتها أن تواريه','3','0','0');
INSERT INTO [t29867] VALUES (3,'الذي يرجع في عطيته: كمثل الكلب أكل حتى إذا شبع قاء ثم عاد فأكله','4','0','0');
INSERT INTO [t29867] VALUES (4,'فتلت قلائد هدي رسول الله صلى الله عليه وسلم بيدي، ثم قلدها وأشعرها فلم يحرم عليه شيء كان','5','0','0');
INSERT INTO [t29867] VALUES (5,'على كل باب مسجد يوم الجمعة ملائكة يكتبون مجيء الرجل فإذا جلس الإمام طويت الصحف , فالمهجر','6','0','0');
INSERT INTO [t29867] VALUES (6,'أتيت رسول الله صلى الله عليه وسلم من آخر الليل فصليت خلفه فأخذ بيدي فجرني حتى جعلني','7','0','0');
INSERT INTO [t29867] VALUES (7,'ينقل معهم الحجارة للكعبة وعليه إزاره، فقال له العباس عمه: يا ابن أخي لو حللت إزارك فجعلته','8','0','0');
INSERT INTO [t29867] VALUES (8,'جلود الميتة، قال: إن دباغه قد أذهب بخبثه أو نجسه أو برجسه','9','0','0');
INSERT INTO [t29867] VALUES (9,'أتي عبيد الله بن زياد برأس الحسين بن علي عليه السلام، فجعل في طست فجعل ينكت عليه، وقال:','10','0','0');
INSERT INTO [t29867] VALUES (10,'فزع الناس، فركب رسول الله صلى الله عليه وسلم فرسا لأبي طلحة بطيئا , ثم خرج يركض وحده،','11','0','0');
INSERT INTO [t29867] VALUES (11,'تسموا باسمي , ولا تكنوا بكنيتي','12','0','0');
INSERT INTO [t29867] VALUES (12,'إذا كفن أحدكم أخاه فليحسن كفنه','13','0','0');
INSERT INTO [t29867] VALUES (13,'جارية بكرا أتت النبي صلى الله عليه وسلم، فذكرت أن أباها زوجها وهي كارهة،','14','0','0');
INSERT INTO [t29867] VALUES (14,'أخذ الله الميثاق من ظهر آدم عليه السلام بنعمان يعني عرفة، فأخرج من صلبه كل ذرية ذرأها ,','15','0','0');
INSERT INTO [t29867] VALUES (15,'سيكون بعدي هنات وهنات وهنات ممدودة فمن رأيتموه يفرق أمة محمد صلى الله عليه وسلم وهي جميع','16','0','0');
INSERT INTO [t29867] VALUES (16,'يقرأ في الركعة الأولى من صلاة الفجر: ق والقرءان المجيد ولا أدري ما قرأ في','17','0','0');
INSERT INTO [t29867] VALUES (17,'يمن الخيل في شقرها','18','0','0');
INSERT INTO [t29867] VALUES (18,'لما عرج بي رأيت إدريس عليه السلام في السماء الرابعة','19','0','0');
INSERT INTO [t29867] VALUES (19,'رجلا، قال: يا رسول الله كيف يحشر الكافر على وجهه يوم القيامة؟ فقال نبي الله عليه السلام:','20','0','0');
INSERT INTO [t29867] VALUES (20,'لما نزلت: {يأيها الذين آمنوا صلوا عليه وسلموا تسليما} [الأحزاب: ٥٦] جاء رجل إلى النبي','21','0','0');
INSERT INTO [t29867] VALUES (21,'رخص رسول الله صلى الله عليه وسلم في العرايا أن تباع بخرصها تمرا','22','0','0');
INSERT INTO [t29867] VALUES (22,'ما منكم من أحد إلا قد كتب مقعده من الجنة ومقعده من النار. قالوا: يا رسول الله أفلا نتكل؟','23','0','0');
INSERT INTO [t29867] VALUES (23,'أتى سباطة قوم فبال قائما.','24','0','0');
INSERT INTO [t29867] VALUES (24,'أرحم أمتي أبو بكر، وأشدها في دين الله عمر، وأصدقها حياء عثمان، وأفرضهم زيد، وأقرؤهم أبي،','25','0','0');
INSERT INTO [t29867] VALUES (25,'من نوقش الحساب هلك. قالت: قلت: يا رسول الله فإن الله عز وجل يقول في كتابه: {فأما من أوتي','26','0','0');
INSERT INTO [t29867] VALUES (26,'رفع عقصة، فقال: سمعت رسول الله صلى الله عليه وسلم، يقول: عذبت أمة من بني إسرائيل؛ لأن','27','0','0');
INSERT INTO [t29867] VALUES (27,'ما تركت استلام الحجر في رخاء ولا شدة منذ رأيت رسول الله صلى الله عليه وسلم','28','0','0');
INSERT INTO [t29867] VALUES (28,'أفطر الحاجم والمحجوم','29','0','0');
INSERT INTO [t29867] VALUES (29,'جاء ماعز بن مالك إلى النبي عليه السلام، فقال: يا رسول الله طهرني. فقال النبي صلى الله','30','0','0');
INSERT INTO [t29867] VALUES (30,'قسم غنما فجعل لكل عشرة من أصحابه شاة، وأنها كانت نهبة , قال: وقد أمر رسول الله صلى الله','31','0','0');
INSERT INTO [t29867] VALUES (31,'يتختم في يمينه ونقشه محمد رسول الله','32','0','0');
INSERT INTO [t29867] VALUES (32,'أراد أن يكتب إلى كسرى وقيصر فقيل له: إنهم لا يقبلون كتابك إلا بخاتم.','33','0','0');
INSERT INTO [t29867] VALUES (33,'إذا رفع رأسه من الركوع لم يحن أحد منا ظهره حتى يضع رسول الله صلى الله عليه','34','0','0');
INSERT INTO [t29867] VALUES (34,'أتانا رسول الله صلى الله عليه وسلم فوضع رجله بيني وبين فاطمة، فعلمنا ما نقول إذا أخذنا','35','0','0');
INSERT INTO [t29867] VALUES (35,'احتجم وهو صائم محرم','36','0','0');
INSERT INTO [t29867] VALUES (36,'المؤمن يأكل في معى واحد , والكافر يأكل في سبعة أمعاء','37','0','0');
INSERT INTO [t29867] VALUES (37,'نهى رسول الله صلى الله عليه وسلم أن ترمى جمرة العقبة يوم النحر حتى تطلع','38','0','0');
INSERT INTO [t29867] VALUES (38,'يصلي ركعتين خفيفتين قبل الفجر','39','0','0');
INSERT INTO [t29867] VALUES (39,'إذا شككتم في طريق فاجعلوه سبعة أذرع تختلف فيه الحاملتان','40','0','0');
INSERT INTO [t29867] VALUES (40,'ما تعنون بالجد؟ فقالوا: الرجل يكون له الإبل والخيل فتنمى، فقال: فقام النبي صلى الله عليه','41','0','0');
INSERT INTO [t29867] VALUES (41,'سيكون عليكم أئمة يميتون الصلاة , فصلوا الصلاة لوقتها , واجعلوا صلواتكم معهم','42','0','0');
INSERT INTO [t29867] VALUES (42,'المسح على الخفين ثلاثة أيام للمسافر ولياليهن، وللمقيم يوم وليلة','43','0','0');
INSERT INTO [t29867] VALUES (43,'المؤمن يؤجر في هدايته السبيل، وإماطته الأذى عن الطريق , وفي تعبيره بلسانه عن الأعجمي،','44','0','0');
INSERT INTO [t29867] VALUES (44,'أهدى في بدنة بعيرا كان لأبي جهل في أنفه برة من فضة','45','0','0');
INSERT INTO [t29867] VALUES (45,'ساق النبي عليه السلام مائة بدنة , فيها جمل لأبي جهل عليه برة من فضة','46','0','0');
INSERT INTO [t29867] VALUES (46,'يدخل الفقراء الجنة قبل الأغنياء بخمسمائة عام، نصف يوم','47','0','0');
INSERT INTO [t29867] VALUES (47,'طوبى لعيش بعد المسيح , طوبى لعيش بعد المسيح، يؤذن للسماء في القطر، ويؤذن للأرض في النبات','48','0','0');
INSERT INTO [t29867] VALUES (48,'أكان عمر يقنت؟ قال: ومن خير من عمر','49','0','0');
INSERT INTO [t29867] VALUES (49,'لا تهلكن عن آية الرجم , قد نزلت وقرأناها الشيخ والشيخة فارجموهما البتة، وقد رجم رسول الله','50','0','0');
INSERT INTO [t29867] VALUES (50,'لعن الله الواصلة والمستوصلة، والواشمة والمستوشمة','51','0','0');
INSERT INTO [t29867] VALUES (51,'أراد أن يصدر , فقيل له: إن صفية قد حاضت. فقال: إنها لحابستنا.','52','0','0');
INSERT INTO [t29867] VALUES (52,'من مات وفي قلبه لا إله إلا الله موقنا دخل الجنة','53','0','0');
INSERT INTO [t29867] VALUES (53,'من كان له ثلاث بنات يأويهن ويكفيهن ويرحمهن فقد وجبت له الجنة البتة.','54','0','0');
INSERT INTO [t29867] VALUES (54,'مر برجل يقود بدنة، فقال: اركبها. قال: إنها بدنة، قال: اركبها. قال: إنها بدنة، قال:','55','0','0');
INSERT INTO [t29867] VALUES (55,'جاءت امرأة رفاعة القرظي إلى رسول الله صلى الله عليه وسلم، فقالت: يا رسول الله إني نكحت','56','0','0');
INSERT INTO [t29867] VALUES (56,'ضرس الكافر مثل أحد، وفخذه مثل البيضاء، ومقعده من النار كما بين قديد ومكة، وكثافة جلده','57','0','0');
INSERT INTO [t29867] VALUES (57,'أينام أحدنا وهو جنب؟ قال: نعم , ويتوضأ','58','0','0');
INSERT INTO [t29867] VALUES (58,'أقبلنا من بدر ففقدنا رسول الله صلى الله عليه وسلم، فنادت الرفاق بعضها بعضا: أفيكم رسول','59','0','0');
INSERT INTO [t29867] VALUES (59,'لا تنكح الثيب حتى تستأمر، ولا البكر حتى تستأذن وإذنها سكوتها.','60','0','0');
INSERT INTO [t29867] VALUES (60,'أحب عبادي إلي أسرعهم فطرا','61','0','0');
INSERT INTO [t29867] VALUES (61,'من أدرك من صلاة ركعة فقد أدركها','62','0','0');
INSERT INTO [t29867] VALUES (62,'أقيمت الصفوف وصف الناس صفوفهم، وجاء رسول الله صلى الله عليه وسلم حتى إذا قام في مقامه ذكر','63','0','0');
INSERT INTO [t29867] VALUES (63,'من حلف على يمين صبر ليقتطع بها مال امرئ مسلم بغير حقه، حرم الله عز وجل عليه الجنة، وأوجب','64','0','0');
INSERT INTO [t29867] VALUES (64,'من جهز غازيا في سبيل الله أو خلفه في أهله كان له مثل أجره، ولم ينتقص من أجر الغازي شيء،','65','0','0');
INSERT INTO [t29867] VALUES (65,'الأجدع شيطان أنت مسروق بن عبد الرحمن , قال الشعبي: فرأيته في الديوان مسروق بن عبد','66','0','0');
INSERT INTO [t29867] VALUES (66,'صلاة الليل، فقال: مثنى مثنى، فإذا خشي أحدكم الصبح فليوتر بواحدة.','67','0','0');
INSERT INTO [t29867] VALUES (67,'نبئ النبي صلى الله عليه وسلم يوم الاثنين، وأسلم علي يوم الثلاثاء','68','0','0');
INSERT INTO [t29867] VALUES (68,'نهى رسول الله صلى الله عليه وسلم أن يجصص القبر وأن يبنى عليه بناء','69','0','0');
INSERT INTO [t29867] VALUES (69,'إذا رجعت إلى بيتك فمرهم فليحسنوا غذاء رباعهم، ومرهم فليقلموا أظفارهم، لا يعبطوا بها ضروع','70','0','0');
INSERT INTO [t29867] VALUES (70,'رجلا يأتيكم من اليمن يقال له أويس، لا يدع باليمن غير أم له، وقد كان به بياض , فدعا الله','71','0','0');
INSERT INTO [t29867] VALUES (71,'من صلى من صلاة الصبح ركعة قبل أن تطلع الشمس فطلعت، فليصل إليها أخرى','72','0','0');
INSERT INTO [t29867] VALUES (72,'الله عز وجل كتب الإحسان على كل شيء، فإذا قتلتم فأحسنوا القتلة، وإذا ذبحتم فأحسنوا الذبحة،','73','0','0');
INSERT INTO [t29867] VALUES (73,'كنت أنام بين يدي رسول الله صلى الله عليه وسلم، ورجلي في قبلته وهو يصلي، فإذا سجد غمزني','74','0','0');
INSERT INTO [t29867] VALUES (74,'تكون فتنة مظلمة مضلة أو مضلة مظلمة جائية , المضطجع فيها خير من القاعد، والقاعد فيها خير','75','0','0');
INSERT INTO [t29867] VALUES (75,'تقتلك الفئة الباغية. أما لولا ما أمرني به من طواعيتك ما سرني هذا','76','0','0');
INSERT INTO [t29867] VALUES (76,'كل عين باكية يوم القيامة إلا عينا غضت عن محارم الله، وعينا سهرت في سبيل الله، وعينا خرج','77','0','0');
INSERT INTO [t29867] VALUES (77,'إذا جاء أحدكم إلى القوم فليسلم، فإذا قام فليسلم، فليست الأولى أحق من','78','0','0');
INSERT INTO [t29867] VALUES (78,'أنخ ناقتك وادخل مسجد رسول الله صلى الله عليه وسلم، وصل فيه الضحى','79','0','0');
INSERT INTO [t29867] VALUES (79,'من أتى الجمعة فليغتسل','80','0','0');
INSERT INTO [t29867] VALUES (80,'قضى الحاجة , ثم أخرج يده من جبة له ضيقة الأكمام فتوضأ ومسح على خفيه','81','0','0');
INSERT INTO [t29867] VALUES (81,'لا يدخل الجنة عاق ولا مدمن خمر ولا منان','82','0','0');
INSERT INTO [t29867] VALUES (82,'يوم أحد: احفروا وأعمقوا وأوسعوا وأحسنوا وادفنوا الاثنين والثلاثة في قبر واحد، وقدموا','83','0','0');
INSERT INTO [t29867] VALUES (83,'صلى رسول الله صلى الله عليه وسلم في بيتي من الضحى أربع ركعات','84','0','0');
INSERT INTO [t29867] VALUES (84,'صلى بنا رسول الله صلى الله عليه وسلم في غزوة ذات الرقاع صلاة الخوف، فكبر بنا جميعا، فصلى','85','0','0');
INSERT INTO [t29867] VALUES (85,'صلاة الليل مثنى مثنى، فإذا أنت خفت أو خشيت أن تصبح فأوتر بركعة','86','0','0');
INSERT INTO [t29867] VALUES (86,'إنا ندخل على سلطاننا فنقول له ما نتكلم بخلافه إذا خرجنا من عندهم، قال: كنا نعد هذا','87','0','0');
INSERT INTO [t29867] VALUES (87,'ما حق امرئ مسلم له شيء يوصي فيه يبيت ليلتين إلا وعنده وصيته','88','0','0');
INSERT INTO [t29867] VALUES (88,'إذا كان في السفر في الليلة الباردة أو الليلة المطيرة ينادي بالصلاة صلاة العشي، ثم ينادي','89','0','0');
INSERT INTO [t29867] VALUES (89,'الخيط الأبيض من الخيط الأسود، فقال: هو بياض النهار من سواد الليل','90','0','0');
INSERT INTO [t29867] VALUES (90,'إذا أذن المؤذن صلى الركعتين ثم خرج إلى المسجد , وحرم الطعام وكان لا يؤذن إلا بعد','91','0','0');
INSERT INTO [t29867] VALUES (91,'صليت مع النبي صلى الله عليه وسلم، ومع أبي بكر، ومع عمر، ومع عثمان، فكلهم كان يصليها قبل','92','0','0');
INSERT INTO [t29867] VALUES (92,'صلى حين انكسفت الشمس ثماني ركعات وأربع سجدات يقرأ في كل ركعة','93','0','0');
INSERT INTO [t29867] VALUES (93,'إذا تواجه المسلمان بسيفيهما كل واحد منهما يريد صاحبه، فقتل أحدهما الآخر فهما في','94','0','0');
INSERT INTO [t29867] VALUES (94,'بايعت رسول الله صلى الله عليه وسلم على إقام الصلاة وإيتاء الزكاة والنصح لكل','95','0','0');
INSERT INTO [t29867] VALUES (95,'يدخل سبعون ألفا من أمتي الجنة بغير حساب. قيل: يا رسول الله من هم؟ قال: هم الذين لا يكتوون','96','0','0');
INSERT INTO [t29867] VALUES (96,'بشر خديجة ببيت في الجنة من قصب لا نصب فيه ولا صخب','97','0','0');
INSERT INTO [t29867] VALUES (97,'بعثني رسول الله صلى الله عليه وسلم في ثلاثين راكبا فيهم عباد بن بشر إلى بني بكر بن كلاب ,','98','0','0');
INSERT INTO [t29867] VALUES (98,'إذا قذف الله عز وجل في قلب أحدكم خطبة امرأة فلا بأس أن ينظر إليها','99','0','0');
INSERT INTO [t29867] VALUES (99,'الصلاة الوسطى: هي العصر','100','0','0');
INSERT INTO [t29867] VALUES (100,'من الشعر لحكمة','101','0','0');
INSERT INTO [t29867] VALUES (101,'لما اشتد المشركون على النبي صلى الله عليه وسلم، قال لعمه العباس بن عبد المطلب: يا عم إن','102','0','0');
INSERT INTO [t29867] VALUES (102,'إذا دعا دعا ثلاثا، وإذا سأل سأل ثلاثا','103','0','0');
INSERT INTO [t29867] VALUES (103,'سمعت رجلا قرأ آية على غير ما أقرأنيها رسول الله صلى الله عليه وسلم فأخذت بيده حتى ذهبت به','104','0','0');
INSERT INTO [t29867] VALUES (104,'ذكاة الميتة دباغها','105','0','0');
INSERT INTO [t29867] VALUES (105,'جلود الميتة، فقال: دباغها طهورها','106','0','0');
INSERT INTO [t29867] VALUES (106,'ثلاثة لا ينظر الله إليهم ولا يزكيهم ولهم عذاب أليم: شيخ زان، وملك كذاب، وعائل','107','0','0');
INSERT INTO [t29867] VALUES (107,'انظر أرفع رجل في عينك. فنظرت فإذا رجل في حلة جالس يحدث قوما، فقلت: هذا، فقال: انظر أوضع','108','0','0');
INSERT INTO [t29867] VALUES (108,'إذا قال الرجل لصاحبه: كافر , فإنها تجب على أحدهما، فإن كان الذي قيل له كافر فهو كافر،','109','0','0');
INSERT INTO [t29867] VALUES (109,'بينما رسول الله صلى الله عليه وسلم في بيتي إذ قالت الخادم: علي وفاطمة بالسدة، قال: قومي','110','0','0');
INSERT INTO [t29867] VALUES (110,'على كل نفس كل يوم طلعت فيه الشمس صدقة منه على نفسه، قال: قلت رسول الله من أين نتصدق وليس','111','0','0');
INSERT INTO [t29867] VALUES (111,'الدواوين عند الله ثلاثة: ديوان لا يعبأ الله به شيئا، وديوان لا يترك الله منه شيئا، وديوان','112','0','0');
INSERT INTO [t29867] VALUES (112,'{دكا} [الأعراف: ١٤٣] , قال: ساخ الجبل فهو يهوي بعد','113','0','0');
INSERT INTO [t29867] VALUES (113,'رجلا أتى النبي صلى الله عليه وسلم، قال: إني أحبك.','114','0','0');
INSERT INTO [t29867] VALUES (114,'أول من يكسى يوم القيامة حلة من النار إبليس فيضعه على حاجبيه ويسحبها من خلفه ويقول: يا','115','0','0');
INSERT INTO [t29867] VALUES (115,'ما شهد رجل على رجل بكفر إلا باء به أحدهما، إن كان كافرا فهو كما قال، وإن لم يكن كافرا فقد','116','0','0');
INSERT INTO [t29867] VALUES (116,'تزوج رسول الله صلى الله عليه وسلم ميمونة بنت الحارث وهو محرم','117','0','0');
INSERT INTO [t29867] VALUES (117,'اطلبوا ليلة القدر في العشر الأواخر','118','0','0');
INSERT INTO [t29867] VALUES (118,'فقال القوم عرس بنا، فقال: من يوقظنا؟ فقلت: أنا أحرسكم فأوقظكم، فنمت وناموا فما أيقظنا إلا','119','0','0');
INSERT INTO [t29867] VALUES (119,'خرج رسول الله صلى الله عليه وسلم إلى صلاة الغداة ورأسه يقطر ماء، ثم صام يومه','120','0','0');
INSERT INTO [t29867] VALUES (120,'لا يزيد في السفر على ركعتين، لا يصلي قبلها ولا بعدها.','121','0','0');
INSERT INTO [t29867] VALUES (121,'إن أتتك الصلاة في السفر أتصلي قبلها وبعدها؟ قال: صحبت رسول الله صلى الله عليه وسلم كذا','122','0','0');
INSERT INTO [t29867] VALUES (122,'الحياء خير كله','123','0','0');
INSERT INTO [t29867] VALUES (123,'ما كان من حلف في الجاهلية لم يزده الإسلام إلا شدة وحدة','124','0','0');
INSERT INTO [t29867] VALUES (124,'لما كان يوم حنين وجمعت هوازن وغطفان للنبي صلى الله عليه وسلم جمعا كثيرا، والنبي صلى الله','125','0','0');
INSERT INTO [t29867] VALUES (125,'بين يدي الساعة تسليم الخاصة وتفشو التجارة، حتى تعين المرأة زوجها على التجارة، وحتى يوشك','126','0','0');
INSERT INTO [t29867] VALUES (126,'ثلاثة لا يكلمهم الله ولا ينظر إليهم يوم القيامة ولا يزكيهم، ولهم عذاب','127','0','0');
INSERT INTO [t29867] VALUES (127,'يؤتى بجهنم يوم القيامة تجر، لها سبعون ألف زمام، مع كل زمام سبعون ألف','128','0','0');
INSERT INTO [t29867] VALUES (128,'لا يعجز أحدكم أو يغلب أن يقرأ كل ليلة ثلث القرآن.','129','0','0');
INSERT INTO [t29867] VALUES (129,'صلاة الليل مثنى مثنى، فإذا هب الصبح فركعة','130','0','0');
INSERT INTO [t29867] VALUES (130,'من كذب علي متعمدا فليتبوأ مقعده من النار','131','0','0');
INSERT INTO [t29867] VALUES (131,'الخيل معقود في نواصيها الخير إلى يوم القيامة','132','0','0');
INSERT INTO [t29867] VALUES (132,'من خلع يدا من طاعة لقي الله يوم القيامة لا حجة له.','133','0','0');
INSERT INTO [t29867] VALUES (133,'العز إزاري والكبرياء ردائي، فمن نازعني شيئا منهما عذبته','134','0','0');
INSERT INTO [t29867] VALUES (134,'أبردوا بالظهر فإن الذي تجدون من الحر من فيح جهنم','135','0','0');
INSERT INTO [t29867] VALUES (135,'أيعجز أحدكم أن يقرأ كل ليلة ثلث القرآن؟ قالوا: ومن يطيق ذلك؟ قال: الله الواحد الصمد ثلث','136','0','0');
INSERT INTO [t29867] VALUES (136,'فسألناه خادما، فقال: لا، بل أعلمكما ما هو خير لكما من خادم، تسبحان الله عند منامكما ثلاثا','137','0','0');
