INSERT INTO [t29868] VALUES (1,'زجر عن الصرف، قال نافع: فانطلقت أنا وعبد الله بن عمر. وهذا عن رسول الله صلى الله عليه','2','0','0');
INSERT INTO [t29868] VALUES (2,'إني نهيتكم عن أشياء: عن الأنبذة أو الأشربة فاشربوا، وكل مسكر حرام، ونهيتكم عن زيارة','3','0','0');
INSERT INTO [t29868] VALUES (3,'من سمع سمع الله به، ومن راءى راءى الله به','4','0','0');
INSERT INTO [t29868] VALUES (4,'من راءى راءى الله به، ومن سمع سمع الله به','5','0','0');
INSERT INTO [t29868] VALUES (5,'يمر على القدر فيأخذ منها العرق فيأكل منه , ثم ينطلق إلى الصلاة ما يمضمض ولا','6','0','0');
INSERT INTO [t29868] VALUES (6,'فاستقبل القبلة، ثم بسط يده، ثم قال: اللهم إنما أنا بشر فأيما عبد من عبادك شتمت أو آذيت','7','0','0');
INSERT INTO [t29868] VALUES (7,'من أجنب من الليل فلم يغتسل حتى يصبح فلا صوم له، فبلغ ذلك عائشة، فقالت: لم يقل شيئا، قد','8','0','0');
INSERT INTO [t29868] VALUES (8,'مات رجل وترك ست رجال فأعتقهم عند موته، فجاء ورثته فذكروا ذلك إلى رسول الله صلى الله عليه','9','0','0');
INSERT INTO [t29868] VALUES (9,'إذا دخل أهل الجنة الجنة نادى مناد: يا أهل الجنة تعيشون فلا تموتون أبدا، وتصحون فلا تمرضون','10','0','0');
INSERT INTO [t29868] VALUES (10,'اتقوا الله عز وجل , واسمعوا وأطيعوا وإن أمر عليكم عبد حبشي مجدع، فاسمعوا وأطيعوا ما أقام','11','0','0');
INSERT INTO [t29868] VALUES (11,'اسمعوا وأطيعوا وإن أمر عليكم عبد حبشي مجدع، ما أقام فيكم كتاب الله','12','0','0');
INSERT INTO [t29868] VALUES (12,'لا يسرق السارق وهو مؤمن , ولا يزني وهو مؤمن , فإن فعل شيئا من ذلك برئ الإيمان من قلبه،','13','0','0');
INSERT INTO [t29868] VALUES (13,'لا تبزق بين يديك وأنت في الصلاة ولا عن يمينك، وابزق عن شمالك أو تحت قدمك','14','0','0');
INSERT INTO [t29868] VALUES (14,'إذا أتي بمريض، قال: أذهب البأس رب الناس، اشف أنت الشافي، لا شفاء إلا شفاؤك، شفاء لا يغادر','15','0','0');
INSERT INTO [t29868] VALUES (15,'علي أقضانا، وأبي أقرؤنا، وإنا لندع بعض ما يقول أبي، وأبي يقول: سمعت من رسول الله صلى الله','16','0','0');
INSERT INTO [t29868] VALUES (16,'إذا زادت الإبل على عشرين ومائة ففي كل خمسين حقة , وفي كل أربعين ابنة','17','0','0');
INSERT INTO [t29868] VALUES (17,'إذا أراد الله عز وجل خلق عبد، جامع الرجل المرأة، طار ماؤه في كل عرق وعضو منها، فإذا كان','18','0','0');
INSERT INTO [t29868] VALUES (18,'يفتتح بالتكبير والقراءة ب: {الحمد لله رب العالمين} [الفاتحة: ٢]','19','0','0');
INSERT INTO [t29868] VALUES (19,'يتعوذ من عذاب القبر، ومن فتنة الأعور','20','0','0');
INSERT INTO [t29868] VALUES (20,'لا يحل لرجل أن يعطي عطية يرجع فيها , إلا الوالد فيما يعطي ولده، ومثل الذي يعطي عطية يرجع','21','0','0');
INSERT INTO [t29868] VALUES (21,'إذا بقي ثلث الليل ينزل الله عز وجل إلى السماء الدنيا، فيقول: من ذا الذي يدعوني فأستجيب','22','0','0');
INSERT INTO [t29868] VALUES (22,'متى توتر؟ ، قال: في أول الليل. وقال لعمر: متى توتر؟ ، قال: أوتر من آخر','23','0','0');
INSERT INTO [t29868] VALUES (23,'تعوذ رسول الله، وتمسح وجهه بيده','24','0','0');
INSERT INTO [t29868] VALUES (24,'لا يدخل الجنة أحد إلا برحمة الله، قلنا: ولا أنت يا رسول الله؟ قال: ولا أنا، إلا أن','25','0','0');
INSERT INTO [t29868] VALUES (25,'يكون في آخر الزمان على مظاهر من الفتن وانقطاع من الزمان إمام أو أمير تكون عطيته للناس أن','26','0','0');
INSERT INTO [t29868] VALUES (26,'إن أهل الدرجات العلا من الجنة لينظر إليهم من أسفل منهم كما ينظر أهل الأرض إلى الكوكب في','27','0','0');
INSERT INTO [t29868] VALUES (27,'أما ترضى أن تنزل مني بمنزلة هارون من موسى','28','0','0');
INSERT INTO [t29868] VALUES (28,'نهى عن الذهب بالذهب، والفضة بالفضة، وقال: إني أخاف عليكم الرماء.','29','0','0');
INSERT INTO [t29868] VALUES (29,'لإن بقيت لأنهين الناس أن يسموا: بركة، وأفلح، وأسلم','30','0','0');
INSERT INTO [t29868] VALUES (30,'ينهى عن شتم الهلكى، فلم تسب عليا وقد مات؟','31','0','0');
INSERT INTO [t29868] VALUES (31,'لو رأيته لرأيت الشمس طالعة','32','0','0');
INSERT INTO [t29868] VALUES (32,'نزلت هذه الآية: حافظوا على الصلوات والصلاة العصر، فقرأناها على عهد رسول الله ما شاء الله','33','0','0');
INSERT INTO [t29868] VALUES (33,'إن مما أتخوف عليكم رجل قرأ القرآن حتى إذا رئيت عليه بهجته وكان ردءا للإسلام غيره إلى ما','34','0','0');
INSERT INTO [t29868] VALUES (34,'عليك بما تعرف وتدع ما تنكر , وعليك بخاصة نفسك ودع عوامها','35','0','0');
INSERT INTO [t29868] VALUES (35,'إن صليت الضحى ركعتين لم تكتب من الغافلين، وإن صليتها أربعا كتبت من العابدين، وإن صليتها','36','0','0');
INSERT INTO [t29868] VALUES (36,'ثلاث من كن فيه فهو منافق وإن صلى وصام وزعم أنه مسلم: إذا حدث كذب، وإذا وعد أخلف، وإذا','37','0','0');
INSERT INTO [t29868] VALUES (37,'إن الدعاء ينفع مما نزل ومما لم ينزل، فعليكم عباد الله بالدعاء','38','0','0');
INSERT INTO [t29868] VALUES (38,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوها عصموا مني دماءهم وأموالهم','39','0','0');
INSERT INTO [t29868] VALUES (39,'من أخذ شبرا من الأرض بغير حق طوق يوم القيامة من سبع أرضين','40','0','0');
INSERT INTO [t29868] VALUES (40,'سمع بكاء صبي وهو في الصلاة فخفف صلاته','41','0','0');
INSERT INTO [t29868] VALUES (41,'لما خلق الله عز وجل الخلق، كتب على نفسه بيده عز وجل: إن رحمتي تغلب','42','0','0');
INSERT INTO [t29868] VALUES (42,'ما من أمير عشرة إلا يؤتى به يوم القيامة مغلولا حتى يفكه عنه العدل أو يوبقه','43','0','0');
INSERT INTO [t29868] VALUES (43,'خير صفوف الرجال أولها وشرها آخرها، وخير صفوف النساء آخرها وشرها أولها','44','0','0');
INSERT INTO [t29868] VALUES (44,'إن العبد إذا أوطن المسجد ثم حبسه حاجة أو علة أو سقم ثم راجع ما كان عليه , تبشبش الله عز','45','0','0');
INSERT INTO [t29868] VALUES (45,'صلى بهم وأمامة بنت أبي العاص على عاتقه، فإذا ركع وضعها، وإذا قام حملها','46','0','0');
INSERT INTO [t29868] VALUES (46,'بقتل الوزغ','47','0','0');
INSERT INTO [t29868] VALUES (47,'يأمر بتأخير الصلاة، فسألت عنه , فقالوا: هذا أنس بن رافع بن خديج','48','0','0');
INSERT INTO [t29868] VALUES (48,'من عرج أو كسر فقد حل، وعليه حجة أخرى','49','0','0');
INSERT INTO [t29868] VALUES (49,'في الرجل يرتهن الرهن فيهلك، قال: إن كان أقل ترد عليه الفضل، وإن كان أكثر فهو أمين في','50','0','0');
INSERT INTO [t29868] VALUES (50,'إنا بأرض مخمصة فما يحل لنا من الميتة؟ قال: إذا لم تصطبحوا ولم تغتبقوا ولم تحتفئوا بقلا','51','0','0');
INSERT INTO [t29868] VALUES (51,'فأهدي لنا لحم وهو راقد , فمنا من أكل ومنا من تورع فلم يأكل، فاستيقظ طلحة فوافق من أكله،','52','0','0');
INSERT INTO [t29868] VALUES (52,'أوصيك بكلمات تقولهن في دبر صلاتك، قل: رب أعني على ذكرك وشكرك وحسن','53','0','0');
INSERT INTO [t29868] VALUES (53,'خير الشهداء الذين يبدرون بالشهادة قبل أن يسألوها','54','0','0');
INSERT INTO [t29868] VALUES (54,'لي ذيلا طويلا فأمر على المكان القذر فأسحبه، ثم آتي بعده على المكان الطيب، فقالت: سمعت','55','0','0');
INSERT INTO [t29868] VALUES (55,'أمر ضعفة بني هاشم أن ينفروا من جمع بليل','56','0','0');
INSERT INTO [t29868] VALUES (56,'إذا أتى أحدكم إلى المجلس فليسلم، فإن بدا له أن يجلس فليجلس، فإن قام والقوم جلوس فليسلم،','57','0','0');
INSERT INTO [t29868] VALUES (57,'جاءه أمر سره , فخر ساجدا شكرا لله عز وجل','58','0','0');
INSERT INTO [t29868] VALUES (58,'إذا كنت بأرض أهل الكتاب فلا تأكل من آنيتهم إلا أن لا تجدوا عنها بدا , فإن لم تجدوا عنها','59','0','0');
INSERT INTO [t29868] VALUES (59,'ولد لفلان غلام فسماه محمدا، وكناه أبا القاسم، فزعموا أنك تكره ذلك , فقال: ما أحل اسمي','60','0','0');
INSERT INTO [t29868] VALUES (60,'دخلوا أربعة قبر النبي صلى الله عليه وسلم، فيهم عبد الرحمن بن عوف','61','0','0');
INSERT INTO [t29868] VALUES (61,'لبى حين استوت به راحلته','62','0','0');
INSERT INTO [t29868] VALUES (62,'طاف على راحلته يستلم الأركان بمحجنه، ثم خرج، فطاف بين الصفا والمروة على','63','0','0');
INSERT INTO [t29868] VALUES (63,'عق عن الحسن والحسين كبشا كبشا','64','0','0');
INSERT INTO [t29868] VALUES (64,'يضع فاه على الموضع الذي أشرب منه','65','0','0');
INSERT INTO [t29868] VALUES (65,'يبعث الناس يوم القيامة والسماء تطش عليهم','66','0','0');
INSERT INTO [t29868] VALUES (66,'ألا قلت: مني وأنا الغلام الأنصاري','67','0','0');
INSERT INTO [t29868] VALUES (67,'بعث إلي عمر بن الخطاب فأتيته , فإذا بين يديه نطع عليه ذهب منثور، فقال: هلم فاقسم هذا بين','68','0','0');
INSERT INTO [t29868] VALUES (68,'هذه الأمة مرحومة جعل الله عز وجل عذابها فيها , فإذا كان يوم القيامة دفع إلى كل امرئ منهم','69','0','0');
INSERT INTO [t29868] VALUES (69,'من أدرك من العصر ركعة قبل أن تغرب الشمس فليصلها، ومن أدرك من صلاة الصبح ركعة قبل أن تطلع','70','0','0');
INSERT INTO [t29868] VALUES (70,'ما من نفس تموت لها عند الله خير تحب أن ترجع ولها نعيم الدنيا إلا القتيل في سبيل الله عز','71','0','0');
INSERT INTO [t29868] VALUES (71,'ليلة القدر ليلة سبع وعشرين من رمضان','72','0','0');
INSERT INTO [t29868] VALUES (72,'مثل الجليس الصالح مثل العطار إن لم تصب من عطره، أو قال: يعطيك من عطره، أصبت من ريحه، ومثل','73','0','0');
INSERT INTO [t29868] VALUES (73,'لا يزال البلاء بالمؤمن في نفسه وأهله وماله حتى يلقى الله عز وجل وما عليه','74','0','0');
INSERT INTO [t29868] VALUES (74,'صلى صلاة فلم يقرأ فيها إلا بفاتحة الكتاب','75','0','0');
INSERT INTO [t29868] VALUES (75,'الإمام ضامن، فما صنع فاصنعوا','76','0','0');
INSERT INTO [t29868] VALUES (76,'ما من نفس إلا قد كتب مدخلها ومخرجها وما هي لاقية، فقال رجل من الأنصار: ففيم يا رسول الله','77','0','0');
INSERT INTO [t29868] VALUES (77,'إن الله عز وجل يرفع الدرجة للعبد في الجنة، فيقول: يا رب أنى لي هذه؟ فيقول: باستغفار ولدك','78','0','0');
INSERT INTO [t29868] VALUES (78,'من كذب علي متعمدا فليتبوأ وجهه أو مقعده من النار','79','0','0');
INSERT INTO [t29868] VALUES (79,'لما ثقل رسول الله صلى الله عليه وسلم ضمته فاطمة إلى صدرها، وجعل يتغشاه الكرب، وجعلت فاطمة','80','0','0');
INSERT INTO [t29868] VALUES (80,'لما دخل المصريون على عثمان رحمه الله والمصحف في حجره يقرأ فيه , فبدروا إليه فمد يده','81','0','0');
INSERT INTO [t29868] VALUES (81,'كان يرقد فإذا استيقظ تسوك ثم توضأ وصلى ثماني ركعات , يجلس في كل ركعتين ويسلم ثم يوتر بخمس','82','0','0');
INSERT INTO [t29868] VALUES (82,'اللهم إني أعوذ بك من المأثم والمغرم، فقال قائل: ما أكثر ما تستعيذ من المغرم، قال: إن','83','0','0');
INSERT INTO [t29868] VALUES (83,'من أعتق نسمة أعتق الله عز وجل بكل عضو منها عضوا منه من النار','84','0','0');
INSERT INTO [t29868] VALUES (84,'الحلال بين وإن الحرام بين , وبين ذلك أمور مشتبهات لا يعلمها كثير من الناس , فمن اتقى','85','0','0');
INSERT INTO [t29868] VALUES (85,'أفطر عنده أو عند غيره، فقال: أفطر عندكم الصائمون , وأكل طعامكم الأبرار، وصلت عليكم','86','0','0');
INSERT INTO [t29868] VALUES (86,'لما مات إبراهيم ابن رسول الله صلى الله عليه وسلم صاح أسامة، فقال رسول الله صلى الله عليه','87','0','0');
INSERT INTO [t29868] VALUES (87,'الجنب هل ينام أو يأكل وهو جنب؟ فقال: إذا توضأ وضوءه للصلاة','88','0','0');
INSERT INTO [t29868] VALUES (88,'أي الإيمان أوثق؟ قال: الحب في الله والبغض في الله','89','0','0');
INSERT INTO [t29868] VALUES (89,'أفضل العمل الحب في الله والبغض في الله','90','0','0');
INSERT INTO [t29868] VALUES (90,'إن أوثق عرى الإيمان أن تحب لله عز وجل، وأن تبغض لله','91','0','0');
INSERT INTO [t29868] VALUES (91,'ألا أخبركم برجالكم من أهل الجنة؟ النبي في الجنة , والصديق في الجنة، والشهيد في الجنة،','92','0','0');
INSERT INTO [t29868] VALUES (92,'إن من عباد الله عز وجل لأناسا ما هم بأنبياء ولا شهداء يغبطهم الأنبياء والشهداء يوم','93','0','0');
INSERT INTO [t29868] VALUES (93,'ما نجاة المؤمن؟ قال: احفظ لسانك، وليسعك بيتك، وابك على خطيئتك','94','0','0');
INSERT INTO [t29868] VALUES (94,'أي العمل أفضل؟ قال: الصلاة على ميقاتها، قال: قلت: ثم أي؟ قال: ثم بر الوالدين، قال: قلت:','95','0','0');
INSERT INTO [t29868] VALUES (95,'لا يجمع حب هؤلاء الأربعة إلا في قلب مؤمن: أبي بكر وعمر وعثمان وعلي','96','0','0');
INSERT INTO [t29868] VALUES (96,'لا يجتمع حب هؤلاء الأربعة إلا في قلب مؤمن: أبي بكر، وعمر، وعثمان وعلي','97','0','0');
INSERT INTO [t29868] VALUES (97,'استتيب أبو حنيفة مرتين، قال: وسمعت عبد الله بن إدريس، يقول: كذب من زعم أن الإيمان لا يزيد','98','0','0');
INSERT INTO [t29868] VALUES (98,'عمد أبو حنيفة إلى عرى الإسلام فنقضها عروة عروة','99','0','0');
INSERT INTO [t29868] VALUES (99,'نشزت على الأعمش امرأته فكان رجل يأتيه يقال له أبو البلاد، مكفوف فصيح يتكلم بالإعراب يطلب','100','0','0');
INSERT INTO [t29868] VALUES (100,'لا تغتروا بطول حلم الله عز وجل عنكم واحذروا أسفه , فإنه قال عز وجل: {فلما آسفونا انتقمنا','101','0','0');
INSERT INTO [t29868] VALUES (101,'أحب الأيام إلي يخرج فيه مسافري، وأنكح فيه أيمي، وأختن فيه صبيتي: يوم','102','0','0');
INSERT INTO [t29868] VALUES (102,'سبب خروجك من المطبق والمهدي كان من أغلظ الناس عليك؟ فقال لي: إني كنت في المطبق وقد خفت','103','0','0');
