INSERT INTO [t29888] VALUES (1,'من لا يرحم الناس لا يرحمه الله عز وجل.','2','0','0');
INSERT INTO [t29888] VALUES (2,'إن هذه الحشوش محتضرة فإذا دخلها أحدكم فليقل اللهم إني أعوذ بك من الخبث','3','0','0');
INSERT INTO [t29888] VALUES (3,'لما فرغ رسول الله صلى الله عليه من تلك العطايا التي أعطى الناس، ولم يعط للأنصار، وتكلمت','4','0','0');
INSERT INTO [t29888] VALUES (4,'ثلاث أوصاني بهن خليلي أبو القاسم صلى الله عليه، لا أدعهن أبدا: صوم ثلاثة أيام من كل شهر،','5','0','0');
INSERT INTO [t29888] VALUES (5,'أردف أختك فأعمرها من التنعيم , فإذا هبطت بها من الأكمة فمرها فلتحرم , فإنها عمرة','6','0','0');
INSERT INTO [t29888] VALUES (6,'أول شيء خلق الله عز وجل القلم، فقال: أجر، فجرى تلك الساعة ما هو كائن.','7','0','0');
INSERT INTO [t29888] VALUES (7,'إذا وضعت الجنازة واحتملها الرجال على أعناقهم، فإن كانت صالحة قالت: قدموني، وإن كانت غير','8','0','0');
INSERT INTO [t29888] VALUES (8,'يصلي في الثوب الذي يجامع فيه إذا لم ير فيه أثر','9','0','0');
INSERT INTO [t29888] VALUES (9,'قد أتى آدم عليه السلام هذا البيت ألف آتية من الهند على رجله لم يركب','10','0','0');
INSERT INTO [t29888] VALUES (10,'يصلي وأنا معترضة بينه وبين القبلة','11','0','0');
INSERT INTO [t29888] VALUES (11,'لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا، ولو صرخ أحدكم حتى ينقطع، وسجد حتى ينقطع','12','0','0');
INSERT INTO [t29888] VALUES (12,'رأيت رسول الله صلى الله عليه ليلة أضحيان وعليه حلة حمراء، وكنت أنظر إليه وإلى القمر، فكان','13','0','0');
INSERT INTO [t29888] VALUES (13,'سيأتي على المسلمين زمان يبعث منهم البعث، فيقولون: انظروا هل فيكم من أصحاب رسول الله صلى','14','0','0');
INSERT INTO [t29888] VALUES (14,'ما خير ما أعطي للإنسان؟ قال: الخلق الحسن','15','0','0');
INSERT INTO [t29888] VALUES (15,'الذي بك شر مما تسأليني عنه، قالت: وما هو يا أبا هريرة؟ قال: رقة خمارك، ما أستطيع أرفع','16','0','0');
INSERT INTO [t29888] VALUES (16,'أنا سيد الناس يوم القيامة، فهل تدرون لم ذاك؟ يجمع الله عز وجل الأولين والآخرين في صعيد','17','0','0');
INSERT INTO [t29888] VALUES (17,'يسلم تسليما يسمع النبهان، ولا يوقظ النائم.','18','0','0');
INSERT INTO [t29888] VALUES (18,'كانت متعة النساء في أول الإسلام، كان الرجل يقدم البلد وليس معه من يبصر متاعه، ويصلح','19','0','0');
INSERT INTO [t29888] VALUES (19,'يصلي يوم الجمعة ركعتين','20','0','0');
INSERT INTO [t29888] VALUES (20,'صلى مع النبي صلى الله عليه، وكان يقول في ركوعه: سبحان ربي العظيم، وفي سجوده: سبحان ربي','21','0','0');
INSERT INTO [t29888] VALUES (21,'متى كتبت نبيا؟ قال: كتبت وآدم صلى الله عليه بين الروح والجسد','22','0','0');
INSERT INTO [t29888] VALUES (22,'يدخل الجنة من أمتي سبعون ألفا وسبع مائة بغير حساب.','23','0','0');
INSERT INTO [t29888] VALUES (23,'قرأ النجم فسجد فيها , فسجد الناس كلهم إلا شيخا كبيرا , فإنه أخذ كفا من حصى فقال: هذا يجزئ','24','0','0');
INSERT INTO [t29888] VALUES (24,'مثل صاحب القرآن كمثل صاحب الإبل المعقلة , إذا عاهد عليها أمسكها , وإن أطلقت','25','0','0');
INSERT INTO [t29888] VALUES (25,'من قرأ القرآن ثم نسيه لقي الله عز وجل وهو أجذم','26','0','0');
INSERT INTO [t29888] VALUES (26,'الحجاج والعمار وفد الله عز وجل , إن دعوه أجابهم وإن استغفروه غفر لهم','27','0','0');
INSERT INTO [t29888] VALUES (27,'خير الصدقة ما كان منها عن ظهر غنى , واليد العليا خير من اليد السفلى , وابدأ بمن تعول.','28','0','0');
INSERT INTO [t29888] VALUES (28,'للأنبياء عليهم السلام منابر من ذهب فيجلسون عليها , ويبقى منبري لا أجلس عليه. أو قال: لا','29','0','0');
INSERT INTO [t29888] VALUES (29,'ما الدنيا في الآخرة إلا مثل ما يجعل أحدكم إصبعه في اليم فلينظر بما يرجع أخبرنا دعلج بن','30','0','0');
INSERT INTO [t29888] VALUES (30,'ما اسم ابنك؟ قال: اسمه عزير، فقال رسول الله صلى الله عليه: لا تسمه عزيرا، ولكن سمه عبد','31','0','0');
INSERT INTO [t29888] VALUES (31,'إذا ركع لم يصوب رأسه ولم يشخصه','32','0','0');
INSERT INTO [t29888] VALUES (32,'في مسجد النبي صلى الله عليه رجل يقرأ فوضع ابن عباس عليه الرقباء، فإذا كان يوم ختمته قام','33','0','0');
INSERT INTO [t29888] VALUES (33,'من كذب علي متعمدا فليتبوأ مقعده من النار.','34','0','0');
INSERT INTO [t29888] VALUES (34,'لا تباشر المرأة المرأة فتنعتها للرجل أو لزوجها كأنه ينظر إليها، ولا يتناجا اثنان دون','35','0','0');
INSERT INTO [t29888] VALUES (35,'متى ترمى الجمار؟ قال: إذا رمى إمامك فارمه، فأعدت عليه المسألة قال: كنا ننحر إذا زالت','36','0','0');
INSERT INTO [t29888] VALUES (36,'التقابل مصافحة المؤمن , قال: قلت أخبرني عن قول الله عز وجل: {وأما بنعمة ربك فحدث} [الضحى:','37','0','0');
INSERT INTO [t29888] VALUES (37,'والذي نفسي بيده لا تدخلوا الجنة حتى تؤمنوا , ولا تؤمنوا حتى تحابوا , أولا أدلكم على شيء','38','0','0');
INSERT INTO [t29888] VALUES (38,'إنها ستكون بعدي أثرة وأمور تنكرونها.','39','0','0');
INSERT INTO [t29888] VALUES (39,'صلاة الليل مثنى مثنى , وإذا خشي أحدكم الصبح صلى ركعة واحدة توتر له ما قد','40','0','0');
INSERT INTO [t29888] VALUES (40,'لعنة الله وملائكته والناس أجمعين على من أحدث في الإسلام حدثا أو آوى','41','0','0');
INSERT INTO [t29888] VALUES (41,'ألا أخبركم بخير هذه الأمة أبو بكر وعمر رضي الله عنهم','42','0','0');
INSERT INTO [t29888] VALUES (42,'إن في الجمعة ساعة لا يوافقها عبد مسلم يسأل الله عز وجل فيها خيرا إلا أعطاه. قال: وهي ساعة','43','0','0');
INSERT INTO [t29888] VALUES (43,'لا صلاة بعد الفجر حتى تشرق الشمس، ولا صلاة بعد العصر حتى تغيب الشمس','44','0','0');
INSERT INTO [t29888] VALUES (44,'يوتر بـ سبح , وقل يأيها الكافرون , وقل هو الله أحد وكان إذا انصرف قال ثلاث مرات: سبحان','45','0','0');
INSERT INTO [t29888] VALUES (45,'أرحم أمتي أبو بكر , وأشدهم عمر في دين الله، وأصدقهم حياء عثمان، وأقرأهم أبي، وأعلمهم','46','0','0');
INSERT INTO [t29888] VALUES (46,'إن لله عز وجل مائة رحمة , أنزل منها رحمة واحدة بين الجن والإنس والبهائم والهوام فبها','47','0','0');
INSERT INTO [t29888] VALUES (47,'من حمل علينا السلاح فليس منا','48','0','0');
INSERT INTO [t29888] VALUES (48,'تطلع الشمس بين قرني شيطان','49','0','0');
INSERT INTO [t29888] VALUES (49,'قام حتى تورمت قدماه , فقيل له: تفعل ذلك وقد غفر لك ما تقدم من ذنبك وما تأخر؟ قال: أفلا','50','0','0');
INSERT INTO [t29888] VALUES (50,'كنت أرى الرؤيا أكرهها تحزنني حتى تضجعني فذكرت ذلك للنبي صلى الله عليه، فقال: إذا رأيتها','51','0','0');
INSERT INTO [t29888] VALUES (51,'تلبية رسول الله صلى الله عليه: لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة لك','52','0','0');
INSERT INTO [t29888] VALUES (52,'إذا كان يوم القيامة قيل: يا أهل الجمع غضوا أبصاركم حتى تمر فاطمة بنت محمد صلى الله عليه،','53','0','0');
INSERT INTO [t29888] VALUES (53,'ما هذه الجماعة؟ فقال: هؤلاء قوم اجتمعوا على صابئ لهم قال: فأشرفنا، فإذا رسول الله صلى','54','0','0');
INSERT INTO [t29888] VALUES (54,'كنت أطيب النبي صلى الله عليه عند إحرامه , وقبل أن ينفر يوم النحر.','55','0','0');
INSERT INTO [t29888] VALUES (55,'أين كان النبي صلى الله عليه يضع جبينه؟ قال: بين كفيه','56','0','0');
INSERT INTO [t29888] VALUES (56,'إن هذا المال حلو خضر، وإنه من جاء بحقه يبارك له فيه، ومن يرد الله به خيرا يفقهه في الدين،','57','0','0');
INSERT INTO [t29888] VALUES (57,'خاتم ذهب , قال: فقال: ألم أنه عن هذا , لعن الله لابسه','58','0','0');
INSERT INTO [t29888] VALUES (58,'إذا نعس أحدكم وهو في الصلاة، فليرقد، حتى يذهب عنه النوم، فإن أحدكم إذا صلى وهو ناعس،','59','0','0');
INSERT INTO [t29888] VALUES (59,'إذا رفع رأسه لم نحن ظهورنا حتى نراه قد سجد','60','0','0');
INSERT INTO [t29888] VALUES (60,'فيكم النبوة والمملكة','61','0','0');
INSERT INTO [t29888] VALUES (61,'لا تصحب الملائكة رفقة أو عيرا فيها جرس','62','0','0');
INSERT INTO [t29888] VALUES (62,'يخرج جيش ينخسف بهم ببيداء من الأرض.','63','0','0');
INSERT INTO [t29888] VALUES (63,'سألت ربي عز وجل اللاهين , فوهبهم، فأعطانيهم.','64','0','0');
INSERT INTO [t29888] VALUES (64,'عوذوا بالله من جب الحزن.','65','0','0');
INSERT INTO [t29888] VALUES (65,'ألا أخبركم بخير هذه الأمة بعد نبيها صلى الله عليه؟ قالوا: بلى، قال: أبو بكر رضي الله عنه.','66','0','0');
INSERT INTO [t29888] VALUES (66,'إذا اشتد الجوع فعليك برغيف وكوز من ماء، وعلى الدنيا وأهلها الدمار','67','0','0');
INSERT INTO [t29888] VALUES (67,'ما نلبس إذا أحرمنا؟ قال: البس الإزار والرداء والنعلين، فإن لم يكن إزارا فسراويل، فإن لم','68','0','0');
INSERT INTO [t29888] VALUES (68,'لا يبولن أحدكم في الجحر، وأطفئوا السرج، فإن الفأرة تأخذ الفتيلة، فتحرق على أهل البيت،','69','0','0');
INSERT INTO [t29888] VALUES (69,'إذا سافر، قال: اللهم أنت الصاحب في السفر، والخليفة في الأهل، اللهم اصحبنا في سفرنا،','70','0','0');
INSERT INTO [t29888] VALUES (70,'رأى رجلا يشرب قائما فقال له: قه.','71','0','0');
INSERT INTO [t29888] VALUES (71,'وكيف أنعم وصاحب القرن قد التقم القرن يستمع متى يؤمر فينفخ.','72','0','0');
INSERT INTO [t29888] VALUES (72,'هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين، لا تخبرهما يا','73','0','0');
INSERT INTO [t29888] VALUES (73,'أن رجلا كان بصق على رسول الله صلى الله عليه ويؤذيه، وكان رسول الله صلى الله عليه يتوعده:','74','0','0');
INSERT INTO [t29888] VALUES (74,'إن في الجنة لسوقا في كثبان من مسك , فيأتيه المؤمنون يوم الجمعة، فيبعث الله عز وجل ريح','75','0','0');
INSERT INTO [t29888] VALUES (75,'أنا عند ظن عبدي بي , فليظن بي ما شاء','76','0','0');
INSERT INTO [t29888] VALUES (76,'جاء رجال من المشركين إلى أبي بكر رضي الله عنه، فقالوا: هل لك إلى صاحبك , يزعم أنه أسري به','77','0','0');
INSERT INTO [t29888] VALUES (77,'ليس عدوك الذي إذا قتلك أدخلك الجنة، وإذا قتلته كان لك نورا، أعدى عدو لك نفسك التي بين','78','0','0');
