INSERT INTO [t29897] VALUES (1,'ومسح على خفيه , فقلت: يا رسول الله أنسيت؟ قال: بل أنت نسيت، بهذا أمرني','2','0','0');
INSERT INTO [t29897] VALUES (2,'اسكب لي ماء أو وضوءا.','3','0','0');
INSERT INTO [t29897] VALUES (3,'خرج رسول الله صلى الله عليه وسلم في سفر فنزل منزلا , فسبقته بإداوة ماء، فلما أقبل تلقيته','4','0','0');
INSERT INTO [t29897] VALUES (4,'ما شأنك؟ قلت: حضت.','5','0','0');
INSERT INTO [t29897] VALUES (5,'ماذا يلبس المحرم من الثياب؟ قال: لا يلبس القميص ولا البرانس ولا العمائم ولا السراويلات','6','0','0');
INSERT INTO [t29897] VALUES (6,'كلكم يناجي ربه تبارك وتعالى ليس بينه وبينه ترجمان , ينظر إلى أيمنه فيرى عمله، ثم ينظر','7','0','0');
INSERT INTO [t29897] VALUES (7,'إن من موجبات المغفرة إدخالك السرور على أخيك المسلم , إشباع جوعته، وتنفيس','8','0','0');
INSERT INTO [t29897] VALUES (8,'أصبح من عبادي مؤمن بي وكافر , فأما من قال: مطرنا بنوء كذا وكذا فذاك مؤمن بالنجم كافر بي،','9','0','0');
INSERT INTO [t29897] VALUES (9,'أعتق رجل غلاما له ولم يكن له مال غيره فرد رسول الله صلى الله عليه','10','0','0');
INSERT INTO [t29897] VALUES (10,'شهدت مع رسول الله صلى الله عليه وسلم جنازة فلما أراد أن يصلي عليها , فرأى امرأة فأمر بها','11','0','0');
INSERT INTO [t29897] VALUES (11,'احتجم رسول الله صلى الله عليه وسلم في رمضان فغشي عليه، ولذلك كرهت','12','0','0');
INSERT INTO [t29897] VALUES (12,'لدرهم من الربا أعظم عند الله من ستة وثلاثين زنية يزنيها الرجل','13','0','0');
INSERT INTO [t29897] VALUES (13,'إذا قام من الليل يتهجد يشوص فاه بالسواك','14','0','0');
INSERT INTO [t29897] VALUES (14,'من جاء الجمعة فليغتسل','15','0','0');
INSERT INTO [t29897] VALUES (15,'ليس بالكاذب الذي يصلح بين الناس، وينمي خيرا , أو يقول','16','0','0');
INSERT INTO [t29897] VALUES (16,'لن تؤمنوا حتى تحابوا , أفلا أدلكم فيما تحابوا عليه؟ قالوا: بلى يا رسول الله , قال: أفشوا','17','0','0');
INSERT INTO [t29897] VALUES (17,'من الكبائر أن يسب الرجل والديه.','18','0','0');
INSERT INTO [t29897] VALUES (18,'إذا قدم من سفر فأبصر جدارات المدينة أوضع ناقته , وإن كان على دابة','19','0','0');
INSERT INTO [t29897] VALUES (19,'لم يكن نبي إلا وله خليل من أمته , وإن خليلي أبا بكر بن أبي قحافة , وإن الله اتخذ صاحبكم','20','0','0');
INSERT INTO [t29897] VALUES (20,'من مس ذكره , أو أنثييه , أو رفغيه , فليعد الوضوء','21','0','0');
INSERT INTO [t29897] VALUES (21,'الدعاء هو العبادة , وقرأ: {وقال ربكم ادعوني أستجب لكم} [غافر: ٦٠]','22','0','0');
INSERT INTO [t29897] VALUES (22,'الدجال هو آدم جعد كث الرأس , مطموس العين اليسرى , معه جنة ونار , ناره جنة , وجنته نار ,','23','0','0');
INSERT INTO [t29897] VALUES (23,'اليهود يحبون ميكائيل , ويقولون: إنه يجئ بالغيوث , ويبغضون جبريل , ويقولون: إنه يجئ','24','0','0');
INSERT INTO [t29897] VALUES (24,'{وتنذر به قوما لدا} [مريم: ٩٧] .','25','0','0');
INSERT INTO [t29897] VALUES (25,'{قوما لدا} [مريم: ٩٧] .','26','0','0');
INSERT INTO [t29897] VALUES (26,'إذا أردت أن تصدق تجعلها عن أبويك , يكون لهما زخرا ولا ينقص من أجرك شيء','27','0','0');
INSERT INTO [t29897] VALUES (27,'لم يرخص لأحد أن يصلي على دابته إلا لمريض أو خائف','28','0','0');
INSERT INTO [t29897] VALUES (28,'الصوم نصف الصبر , ولكل شيء زكاة , وزكاة الجسد الصوم','29','0','0');
INSERT INTO [t29897] VALUES (29,'فرض الزكاة في الذهب والفضة , والإبل والبقر , والحنطة والشعير','30','0','0');
INSERT INTO [t29897] VALUES (30,'من لعب بالنرد فقد عصى الله ورسوله','31','0','0');
INSERT INTO [t29897] VALUES (31,'من لعب بالنرد فقد عصى الله ورسوله','32','0','0');
INSERT INTO [t29897] VALUES (32,'يصلي صلاة الضحى في بيته , ولا يصليها في المسجد مخافة أن يراه الناس فيتخذونها','33','0','0');
INSERT INTO [t29897] VALUES (33,'كنت أفرك المني من ثوب رسول الله صلى الله عليه وسلم , ثم يصلي فيه','34','0','0');
INSERT INTO [t29897] VALUES (34,'يقسم الفارس ثلاثة أسهم , للفرس سهمان , ولصاحبه سهم , وللراجل سهم','35','0','0');
INSERT INTO [t29897] VALUES (35,'بياض إبط رسول الله صلى الله عليه وسلم , حين رفع يديه استقبل بهما','36','0','0');
INSERT INTO [t29897] VALUES (36,'فجاءتها جارية لها , أو مولاة بقديدة , فقالت: كلي هذه يا سيدتي , فقد أعجبني طيبها , فقالت:','37','0','0');
INSERT INTO [t29897] VALUES (37,'من مسح رأس يتيم لم يمسحه إلا لله , كانت له بكل شعرة مرت عليها يده حسنات، ومن أحسن إلى','38','0','0');
INSERT INTO [t29897] VALUES (38,'ما الإسلام؟ فقال: أن تشهد أن لا إله إلا الله , وأن محمدا رسول الله.','39','0','0');
INSERT INTO [t29897] VALUES (39,'سبق الكتاب الحذر','40','0','0');
INSERT INTO [t29897] VALUES (40,'بيع الغرر','41','0','0');
INSERT INTO [t29897] VALUES (41,'من مات وليس عليه إمام عامة، فهي ميتة جاهلية يحاسب بما عمل في الإسلام','42','0','0');
INSERT INTO [t29897] VALUES (42,'لو أن لابن آدم واديا من مال لابتغى إليه واديا ثانيا , ولو أعطي ثانيا لابتغى','43','0','0');
INSERT INTO [t29897] VALUES (43,'اجعلوا من صلاتكم في بيوتكم , ولا تتخذوها قبورا أو تجعلوها.','44','0','0');
INSERT INTO [t29897] VALUES (44,'ما من آدمي إلا وله أربعة أعين: عينان في رأسه يبصر منهما أمر دنياه , وعينان في قلبه يبصر','45','0','0');
INSERT INTO [t29897] VALUES (45,'من كذب علي متعمدا فليتبوأ مقعده من النار','46','0','0');
INSERT INTO [t29897] VALUES (46,'يسأل عن منزل خالد بن الوليد , قال: فأتي بشارب , فأمرهم فضربوه بما في أيديهم , فمنهم من','47','0','0');
INSERT INTO [t29897] VALUES (47,'ويسك.','48','0','0');
INSERT INTO [t29897] VALUES (48,'لا يؤمن عبد حتى أكون أحب إليه من نفسه , وتكون عترتي أحب إليه من عترته , ويكون أهلي أحب','49','0','0');
INSERT INTO [t29897] VALUES (49,'من كان له لسانان في الدنيا جعل الله له لسانين من نار في النار يوم','50','0','0');
INSERT INTO [t29897] VALUES (50,'من كان ذا لسانين في الدنيا , جعل الله له لسانين. . . . . . . .','51','0','0');
INSERT INTO [t29897] VALUES (51,'فلما بلغ الشعب الأيسر الذي بين عرفة والمزدلفة أناخ به فبال , فصببت عليه من الإداوة ,','52','0','0');
INSERT INTO [t29897] VALUES (52,'يخطب , فجلست قريبا من أبي بن كعب , فقرأ النبي صلى الله عليه وسلم براءة , فقلت لأبي: متى','53','0','0');
INSERT INTO [t29897] VALUES (53,'من صلى علي عند قبري سمعته , ومن صلى علي نائيا عن قبري أبلغته','54','0','0');
INSERT INTO [t29897] VALUES (54,'احتجم تحت كتفه من الشاة التي أكل يوم خيبر','55','0','0');
INSERT INTO [t29897] VALUES (55,'الرجل يريد أن يأتي أهله فيمذي؟ قال: يغسل ذكره , ويتوضأ.','56','0','0');
INSERT INTO [t29897] VALUES (56,'{فمن تبع هداي} [البقرة: ٣٨]','57','0','0');
INSERT INTO [t29897] VALUES (57,'إن الله جعل الحق على لسان عمر وقلبه أو قلبه ولسانه','58','0','0');
INSERT INTO [t29897] VALUES (58,'يقبل وهو على وضوء , ثم يصلي','59','0','0');
INSERT INTO [t29897] VALUES (59,'لا يعدي شيء شيئا. ثلاثا , قال رجل: يا رسول الله إن النقبة من الجرب تكون بالبعير , أو قال:','60','0','0');
INSERT INTO [t29897] VALUES (60,'الدجال , قال: عينه خضراء كأنها زجاجة , فتعوذوا بالله من عذاب القبر','61','0','0');
INSERT INTO [t29897] VALUES (61,'أفطر الحاجم والمحجوم','62','0','0');
INSERT INTO [t29897] VALUES (62,'نزلت هذه الآية في علي بن أبي طالب: {يأيها الرسول بلغ ما أنزل إليك من ربك} [المائدة:','63','0','0');
INSERT INTO [t29897] VALUES (63,'نزلت هذه الآية: {إنما يريد الله ليذهب عنكم الرجس أهل البيت} [الأحزاب: ٣٣]','64','0','0');
INSERT INTO [t29897] VALUES (64,'لا تقوم الساعة حتى تظهر التجارة والعلم , ويكثر المال حتى يفيض','65','0','0');
INSERT INTO [t29897] VALUES (65,'إذا ما القوم يوما قايسونا ... بمعضلة من الفتيا عنيفة رميناهم بمقياس صليب ... مصيب من طراز','66','0','0');
