INSERT INTO [t29900] VALUES (1,'بعث رجلا على سرية، وكان يقرأ لأصحابه في صلاتهم فيختم ب {قل هو الله أحد {١} الله}','2','0','0');
INSERT INTO [t29900] VALUES (2,'ومن يعدل إذا لم أعدل، قد خبت وخسرت إن لم أعدل. قال عمر: يا رسول الله ائذن لي فيه أضرب','3','0','0');
INSERT INTO [t29900] VALUES (3,'لا تبيعوا الدينار بالدينارين، ولا الدرهم بالدرهمين رواه مسلم وحده في البيوع، عن أبي','4','0','0');
INSERT INTO [t29900] VALUES (4,'فجاءته امرأة تعرض نفسها عليه، فخفض فيها النظر ورفعه، فلم يردها، فقال رجل من أصحابه: يا','5','0','0');
INSERT INTO [t29900] VALUES (5,'لا أدري أنهى رسول الله صلى الله عليه وسلم من أجل أنها كانت حمولة للناس فكره أن تذهب','6','0','0');
INSERT INTO [t29900] VALUES (6,'وقت صلاة الفجر ما لم يطلع قرن الشمس الأول، ووقت صلاة الظهر إذا زالت الشمس عن بطن السماء،','7','0','0');
INSERT INTO [t29900] VALUES (7,'اسكن حراء فما عليك إلا نبي، أو صديق، أو شهيد. وكان عنده أبو بكر، وعمر، وطلحة، والزبير،','8','0','0');
INSERT INTO [t29900] VALUES (8,'قال أبو جهل: اللهم إن كان هذا هو الحق من عندك فأمطر علينا حجارة من السماء، أو ائتنا بعذاب','9','0','0');
INSERT INTO [t29900] VALUES (9,'قال لأبي بن كعب: إن الله أمرني أن أقرئك القرآن، أو أقرأ عليك القرآن. فقال: آلله سماني لك،','10','0','0');
INSERT INTO [t29900] VALUES (10,'{فلا تعضلوهن أن ينكحن أزواجهن} [البقرة: ٢٣٢] قال: حدثني معقل بن يسار المزني أنها نزلت','11','0','0');
INSERT INTO [t29900] VALUES (11,'ما ترك رسول الله صلى الله عليه وسلم بعد موته دينارا ولا درهما ولا أمة ولا شيئا، إلا بغلته','12','0','0');
INSERT INTO [t29900] VALUES (12,'{ومن الناس من يعبد الله على حرف} [الحج: ١١] , قال: كان الرجل يقدم المدينة فإن ولدت امرأته','13','0','0');
INSERT INTO [t29900] VALUES (13,'يستفتح الصلاة بالتكبير، والقراءة ب {الحمد لله رب العالمين} [الفاتحة: ٢] ، وكان إذا ركع لم','14','0','0');
INSERT INTO [t29900] VALUES (14,'في الواشمة لعبد الرحمن بن عابس، فقال: قد سمعته من أم يعقوب , عن عبد الله، فلا أجيء به كما','15','0','0');
INSERT INTO [t29900] VALUES (15,'ليراجعها، فإذا طهرت فإن شاء فليطلقها.','16','0','0');
INSERT INTO [t29900] VALUES (16,'لعن رسول الله صلى الله عليه وسلم الواشمات والموتشمات والمتفلجات للحسن المغيرات خلق الله.','17','0','0');
INSERT INTO [t29900] VALUES (17,'إذا اجتمع ثلاثة فليؤمهم أحدهم، وأحقهم بالإمامة أقرؤهم رواه مسلم، عن الحسن , هذا الحسن بن','18','0','0');
INSERT INTO [t29900] VALUES (18,'خرج رسول الله صلى الله عليه وسلم بالهاجرة إلى البطحاء فتوضأ وصلى الظهر ركعتين، والعصر','19','0','0');
INSERT INTO [t29900] VALUES (19,'يصلي ركعتين بعد العصر، ويخبر أن عائشة رضي الله عنها أخبرته أن رسول الله صلى الله عليه','20','0','0');
INSERT INTO [t29900] VALUES (20,'إذا قحطوا استسقى بالعباس بن عبد المطلب، فقال: اللهم إنا كنا نتوسل إليك بنبينا صلى الله','21','0','0');
INSERT INTO [t29900] VALUES (21,'{أيود أحدكم أن تكون له جنة من نخيل وأعناب} [البقرة: ٢٦٦] .','22','0','0');
INSERT INTO [t29900] VALUES (22,'{ألا إنهم يثنون صدورهم} [هود: ٥] قال: سألته عنها، فقال: أناس كانوا يستحيون أن يتخلوا','23','0','0');
INSERT INTO [t29900] VALUES (23,'قدم ركب من بني تميم على النبي صلى الله عليه وسلم، فقال أبو بكر: أمر القعقاع بن معبد، وقال','24','0','0');
INSERT INTO [t29900] VALUES (24,'يمكث عند زينب بنت جحش ويشرب عندها عسلا، فتواصيت أنا وحفصة، أن أيتنا دخل عليه فلتقل: إني','25','0','0');
INSERT INTO [t29900] VALUES (25,'الحياء من الإيمان.','26','0','0');
INSERT INTO [t29900] VALUES (26,'صفية قد حاضت، قال: أحابستنا هي؟ قالت: قلت: يا رسول الله إنها قد أفاضت، قال: فلا','27','0','0');
INSERT INTO [t29900] VALUES (27,'قضى بالسلب للقاتل، قال: بلى، ولكني استكثرته.','28','0','0');
INSERT INTO [t29900] VALUES (28,'اشتريها وأعتقيها , فإن الولاء لمن أعتق. قالت: وعتقت , فخيرها رسول الله صلى الله عليه وسلم','29','0','0');
INSERT INTO [t29900] VALUES (29,'اشتريها وأعتقيها , فإن الولاء لمن أعتق. قالت: وعتقت، فخيرها رسول الله صلى الله عليه وسلم،','30','0','0');
INSERT INTO [t29900] VALUES (30,'طول صلاة الرجل وقصر خطبته مئنة من فقهه. فأطيلوا الصلاة واقصروا الخطبة , وإن من البيان','31','0','0');
INSERT INTO [t29900] VALUES (31,'صلى بين العمودين تلقاء وجهه في وجه الكعبة','32','0','0');
INSERT INTO [t29900] VALUES (32,'في غار , فأنزلت عليه: والمرسلات عرفا. فإنا لنتلقاها من فيه إذ خرجت علينا حية، فابتدرناها،','33','0','0');
INSERT INTO [t29900] VALUES (33,'من صام يوما في سبيل الله باعده الله عن النار سبعين خريفا. رواه مسلم، عن عبد الرحمن بن','34','0','0');
INSERT INTO [t29900] VALUES (34,'قرأ هذه الآية: {يوم يقوم الناس لرب العالمين} [المطففين: ٦] , قال: يقومون حتى يبلغ الرشح','35','0','0');
INSERT INTO [t29900] VALUES (35,'الربا في النسيئة. رواه مسلم، عن أبي بكر بن أبي شيبة، وسفيان هذا هو ابن','36','0','0');
INSERT INTO [t29900] VALUES (36,'يقرأ علينا القرآن , فيقرأ السجدة فيسجد ونسجد معه , حتى لا يجد أحدنا موضعا لجبينه. رواه','37','0','0');
INSERT INTO [t29900] VALUES (37,'اللهم إني أعوذ بك من زوال نعمتك، وتحول عافيتك، وفجاءة نقمتك، وجميع سخطك. أخرجه مسلم في','38','0','0');
INSERT INTO [t29900] VALUES (38,'بين مصلى النبي صلى الله عليه وسلم وبين الجدار ممر الشاة. اتفقا عليه من حديث عبد العزيز ,','39','0','0');
INSERT INTO [t29900] VALUES (39,'لا آكل وأنا متكئ','40','0','0');
INSERT INTO [t29900] VALUES (40,'في الليل لساعة لا يوافقها رجل مسلم سأل الله عز وجل فيها خيرا من أمر الدنيا والآخرة إلا','41','0','0');
INSERT INTO [t29900] VALUES (41,'حوضي لأبعد من أيلة وعدن , والذي نفسي بيده لآنيته أكثر من عدد النجوم، ولهو أشد بياضا من','42','0','0');
INSERT INTO [t29900] VALUES (42,'نفست أسماء بنت عميس بمحمد بن أبي بكر بالشجرة، وأمر رسول الله صلى الله عليه وسلم أبا بكر','43','0','0');
INSERT INTO [t29900] VALUES (43,'سمل النبي صلى الله عليه وسلم أعينهم لأنهم سملوا أعين الرعاة. تفرد به مسلم فرواه في كتابه','44','0','0');
INSERT INTO [t29900] VALUES (44,'شرب لبنا، ثم دعا بماء فتمضمض، وقال: إن له دسما. أخرجه عن قتيبة','45','0','0');
INSERT INTO [t29900] VALUES (45,'من أبي؟ قال: أبوك فلان. فنزلت: {يأيها الذين آمنوا لا تسألوا عن أشياء} [المائدة: ١٠١]','46','0','0');
INSERT INTO [t29900] VALUES (46,'من عادى لي وليا فقد آذنني بالحرب، وما تقرب إلي عبدي بشيء أحب إلي مما افترضت عليه، وما','47','0','0');
INSERT INTO [t29900] VALUES (47,'من سره أن يبسط عليه رزقه وينسأ في أثره فليصل رحمه. تفرد به البخاري فرواه في البيوع، عن','48','0','0');
INSERT INTO [t29900] VALUES (48,'صليت وراء رسول الله صلى الله عليه وسلم على امرأة ماتت في نفاسها، فقام عليها في الصلاة','49','0','0');
INSERT INTO [t29900] VALUES (49,'لما جاء إلى مكة دخلها من أعلاها وخرج من أسفلها','50','0','0');
INSERT INTO [t29900] VALUES (50,'رجلا مات فدخل الجنة , فقيل له: ما كنت تعمل؟ فإما ذكر، وإما ذكر، فقال: إني كنت أبايع','51','0','0');
INSERT INTO [t29900] VALUES (51,'أنظر هذا الغلام لا تصنعن شيئا حتى تغدو به إلى النبي صلى الله عليه وسلم يحنكه، قال: فغدوت','52','0','0');
INSERT INTO [t29900] VALUES (52,'اشتريها، فإنما الولاء لمن أعتق. وخيرها من زوجها، وكان حرا، وأتي النبي صلى الله عليه وسلم','53','0','0');
INSERT INTO [t29900] VALUES (53,'أما ترضون أن يرجع الناس بالدنيا إلى بيوتهم، وترجعون برسول الله صلى الله عليه وسلم إلى','54','0','0');
INSERT INTO [t29900] VALUES (54,'إذا دخلت على أهلك فلا تدخل ليلا حتى تستحد المغيبة، وتمتشط الشعثة، وقال: عليك بالكيس عليك','55','0','0');
INSERT INTO [t29900] VALUES (55,'أقل سكان الجنة النساء. رواه مسلم، عن محمد بن الوليد في الدعوات','56','0','0');
INSERT INTO [t29900] VALUES (56,'من أنظر معسرا، أو وضع له، أظله الله في ظله.','57','0','0');
INSERT INTO [t29900] VALUES (57,'الربا في النسيئة','58','0','0');
INSERT INTO [t29900] VALUES (58,'لما بنيت الكعبة ذهب عباس والنبي صلى الله عليه وسلم ينقلان الحجارة، فقال عباس: اجعل إزارك','59','0','0');
INSERT INTO [t29900] VALUES (59,'نزلت هذه الآية: {وكلوا واشربوا حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر}','60','0','0');
INSERT INTO [t29900] VALUES (60,'أحدث نفسي بالشيء لأن أخر من السماء أحب إلي من أن أتكلم به، قال: ذاك صريح','61','0','0');
INSERT INTO [t29900] VALUES (61,'أحدث نفسي بالحديث لأن أخر من السماء أحب إلي من أن أتكلم به، فقال: ذاك صريح','62','0','0');
INSERT INTO [t29900] VALUES (62,'إن كان الشؤم في شيء ففي الفرس، والمرأة، والمسكن.','63','0','0');
INSERT INTO [t29900] VALUES (63,'حرمت الظلم على نفسي وجعلته بينكم محرما فلا تظالموا، يا عبادي إنكم الذين تخطئون بالليل','64','0','0');
INSERT INTO [t29900] VALUES (64,'جنتان من ذهب آنيتهما وما فيهما، وجنتان من فضة آنيتهما وما فيهما، وما بين القوم وبين أن','65','0','0');
INSERT INTO [t29900] VALUES (65,'اللهم اغفر لي ذنبي كله دقه وجله أوله وآخره سره وعلانيته.','66','0','0');
INSERT INTO [t29900] VALUES (66,'لا يسمع أحد من هذه الأمة من يهودي أو نصراني ثم يموت ولم يؤمن بالذي أرسلت به إلا كان من','67','0','0');
INSERT INTO [t29900] VALUES (67,'لا ينجي أحدكم عمله. فقال رجل: ولا إياك يا رسول الله، قال: ولا إياي إلا أن يتغمدني الله','68','0','0');
INSERT INTO [t29900] VALUES (68,'نهى عن لقطة الحاج.','69','0','0');
INSERT INTO [t29900] VALUES (69,'لكل نبي دعوة، فأريد أن أختبئ دعوتي إن شاء الله شفاعة لأمتي يوم','70','0','0');
INSERT INTO [t29900] VALUES (70,'أصيب رجل في عهد رسول الله صلى الله عليه وسلم في ثمار ابتاعها , فكثر دينه، فقال رسول الله','71','0','0');
INSERT INTO [t29900] VALUES (71,'من آوى ضالة فهو ضال ما لم يعرفها.','72','0','0');
INSERT INTO [t29900] VALUES (72,'كفارة النذر كفارة اليمين.','73','0','0');
INSERT INTO [t29900] VALUES (73,'لأن يحتزم أحدكم حزمة حطب فيحملها على ظهره فيبيعها، خير من أن يسأل رجلا يعطيه أو','74','0','0');
INSERT INTO [t29900] VALUES (74,'يقول الله عز وجل في إبراهيم: {رب إنهن أضللن كثيرا من الناس فمن تبعني فإنه مني ومن عصاني','75','0','0');
INSERT INTO [t29900] VALUES (75,'أمشي مع رسول الله صلى الله عليه وسلم وعليه برد نجراني غليظ الحاشية، فأدركه أعرابي , فجبذ','76','0','0');
INSERT INTO [t29900] VALUES (76,'وصف لي ناسا إني لأعرف وصفهم في هؤلاء , يقولون الحق بألسنتهم لا يجوز هذا منهم وأشار إلى','77','0','0');
INSERT INTO [t29900] VALUES (77,'دخل علي رسول الله صلى الله عليه وسلم وعندي جاريتان تغنيان بغناء بعاث، فاضطجع على الفراش','78','0','0');
INSERT INTO [t29900] VALUES (78,'لأن يحتزم أحدكم حزمة من حطب فيحملها على ظهره فيبيعها , خير له من أن يسأل رجلا يعطيه أو','79','0','0');
INSERT INTO [t29900] VALUES (79,'ما من صاحب ذهب ولا فضة لا يؤدي حقها إلا جعلت له يوم القيامة صفائح، ثم يحمى عليها في نار','80','0','0');
INSERT INTO [t29900] VALUES (80,'ما من صاحب ذهب ولا فضة لا يؤدي حقها إلا جعلت له يوم القيامة صفائح، ثم يحمى عليها في نار','81','0','0');
