INSERT INTO [t29922] VALUES (1,'نهى عن الوصال. فقيل: إنك تواصل. فقال: لست بمثلكم، إني أطعم وأسقي. هذا حديث صحيح أخرجه أبو','2','0','0');
INSERT INTO [t29922] VALUES (2,'بلالا ينادي بليل، فكلوا واشربوا حتى ينادي ابن أم مكتوم. أنبا أبو عبد الله الفراء، وحدثناه','3','0','0');
INSERT INTO [t29922] VALUES (3,'لطمت جارية فكسرت سنها، فعرضوا عليهم الأرش فأبوا، فطلبوا العفو فأبوا، فأتوا النبي صلى الله','4','0','0');
INSERT INTO [t29922] VALUES (4,'يسوق بهم رجل يقال له: أنجشة بأمهات المؤمنين، قال: فاشتد بهم السير، فقال النبي صلى الله','5','0','0');
INSERT INTO [t29922] VALUES (5,'ثلاثة لا ينظر الله إليهم يوم القيامة: الشيخ الزاني، والإمام الكذاب، والعابد المزهو. قال','6','0','0');
INSERT INTO [t29922] VALUES (6,'مما أدرك الناس من كلام النبوة الأولى: إذا لم تستحي فاصنع ما شئت. قال عبيد الله بن سعيد','7','0','0');
INSERT INTO [t29922] VALUES (7,'يصاح برجل من أمتي على رءوس الخلائق يوم القيامة، فينشر له تسعة وتسعين سجلا، كل سجل منهما','8','0','0');
INSERT INTO [t29922] VALUES (8,'إذا كان يوم القيامة أعطى الله تبارك وتعالى الرجل من أمة محمد صلى الله عليه وسلم اليهودي،','9','0','0');
INSERT INTO [t29922] VALUES (9,'إذا قام يتهجد من الليل قال: اللهم لك الحمد أنت قيم السموات والأرض ومن فيهن، ولك الحمد أنت','10','0','0');
INSERT INTO [t29922] VALUES (10,'إذا قام يتهجد من الليل قال: اللهم لك الحمد ملء السموات والأرض ومن فيهن، ولك الحمد أنت نور','11','0','0');
INSERT INTO [t29922] VALUES (11,'أتي رسول الله صلى الله عليه وسلم بالبراق ليلة أسري به مسرجا ملجما، فاستصعب عليه، فقال','12','0','0');
INSERT INTO [t29922] VALUES (12,'لا يزال العبد في صلاة ما انتظر الصلاة، تقول الملائكة: اللهم اغفر له، اللهم ارحمه، ما لم','13','0','0');
INSERT INTO [t29922] VALUES (13,'ناولني نعلي. فقال: يا رسول الله دعني أنعلك. قال: وما تريد بذلك؟ قال: حبك. فقال رسول الله','14','0','0');
INSERT INTO [t29922] VALUES (14,'الصدقات بالغدوات تذهب بالعاهات. قال جعفر الأندلسي الحافظ على هذا الحديث، لما أخرجه علي بن','15','0','0');
INSERT INTO [t29922] VALUES (15,'أرأيت إن ابتلينا بالبقاء بعدك، فأين ننزل؟ قال: بيت المقدس؛ فإن الصلاة فيه ألف صلاة، لعل','16','0','0');
INSERT INTO [t29922] VALUES (16,'يحمل هذا العلم من كل خلف عدوله، ينفون عنه انتحال المبطلين، وتأويل','17','0','0');
INSERT INTO [t29922] VALUES (17,'من الشعر حكمة','18','0','0');
INSERT INTO [t29922] VALUES (18,'{والبحر المسجور} [الطور: ٦] . قال: الفارغ، خرجت أمة تستقي فرجعت، فقالت: إن الحوض مسجور.','19','0','0');
INSERT INTO [t29922] VALUES (19,'سترون ربكم عز وجل حتى إن أحدكم ليحاضر ربه تبارك وتعالى محاضرة، يقول: عبدي هل تعرف ذنب كذا','20','0','0');
INSERT INTO [t29922] VALUES (20,'المسلم يصلي وخطاياه موضوعة على رأسه، كلما سجد تحاتت فيفرغ حين يفرغ، وقد تحاتت خطاياه قال','21','0','0');
INSERT INTO [t29922] VALUES (21,'لو زرتنا أكثر مما تزورنا.','22','0','0');
INSERT INTO [t29922] VALUES (22,'رجلا جاء بابنه إلى النبي صلى الله عليه وسلم، فقال له رسول الله صلى الله عليه وسلم: أتحبه؟','23','0','0');
INSERT INTO [t29922] VALUES (23,'توضع موازين القسط يوم القيامة، فيجاء بعمل الرجل، فيوضع في الميزان، فيخف، فيجاء بشيء مثل','24','0','0');
INSERT INTO [t29922] VALUES (24,'{نبئنا بتأويله إنا نراك من المحسنين} [يوسف: ٣٦] .','25','0','0');
INSERT INTO [t29922] VALUES (25,'{فوربك لنسألنهم أجمعين {٩٢} عما كانوا يعملون} [الحجر: ٩٢-٩٣] .','26','0','0');
INSERT INTO [t29922] VALUES (26,'{من جاء بالحسنة} [الأنعام: ١٦٠] .','27','0','0');
INSERT INTO [t29922] VALUES (27,'ما أحلى كلام أصحاب النبي صلى الله عليه وسلم. قال: يا بني وتدري لم حلا؟ قال:','28','0','0');
INSERT INTO [t29922] VALUES (28,'ما بال المتهجدين بالليل أحسن الناس وجوها؟ قال: لأنهم خلوا بالرحمن جل وعز، فألبسهم نورا من','29','0','0');
INSERT INTO [t29922] VALUES (29,'فالزم الحق ينزلك الحق منازل أهل الحق يوم لا يقضى إلا بالحق','30','0','0');
INSERT INTO [t29922] VALUES (30,'فلم قال عمر بن الخطاب: يا لله يا للمسلمين. لم فتح تلك وكسر هذه؟ قال: قلت: فتح تلك اللام','31','0','0');
INSERT INTO [t29922] VALUES (31,'هارون الرشيد والناس يعزونه في ابن له توفي في الليل، ويهنئونه في آخر ولد له في تلك الليلة،','32','0','0');
INSERT INTO [t29922] VALUES (32,'إذا سمع الذكر ارتعد، فقال له إبراهيم: إن كنت تملك هذا فما أبالي أن لا أعتد بك، وإن كنت لا','33','0','0');
INSERT INTO [t29922] VALUES (33,'من قبل الله عز وجل منه حسنة أدخله الجنة. قالوا: يا أبا سعيد فأين تذهب حسنات الناس؟ قال:','34','0','0');
INSERT INTO [t29922] VALUES (34,'فقال رجل من القوم: {ومن يقتل مؤمنا متعمدا فجزاؤه جهنم} [النساء: ٩٣] . حتى ختم الآية. قال:','35','0','0');
INSERT INTO [t29922] VALUES (35,'{يحول بين المرء وقلبه} [الأنفال: ٢٤] .','36','0','0');
INSERT INTO [t29922] VALUES (36,'قل: يا كهفي حين تعييني المذاهب، ويا بدئ خلقي لما يشاء، وكنت عن خلقي غنيا، اجعل لي من أمري','37','0','0');
INSERT INTO [t29922] VALUES (37,'بينما أنا أرمي بعض الجمار، إذا أنا بأعرابي جاء فوقف فرمى جماره، ثم دعا بدعوات، وقال في','38','0','0');
INSERT INTO [t29922] VALUES (38,'يكثر من قول: ما شاء الله. فقال رجل: ما أكثر ما يقول مالك ما شاء الله.','39','0','0');
INSERT INTO [t29922] VALUES (39,'كل دعاء محجوب عن السماء، حتى يصلى على محمد وعلى آل محمد صلى الله عليه','40','0','0');
