INSERT INTO [t29933] VALUES (1,'لا يسرق السارق وهو مؤمن، ولا يزني وهو مؤمن، ولا يشرب الخمر وهو مؤمن، فإن فعل شيئا من ذلك','2','0','0');
INSERT INTO [t29933] VALUES (2,'إذا قعدت بين شعبها الأربع، ثم جهدتها به فقد وجب الغسل','3','0','0');
INSERT INTO [t29933] VALUES (3,'لغدوة في سبيل الله عز وجل أو روحة خير من الدنيا جميعا، ولقاب قوس أحدكم من الجنة أو قيد','4','0','0');
INSERT INTO [t29933] VALUES (4,'أيما رجل ترك كنزا مثل له يوم القيامة شجاعا أقرع، قال: يقول: ويلك ما أنت؟ قال: يقول: أنا','5','0','0');
INSERT INTO [t29933] VALUES (5,'نهى أن تصوم المرأة إلا بإذن زوجها','6','0','0');
INSERT INTO [t29933] VALUES (6,'الحمى من كير جهنم , فمن وجد منها شيئا فليسيحها عنه بالماء البارد','7','0','0');
INSERT INTO [t29933] VALUES (7,'إذا صام أحدكم فلا يرفث، ولا يجهل، فإن ظلم أو شتم فليقل: إني صائم','8','0','0');
INSERT INTO [t29933] VALUES (8,'مثل الغازي في سبيل الله عز وجل كمثل القائم لا يفتر من صلاة ولا صوم حتى يرجع , وقال ربكم','9','0','0');
INSERT INTO [t29933] VALUES (9,'إن الله عز وجل يوم خلق السماوات والأرض خلق مائة رحمة كل رحمة طباقها، فقسم رحمة واحدة بين','10','0','0');
INSERT INTO [t29933] VALUES (10,'حرم الله عز وجل الجنة على آدمي يدخلها قبلي , غير أني أنظر فإذا أنا بامرأة بباب من أبواب','11','0','0');
INSERT INTO [t29933] VALUES (11,'العائد في عطيته كمثل الكلب أكل حتى إذا شبع قاء، ثم عاد في قيئه فأكله','12','0','0');
INSERT INTO [t29933] VALUES (12,'إذا صام أحدكم يوما فنسي فأكل وشرب فليتم صومه، فإنما أطعمه الله وسقاه','13','0','0');
INSERT INTO [t29933] VALUES (13,'لا تسبوا الدهر؛ فإن الله عز وجل هو الدهر','14','0','0');
INSERT INTO [t29933] VALUES (14,'لا يبولن أحدكم في الماء الدائم ثم يتوضأ منه','15','0','0');
INSERT INTO [t29933] VALUES (15,'من اشترى نعجة مصراة أو شاة مصراة فحلبها فهو يأخذ النظرين بالخيار بين أن يحوزها , أو يردها','16','0','0');
INSERT INTO [t29933] VALUES (16,'لا تقوم الساعة حتى تقاتلوا أقواما عراض الوجوه، صغار الأعين، خنس الأنف، كأن وجوههم المجان','17','0','0');
INSERT INTO [t29933] VALUES (17,'لولا بنو إسرائيل لم يخنز اللحم ولم يخبث الطعام، ولولا حواء لم تخن أنثى','18','0','0');
INSERT INTO [t29933] VALUES (18,'اشتد غضب الله عز وجل على رجل قتله رسول الله صلى الله عليه وسلم , واشتد غضب الله عز وجل','19','0','0');
INSERT INTO [t29933] VALUES (19,'لله عز وجل مائة رحمة، وأنه قسم واحدة بين أهل الدنيا فوسعتهم إلى آجالهم، وادخر تسعة وتسعين','20','0','0');
INSERT INTO [t29933] VALUES (20,'إن مثل الذي يعود في عطيته كمثل الكلب يعود في قيئه','21','0','0');
INSERT INTO [t29933] VALUES (21,'لولا بنو إسرائيل لم يخنز اللحم، ولم يخبث الطعام، ولولا حواء لم تخن أنثى','22','0','0');
INSERT INTO [t29933] VALUES (22,'أوصاني خليلي صلى الله عليه وسلم بثلاث لست بتاركهن: ألا أنام إلا على وتر، وأن أصوم ثلاثة','23','0','0');
INSERT INTO [t29933] VALUES (23,'إنما ذلك عرق، وليست بالحيضة، فإذا أقبلت الحيضة فاتركي الصلاة، وإذا أدبرت فاغسلي عنك الدم','24','0','0');
INSERT INTO [t29933] VALUES (24,'اللهم إني أعوذ بك من عذاب النار، ومن فتنة النار، ومن فتنة القبر، ومن عذاب القبر، ومن شر','25','0','0');
INSERT INTO [t29933] VALUES (25,'قدم رسول الله صلى الله عليه وسلم من سفر وقد سترت على بيتي درنوكا فيه الخيل أولات الأجنحة','26','0','0');
INSERT INTO [t29933] VALUES (26,'حك رسول الله صلى الله عليه وسلم من القبلة نخامة أو مخاطا أو بزاقا','27','0','0');
INSERT INTO [t29933] VALUES (27,'طيبت رسول الله صلى الله عليه وسلم عند إحرامه بأطيب ما أجد','28','0','0');
INSERT INTO [t29933] VALUES (28,'كانت تتم الصلاة في السفر، ولا تراه على أحد','29','0','0');
INSERT INTO [t29933] VALUES (29,'إذا نعس أحدكم فليرقد حتى يذهب عنه النوم، إن أحدكم إذا صلى وهو ناعس لعله يذهب يستغفر فيسب','30','0','0');
INSERT INTO [t29933] VALUES (30,'يصلي بالليل، وأنا معترضة فيما بينه وبين القبلة، على فراشه الذي ينام عليه، فإذا أراد أن','31','0','0');
INSERT INTO [t29933] VALUES (31,'إن الحمى من فيح جهنم، فأبردوها بالماء','32','0','0');
INSERT INTO [t29933] VALUES (32,'لوددت أني إذا مت كنت نسيا منسيا','33','0','0');
INSERT INTO [t29933] VALUES (33,'كنا نفضل أبا بكر وعمر وعثمان رضي الله عنهم , ثم لا نفضل أحدا على أحد','34','0','0');
INSERT INTO [t29933] VALUES (34,'تنازع زيد بن حارثة وجعفر وعلي في بنت حمزة، فقال رسول الله صلى الله عليه وسلم: أنا أقضي','35','0','0');
INSERT INTO [t29933] VALUES (35,'لقد وارت القبور رجالا لو كانوا أحياء فنظروا إلى مجلسكم لاستحيت منهم','36','0','0');
INSERT INTO [t29933] VALUES (36,'أصاب جارية له، وهو يخفي ذلك من امرأته، فأوجست بذلك، فكافرها، فقالت له: أقره، فقال: قام','37','0','0');
INSERT INTO [t29933] VALUES (37,'كان للزبير بن العوام ألف مملوك يؤدي إليه الخراج، فما يدخل بيته من خراجهم درهم','38','0','0');
INSERT INTO [t29933] VALUES (38,'توضأ أبو الدرداء عند ثغر مطاهر مسجد دمشق، فجعل نعليه إلى جانبه، فجاء رجل فأخذهما، فالتفت','39','0','0');
INSERT INTO [t29933] VALUES (39,'لما عزل عمر بن الخطاب رضي الله عنه معاوية بن أبي سفيان رضي الله عنه عن الشام، وبعث سعيد','40','0','0');
INSERT INTO [t29933] VALUES (40,'أتى رسول الله صلى الله عليه وسلم سعد بن عبادة، فقال: السلام عليكم. قال: فرد عليه سعد وهو','41','0','0');
INSERT INTO [t29933] VALUES (41,'ما تركت بعدي في الناس فتنة أمر على الرجال من النساء','42','0','0');
INSERT INTO [t29933] VALUES (42,'كان يقاوم الرجل بعدما تقام الصلاة فيحدثه فيحدثه','43','0','0');
INSERT INTO [t29933] VALUES (43,'أنا فتيكم','44','0','0');
INSERT INTO [t29933] VALUES (44,'سأل رجل عمر بن الخطاب رضي الله عنه عن النازعات، والمرسلات، والذاريات. أو بعضهن، فقال له','45','0','0');
INSERT INTO [t29933] VALUES (45,'بدء هلاك الأمم من قبل القدر، وأنتم تبلونا، وستبلون بهم أيتها الأمة، فإن لقيتموهم أو','46','0','0');
INSERT INTO [t29933] VALUES (46,'صليت معه الجمعة في المنصورة فلما سلم الإمام قمت في مقامي فصليت، فلما دخل أرسل إلي، فقال:','47','0','0');
INSERT INTO [t29933] VALUES (47,'لتملأ الأرض ظلما وعدوانا، ثم ليخرج إما قال: من أهل بيتي، وإما قال: من عترتي، من يملؤها','48','0','0');
INSERT INTO [t29933] VALUES (48,'يتوضأ ثلاثا ثلاثا، وقال: هكذا رأيت رسول الله صلى الله عليه وسلم يتوضأ','49','0','0');
INSERT INTO [t29933] VALUES (49,'توضأ ثلاثا ثلاثا، ثم قال: هكذا رأيت رسول الله صلى الله عليه وسلم يتوضأ','50','0','0');
INSERT INTO [t29933] VALUES (50,'ما حملكم على أن عمدتم إلى الأنفال وهي من المثاني، وإلى براءة وهي من المئين , فقرنتم','51','0','0');
INSERT INTO [t29933] VALUES (51,'لا يحل دم امرئ مسلم إلا بإحدى ثلاث: رجل ارتد بعد إسلامه فعليه القتل، أو زنى بعد إحصانه','52','0','0');
INSERT INTO [t29933] VALUES (52,'خيركم قال يحيى: وقال الآخر: أفضلكم من تعلم القرآن وعلمه','53','0','0');
INSERT INTO [t29933] VALUES (53,'خيركم أو أفضلكم من تعلم القرآن وعلمه. قال أبو عبد الرحمن: فذاك الذي أقعدني هذا','54','0','0');
INSERT INTO [t29933] VALUES (54,'إن أفضلكم من تعلم القرآن وعلمه','55','0','0');
INSERT INTO [t29933] VALUES (55,'خيركم من تعلم القرآن وعلمه.','56','0','0');
INSERT INTO [t29933] VALUES (56,'من قال علي ما لم أقل فليتبوأ مقعده من النار','57','0','0');
INSERT INTO [t29933] VALUES (57,'من كذب علي متعمدا فليتبوأ مقعده من النار','58','0','0');
INSERT INTO [t29933] VALUES (58,'أنا لك كأبي زرع لأم زرع. قالت: يا رسول الله وما حديث أبي زرع وأم زرع؟ فقال رسول الله صلى','59','0','0');
INSERT INTO [t29933] VALUES (59,'صلاة في مسجدي هذا أحب إلي من ألف صلاة لا تكون عند البيت','60','0','0');
INSERT INTO [t29933] VALUES (60,'الرجل يجامع امرأته ولا يمني، فقال عثمان: يتوضأ كما يتوضأ للصلاة، ويغسل ذكره. قال: وقال','61','0','0');
INSERT INTO [t29933] VALUES (61,'الرجل يجامع فلا ينزل، قال: ليس عليه الغسل إلا الطهور. قال عثمان: سمعته من رسول الله صلى','62','0','0');
INSERT INTO [t29933] VALUES (62,'حين قال لها أهل الإفك ما قالوا , فبرأها الله عز وجل , وذكر الحديث، ولم يذكر فيه','63','0','0');
INSERT INTO [t29933] VALUES (63,'أمرنا أن ننزل الناس منازلهم','64','0','0');
