INSERT INTO [t29940] VALUES (1,'كان أبواك، تعني أبا بكر رضي الله عنه والزبير من {الذين استجابوا لله والرسول من بعد ما','2','0','0');
INSERT INTO [t29940] VALUES (2,'{أرنا الله جهرة} [النساء: ١٥٣] ، يقول: عيانا','3','0','0');
INSERT INTO [t29940] VALUES (3,'بعثني النبي صلى الله عليه وسلم حين نزلت براءة: أن لا يطوف بالبيت عريان','4','0','0');
INSERT INTO [t29940] VALUES (4,'مر النبي صلى الله عليه وسلم في ثماني عشرة من رمضان على رجل يحتجم، فقال: أفطر الحاجم','5','0','0');
INSERT INTO [t29940] VALUES (5,'يقف على بعير له بعرفات من بين قومه، حتى يدفع معهم توفيقا من الله عز وجل','6','0','0');
INSERT INTO [t29940] VALUES (6,'تقرءون خلف الإمام؟ فقلنا: إن فينا من يقرأ.','7','0','0');
INSERT INTO [t29940] VALUES (7,'لا يموتن أحدكم إلا وهو حسن الظن بالله','8','0','0');
INSERT INTO [t29940] VALUES (8,'القاص ينتظر اللعنة، والمستمع ينتظر الرحمة، والتاجر ينتظر الرزق، والمحتكر ينتظر اللعنة ,','9','0','0');
INSERT INTO [t29940] VALUES (9,'لا تتخذوا الضيعة فترغبوا في الدنيا.','10','0','0');
INSERT INTO [t29940] VALUES (10,'للجنة أقرب إلى أحدكم من شراك نعله، والنار مثل ذلك','11','0','0');
INSERT INTO [t29940] VALUES (11,'لا صلاة لمن لا وضوء له، ولا وضوء لمن لم يذكر اسم الله عليه، ولا يؤمن بالله إلا من يؤمن','12','0','0');
INSERT INTO [t29940] VALUES (12,'من مات في طريق مكة حاجا، لم يعرضه الله يوم القيامة، ولم يحاسبه','13','0','0');
INSERT INTO [t29940] VALUES (13,'إذا أراد أحدكم أن يتزوج امرأة فليسأل عن شعرها كما يسأل عن وجهها، فإن الشعر أحد','14','0','0');
INSERT INTO [t29940] VALUES (14,'ما من أمتي من أحد تولد له جارية فلم يسخط ما خلق الله إلا هبط ملك من السماء، له جناحان','15','0','0');
INSERT INTO [t29940] VALUES (15,'خيركم من تعلم القرآن وعلمه. قال أبو عبد الرحمن: فذاك الذي أقعدني هذا المقعد. قال أبو عبد','16','0','0');
INSERT INTO [t29940] VALUES (16,'كان رجل من بني إسرائيل لا يأتيه أحد يستسلفه شيئا إلا أسلفه إياه بكفيل، فأتاه رجل، فقال:','17','0','0');
INSERT INTO [t29940] VALUES (17,'من قاد مكفوفا أربعين ذراعا كان كعدل عتاق رقبة','18','0','0');
INSERT INTO [t29940] VALUES (18,'النظر إلى علي عبادة','19','0','0');
INSERT INTO [t29940] VALUES (19,'القوم يقرأ عندهم القرآن فيصعقون؟ قال: ذاك فعل الخوارج','20','0','0');
INSERT INTO [t29940] VALUES (20,'رأيت النبي صلى الله عليه وسلم عند الصفا وهو مقبل على شخص في صورة الفيل، وهو يلعنه. فقلت:','21','0','0');
INSERT INTO [t29940] VALUES (21,'أتاني جبريل فأمرني أن آمر أصحابي أن يرفعوا أصواتهم بالإهلال','22','0','0');
INSERT INTO [t29940] VALUES (22,'رفاعة طلقني وبت طلاقي، فتزوجت عبد الرحمن بن الزبير، فإنما معه مثل هدبة الثوب. فقال:','23','0','0');
INSERT INTO [t29940] VALUES (23,'هذا شهر زكواتكم، فمن كان عليه دين فليقضه، وزكوا بقية أموالكم','24','0','0');
INSERT INTO [t29940] VALUES (24,'إنك رجل قوي، وإنك تزاحم على الركن فتؤذي الضعيف، فإذا رأيت خلوة فاستلمه، وإلا فكبر','25','0','0');
INSERT INTO [t29940] VALUES (25,'من كانت له إلى الله تعالى حاجة، فليصم الأربعاء والخميس والجمعة، فإذا كان يوم الجمعة تطهر','26','0','0');
INSERT INTO [t29940] VALUES (26,'الله أكبر الله أكبر، بسم الله على ديني ونفسي، بسم الله على ولدي وأهلي، بسم الله على كل','27','0','0');
INSERT INTO [t29940] VALUES (27,'هذا دحية يا رسول الله. فقال: هذا جبريل عليه السلام. قال: يا رسول الله ما يمنعك من بني','28','0','0');
INSERT INTO [t29940] VALUES (28,'لما طردت هاجر أم إسماعيل سارة، وضعها إبراهيم بمكة، عطشت هاجر أم إسماعيل، فنزل جبريل عليها','29','0','0');
INSERT INTO [t29940] VALUES (29,'أهل الدرجات العلا ليراهم من تحتهم، كما يرى الكوكب الدري بالأفق من آفاق السماوات، وإن أبا','30','0','0');
INSERT INTO [t29940] VALUES (30,'من مات لا يشرك بالله شيئا دخل الجنة.','31','0','0');
INSERT INTO [t29940] VALUES (31,'قال رجل من أهل الكتاب: إن الله تعالى يحمل الخلائق على إصبع، والشجر على إصبع، والثرى على','32','0','0');
INSERT INTO [t29940] VALUES (32,'{والأرض جميعا قبضته يوم القيامة} [الزمر: ٦٧] . يعني بين إصبعيه.','33','0','0');
INSERT INTO [t29940] VALUES (33,'أدركت أنا ورجل من الأنصار رجلا، فلما شهرنا عليه السيف، قال: لا إله إلا الله. فلم ينزع حتى','34','0','0');
INSERT INTO [t29940] VALUES (34,'يضرب على آذانهم في القبور أربعين.','35','0','0');
INSERT INTO [t29940] VALUES (35,'لولا أن تضعفوا عن السواك لأمرتكم به عند كل صلاة','36','0','0');
INSERT INTO [t29940] VALUES (36,'رأى رجلا منبطحا على وجهه، فقال: إن هذه لضجعة ما يحبها الله تعالى','37','0','0');
INSERT INTO [t29940] VALUES (37,'علمني عملا يقربني من الجنة ويباعدني من النار. قال: إذا عملت سيئة فأتبعها','38','0','0');
INSERT INTO [t29940] VALUES (38,'{فليس عليكم جناح أن تقصروا من الصلاة إن خفتم} [النساء: ١٠١] . وقد آمن','39','0','0');
INSERT INTO [t29940] VALUES (39,'أبصر علي رسول الله صلى الله عليه وسلم يوما ثيابا خلقانا، قال: ألك مال؟ . قال:','40','0','0');
INSERT INTO [t29940] VALUES (40,'إن رجلا مر بي فأقريته، فمررت به فلم يقرني، أفأقريه؟ قال: نعم','41','0','0');
INSERT INTO [t29940] VALUES (41,'إذا أنفقت المرأة في بيت زوجها غير مفسدة كان لها أجرة، وللزوج مثل ذلك في اكتسابه، وللخازن','42','0','0');
INSERT INTO [t29940] VALUES (42,'آذنك على أن ترفع الحجاب، وأن تستمع سوادي حتى أنهاك','43','0','0');
INSERT INTO [t29940] VALUES (43,'لعن الله السارق يسرق الحبل فتقطع يده، ويسرق البيضة فتقطع يده','44','0','0');
INSERT INTO [t29940] VALUES (44,'إن لي عبدا قد رضيت دينه وأمانته، وإني أريد أن أتزوجه. فقال لها عمر: ليس ذلك لك. قالت: لم','45','0','0');
INSERT INTO [t29940] VALUES (45,'لأن أقول سبحان الله ولا إله إلا الله والله أكبر أحب إلي مما طلعت عليه','46','0','0');
INSERT INTO [t29940] VALUES (46,'ما أدري أي النعمتين لله علي أفضل، هداني إلى الإسلام، وأعفاني عن الهوى.','47','0','0');
INSERT INTO [t29940] VALUES (47,'من قضى لأخيه المؤمن حاجة فكأنما خدم الله تعالى عمره حدثنا محمد بن البراء، حدثني أحمد بن','48','0','0');
INSERT INTO [t29940] VALUES (48,'أنا محمد وأحمد والحاشر والمقفى ونبي الرحمة','49','0','0');
INSERT INTO [t29940] VALUES (49,'إني لي أسماء: أنا محمد وأحمد، والماحي يمحو الله تعالى بي الكفر، والحاشر أحشر الناس على','50','0','0');
INSERT INTO [t29940] VALUES (50,'أصبحوا بالصبح؛ فإنه أعظم الأجر','51','0','0');
INSERT INTO [t29940] VALUES (51,'إذا صمت من الشهر فصم ثلاث عشرة وأربع عشرة وخمس عشرة حدثنا حنبل بن إسحاق، ثنا الحميدي، عن','52','0','0');
INSERT INTO [t29940] VALUES (52,'لا تسأل الإمارة؛ فإنك إن أعطيتها عن مسألة أكلت إليها، وإن أعطيتها عن غير مسألة أعنت','53','0','0');
INSERT INTO [t29940] VALUES (53,'نهى عن بيع التمر حتى يبدو صلاحه','54','0','0');
INSERT INTO [t29940] VALUES (54,'إذا بويع لخليفتين فاقتلوا الأحدث منهما','55','0','0');
INSERT INTO [t29940] VALUES (55,'ثلاثة لا يخيب قائلهن أو فاعلهن، ثلاث وثلاثون تسبيحة دبر الصلاة، وثلاث وثلاثون تحميدة،','56','0','0');
INSERT INTO [t29940] VALUES (56,'إذا ركع فرج أصابعه، وإذا سجد ضم أصابعه الخمس.','57','0','0');
INSERT INTO [t29940] VALUES (57,'كان للنبي صلى الله عليه وسلم تسع نسوة. فقال ذات يوم خيركن أطولكن يدا. فجعلت كل واحدة منهن','58','0','0');
INSERT INTO [t29940] VALUES (58,'رجل أقلف يحج بيت الله؟ قال: حتى يختتن','59','0','0');
INSERT INTO [t29940] VALUES (59,'لا فرع ولا عتيرة.','60','0','0');
INSERT INTO [t29940] VALUES (60,'بينا أنا نائم رأيتني على قليب، فنزعت منها ما شاء الله، ثم نزع ابن أبي قحافة ذنوبا أو','61','0','0');
INSERT INTO [t29940] VALUES (61,'أتاني آت فخيرني بين أن يدخل نصف أمتي الجنة وبين الشفاعة، فاخترت الشفاعة. فقلنا: يا رسول','62','0','0');
INSERT INTO [t29940] VALUES (62,'اللهم بارك لأمتي في بكورها','63','0','0');
INSERT INTO [t29940] VALUES (63,'أما أنا فلا آكل متكئا','64','0','0');
INSERT INTO [t29940] VALUES (64,'لا آكل متكئا','65','0','0');
INSERT INTO [t29940] VALUES (65,'لما حكم سعد أن تقتل مقاتلتهم، وتسبى ذراريهم، وتقسم أموالهم كنت فيهم فأخذوني. . . .','66','0','0');
INSERT INTO [t29940] VALUES (66,'أهل الجنة عشرون ومائة صف , أمتي منهم ثمانون صفا','67','0','0');
INSERT INTO [t29940] VALUES (67,'إني أوعك كما يوعك رجلان منكم','68','0','0');
INSERT INTO [t29940] VALUES (68,'ما من مسلم يصيبه من مرض فما سواه إلا حط عنه سيئاته كما تحط الشجرة','69','0','0');
INSERT INTO [t29940] VALUES (69,'لن يغني حذر من قدر، والدعاء ينفع مما نزل ومما لم ينزل، وإن الدعاء ليستقبل البلاء في','70','0','0');
INSERT INTO [t29940] VALUES (70,'صلاة في مسجدي هذا أفضل من ألف صلاة فيما سواه من المساجد','71','0','0');
INSERT INTO [t29940] VALUES (71,'يتوضأ ويمسح على خفيه','72','0','0');
INSERT INTO [t29940] VALUES (72,'إذا أوى إلى فراشه، قال: اللهم عافني في جسدي، وعافني في سمعي وفي بصري، واجعلهما الوارث','73','0','0');
INSERT INTO [t29940] VALUES (73,'من قتل عصفورا عبثا عج إلى الله يوم القيامة، قال: يا رب إن فلانا قتلني عبثا، ولم يقتلني','74','0','0');
INSERT INTO [t29940] VALUES (74,'حين ماتت ابنته الثانية: ألا أبو أيم أو أخوها يزوج عثمان، فلو كانت عندي ثالثة','75','0','0');
