INSERT INTO [t29952] VALUES (1,'ألا أنبئكم بأكبر الكبائر؟ . قلنا: بلى يا رسول الله. قال: الإشراك بالله، وعقوق الوالدين.','2','0','0');
INSERT INTO [t29952] VALUES (2,'إذا قال القارئ: {غير المغضوب عليهم ولا الضالين} . فقال من خلفه آمين، فوافق قوله قول أهل','3','0','0');
INSERT INTO [t29952] VALUES (3,'اعتكفنا مع رسول الله صلى الله عليه وسلم، العشر الأوسط من شهر رمضان، فلما كان صبيحة عشرين،','4','0','0');
INSERT INTO [t29952] VALUES (4,'تسحروا فإن في السحور بركة.','5','0','0');
INSERT INTO [t29952] VALUES (5,'{إنا فتحنا لك فتحا مبينا} . قال: الحديبية.','6','0','0');
INSERT INTO [t29952] VALUES (6,'يأتي الشيطان أحدكم في صلاته فيلبس عليه صلاته، حتى لا يدري كم صلى، فإذا وجد ذلك أحدكم ,','7','0','0');
INSERT INTO [t29952] VALUES (7,'ما مسست بيدي ديباجا، ولا حريرا، ولا شيئا كان ألين من كف رسول الله صلى الله عليه وسلم، ولا','8','0','0');
INSERT INTO [t29952] VALUES (8,'إن كان الشؤم في شيء، ففي الفرس والمرأة والمسكن.','9','0','0');
INSERT INTO [t29952] VALUES (9,'{ومن الناس من يعبد الله على حرف} . قال: كان الرجل يقدم المدينة، فإن ولدت امرأته غلاما','10','0','0');
INSERT INTO [t29952] VALUES (10,'آمركم بأربع، وأنهاكم عن أربع. الإيمان بالله، شهادة أن لا إله إلا الله وعقد بيده , وإقام','11','0','0');
INSERT INTO [t29952] VALUES (11,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا شهدوا أن لا إله إلا الله، وآمنوا بي','12','0','0');
INSERT INTO [t29952] VALUES (12,'كان على جبل فتحرك، فقال رسول الله صلى الله عليه وسلم: اسكن حراء، فما عليك إلا نبي أو صديق','13','0','0');
INSERT INTO [t29952] VALUES (13,'وأبيض يستسقى الغمام بوجهه ... ثمال اليتامى عصمة للأرامل.','14','0','0');
INSERT INTO [t29952] VALUES (14,'حبسونا عن الصلاة الوسطى حتى غربت الشمس، ملأ الله بيوتهم، وقبورهم، وأجوافهم نارا. شك يحيى','15','0','0');
INSERT INTO [t29952] VALUES (15,'أعتق صفية، وجعل عتقها صداقها.','16','0','0');
INSERT INTO [t29952] VALUES (16,'باع مدبرا','17','0','0');
INSERT INTO [t29952] VALUES (17,'السفر قطعة من العذاب يمنع أحدكم نومه، وطعامه، فإذا قضى أحدكم مهمته , فليعجل الرجوع إلى','18','0','0');
INSERT INTO [t29952] VALUES (18,'كأني أنظر إلى وبيص الطيب في مفرق رسول الله صلى الله عليه وسلم، وهو','19','0','0');
INSERT INTO [t29952] VALUES (19,'هل تدري ما حق الله على العباد؟ أن يعبدوه، ولا يشركوا به شيئا.','20','0','0');
INSERT INTO [t29952] VALUES (20,'لتسون صفوفكم أو ليخالفن الله بين وجوهكم.','21','0','0');
INSERT INTO [t29952] VALUES (21,'ليس أحد يحاسب إلا هلك. قلت: يا رسول الله أليس الله يقول: حسابا يسيرا؟ قال: ذاك، ولكن من','22','0','0');
INSERT INTO [t29952] VALUES (22,'أينام أحدنا وهو جنب؟ قال: نعم إذا توضأ.','23','0','0');
INSERT INTO [t29952] VALUES (23,'من أعتق رقبة مؤمنة أعتق الله بكل عضو منه عضوا منها من النار حتى فرجه','24','0','0');
INSERT INTO [t29952] VALUES (24,'خير صفوف الرجال أولها، وشرها آخرها، وخير صفوف النساء آخرها، وشرها','25','0','0');
INSERT INTO [t29952] VALUES (25,'إني أجد في نفسي الحديث، لأن أخر من السماء أحب إلي من أن أتكلم به. قال: ذاك محض','26','0','0');
INSERT INTO [t29952] VALUES (26,'إن أهون أهل النار عذابا أبو طالب، في رجليه نعلان من نار يغلي منهما','27','0','0');
INSERT INTO [t29952] VALUES (27,'يكبر كلما خفض ورفع، ويحدث أن رسول الله صلى الله عليه وسلم، كان يفعل','28','0','0');
INSERT INTO [t29952] VALUES (28,'إن الذي يجر ثوبه من الخيلاء لا ينظر الله إليه يوم القيامة، م عنه، خ تابعه','29','0','0');
INSERT INTO [t29952] VALUES (29,'{يوم يقوم الناس لرب العالمين} . قال: يقومون حتى يبلغ الرشح إلى أطراف','30','0','0');
INSERT INTO [t29952] VALUES (30,'إن أبغض الرجال إلى الله الألد الخصم، خ عن قبيصة','31','0','0');
INSERT INTO [t29952] VALUES (31,'صلى صلاة الظهر أو العصر، ورجل يقرأ خلفه، فلما انصرف، قال: أيكم قرأ بـ {سبح اسم ربك','32','0','0');
INSERT INTO [t29952] VALUES (32,'وقت الصلوات؟ فقال: وقت صلاة الفجر ما لم يطلع قرن الشمس الأول، ووقت صلاة الظهر إذا زالت','33','0','0');
INSERT INTO [t29952] VALUES (33,'صلاة الليل مثنى مثنى، فإذا خفت الصبح فأوتر بواحدة','34','0','0');
INSERT INTO [t29952] VALUES (34,'قنت بعد الركوع شهرا يدعو على بني عصية','35','0','0');
INSERT INTO [t29952] VALUES (35,'أكان رسول الله صلى الله عليه وسلم، يقرأ في الظهر والعصر؟ فقال: نعم. فقلنا: من أين علمت','36','0','0');
INSERT INTO [t29952] VALUES (36,'ينزل الله إلى السماء الدنيا كل ليلة حين يمضي ثلث الليل، فيقول: أنا الملك، أنا الملك من ذا','37','0','0');
INSERT INTO [t29952] VALUES (37,'مثل المؤمن الذي يقرأ القرآن كمثل الأترجة، ريحها طيب وطعمها طيب، ومثل المؤمن الذي لا يقرأ','38','0','0');
INSERT INTO [t29952] VALUES (38,'صليت مع رسول الله صلى الله عليه وسلم، المغرب فقرأ: بالتين والزيتون.','39','0','0');
INSERT INTO [t29952] VALUES (39,'رويدا يا أنجشة، لا تكسر القوارير.','40','0','0');
INSERT INTO [t29952] VALUES (40,'يمسح من البيت إلا الركنين اليمانيين.','41','0','0');
INSERT INTO [t29952] VALUES (41,'أنتم لخيار أهل البصرة وقراؤهم، فاتلوه، ولا يطولن عليكم الأمد فتقسوا قلوبكم كما قست قلوب','42','0','0');
INSERT INTO [t29952] VALUES (42,'ما كنا ندعوا زيد بن حارثة إلا زيد بن محمد، حتى نزل في القرآن: {ادعوهم لآبائهم هو أقسط عند','43','0','0');
INSERT INTO [t29952] VALUES (43,'إن الله إذا أراد رحمة أمة من عباده قبض نبيها قبلها، فجعله لها فرطا وسلفا، وإذا أراد هلكة','44','0','0');
INSERT INTO [t29952] VALUES (44,'من نسي صلاة فليصلها إذا ذكرها.','45','0','0');
INSERT INTO [t29952] VALUES (45,'أي الإسلام أفضل؟ قال: من سلم المسلمون من لسانه ويده.','46','0','0');
INSERT INTO [t29952] VALUES (46,'إذا كانوا ثلاثة فليؤمهم أحدهم، وأحقهم بالإمامة أقرؤهم.','47','0','0');
INSERT INTO [t29952] VALUES (47,'هم أهل الكتاب جزءوه أجزاء، فآمنوا ببعضه، وكفروا ببعضه.','48','0','0');
INSERT INTO [t29952] VALUES (48,'أرأيتم لو أن نهرا بباب أحدكم يغتسل كل يوم خمس مرات، هل يبقى من درنه شيء؟ . قال: فذلك مثل','49','0','0');
INSERT INTO [t29952] VALUES (49,'من ظلم قيد شبر من الأرض طوق من سبع أرضين يوم القيامة','50','0','0');
INSERT INTO [t29952] VALUES (50,'برئ من الطالقة والحالقة والشاقة.','51','0','0');
INSERT INTO [t29952] VALUES (51,'حججت مع رسول الله صلى الله عليه وسلم، حجة الوداع، فرأيت أسامة بن زيد، وبلالا , أحدهما أخذ','52','0','0');
INSERT INTO [t29952] VALUES (52,'من حمل علينا السلاح فليس منا.','53','0','0');
INSERT INTO [t29952] VALUES (53,'دعا حذيفة وهو جنب فحاد عنه فاغتسل ثم جاء، فقال النبي صلى الله عليه وسلم: مالك؟','54','0','0');
INSERT INTO [t29952] VALUES (54,'لما قدم النبي صلى الله عليه وسلم، المدينة وجد اليهود يصومون عاشوراء، فسئلوا عن ذلك،','55','0','0');
INSERT INTO [t29952] VALUES (55,'لله أشد فرحا بتوبة عبده من أحدكم يسقط على بعيره قد أضله بأرض فلاة.','56','0','0');
INSERT INTO [t29952] VALUES (56,'المرء مع من أحب','57','0','0');
INSERT INTO [t29952] VALUES (57,'وقت صلاة الظهر إذا زالت الشمس، وكان ظل كل شيء مثله ما لم يحضر العصر، ووقت العصر ما لم','58','0','0');
INSERT INTO [t29952] VALUES (58,'من صلى من الليل فليجعل آخر صلاته وترا، فإن رسول الله صلى الله عليه وسلم، كان يأمر','59','0','0');
INSERT INTO [t29952] VALUES (59,'هل تدري ما حق الله على العباد؟ . قلت: الله ورسوله أعلم. قال: أن تعبدوه، ولا تشركوا به','60','0','0');
INSERT INTO [t29952] VALUES (60,'إذا أراد أحدكم أن يأتي الجمعة فليغتسل.','61','0','0');
INSERT INTO [t29952] VALUES (61,'ما من نبي بعثه الله عز وجل في أمة قبلي إلا كان في أمته حواري وأصحاب، يأخذون بسنته ويقتدون','62','0','0');
INSERT INTO [t29952] VALUES (62,'اللهم بارك لنا في شامنا، اللهم بارك لنا في يمننا. قال: قالوا: وفي نجدنا. قال: هنالك','63','0','0');
INSERT INTO [t29952] VALUES (63,'بينما ثلاثة يتماشون. فذكر حديث الغار.','64','0','0');
INSERT INTO [t29952] VALUES (64,'أكل الضب؟ فقال: لا آكله ولا أحرمه.','65','0','0');
