INSERT INTO [t29959] VALUES (1,'اشتد غضب الله على من قتله نبي الله بيده، واشتد غضب الله على من دمى وجه رسول الله صلى الله','1','0','0');
INSERT INTO [t29959] VALUES (2,'إن الإسلام بدأ غريبا','2','0','0');
INSERT INTO [t29959] VALUES (3,'تزعمون أني من آخركم وفاة ألا وأني من أولكم وفاة، ولتتبعني أفنادا يضرب بعضكم رقاب','3','0','0');
INSERT INTO [t29959] VALUES (4,'من توضأ يوم الجمعة، فبها ونعمت، ومن اغتسل فالغسل أفضل','4','0','0');
INSERT INTO [t29959] VALUES (5,'الأرواح جنود مجندة تطوف بالليل، فما تعارف منها ائتلف، وما تناكر منها','5','0','0');
INSERT INTO [t29959] VALUES (6,'قضى بشاهد ويمين','6','0','0');
INSERT INTO [t29959] VALUES (7,'لا يزني الزاني حين يزني وهو مؤمن، ولا يشرب الخمر حين يشربها وهو مؤمن','7','0','0');
INSERT INTO [t29959] VALUES (8,'لينتهين رجال يشخصون بأبصارهم إلى السماء، أو لا ترجع إليهم أبصارهم','8','0','0');
INSERT INTO [t29959] VALUES (9,'زر على النبي صلى الله عليه وسلم قميصه حين قبض. قال ابن سيرين: وزر على أبي هريرة قميصه.','9','0','0');
INSERT INTO [t29959] VALUES (10,'إذا اشتكى قرأ على نفسه بالمعوذات، ونفث','10','0','0');
INSERT INTO [t29959] VALUES (11,'أفطر الحاجم والمحجوم','11','0','0');
INSERT INTO [t29959] VALUES (12,'فرض على النبي صلى الله عليه وسلم، ليلة أسري به الصلوات خمسين، ثم نقصت حتى جعلت خمسا، ثم','12','0','0');
INSERT INTO [t29959] VALUES (13,'العجماء جرحها جبار، والبئر جبار، والمعدن جبار، وفي الركاز الخمس','13','0','0');
INSERT INTO [t29959] VALUES (14,'إذا أعطى أهله قوت سنة تصدق بما بقي','14','0','0');
INSERT INTO [t29959] VALUES (15,'جاء عثمان بن عفان بألف دينار حين جهز النبي صلى الله عليه وسلم جيش العسرة , فصبها في حجر','15','0','0');
INSERT INTO [t29959] VALUES (16,'رجلا من الأنصار ولد له غلام، فسماه القاسم.','16','0','0');
INSERT INTO [t29959] VALUES (17,'إن شرار الناس يوم القيامة من تركه الناس لشره','17','0','0');
INSERT INTO [t29959] VALUES (18,'بايعنا رسول الله صلى الله عليه وسلم، يلقننا فيما استطعتم','18','0','0');
INSERT INTO [t29959] VALUES (19,'غدونا مع رسول الله صلى الله عليه وسلم من منى إلى عرفات , فمنا الملبي، ومنا','19','0','0');
INSERT INTO [t29959] VALUES (20,'طلق امرأته حائضا تطليقة واحدة على عهد النبي صلى الله عليه وسلم، فسأل عمر النبي صلى الله','20','0','0');
INSERT INTO [t29959] VALUES (21,'ابنوا المساجد واتخذوها جما','21','0','0');
INSERT INTO [t29959] VALUES (22,'أرحم الناس بالعيال والصبيان','22','0','0');
INSERT INTO [t29959] VALUES (23,'إن الرجل ليتكلم بالكلمة يضحك بها جلساءه، يهوي بها أبعد من الثريا','23','0','0');
INSERT INTO [t29959] VALUES (24,'مر على جرهد وهو كاشف عن فخذه، فقال: غطها فإنها من العورة','24','0','0');
INSERT INTO [t29959] VALUES (25,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، ويقيموا الصلاة، ويؤتوا الزكاة، فإذا','25','0','0');
INSERT INTO [t29959] VALUES (26,'الحياء من الإيمان','26','0','0');
INSERT INTO [t29959] VALUES (27,'ذكاة الجنين ذكاة أمه','27','0','0');
INSERT INTO [t29959] VALUES (28,'رأى رسول الله صلى الله عليه وسلم حمارا قد وشم في وجهه، فقال: لعن الله من فعل هذا ألا لا','28','0','0');
INSERT INTO [t29959] VALUES (29,'صلاة الليل مثنى مثنى، فإذا خفت الصبح فأوتر بواحدة، واجعل آخر صلاتك','29','0','0');
INSERT INTO [t29959] VALUES (30,'لو تعلمون ما في الصف المقدم لكانت قرعة','30','0','0');
INSERT INTO [t29959] VALUES (31,'خيركم وسفيان: أفضلكم من تعلم القرآن وعلمه','31','0','0');
INSERT INTO [t29959] VALUES (32,'إذا كبر رفع يديه حذو منكبيه','32','0','0');
INSERT INTO [t29959] VALUES (33,'أعرابيا كان على يمين رسول الله صلى الله عليه وسلم، وأبو بكر عن يساره، فأتى بشراب فشرب','33','0','0');
INSERT INTO [t29959] VALUES (34,'من صنع إليه معروف، فقال لفاعله: جزاك الله خيرا , فقد أبلغ في الثناء','34','0','0');
INSERT INTO [t29959] VALUES (35,'يتبع الدباء في الصحفة، فلا أزال أحبه','35','0','0');
INSERT INTO [t29959] VALUES (36,'كلكم راع وكلكم مسئول عن رعيته , فالأمير الذي على الناس راع ومسئول عنهم، والرجل راع على','36','0','0');
INSERT INTO [t29959] VALUES (37,'مر بي الخبيث فأخذته فقبضته قبضا شديدا، حتى قال: قد أوجعتني','37','0','0');
INSERT INTO [t29959] VALUES (38,'لقد هممت أن أبعث رجالا يعلمون الناس السنة والفرائض، كما بعث عيسى ابن مريم الحواريين في','38','0','0');
INSERT INTO [t29959] VALUES (39,'صلى على بساط','39','0','0');
INSERT INTO [t29959] VALUES (40,'إذا دخل أهل الجنة الجنة، قال الله عز وجل: هل تشتهون شيئا، فأزيدكم؟ .','40','0','0');
INSERT INTO [t29959] VALUES (41,'لا تصدقوا أهل الكتاب ولا تكذبوهم، وقولوا: آمنا بالله وما أنزل إلينا وما أنزل إليكم وإلهنا','41','0','0');
INSERT INTO [t29959] VALUES (42,'الرحم شجنة مني، فمن وصلها وصلته، ومن قطعها قطعته','42','0','0');
INSERT INTO [t29959] VALUES (43,'القرآن أفضل من كل شيء دون الله عز وجل , فمن وقر القرآن فقد وقر الله عز وجل، ومن لم يوقر','43','0','0');
INSERT INTO [t29959] VALUES (44,'ضليع الفم، أشكل العينين، منهوس العقب.','44','0','0');
INSERT INTO [t29959] VALUES (45,'ضرب الوجه أو أن يضرب الوجه، قال أبو حاتم: لم يرو هذا عن سفيان غير عبيد الله بن موسى، على','45','0','0');
INSERT INTO [t29959] VALUES (46,'أن رجلا في عهد رسول الله صلى الله عليه وسلم , كان يتبع قذى المسجد فيلقطه، ففقده رسول الله','46','0','0');
INSERT INTO [t29959] VALUES (47,'ركع دون الصف، فقال له النبي صلى الله عليه وسلم: زادك الله حرصا ولا تعد','47','0','0');
INSERT INTO [t29959] VALUES (48,'لبيك بحجة وعمرة معا','48','0','0');
INSERT INTO [t29959] VALUES (49,'من أراد أن ينظر إلى آدم في علمه، وإلى نوح في فهمه، وإلى إبراهيم في حلمه، وإلى يحيى بن','49','0','0');
INSERT INTO [t29959] VALUES (50,'يرفع يديه إذا كبر، وإذا ركع، وإذا رفع رأسه من الركوع','50','0','0');
INSERT INTO [t29959] VALUES (51,'فأي امرئ وطئ امرأة فولدت منه، فله أن يستمتع منها ما عاش، وإذا مات فهي حرة. فقال عبد','52','0','0');
INSERT INTO [t29959] VALUES (52,'عائشة حاضت قبل أن تطوف بالبيت، فأمرها النبي صلى الله عليه وسلم، أن تفعل ما يفعل الحاج،','53','0','0');
INSERT INTO [t29959] VALUES (53,'أما يخشى الذي يرفع رأسه قبل الإمام أن يحول الله رأسه رأس حمار','54','0','0');
INSERT INTO [t29959] VALUES (54,'دخل يوم فتح مكة، وعليه عمامة سوداء','55','0','0');
INSERT INTO [t29959] VALUES (55,'رفعت لي السدرة، فإذا أربعة أنهار: نهران ظاهران، ونهران باطنان. فأما الظاهران: فالنيل،','56','0','0');
INSERT INTO [t29959] VALUES (56,'اتقوا النار ولو بشق تمرة','57','0','0');
INSERT INTO [t29959] VALUES (57,'يصلي ليلا طويلا قائما، وليلا طويلا قاعدا.','58','0','0');
INSERT INTO [t29959] VALUES (58,'لولا أن الكلاب أمة من الأمم لأمرت بقتلها، فاقتلوا منها كل أسود بهيم، ومن اتخذ كلبا غير','59','0','0');
INSERT INTO [t29959] VALUES (59,'عن المواقعة قبل الملاعنة','60','0','0');
INSERT INTO [t29959] VALUES (60,'العلم خزائن ومفتاحه السؤال، فسلوا يرحمكم الله، فإنه يؤجر فيه أربعة: السائل والمستمع،','61','0','0');
INSERT INTO [t29959] VALUES (61,'صلاة في مسجدي هذا أفضل من ألف صلاة فيما سواه من المساجد إلا الكعبة','62','0','0');
INSERT INTO [t29959] VALUES (62,'يأتيكم رجال من قبل المشرق ويتعلمون، فإذا جاءوكم , فاستوصوا بهم خيرا.','63','0','0');
INSERT INTO [t29959] VALUES (63,'من أراد هوان قريش أهانه الله. وأنك يا عدو الله لم ترض بأن أردت ذلك من قريش، حتى تخطيت فيه','64','0','0');
INSERT INTO [t29959] VALUES (64,'تعلموا الرمي، فإن ما بين الفرضتين روضة من رياض الجنة','65','0','0');
INSERT INTO [t29959] VALUES (65,'طلب العلم فريضة على كل مسلم.','66','0','0');
INSERT INTO [t29959] VALUES (66,'من لم يطلب العلم صغيرا وطلبه كبيرا، فمات على ذلك مات شهيدا','67','0','0');
INSERT INTO [t29959] VALUES (67,'إن هذا الدين متين، فأوغل فيه برفق، ولا تبغض إلى نفسك عبادة الله، فإن المنبت لا أرضا قطع','68','0','0');
INSERT INTO [t29959] VALUES (68,'من رأى صاحب بلاء، فقال: الحمد لله الذي عافاني مما ابتلاك به , وفضلني عليك وعلى كثير ممن','69','0','0');
INSERT INTO [t29959] VALUES (69,'أما الذي لربي: فتعبدوه ولا تشركوا به شيئا، وأما الذي أسأل لنفسي: فتمنعوني مما تمنعون منه','70','0','0');
INSERT INTO [t29959] VALUES (70,'المساجد سوق من أسواق الآخرة، من دخلها كان ضيف الله عز وجل، يراه أهلا للمغفرة، ويحييه','71','0','0');
INSERT INTO [t29959] VALUES (71,'إن لكم في كل جمعة حجة وعمرة، فالحجة الهجير للجمعة، والعمرة انتظار العصر بعد','72','0','0');
INSERT INTO [t29959] VALUES (72,'إذا سافر ومعه النساء، قال: يا أنجشة أرفق بالقوارير.','73','0','0');
INSERT INTO [t29959] VALUES (73,'خاتم النبي صلى الله عليه وسلم، من فضة، وكان فصه منه','74','0','0');
INSERT INTO [t29959] VALUES (74,'المؤذنون أمناء المؤمنين على صلاتهم، وفطرهم وأضحياتهم','75','0','0');
INSERT INTO [t29959] VALUES (75,'من صام يوما في سبيل الله باعد الله وجهه من النار سبعين خريفا','76','0','0');
INSERT INTO [t29959] VALUES (76,'حاضت صفية بنت حيي، فذكر ذلك لرسول الله صلى الله عليه وسلم، فقال أحابستنا هي؟ . فقيل إنها','77','0','0');
INSERT INTO [t29959] VALUES (77,'من مات في هذا الوجه من حاج أو معتمر لم يعرض، ولم يحاسب، وقيل له: ادخل','78','0','0');
INSERT INTO [t29959] VALUES (78,'التارك الأمر بالمعروف والنهي عن المنكر ليس بمؤمن بالقرآن ولا بي','79','0','0');
INSERT INTO [t29959] VALUES (79,'إن السلام اسم من أسماء الله عز وجل وضعه في الأرض، فأفشوه بينكم، وإن الرجل المسلم إذا مر','80','0','0');
INSERT INTO [t29959] VALUES (80,'برئ من الشح من أدى الزكاة وقرى الضيف','81','0','0');
INSERT INTO [t29959] VALUES (81,'من أحب لقاء الله أحب الله لقاءه، ومن كره لقاء الله كره الله لقاءه، والموت قبل لقاء','82','0','0');
INSERT INTO [t29959] VALUES (82,'إني أنا الله لا إله إلا أنا، من أقر لي بالتوحيد دخل حصني، ومن دخل حصني أمن من','83','0','0');
INSERT INTO [t29959] VALUES (83,'إن الله عز وجل يعطي المؤمن جوازا على الصراط، بسم الله الرحمن الرحيم: هذا الكتاب من الله','84','0','0');
INSERT INTO [t29959] VALUES (84,'فضل دهن البنفسج على سائر الأدهان، كفضلي عليكم','85','0','0');
INSERT INTO [t29959] VALUES (85,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوا عصموا مني، دماءهم','86','0','0');
