INSERT INTO [t29972] VALUES (1,'إذا جاء أحدكم إلى الصلاة فلا يسع، ولكن يمشي وعليه السكينة والوقار، فليصل ما أدرك وليقض ما','2','0','0');
INSERT INTO [t29972] VALUES (2,'إنما نسمة المسلم طير يعلق في شجر الجنة حتى يرجعه الله في جسده يوم','3','0','0');
INSERT INTO [t29972] VALUES (3,'قل ما كان رسول الله صلى الله عليه وسلم يخرج إذا أراد سفرا إلا يوم','4','0','0');
INSERT INTO [t29972] VALUES (4,'أفطر الحاجم والمحجوم','5','0','0');
INSERT INTO [t29972] VALUES (5,'لأن يجلس أحدكم على جمرة فتحرق ثيابه حتى تصل إلى جلده خير له من أن يجلس على','6','0','0');
INSERT INTO [t29972] VALUES (6,'إذا أشكل على أحدكم وهو في صلاته أخرج منه شيء أم لم يخرج؟ فلا ينفتل حتى يسمع صوتا أو يشم','7','0','0');
INSERT INTO [t29972] VALUES (7,'يا أنس أكثر من الصلاة في بيتك يكثر خير بيتك، وسلم على من لقيت من أمتي تكثر','8','0','0');
INSERT INTO [t29972] VALUES (8,'إذا كان أحدكم صائما فلا يرفث ولا يجهل وإن امرؤ شاتمه أو قاتله فليقل إني','9','0','0');
INSERT INTO [t29972] VALUES (9,'لكل عمل باب من أبواب الجنة يدعون بذلك العمل، ولأهل الصيام باب، يقال له','10','0','0');
INSERT INTO [t29972] VALUES (10,'لكل عمل كفارة، والصوم لي وأنا أجزي به، ولخلوف فم الصائم أطيب عند الله من ريح','11','0','0');
INSERT INTO [t29972] VALUES (11,'للصائم فرحتان عند فطره، وحين يلقى ربه عز وجل','12','0','0');
INSERT INTO [t29972] VALUES (12,'من تسمع إلى حديث قوم وهم له كارهون، صب في أذنه الآنك، ومن تحلم كلف أن يعقد شعيرة، ويعذب','13','0','0');
INSERT INTO [t29972] VALUES (13,'لا تزال طائفة من أمتي ظاهرة على الدين عزيزة إلى يوم القيامة','14','0','0');
INSERT INTO [t29972] VALUES (14,'إن أهل الدرجات العلا ليراهم من تحتهم، كما يرى الكوكب الدري في الأفق من آفاق السماء، وإن','15','0','0');
INSERT INTO [t29972] VALUES (15,'أد الأمانة إلى من ائتمنك، ولا تخن من خانك','16','0','0');
INSERT INTO [t29972] VALUES (16,'لقي شيطان المؤمن شيطان الكافر، فإذا شيطان المؤمن مهزول، وإذا شيطان الكافر سمين، فقال: ما','17','0','0');
INSERT INTO [t29972] VALUES (17,'من رآني في المنام فقد رآني، فإن الشيطان لا يتمثل على صورتي','18','0','0');
INSERT INTO [t29972] VALUES (18,'من بات وفي يده ريح غمر فأصابه شيء، فلا يلومن إلا نفسه','19','0','0');
INSERT INTO [t29972] VALUES (19,'إذا قال الرجل سبحان الله، قال الملك: والحمد لله، وإذا قال: سبحان الله والحمد لله، قال','20','0','0');
INSERT INTO [t29972] VALUES (20,'إذا عملت سيئة فأتبعها حسنة.','21','0','0');
INSERT INTO [t29972] VALUES (21,'إن هذه لضجعة ما يحبها الله تعالى','22','0','0');
INSERT INTO [t29972] VALUES (22,': {فليس عليكم جناح أن تقصروا من الصلاة إن خفتم} [النساء: ١٠١] .','23','0','0');
INSERT INTO [t29972] VALUES (23,'إذا انقطع شسع أحدكم فلا يمش في نعله الأخرى حتى يصلحها','24','0','0');
INSERT INTO [t29972] VALUES (24,'لا تسبوا أصحابي، فوالذي نفسي بيده لو أن أحدكم أنفق مثل أحد ذهبا ما أدرك مد أحدهم ولا','25','0','0');
INSERT INTO [t29972] VALUES (25,'طلب الحلال واجب على كل مسلم','26','0','0');
INSERT INTO [t29972] VALUES (26,'أول ما يحاسب به العبد صلاته، فإن تمت قبل منه سائر عمله، وإن نقصت، قال: انظروا هل له من','27','0','0');
INSERT INTO [t29972] VALUES (27,'تنكح المرأة لمالها ولجمالها ولدينها، فعليك بذات الدين تربت يداك','28','0','0');
INSERT INTO [t29972] VALUES (28,'أول ما يحاسب به العبد يوم القيامة، يحاسب بصلاته، فإن صلحت فقد أفلح وأنجح، وإن فسدت فقد','29','0','0');
INSERT INTO [t29972] VALUES (29,'نعم الرجل أبو بكر، نعم الرجل عمر.','30','0','0');
INSERT INTO [t29972] VALUES (30,'قل. قلت: وما أقول؟ قال: قل أعوذ برب الفلق. حتى ختمها قل أعوذ برب','31','0','0');
INSERT INTO [t29972] VALUES (31,'إن الشونيز ينفع بإذن الله من كل داء إلا الموت','32','0','0');
INSERT INTO [t29972] VALUES (32,'ما شبع رسول الله صلى الله عليه وسلم من طعام مأدوم حتى فارق الدنيا','33','0','0');
INSERT INTO [t29972] VALUES (33,'استقيموا ولن تحصوا، واعلموا أن خير أعمالكم الصلاة، ولن يحافظ على الوضوء إلا','34','0','0');
INSERT INTO [t29972] VALUES (34,'بعثني النبي صلى الله عليه وسلم إلى رجل تزوج امرأة أبيه آتيه برأسه','35','0','0');
INSERT INTO [t29972] VALUES (35,'كان موسى إذا اغتسل اعتزل وحده، فقال بنو إسرائيل، أو من قال منهم: ما يفعل ذلك إلا أنه آدر،','36','0','0');
INSERT INTO [t29972] VALUES (36,'{غير المغضوب عليهم ولا الضالين} [الفاتحة: ٧] .','37','0','0');
INSERT INTO [t29972] VALUES (37,'ما ذئبان جائعان في حظيرة وثيقة يأكلان ويفترسان، بأسرع من حب الشرف وحب المال في دين المرء','38','0','0');
INSERT INTO [t29972] VALUES (38,'من أراد سخط الله ورضا الناس عاد حامده من الناس ذاما','39','0','0');
INSERT INTO [t29972] VALUES (39,'أذهب البأس رب الناس، واشف أنت الشافي لا شفاء إلا شفاؤك، شفاء لا يغادر','40','0','0');
INSERT INTO [t29972] VALUES (40,'من يحرم الرفق يحرم الخير','41','0','0');
INSERT INTO [t29972] VALUES (41,'صلوا على من قال: لا إله إلا الله، وصلوا وراء من قال: لا إله إلا الله','42','0','0');
INSERT INTO [t29972] VALUES (42,'إذا أنفق المسلم على أهله نفقة وهو يحتسبها كانت له صدقة','43','0','0');
