INSERT INTO [t29972] VALUES (44,'ليس الغنى عن كثرة العرض إنما الغنى غنى النفس','45','0','0');
INSERT INTO [t29972] VALUES (45,'ألا تصفون كما تصف الملائكة عند ربهم.','46','0','0');
