INSERT INTO [t29972] VALUES (54,'يا علي إن مثل المصلي مثل حبلى حملت فلما دنا نفاسها أسقطت لا هي ذات حمل ولا هي ذات أولاد،','55','0','0');
INSERT INTO [t29972] VALUES (55,'من قال: رضيت بالله ربا وبالإسلام دينا وبمحمد، صلى الله عليه وسلم، رسولا وجبت له','56','0','0');
