INSERT INTO [t29972] VALUES (81,'يا أيها الناس ضعوا أصواتكم، فإنكم لا تدعون أصم ولا غائبا، إن الذي تدعون دون ركابكم، ثم','82','0','0');
INSERT INTO [t29972] VALUES (82,'يا أيها الناس إياكم والظلم، فإن الظلم ظلمات يوم القيامة','83','0','0');
