INSERT INTO [t29972] VALUES (84,'من أتاه الله مالا فلم يؤد زكاته مثل له يوم القيامة شجاع أقرع له زبيبتان، فيأخذ بلهزمته،','85','0','0');
INSERT INTO [t29972] VALUES (85,'يا براء كيف تقول إذا أخذت مضجعك؟ قال: قلت: الله ورسوله أعلم. قال: فإذا أويت إلى فراشك،','86','0','0');
INSERT INTO [t29972] VALUES (86,'رب قني عذابك يوم تبعث عبادك','87','0','0');
