INSERT INTO [t29972] VALUES (88,'قفوا.','89','0','0');
INSERT INTO [t29972] VALUES (89,'يكره أن يطرق الرجل أهله ليلا أو كره أن يطرق الرجل أهله ليلا','90','0','0');
INSERT INTO [t29972] VALUES (90,'لا يبغض الأنصار رجل يؤمن بالله إلا أبغضه الله ورسوله','91','0','0');
INSERT INTO [t29972] VALUES (91,'لا تسبوا الدهر، فإن الله هو الدهر','92','0','0');
INSERT INTO [t29972] VALUES (92,'صلى رسول الله صلى الله عليه وسلم، بعدما قدم المدينة ستة عشر شهرا نحو بيت المقدس، ثم حولت','93','0','0');
INSERT INTO [t29972] VALUES (93,'لما طردت هاجر أم إسماعيل سارة، وضعها إبراهيم بمكة، عطشت هاجر، فنزل عليها جبريل، فقال لها:','94','0','0');
INSERT INTO [t29972] VALUES (94,'كان رسول الله صلى الله عليه وسلم، لا يتوضأ بعد الغسل','95','0','0');
INSERT INTO [t29972] VALUES (95,'لا يتمنى أحدكم الموت إما مسيئا فيستعتب، وإما محسنا فيزداد','96','0','0');
INSERT INTO [t29972] VALUES (96,'صلاة الليل مثنى مثنى فإذا خفت الصبح فركعة','97','0','0');
INSERT INTO [t29972] VALUES (97,'لا يقبل الله تعالى صلاة بغير طهور، ولا صدقة من غلول','98','0','0');
INSERT INTO [t29972] VALUES (98,'من طلب القضاء واستعان عليه وكل إليه، ومن لم يطلبه ولم يستعن عليه أنزل الله تعالى إليه','99','0','0');
INSERT INTO [t29972] VALUES (99,'من توضأ يوم الجمعة فبها ونعمت، ومن اغتسل فالغسل أفضل','100','0','0');
INSERT INTO [t29972] VALUES (100,'رؤيا المسلم جزء من ستة وأربعين جزءا من النبوة','101','0','0');
INSERT INTO [t29972] VALUES (101,'إن الهدي الصالح، والسمت الصالح، والاقتصاد جزء من خمسة وعشرين جزءا من','102','0','0');
INSERT INTO [t29972] VALUES (102,'لو دعيت إلى كراع أو ذراع لأجبت، ولو أهدي إلي كراع أو ذراع لقبلت','103','0','0');
INSERT INTO [t29972] VALUES (103,'إنما تشد الرحال إلى ثلاثة مساجد: مسجد إبراهيم قال: نا عبد الوهاب يعني','104','0','0');
INSERT INTO [t29972] VALUES (104,'فضحك رسول الله حتى بدت نواجذه، فأنزل الله عز وجل: {وما قدروا الله حق قدره} [الأنعام:','105','0','0');
INSERT INTO [t29972] VALUES (105,'أيما رجل من المسلمين سببته أو جلدته أو لعنته، فاجعلها له كفارة، وأجرا','106','0','0');
INSERT INTO [t29972] VALUES (106,'احتج آدم وموسى، فقال موسى: يا آدم أنت الذي خلقك الله بيده، ونفخ فيك من روحه، وأسكنك','107','0','0');
INSERT INTO [t29972] VALUES (107,'من أكل من هذه الخضرة أو الخضر فلا يقربن مسجدنا، البصل والكراث والثوم، فإن الملائكة تتأذى','108','0','0');
INSERT INTO [t29972] VALUES (108,'من مس ذكره فليتوضأ','109','0','0');
INSERT INTO [t29972] VALUES (109,'أتيت ليلة أسري بي على قوم تقرض شفاههم بمقاريض من نار كلما قرضت وفت، فقلت: يا جبريل من','110','0','0');
INSERT INTO [t29972] VALUES (110,'وفيتم سبعين أمة أنتم خيرهم، وأكرمهم على الله','111','0','0');
INSERT INTO [t29972] VALUES (111,'بعثت على إثر ثمانية آلاف نبي، أربعة آلاف من بني إسرائيل','112','0','0');
INSERT INTO [t29972] VALUES (112,'سبق درهم مائة ألف درهم، كان لرجل درهمان فتصدق بأحدهما، وكان لرجل مال فتصدق من عرض ماله','113','0','0');
INSERT INTO [t29972] VALUES (113,'من ارتبط كلبا ليس بكلب صيد، ولا بكلب ماشية، ولا كلب زرع، نقص من أجورهم كل يوم قيراط،','114','0','0');
INSERT INTO [t29972] VALUES (114,'من أتي معروفا فوجد فليكافئ فإن لم يجد فليثن به فإن من أثنى به فقد شكره، ومن كتمه فقد','115','0','0');
INSERT INTO [t29972] VALUES (115,'إن الله إذا غضب على قوم لم يجعل لهم نسلا ولا عاقبة','116','0','0');
INSERT INTO [t29972] VALUES (116,'نعم، ولك أجر','117','0','0');
INSERT INTO [t29972] VALUES (117,'عيانا','118','0','0');
INSERT INTO [t29972] VALUES (118,'يا عبد الله إذنك علي أن ترفع الحجاب وأن تسمع سوادي حتى أنهاك','119','0','0');
INSERT INTO [t29972] VALUES (119,'من تعلم الرمي ثم تركه فهي نعمة كفرها','120','0','0');
INSERT INTO [t29972] VALUES (120,'أمرني رسول الله أن أتصدق بجلال البدن، وجلودها، وأن نعطي الجازر من عنده','121','0','0');
INSERT INTO [t29972] VALUES (121,'إن الله يحب العبد التقي الغني الخفي','122','0','0');
INSERT INTO [t29972] VALUES (122,'إنا كنا نؤمر بذلك','123','0','0');
INSERT INTO [t29972] VALUES (123,'إن سرك أن تلحق بصاحبيك فأقصر الأمل، وكل دون الشبع، وأنكس الإزار، وأرقع القميص، واخصف','124','0','0');
INSERT INTO [t29972] VALUES (124,'من كذب علي متعمدا فليتبوأ مقعده من النار','125','0','0');
INSERT INTO [t29972] VALUES (125,'اللهم اجعل في بصري نورا، وفي سمعي نورا، وفي قلبي نورا، اللهم اشرح صدري، ويسر لي','126','0','0');
INSERT INTO [t29972] VALUES (126,'اللهم أعز الإسلام بأبي جهل بن هشام، أو بعمر بن الخطاب.','127','0','0');
INSERT INTO [t29972] VALUES (127,'أوصيك بتقوى الله، وإياك وذكر أصحاب النبي صلى الله عليه وسلم إلا بخير، فإنك لا تدري ما سبق','128','0','0');
INSERT INTO [t29972] VALUES (128,'من أحب أن تستجاب دعوته، وأن يزكو عمله، وأن يقبل منه، فليصدق حديثه، وليؤد أمانته، ويسلم','129','0','0');
INSERT INTO [t29972] VALUES (129,'إذا نعس أحدكم في مجلسه يوم الجمعة فليتحول إلى غيره','130','0','0');
INSERT INTO [t29972] VALUES (130,'اللهم إني أعوذ بك من النار، ويل لأهل النار','131','0','0');
INSERT INTO [t29972] VALUES (131,'الأرواح جنود مجندة، فما تعارف منها ائتلف، وما تناكر منها اختلف','132','0','0');
INSERT INTO [t29972] VALUES (132,'إن أحساب أهل هذه الدنيا المال','133','0','0');
INSERT INTO [t29972] VALUES (133,'أفطر الحاجم والمحجوم','134','0','0');
INSERT INTO [t29972] VALUES (134,'يا جبريل فأمرني أن آمر أصحابي أن يرفعوا أصواتهم بالإهلال','135','0','0');
INSERT INTO [t29972] VALUES (135,'رأيت رسول الله إذا افتتح الصلاة رفع يديه حتى يجافي منكبيه، وإذا أراد أن يرفع من الركوع،','136','0','0');
INSERT INTO [t29972] VALUES (136,'لا يدخل الجنة قتات','137','0','0');
INSERT INTO [t29972] VALUES (137,'أتريدين أن ترجعي إلى رفاعة، لا، حتى تذوقي عسيلته ويذوق عسيلتك. وأبو بكر عند النبي، وخالد','138','0','0');
INSERT INTO [t29972] VALUES (138,'صلوا على من قال: لا إله إلا الله، وصلوا وراء من قال: لا إله إلا الله','139','0','0');
INSERT INTO [t29972] VALUES (139,'يحرم على النار كل هين لين سهل قريب','140','0','0');
INSERT INTO [t29972] VALUES (140,'اتقوا النار ولو بشق تمرة، فإن لم تجدوا فبكلمة طيبة','141','0','0');
INSERT INTO [t29972] VALUES (141,'إذا أنفق المسلم نفقة وهو يحتسبها كانت له صدقة','142','0','0');
INSERT INTO [t29972] VALUES (142,'إذا ركع لم يصوب رأسه، ولم يشخصه','143','0','0');
INSERT INTO [t29972] VALUES (143,'دينارا أعطيته مسكينا، ودينارا أعطيته ذا قرابة، ودينارا أنفقته في الله، ودينارا أنفقته على','144','0','0');
INSERT INTO [t29972] VALUES (144,'كان يدخل يديه بين فخذيه في الصلاة','145','0','0');
INSERT INTO [t29972] VALUES (145,'من نذر أن يطيع الله فليطعه، ومن نذر أن يعصي الله فلا يعصه','146','0','0');
INSERT INTO [t29972] VALUES (146,'انظروا عبادي هؤلاء جاءوا شعثا غبرا','147','0','0');
INSERT INTO [t29972] VALUES (147,'إذا شرب أحدكم فليمصه مصا فإنه أهنأ وأمرأ وأبرأ','148','0','0');
INSERT INTO [t29972] VALUES (148,'لا ينبغي لرجل مسلم يشهد مقاما فيه مقال حق إلا تكلم به فإنه لن يقدم أجله، ولن يحرم رزقا هو','149','0','0');
INSERT INTO [t29972] VALUES (149,'وماذا أعددت لها؟ قال: لا والذي نفسي بيده، ما أعددت لها من كبير صلاة ولا صيام إلا أني أحب','150','0','0');
INSERT INTO [t29972] VALUES (150,'من علم آية من كتاب الله كان له ثوابها ما تليت','151','0','0');
INSERT INTO [t29972] VALUES (151,'إذا عرف الصبي يمينه من شماله أمر بالصلاة','152','0','0');
INSERT INTO [t29972] VALUES (152,'لولا أن تضعفوا عن السواك لأمرتكم به عند كل صلاة','153','0','0');
INSERT INTO [t29972] VALUES (153,'لقد رأيت النبي، وهو يقف على بعير له بعرفات من بين قومه حتى يدفع معهم توفيقا من الله','154','0','0');
INSERT INTO [t29972] VALUES (154,'يضرب على آذانهما في القبور أربعين. قيل: يا أبا هريرة عاما؟ قال: أعييت. قال: قيل: أربعين','155','0','0');
INSERT INTO [t29972] VALUES (155,'لخلفة فم الصائم أطيب عند الله من ريح المسك','156','0','0');
INSERT INTO [t29972] VALUES (156,'الحسد يأكل الحسنات كما تأكل النار الحطب','157','0','0');
INSERT INTO [t29972] VALUES (157,'غنيمتان غبنهما كثير من الناس: الصحة، والفراغ','158','0','0');
INSERT INTO [t29972] VALUES (158,'اللهم لك الحمد أنت كسوتنيه، أسألك من خيره وخير ما صنع له، وأعوذ بك من شره وشر ما صنع','159','0','0');
INSERT INTO [t29972] VALUES (159,'إذا توضأ العبد فمضمض واستنشق خرجت خطاياه من فيه، وخرجت خطاياه من أنفه، وإذا غسل وجهه خرجت','160','0','0');
