INSERT INTO [t29976] VALUES (1,'إنه لم يعط الناس شيئا أفضل من العافية، فاسألوا الله العافية، وعليكم بالصدق والبر، فإنهما','2','0','0');
INSERT INTO [t29976] VALUES (2,'رب أعني ولا تعن علي، وانصرني ولا تنصر علي، وامكر لي ولا تمكر علي، ويسر لي الهدى، وانصرني','3','0','0');
INSERT INTO [t29976] VALUES (3,'إن شئت أصبح لهم الصفا ذهبا، فمن كفر بعد منهم عذبته عذابا لا أعذبه أحدا من العالمين، وإن','4','0','0');
INSERT INTO [t29976] VALUES (4,'ما منكم من أحد إلا ومعه قرينه من الجن، وقرينه من الملائكة.','5','0','0');
INSERT INTO [t29976] VALUES (5,'اقرأ. قال: قلت: يا رسول الله أقرأ عليك وعليك أنزل القرآن. قال: نعم. قال: فقرأت سورة','6','0','0');
INSERT INTO [t29976] VALUES (6,'ساعة وساعة، لو كنتم تكونون كما تكونون عندي لصافحتكم الملائكة في بيوتكم أو على فرشكم.','7','0','0');
INSERT INTO [t29976] VALUES (7,'أريت في المنام أني أهاجر من مكة إلى أرض بها نخل، فذهب وهلي إلى أنها اليمامة، أو هجر، فإذا','8','0','0');
INSERT INTO [t29976] VALUES (8,'إني لأعرف أصوات رفقة الأشعريين بالقرآن حين يدخلون بالليل، وأعرف منازلهم من أصواتهم','9','0','0');
INSERT INTO [t29976] VALUES (9,'{ونفخ في الصور فصعق من في السموات ومن في الأرض إلا من شاء الله} [الزمر: ٦٨]. قال: من','10','0','0');
INSERT INTO [t29976] VALUES (10,'والله لتنتهين عائشة أو لأحجرن عليها. فقالت عائشة: هو لله علي نذر أن لا أكلم ابن الزبير','11','0','0');
INSERT INTO [t29976] VALUES (11,'أي سعد ألم تسمع إلى ما قال أبو حباب؟. يريد عبد الله بن أبي. قال: كذا وكذا. قال: اعف عنه','12','0','0');
INSERT INTO [t29976] VALUES (12,'أرسله، اقرأ يا هشام. فقرأ عليه القراءة التي سمعت , فقال النبي صلى الله عليه وسلم: هكذا','13','0','0');
INSERT INTO [t29976] VALUES (13,'ما من أحد أصبر على أذى يسمعه من الله عز وجل، إنهم يجعلون له ندا ويجعلون له ولدا وهو كذلك','14','0','0');
INSERT INTO [t29976] VALUES (14,'جنة واحدة، إنها جنان كثيرة، وإنه في الفردوس الأعلى. وهذا أيضا صحيح مشهور من حديث أبي','15','0','0');
INSERT INTO [t29976] VALUES (15,'تسموا باسمي، ولا تكنوا بكنيتي، فإنما جعلت قاسما أقسم بينكم.','16','0','0');
INSERT INTO [t29976] VALUES (16,'يأيها الناس قولوا: لا إله إلا الله تفلحوا. ورجل يتبعه يرميه بالحجارة، ويقول: يأيها الناس','17','0','0');
INSERT INTO [t29976] VALUES (17,'أدخلوا عبدي الجنة برحمتي. فيقول: بل بعملي. فيقول: أدخلوا عبدي الجنة برحمتي. فيقول: بل','18','0','0');
INSERT INTO [t29976] VALUES (18,'كان في بني إسرائيل رجل عابد وكان معتزلا في كهف. قال: فكأن بني إسرائيل قد أعجبوا بعبادته،','19','0','0');
INSERT INTO [t29976] VALUES (19,'إني أعلم بالخير منه أو عند من هو , إن في الأرض رجلا هو أعلم منك. قال: يا رب دلني عليه،','20','0','0');
INSERT INTO [t29976] VALUES (20,'أي عبادي سلوا. فتقول نسألك رضوانك. قال: فيقول: قد رضيت عنكم. فيسألون فيعطيهم ما سألوا','21','0','0');
INSERT INTO [t29976] VALUES (21,'خلق الله الأرض يوم الأحد والاثنين، وخلق الجبال يوم الثلاثاء وما فيهن من منافع، وخلق يوم','22','0','0');
INSERT INTO [t29976] VALUES (22,'من تسمى بغير الإسلام فليس منا، سمانا الله المسلمين، الله ربنا والإسلام ديننا، والقرآن','23','0','0');
INSERT INTO [t29976] VALUES (23,'ما مسست بيدي ديباجا ولا حريرا ولا شيئا ألين من كف رسول الله صلى الله عليه، ولقد خدمت رسول','24','0','0');
INSERT INTO [t29976] VALUES (24,'اللهم إني أعوذ بك من وعثاء السفر، وكآبة المنقلب، ومن الحور بعد الكون، ودعوة المظلوم، وسوء','25','0','0');
INSERT INTO [t29976] VALUES (25,'. . . .','26','0','0');
INSERT INTO [t29976] VALUES (26,'كلمتك بقوة عشرة آلاف لسان، ولي قوة الألسنة كلها، وأنا أقوى من ذلك. فلما رجع موسى إلى بني','27','0','0');
INSERT INTO [t29976] VALUES (27,'يا بريرة أعطيه السائل. فتثاقلت، ثم تكلم السائل. فقالت: يا بريرة قومي فأعطيه. فتثاقلت، ثم','28','0','0');
INSERT INTO [t29976] VALUES (28,'كذا فتعوذ، فما تعوذ المتعوذون بمثله قط','30','0','0');
INSERT INTO [t29976] VALUES (29,'كلوا الزبيب، واطرحوا عجمه، فإن في عجمه داء، وشحمه دواء.','31','0','0');
INSERT INTO [t29976] VALUES (30,'إذا هلك أهل الشام فلا خير في أمتي، ولا تزال طائفة من أمتي ظاهرين على الحق حتى تقاتلوا','32','0','0');
INSERT INTO [t29976] VALUES (31,'نهى رسول الله صلى الله عليه وسلم عن بيع الولاء، وعن هبته','33','0','0');
INSERT INTO [t29976] VALUES (32,'إن من التواضع لله الرضا بالدون من شرف المجلس','34','0','0');
INSERT INTO [t29976] VALUES (33,'البسوا الثياب البيض فإنها أطيب، وكفنوا فيها موتاكم','35','0','0');
INSERT INTO [t29976] VALUES (34,'إذا قام الإمام في الركعتين فذكر قبل أن يستتم قائما فليجلس، فإن استتم قائما فلا يجلس،','36','0','0');
INSERT INTO [t29976] VALUES (35,'ما أنعم الله على عبد بنعمة فأراد بقاءها ودوامها فليكثر من قول ما شاء الله لا قوة إلا','37','0','0');
INSERT INTO [t29976] VALUES (36,'لعن الواصلة والمستوصلة والواشمة والمستوشمة','38','0','0');
INSERT INTO [t29976] VALUES (37,'أوف بنذرك','39','0','0');
INSERT INTO [t29976] VALUES (38,'نهى عن بيع حبل الحبلة','40','0','0');
INSERT INTO [t29976] VALUES (39,'{يوم يقوم الناس لرب العالمين} [المطففين: ٦].','41','0','0');
INSERT INTO [t29976] VALUES (40,'صلى المغرب والعشاء بالمزدلفة جميعا','42','0','0');
INSERT INTO [t29976] VALUES (41,'لعلها حابستها. فقيل: يا رسول الله إنها قد أفاضت.','43','0','0');
INSERT INTO [t29976] VALUES (42,'احلق رأسك وصم ثلاثة أيام، أو أطعم ستة مساكين مدين مدين لكل إنسان، أو انسك بشاة، أي ذلك','44','0','0');
INSERT INTO [t29976] VALUES (43,'لعلك آذاك هوامك؟ قلت: نعم يا رسول الله.','45','0','0');
INSERT INTO [t29976] VALUES (44,'نهى رسول الله صلى الله عليه وسلم أن يسافر بالقرآن إلى أرض العدو','46','0','0');
INSERT INTO [t29976] VALUES (45,'أن رسول الله صلى الله عليه وسلم بعث سرية فيها عبد الله قبل نجد، فغنمنا إبلا كثيرة، فكانت','47','0','0');
INSERT INTO [t29976] VALUES (46,'خمس من الدواب من قتلهن فلا جناح عليه: العقرب، والفأرة، والكلب العقور، والغراب،','48','0','0');
INSERT INTO [t29976] VALUES (47,'خمس من الدواب ليس على المحرم في قتلهن جناح: الغراب، والحدأة، والعقرب، والفأرة، والكلب','49','0','0');
INSERT INTO [t29976] VALUES (48,'لا إله إلا الله وحده لا شريك له الملك وله الحمد وهو على كل شيء قدير.','50','0','0');
INSERT INTO [t29976] VALUES (49,'كان إذا نزل من الصفا مشى حتى إذا انصبت قدماه في بطن الوادي سعى حتى يخرج','51','0','0');
INSERT INTO [t29976] VALUES (50,'إن لله عز وجل في كل يوم وليلة عشرين ومائة رحمة تنزل على هذا البيت: فستون للطائفين،','52','0','0');
INSERT INTO [t29976] VALUES (51,'استأنفوا العمل فقد كفيتم ما مضى','53','0','0');
INSERT INTO [t29976] VALUES (52,'أكل كتف شاة، ثم صلى، ولم يتوضأ','54','0','0');
INSERT INTO [t29976] VALUES (53,'لا يؤمن مؤمن حتى يؤمن بالقدر كله حتى يعلم أن ما أصابه لم يكن ليخطئه، وما أخطأه لم يكن','55','0','0');
INSERT INTO [t29976] VALUES (54,'لا تطرقوا النساء بعد صلاة العتمة','56','0','0');
INSERT INTO [t29976] VALUES (55,'إذا كان يوم القيامة نودي أين أبناء الستين؟ وهو العمر الذي قال الله عز وجل: {أولم نعمركم','57','0','0');
INSERT INTO [t29976] VALUES (56,'أبالله وآياته ورسوله كنتم تستهزئون','58','0','0');
INSERT INTO [t29976] VALUES (57,'دعا بإداوة يوم أحد فأخنثت الإداوة، ثم شرب من فيها','59','0','0');
INSERT INTO [t29976] VALUES (58,'دخلت امرأة النار في هرة ربطتها فلم تطعمها، ولم تسقها، ولم تتركها فتأكل من خشاش','60','0','0');
INSERT INTO [t29976] VALUES (59,'لا تحاسدوا، ولا تباغضوا، وكونوا عباد الله إخوانا','61','0','0');
INSERT INTO [t29976] VALUES (60,'إذا عمل خادم أحدكم طعامه فليقعده معه، أو ليناوله منه فإنه ولي حره','62','0','0');
INSERT INTO [t29976] VALUES (61,'العج والثج','63','0','0');
INSERT INTO [t29976] VALUES (62,'بني الإسلام على خمس: شهادة أن لا إله إلا الله، وإقام الصلاة، وإيتاء الزكاة، وحج البيت،','64','0','0');
INSERT INTO [t29976] VALUES (63,'يتبرز لحاجته فآتيه بماء فيغتسل به','65','0','0');
INSERT INTO [t29976] VALUES (64,'أمك أمرتك بهذا؟. قلت: أغسلهما.','66','0','0');
INSERT INTO [t29976] VALUES (65,'لن يبرح الناس يسألون حتى يقولوا: هذا الله خلق كل شيء.','67','0','0');
INSERT INTO [t29976] VALUES (66,'كان هذا شيء كانت فلانة تجده','68','0','0');
INSERT INTO [t29976] VALUES (67,'من يعلم لي علمه؟ فقال رجل: أنا يا رسول الله. قال: فذهب فوجده في منزله جالسا منكسا رأسه،','69','0','0');
INSERT INTO [t29976] VALUES (68,'ما أنفقت الورق في شيء أفضل من نحيرة في يوم عيد','70','0','0');
INSERT INTO [t29976] VALUES (69,'ما يلزمك هذه السورة؟ قال: إني أحبها.','71','0','0');
INSERT INTO [t29976] VALUES (70,'اقتدوا باللذين من بعدي: أبي بكر، وعمر','72','0','0');
INSERT INTO [t29976] VALUES (71,'الحلال ما أحل الله، والحرام ما حرم الله في كتابه، وما سكت الله عنه فهو مما عفا الله عنه','73','0','0');
INSERT INTO [t29976] VALUES (72,'إذا كانوا ثلاثة فلا يتناج اثنان دون الثالث','74','0','0');
INSERT INTO [t29976] VALUES (73,'من أتى الجمعة فليغتسل','75','0','0');
INSERT INTO [t29976] VALUES (74,'خادم القوم سيدهم، وساقيهم آخرهم شربا','76','0','0');
INSERT INTO [t29976] VALUES (75,'من أعتق شركا له في عبد فكان له مال يبلغ ثمن العبد قوم العبد عليه قيمة العدل، فأعطى شركاءه','77','0','0');
INSERT INTO [t29976] VALUES (76,'أما بعد، فما بال رجال يشترطون شروطا ليس في كتاب الله، ما كان من شرط ليس في كتاب الله فهو','78','0','0');
INSERT INTO [t29976] VALUES (77,'الولاء لمن أعتق','79','0','0');
INSERT INTO [t29976] VALUES (78,'لا يمنعك ذلك فإنما الولاء لمن أعتق','80','0','0');
INSERT INTO [t29976] VALUES (79,'نهى عن بيع الولاء، وعن هبته','81','0','0');
INSERT INTO [t29976] VALUES (80,'مثل المجاهد في سبيل الله كمثل الصائم القائم الدائم , الذي لا يفتر من صيام ولا صلاة حتى','82','0','0');
INSERT INTO [t29976] VALUES (81,'في الركاز الخمس','83','0','0');
INSERT INTO [t29976] VALUES (82,'في الركاز الخمس','84','0','0');
