INSERT INTO [t29976] VALUES (84,'إن ربكم عز وجل ليس بأصم، ولا غائب هو بين رؤوس رواحلكم.','86','0','0');
INSERT INTO [t29976] VALUES (85,'لا تسموا العنب الكرم، إنما الكرم المسلم.','87','0','0');
INSERT INTO [t29976] VALUES (86,'الكريم لو دعي إلى طعنة ليلا لأجاب. قال محمد بن مسلمة: إني إذا فسوف أمد يدي إلى رأسه، فإذا','88','0','0');
INSERT INTO [t29976] VALUES (87,'يا جبريل اذهب إلى محمد فأقرئه السلام، واسأله ما يبكيك؟ فجاءه جبريل فسأله، فأخبره رسول','89','0','0');
INSERT INTO [t29976] VALUES (88,'لا تبرحن خطي، فإنه سينتهي إليك رجال فلا تكلمهم، فإنهم لن يكلموك. ثم مضى رسول الله صلى','90','0','0');
INSERT INTO [t29976] VALUES (89,'أن رسول الله صلى الله عليه وسلم دخل الكعبة، هو وأسامة بن زيد وبلال وعثمان بن طلحة الحجبي،','91','0','0');
INSERT INTO [t29976] VALUES (90,'إذا ولغ الكلب في إناء أحدكم فليغسله سبع مرات أولهن بالتراب','92','0','0');
INSERT INTO [t29976] VALUES (91,'إن الملائكة تصلي على أحدكم ما دام في مصلاه: اللهم اغفر له اللهم ارحمه.','93','0','0');
INSERT INTO [t29976] VALUES (92,'الإيمان يمان، والفقه يمان.','94','0','0');
INSERT INTO [t29976] VALUES (93,'لا تنكح المرأة على عمتها ولا على خالتها','95','0','0');
INSERT INTO [t29976] VALUES (94,'الناس معادن في الخير والشر، خيارهم في الجاهلية خيارهم في الإسلام إذا','96','0','0');
INSERT INTO [t29976] VALUES (95,'أولكلكم ثوبان؟','97','0','0');
INSERT INTO [t29976] VALUES (96,'تقتلك الفئة الباغية','98','0','0');
INSERT INTO [t29976] VALUES (97,'إذا جاء أحدكم إلى الصلاة فليمش على هينته، فيصلي ما أدرك، وليقض ما سبق','99','0','0');
INSERT INTO [t29976] VALUES (98,'إن من أكمل الإيمان حسن الخلق','100','0','0');
INSERT INTO [t29976] VALUES (99,'رؤيا المسلم جزء من ستة وأربعين جزءا من النبوة','101','0','0');
INSERT INTO [t29976] VALUES (100,'ينهى عن الصلاة في ثلاث ساعات: عند طلوع الشمس حتى تطلع، وعند غروبها حتى تغرب، ونصف','102','0','0');
INSERT INTO [t29976] VALUES (101,'كنت أنا ورسول الله صلى الله عليه وسلم نغتسل من إناء واحد , فأقول له: ابق لي ابق','103','0','0');
