INSERT INTO [t29976] VALUES (103,'ما أسكر كثيره فقليله حرام','105','0','0');
INSERT INTO [t29976] VALUES (104,'سئل ابن عباس عن سجدة ص. فقرأ: {أولئك الذين هدى الله فبهداهم اقتده} [الأنعام: ٩٠]','106','0','0');
INSERT INTO [t29976] VALUES (105,'توفي رسول الله صلى الله عليه وسلم يوم توفي، وقد أتى عليه ستون سنة، وما في رأسه ولحيته','107','0','0');
INSERT INTO [t29976] VALUES (106,'سددوا، وقاربوا، واعلموا أنه لم يدخل أحدكم عمله الجنة، وإن أحب الأعمال إلى الله عز وجل','108','0','0');
INSERT INTO [t29976] VALUES (107,'كان خاتم رسول الله صلى الله عليه وسلم من فضة، وفصه منه، نقشه محمد رسول','109','0','0');
INSERT INTO [t29976] VALUES (108,'{ثلة من الأولين {٣٩} وثلة من الآخرين {٤٠}} [الواقعة: ٣٩ - ٤٠]. قال: قال رسول الله صلى','110','0','0');
INSERT INTO [t29976] VALUES (109,'فانتهينا إلى القبر ولم يلحد بعد , فجلس النبي صلى الله عليه وسلم، فسلم مستقبل القبلة،','111','0','0');
INSERT INTO [t29976] VALUES (110,'رأيت النبي صلى الله عليه وسلم في ظل الكعبة محتبيا بيديه.','112','0','0');
INSERT INTO [t29976] VALUES (111,'إن الله اختار العرب، فاختار منهم كنانة، والنضر بن كنانة، ثم اختار منهم قريشا، ثم اختار','113','0','0');
INSERT INTO [t29976] VALUES (112,'ما هذه الجنازة؟ قالوا: جنازة فلان الفلاني، كان يحب الله ورسوله، ويعمل بطاعة الله ويسعى','114','0','0');
