INSERT INTO [t29976] VALUES (114,'سبعة في ظل العرش يوم لا ظل إلا ظله: رجل ذكر الله ففاضت عيناه، ورجل يحب عبدا لله لا يحبه','116','0','0');
INSERT INTO [t29976] VALUES (115,'قتال المؤمن كفر، وسبابه فسوق','117','0','0');
INSERT INTO [t29976] VALUES (116,'أتى سباطة قوم فبال، ثم توضأ، ومسح على الخفين','118','0','0');
INSERT INTO [t29976] VALUES (117,'كنت أغتسل أنا ورسول الله صلى الله عليه وسلم من إناء واحد','119','0','0');
INSERT INTO [t29976] VALUES (118,'كان إذا افتتح الصلاة بدأ: بـ بسم الله الرحمن الرحيم','120','0','0');
INSERT INTO [t29976] VALUES (119,'الطاعون شهادة، والغريق شهادة، والبطين، والنفساء','121','0','0');
INSERT INTO [t29976] VALUES (120,'أجديد هذا أم غسيل؟ قال: بلى. قال: البس جديدا، وعش حميدا، ومت شهيدا إذا كان يوم عرفة فإن','122','0','0');
INSERT INTO [t29976] VALUES (121,'يأيها الناس هل فيكم أحد يريد علما بغير تعلم، وهدى من غير هداية؟ هل فيكم أحد يريد أن يذهب','123','0','0');
INSERT INTO [t29976] VALUES (122,'كان يصلي بعد المغرب ركعتين يطيل فيهما القراءة حتى ينصدع عن أهل المسجد','124','0','0');
INSERT INTO [t29976] VALUES (123,'يا أبا ذر أحبك الذي أحببتني له , أبا ذر. قلت: لبيك وسعديك يا رسول الله. قال: هلاك أمتي','125','0','0');
INSERT INTO [t29976] VALUES (124,'ادن بني، وسم الله، وكل بيمينك، وكل مما يليك. هذا حديث كبير. . . . . . . . . . . . . . . .','126','0','0');
INSERT INTO [t29976] VALUES (125,'رأيت النبي صلى الله عليه وسلم فخفت أن يرسلني في حاجة فاستترت منه بجدم','127','0','0');
INSERT INTO [t29976] VALUES (126,'هو محمد بن عبد الله بن عبد المطلب بن هاشم بن عبد مناف بن قصي بن كلاب بن مرة بن كعب بن لؤي','128','0','0');
INSERT INTO [t29976] VALUES (127,'عشرة أسماء عند ربي. قال أبو الطفيل: فحفظت ثمانية، ونسيت اثنتين، أنا محمد، وأحمد , الفاتح،','129','0','0');
INSERT INTO [t29976] VALUES (128,'بين السماء الدنيا والتي تليها مسيرة خمس مائة عام، وبين كل سماءين مسيرة خمس مائة عام، وبين','130','0','0');
INSERT INTO [t29976] VALUES (129,'إذا قال العبد: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر , وتبارك الله، أخذهن','131','0','0');
INSERT INTO [t29976] VALUES (130,'قولوا لها: تقول: سبحان الله وبحمده ملء سمائه وملء أرضه، وعدد خلقه، ورضا نفسه، وزنة عرشه.','132','0','0');
INSERT INTO [t29976] VALUES (131,'قبضتم ثمرة فؤاد عبدي؟ قالوا: نعم. قال: فما قال؟ قالوا: حمدك واسترجع. قال: ابنوا له بيتا','133','0','0');
INSERT INTO [t29976] VALUES (132,'كيف أنعم وقد التقم صاحب القرن القرن، وحنى جبهته ينتظر متى يؤمر فينفخ في الصور؟ قيل: فما','134','0','0');
INSERT INTO [t29976] VALUES (133,'إن الله عز وجل خلق آدم من قبضة قبضها من جميع الأرض، فجاء بنو آدم على ذلك: الأحمر،','135','0','0');
INSERT INTO [t29976] VALUES (134,'لا يزني الزاني وهو مؤمن، ولا يسرق السارق وهو مؤمن، ولا يشرب الخمر وهو مؤمن. فقام رجل،','136','0','0');
INSERT INTO [t29976] VALUES (135,'إن هذا لدق فاطمة، ولقد أتتنا في ساعة ما عودتنا أن تأتينا في مثلها، قومي فافتحي لها. قالت','137','0','0');
INSERT INTO [t29976] VALUES (136,'فافضوا بنا إليه إنا إن دعوناه يظن بنا أنا تجسسنا. يريد قبح أمره. فقام عمر رضي الله عنه','138','0','0');
INSERT INTO [t29976] VALUES (137,'نعم. قلت: هل بعد ذلك الشر من خير؟ قال: نعم، وفيه دخن. قلت: وما دخنه؟ قال: قوم يهدون بغير','139','0','0');
INSERT INTO [t29976] VALUES (138,'رجل جاهد بنفسه وماله، ورجل في شعب من الشعاب يعبد ربه عز وجل ويدع الناس من','140','0','0');
INSERT INTO [t29976] VALUES (139,'اكتب. فأخذت كتفا. فقال: اكتب: {لا يستوي القاعدون من المؤمنين. . . . . والمجاهدون في سبيل','141','0','0');
INSERT INTO [t29976] VALUES (140,'ما هذه الريح يا جبريل؟ قال: هذه ريح ماشطة بنت فرعون، كانت تمشطها، فوقع المشط من يدها،','142','0','0');
INSERT INTO [t29976] VALUES (141,'مسح الله عز وجل ظهر آدم فأخرج ذريته كالذر بأرض يقال لها دحنا قريبة من مكة، فاستخرج من ظهر','143','0','0');
INSERT INTO [t29976] VALUES (142,'فيم يختصم الملأ الأعلى يا محمد؟ قال: قلت: أنت أعلم أي رب. قال: فيم يختصم الملأ الأعلى يا','144','0','0');
INSERT INTO [t29976] VALUES (143,'ما من امرئ يتصدق بصدقة من كسب طيب، ولا يقبل الله إلا طيبا، حتى ولو بتمرة إلا أخذها الله','145','0','0');
INSERT INTO [t29976] VALUES (144,'ما أطعمت نفسك فهو لك صدقة، وما أطعمت ولدك فهو لك صدقة، وما أطعمت زوجتك فهو لك','146','0','0');
INSERT INTO [t29976] VALUES (145,'شتوذ اشكمت درد؟ قال: قلت: نعم.','147','0','0');
INSERT INTO [t29976] VALUES (146,'أميطي عنه الأذى.','148','0','0');
INSERT INTO [t29976] VALUES (147,'أمره أن يخضبها بالحناء. الأحاديث الثلاثة غرائب المتون والأسانيد، حسان عزيزة، وشريك هو ابن','149','0','0');
INSERT INTO [t29976] VALUES (148,'نضر عيني هاتين وسمع أذني رسول الله صلى الله عليه وسلم أخذ بيد الحسن والحسين، وهو يقول','150','0','0');
