INSERT INTO [t29984] VALUES (52,'أول زمرة تعبر الصراط مثل البرق، ثم الذين يلونهم مثل الريح، ثم الذين يلونهم كجري الخيل، ثم','52','0','0');
INSERT INTO [t29984] VALUES (53,'كيف بأحدكم إذا كان بفلاة من الأرض ومعه زاده وراحلته وماؤه، فنزل فنام ثم قام، وقد ذهبت','53','0','0');
INSERT INTO [t29984] VALUES (54,'من سأل مسألة وهو غني عنها كانت شيئا في وجهه يوم القيامة','54','0','0');
INSERT INTO [t29984] VALUES (55,'إذا أراد أحدكم أن يتوضأ فلا يغمس يده في الإناء حتى يغسلها ثلاثا، فإنه لا يدري أين باتت','55','0','0');
INSERT INTO [t29984] VALUES (56,'أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوها عصموا مني دماءهم وأموالهم','56','0','0');
INSERT INTO [t29984] VALUES (57,'إذا كان يوم القيامة جمع الله الأولين والآخرين حتى إن المؤمن ليعرق في الموقف حتى يلجمه','57','0','0');
INSERT INTO [t29984] VALUES (58,'ما من عبد يموت له ثلاثة من الولد لم يبلغوا الحنث إلا ستر الله كل عضو منه بعضو من ولده من','58','0','0');
INSERT INTO [t29984] VALUES (59,'من لبس الحرير في الدنيا لم يلبسه في الآخرة، ومن شرب الخمر في الدنيا لم يشربها في','59','0','0');
