INSERT INTO [t29991] VALUES (1,'اللهم جنبني منكرات الأخلاق، والأهواء، والأدواء','2','0','0');
INSERT INTO [t29991] VALUES (2,'لا تكذبوا علي فإنه من يكذب علي يلج النار','3','0','0');
INSERT INTO [t29991] VALUES (3,'إن خلق أحدكم يجمع في بطن أمه أربعين يوما، وقال محاضر: أربعين ليلة، ثم يكون علقة مثل ذلك،','4','0','0');
INSERT INTO [t29991] VALUES (4,'إذا كان يوم القيامة دفع إلى كل رجل مؤمن رجل من أهل الملل فقيل: هذا فداؤك من','5','0','0');
INSERT INTO [t29991] VALUES (5,'كان رسول الله صلى الله عليه وسلم إذا كان في سفر فأراد أن يجمع بين الصلاتين أخر الظهر حتى','6','0','0');
INSERT INTO [t29991] VALUES (6,'قال: المؤمن للمؤمن مثل البنيان يشد بعضه بعضا','7','0','0');
INSERT INTO [t29991] VALUES (7,'اشفعوا فلتؤجروا ويقضي الله على لسان نبيه ما يشاء','8','0','0');
INSERT INTO [t29991] VALUES (8,'الخازن الأمين إذا أعطى ما أمر به طيبة به نفسه كان أحد المتصدقين','9','0','0');
INSERT INTO [t29991] VALUES (9,'من كانت عنده مظلمة لأخيه في عرضه أو ماله فليتحللها منه اليوم قبل أن تؤخذ حيث لا يكون','10','0','0');
INSERT INTO [t29991] VALUES (10,'أراد أن يكتب إلى كسرى وقيصر، فقيل: إنهم لا يقبلون كتابا إلا بخاتم، فاتخذ خاتما من فضة','11','0','0');
INSERT INTO [t29991] VALUES (11,'فاستعد للفاقة','12','0','0');
INSERT INTO [t29991] VALUES (12,'إن الصائم إذا لم يدع قول الزور والعمل به فليس لله حاجة في أن يدع طعامه','13','0','0');
INSERT INTO [t29991] VALUES (13,'سمعت رسول الله صلى الله عليه وسلم يقرأ في العشاء بالتين والزيتون، فما سمعت أحدا أحسن صوتا','14','0','0');
INSERT INTO [t29991] VALUES (14,'المسلم من سلم المسلمون من لسانه ويده، والمهاجر من هجر ما نهى الله عنه','15','0','0');
INSERT INTO [t29991] VALUES (15,'إني لأعلم المكان الذي نزلت فيه، واليوم الذي نزلت فيه، نزلت على رسول الله صلى الله عليه','16','0','0');
INSERT INTO [t29991] VALUES (16,'أيها الناس تحبون أن يكذب الله ورسوله؟ حدثوا الناس بما يعرفون، ودعوا ما','17','0','0');
INSERT INTO [t29991] VALUES (17,'لو أن لابن آدم واديين من ذهب لابتغى إليهما ثالثا، ولا يملأ جوف ابن آدم إلا التراب، ويتوب','18','0','0');
INSERT INTO [t29991] VALUES (18,'العز إزاري، والكبرياء ردائي فمن نازعني شيئا منهما عذبته','19','0','0');
INSERT INTO [t29991] VALUES (19,'اللهم لك أسلمت، وبك آمنت، وعليك توكلت، وإليك أنبت، وبك خاصمت، أعوذ بعزتك لا إله إلا أنت','20','0','0');
INSERT INTO [t29991] VALUES (20,'سيد الاستغفار أن تقول: اللهم أنت ربي لا إله إلا أنت خلقتني وأنا عبدك وأنا على عهدك ووعدك','21','0','0');
INSERT INTO [t29991] VALUES (21,'اللهم إني أعوذ بك من زوال نعمتك، ومن تحول عافيتك، ومن فجاءة نقمتك، ومن جميع','22','0','0');
INSERT INTO [t29991] VALUES (22,'أعوذ بك من البخل، وأعوذ بك من الجبن، وأعوذ بك من الكسل، وأعوذ بك من','23','0','0');
INSERT INTO [t29991] VALUES (23,'اللهم إني أعوذ بك من حلول البلاء، ومن درك الشقاء، ومن شماتة الأعداء.','24','0','0');
INSERT INTO [t29991] VALUES (24,'هي ناري أسلطها على عبدي المؤمن في الدنيا لتكون حظه من النار في الآخرة','25','0','0');
INSERT INTO [t29991] VALUES (25,'من نوقش الحساب هلك. قال: قلت: يا رسول الله: {فأما من أوتي كتابه بيمينه {٧} فسوف يحاسب','26','0','0');
INSERT INTO [t29991] VALUES (26,'كن في الدنيا كأنك غريب أو كعابر سبيل، واعدد نفسك في الموتى.','27','0','0');
INSERT INTO [t29991] VALUES (27,'يكبر ابن آدم، ويكبر معه اثنتان: حب المال، وطول العمر','28','0','0');
INSERT INTO [t29991] VALUES (28,'لو أن لابن آدم واديا من ذهب أحب أن يكون له ثانيا، ولو أعطي ثانيا أحب إليها ثالثا، ولا يسد','29','0','0');
INSERT INTO [t29991] VALUES (29,'ليس الغنى عن كثرة العرض , إنما الغنى غنى النفس','30','0','0');
INSERT INTO [t29991] VALUES (30,'لم يأكل على خوان حتى مات ولا أكل خبزا مرققا حتى مات','31','0','0');
INSERT INTO [t29991] VALUES (31,'كلوا فما أعلم رسول الله صلى الله عليه وسلم رأى رغيفا مرققا حتى لحق بالله عز وجل ولا رأى','32','0','0');
INSERT INTO [t29991] VALUES (32,'خرج رسول الله صلى الله عليه وسلم من الدنيا ولم يشبع من خبز الشعير','33','0','0');
INSERT INTO [t29991] VALUES (33,'اتقوا النار ولو بشق تمرة.','34','0','0');
INSERT INTO [t29991] VALUES (34,'يؤتى بجهنم يوم القيامة تجر بسبعين ألف زمام مع كل زمام سبعون ألف ملك','35','0','0');
INSERT INTO [t29991] VALUES (35,'إن أهون أهل النار عذابا يوم القيامة رجل في أخمص قدميه جمرتان تغلي منهما دماغه كما يغلي','36','0','0');
INSERT INTO [t29991] VALUES (36,'مستريح ومستراح منه.','37','0','0');
INSERT INTO [t29991] VALUES (37,'يتبع الميت إلى قبره ثلاثة: أهله، وماله، وعمله، فيرجع اثنان، ويبقى واحد، يرجع أهله، وماله،','38','0','0');
INSERT INTO [t29991] VALUES (38,'لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا','39','0','0');
INSERT INTO [t29991] VALUES (39,'لا إله إلا الله إن للموت لسكرات','40','0','0');
INSERT INTO [t29991] VALUES (40,'إن أحدكم إذا مات عرض عليه مقعده بالغداة والعشي، فإن كان من أهل الجنة فمن الجنة، وإن كان','41','0','0');
INSERT INTO [t29991] VALUES (41,'إن الذي أمشاه على رجليه قادر أن يمشيه على وجهه','42','0','0');
INSERT INTO [t29991] VALUES (42,'لولا أنكم تذنبون لأتى الله بقوم يذنبون فيغفر لهم','43','0','0');
INSERT INTO [t29991] VALUES (43,'أترون هذه طارحة ولدها في النار. قلنا: لا وهي تقدر على أن لا تطرحه.','44','0','0');
INSERT INTO [t29991] VALUES (44,'لو يعلم المؤمن ما عند الله من العقوبة ما طمع بجنته أحد، ولو يعلم الكافر ما عند الله من','45','0','0');
INSERT INTO [t29991] VALUES (45,'المؤمن يغار، والله يغار، ومن غيرة الله أن يأتي المؤمن ما حرم الله','46','0','0');
INSERT INTO [t29991] VALUES (46,'لا أحد أغير من الله عز وجل، ولذلك حرم الفواحش ما ظهر منها وما بطن','47','0','0');
INSERT INTO [t29991] VALUES (47,'من قتل دون ماله مظلوما فهو شهيد','48','0','0');
INSERT INTO [t29991] VALUES (48,'نهى رسول الله صلى الله عليه وسلم أن يقرن الرجل بين التمرتين جميعا حتى يستأذن','49','0','0');
INSERT INTO [t29991] VALUES (49,'ما أصبح لآل محمد ولا أمسى إلا صاع.','50','0','0');
INSERT INTO [t29991] VALUES (50,'إيمان بالله، وجهاد في سبيله. قلت: فأي الرقاب أفضل؟ قال: أغلاها ثمنا وأنفسها عند أهلها.','51','0','0');
INSERT INTO [t29991] VALUES (51,'كل معروف صدقة','52','0','0');
INSERT INTO [t29991] VALUES (52,'أي يوم هذا؟ . قلنا: الله ورسوله أعلم. فسكت، حتى ظننا أنه سيسميه بغير اسمه، فقال: أليس يوم','53','0','0');
INSERT INTO [t29991] VALUES (53,'توفي النبي صلى الله عليه وسلم ودرعه مرهونة بثلاثين صاعا من شعير','54','0','0');
INSERT INTO [t29991] VALUES (54,'ما ترك رسول الله صلى الله عليه وسلم إلا سلاحه وبغلته البيضاء، وأرضا جعلها','55','0','0');
INSERT INTO [t29991] VALUES (55,'لم يترك رسول الله صلى الله عليه وسلم شيئا يوصي فيه. قلت: فكيف أمر الناس بالوصية، ولم يوص؟','56','0','0');
INSERT INTO [t29991] VALUES (56,'ما ترك رسول الله صلى الله عليه وسلم دينارا ولا درهما، ولا شاة، ولا بعيرا، ولا أوصى','57','0','0');
INSERT INTO [t29991] VALUES (57,'كل شيء يفضل على ابن آدم من جلف الخبز وثوب يواري سوءته وبيت يكنه، وما سوى ذلك فهو حساب','58','0','0');
INSERT INTO [t29991] VALUES (58,'يا أيها الناس إن الله طيب لا يقبل إلا الطيب، وإن الله أمر المؤمنين بما أمر به المرسلين،','59','0','0');
INSERT INTO [t29991] VALUES (59,'إن الدنيا خضرة حلوة، وإن رجالا سيخوضون في مال الله ورسوله بغير حق، لهم النار يوم','60','0','0');
INSERT INTO [t29991] VALUES (60,'قد أفلح من رزقه الله كفافا، وقنعه بما أتاه','61','0','0');
INSERT INTO [t29991] VALUES (61,'من حافظ على شفعة الضحى غفرت له ذنوبه وإن كانت أكثر من زبد البحر','62','0','0');
INSERT INTO [t29991] VALUES (62,'من تاب قبل أن تطلع الشمس من مغربها تاب الله عليه','63','0','0');
INSERT INTO [t29991] VALUES (63,'صدقت المسلم أخو المسلم','64','0','0');
INSERT INTO [t29991] VALUES (64,'من قال: سبحان الله والحمد لله، ولا إله إلا الله، والله أكبر، ولا حول ولا قوة إلا بالله،','65','0','0');
INSERT INTO [t29991] VALUES (65,'هي آتية، فما أعددت لها؟ قال: ما أعددت لها من كبير عمل إلا أني أحب الله','66','0','0');
INSERT INTO [t29991] VALUES (66,'اللهم أحيني مسكينا وأمتني مسكينا، واحشرني في زمرة المساكين يوم','67','0','0');
INSERT INTO [t29991] VALUES (67,'لو خرجتم إلى إبلنا فأصبتم من ألبانها.','68','0','0');
INSERT INTO [t29991] VALUES (68,'دخل أعرابي المسجد ورسول الله صلى الله عليه وسلم فيه، فأتى النبي صلى الله عليه وسلم فقضى','69','0','0');
INSERT INTO [t29991] VALUES (69,'الموت كفارة لكل مسلم','70','0','0');
INSERT INTO [t29991] VALUES (70,'الموت كفارة لكل مسلم','71','0','0');
INSERT INTO [t29991] VALUES (71,'يقول ابن آدم: مالي مالي، وهل لك من مالك إلا ما أكلت فأفنيت، أو تصدقت فأمضيت، أو لبست','72','0','0');
INSERT INTO [t29991] VALUES (72,'كان يصليها والشمس بيضاء نقية','73','0','0');
INSERT INTO [t29991] VALUES (73,'من كذب علي متعمدا فليتبوأ مقعده من النار','74','0','0');
INSERT INTO [t29991] VALUES (74,'إطعام الطعام وطيب الكلام','75','0','0');
INSERT INTO [t29991] VALUES (75,'إنما الأعمال بالنيات، وإنما لكل امرئ ما نوى، فمن كانت هجرته إلى الله ورسوله فهجرته إلى','76','0','0');
