INSERT INTO [t30071] VALUES (1,'نهى رسول الله صلى الله عليه وسلم أن تستقبل القبلة بالغائط أو بول، ولكن يشرق أو يغرب ,','2','0','0');
INSERT INTO [t30071] VALUES (2,'إن رجلا من أهل الجنة يشرف يوم القيامة على أهل النار، فيناديه رجل من أهل النار، فيقول: يا','3','0','0');
INSERT INTO [t30071] VALUES (3,'ثلاث ساعات كان رسول الله صلى الله عليه وسلم ينهانا أن نصلي فيهن أو نقبر فيهن موتانا: حين','4','0','0');
INSERT INTO [t30071] VALUES (4,'ومن الناس ناس مفاتيح للخير مغاليق للشر , ومن الناس مفاتيح للشر مغاليق للخير , فطوبى لعبد','5','0','0');
INSERT INTO [t30071] VALUES (5,'إن الله تعالى قد قسم بينكم أخلاقكم كما قسم بينكم أرزاقكم , والله يؤتي الدنيا من يحب ومن','6','0','0');
INSERT INTO [t30071] VALUES (6,'يا أبا هريرة ارض بقسم الله تكن أغنى الناس، وكن ورعا تكن أعبد الناس، وأحب للناس ما تحب','7','0','0');
INSERT INTO [t30071] VALUES (7,'قل: ربي الله , ثم استقم.','8','0','0');
INSERT INTO [t30071] VALUES (8,'نعم، حق الولد على الوالد أن يعلمه كتاب الله عز وجل والسباحة والرمي، وأن تؤدبه','9','0','0');
INSERT INTO [t30071] VALUES (9,'ويل للعرب من شر قد اقترب، فتن كقطع الليل المظلم، يصبح الرجل فيها مؤمنا ويمسي كافرا، يبيع','10','0','0');
INSERT INTO [t30071] VALUES (10,'من أحيا سنتي فقد أحبني، ومن أحبني كان معي في الجنة','11','0','0');
INSERT INTO [t30071] VALUES (11,'إن كل نبي أعطي سبعة نجباء، وإن نبيكم صلى الله عليه وسلم أعطي أربعة عشر: أنا، وابني الحسن','12','0','0');
INSERT INTO [t30071] VALUES (12,'كان ليس بالذاهب طويلا فوق الربعة، إذا جامع القوم غمرهم، أبيض شديد الوضح، ضخم الهامة، أغر','13','0','0');
INSERT INTO [t30071] VALUES (13,'ومن أنت؟ قال: أنا الباهلي الذي جئتك عام أول، قال: فما غيرك وقد كنت حسن الهيئة؟ قال: ما','14','0','0');
INSERT INTO [t30071] VALUES (14,'اللهم إني أعوذ بك من علم لا ينفع، ودعاء لا يسمع، وقلب لا يخشع، ونفس لا تشبع، اللهم إني','15','0','0');
INSERT INTO [t30071] VALUES (15,'أصبحنا على فطرة الإسلام، وكلمة الإخلاص، وسنة نبينا محمد صلى الله عليه وسلم، وملة أبينا','16','0','0');
INSERT INTO [t30071] VALUES (16,'وجهت وجهي إلى الذي فطر السموات والأرض حنيفا مسلما وما أنا من المشركين، سبحانك اللهم','17','0','0');
INSERT INTO [t30071] VALUES (17,'سمع الله لمن حمده. قال رجل من خلفه: ربنا لك الحمد حمدا كثيرا طيبا مباركا فيه. فلما انصرف','18','0','0');
INSERT INTO [t30071] VALUES (18,'إذا سبقت للعبد من الله منزلة لم ينلها بعمله، ابتلاه في جسده أو في ماله أو ولده، ثم يصبر','19','0','0');
INSERT INTO [t30071] VALUES (19,'إن الرجل من أهل عليين ليشرف على أهل الجنة فيضئ وجهه كأنه كوكب دري، وإن أبا بكر وعمر منهم','20','0','0');
INSERT INTO [t30071] VALUES (20,'فيك مثل من عيسى عليه السلام، أبغضته يهود حتى بهتوا أمه، وأحبته النصارى حتى أنزلوه','21','0','0');
INSERT INTO [t30071] VALUES (21,'لا ترم منزلك أنت وبنوك غدا، حتى آتيكم فإن لي فيكم حاجة. قال فانتظروه حتى بعدما أضحى، فدخل','22','0','0');
INSERT INTO [t30071] VALUES (22,'عن الصديق تسأل؟ قال: رحمك الله وتسميه الصديق.','23','0','0');
INSERT INTO [t30071] VALUES (23,'يخرج في آخر الزمان قوم لهم نبز ينتحلون شيعتنا، وليس من شيعتنا، آية ذلك أنهم يشتمون أبا','24','0','0');
