INSERT INTO [t30173] VALUES (1,'فبعثهم رسول الله إلى ذود له، فشربوا من ألبانها وأبوالها، فلما صحوا ارتدوا عن الإسلام','2','0','0');
INSERT INTO [t30173] VALUES (2,'قدم ثمانية رهط من عكل فاستوخموا المدينة، ثم ذكر الحديث، قال أبو قلابة: ثم ارتدوا عن','3','0','0');
INSERT INTO [t30173] VALUES (3,'فبعث بهم رسول الله عليه السلام إلى لقاح له، ليشربوا من ألبانها فكانوا فيها، ثم عمدوا إلى','4','0','0');
INSERT INTO [t30173] VALUES (4,'واستاقوها وقتلوا غلاما له فيها، فبعث في آثارهم فأخذوا، فقطع أيديهم وأرجلهم وسمل','5','0','0');
INSERT INTO [t30173] VALUES (5,'ونزلت فيهم آية المحاربة','6','0','0');
INSERT INTO [t30173] VALUES (6,'قطع الذين سرقوا لقاحه وسمل أعينهم بالنار، عاتبه الله في ذلك فأنزل الله: {إنما جزاء الذين','7','0','0');
INSERT INTO [t30173] VALUES (7,'لا يحل قتل المؤمن إلا في ثلاث خلال: أن يقتل فيقتل به، أو بزنا بين فيرجم، أو بفساد في','8','0','0');
INSERT INTO [t30173] VALUES (8,'يحل قتل المسلم إلا بأربعة: بأن يكفر بعد إيمانه، أو يقتل نفسا فيقاد بها، أو يزني وقد أحصن','9','0','0');
INSERT INTO [t30173] VALUES (9,'من قتل دون ماله فأفضل شهيد قتل في الإسلام بعد أن يتعوذ بالله وبالإسلام ثلاث مرات، وإن قتل','10','0','0');
INSERT INTO [t30173] VALUES (10,'من قاتل دون ماله حتى يقتل فهو شهيد ابن وهب قال: أخبرني ابن لهيعة , عن عبيد الله بن أبي','11','0','0');
INSERT INTO [t30173] VALUES (11,'من حمل علينا السلاح فليس منا، ولا راصد بطريق','12','0','0');
INSERT INTO [t30173] VALUES (12,'من حمل علينا السلاح فليس منا وأخبرني ابن سمعان قال: كان مجاهد بن جبر يقول في المحارب','13','0','0');
INSERT INTO [t30173] VALUES (13,'ومن يعدل إذا لم أعدل، قد خبت وخسرت إن لم أعدل، قال عمر: يا رسول الله، ائذن لي فيه أضرب','14','0','0');
INSERT INTO [t30173] VALUES (14,'وصف ناسا، إني لأعرف صفتهم في هؤلاء، يقولون الحق بألسنتهم لا يجاوز هذا منهم، وأشار إلى','15','0','0');
INSERT INTO [t30173] VALUES (15,'يمرقون من الإسلام مروق السهم من الرمية وأخبرني عمرو بن الحارث، أن بكيرا حدثه أنه سأل','16','0','0');
INSERT INTO [t30173] VALUES (16,'من غير دينه فاقتلوه، قال مالك: فإنما يعني بذلك من خرج من الإسلام وكفر بالله، ولم يعن بذلك','17','0','0');
INSERT INTO [t30173] VALUES (17,'من كفر بالله من بعد إيمانه طائعا فاقتلوه، قال ابن سمعان: ولا أعلم إلا أن نافعا، وزيد بن','18','0','0');
INSERT INTO [t30173] VALUES (18,'من بدل دينه فاقتلوه، ولا تعذبوا بعذاب الله ابن وهب قال: أخبرني يونس بن يزيد، وابن سمعان،','19','0','0');
INSERT INTO [t30173] VALUES (19,'لا يحل لنبي أن يومئ','20','0','0');
INSERT INTO [t30173] VALUES (20,'لا يحل لنبي أن يومئ','21','0','0');
INSERT INTO [t30173] VALUES (21,'أسيرا فأسلم , فخلى سبيله فكفر , ثم أتي به فأسلم , ثم كفر، أربعا أو خمسا , ثم قال: اللهم','22','0','0');
INSERT INTO [t30173] VALUES (22,'استتاب نبهان أربع مرات , وكان نبهان قد ارتد قال سفيان: وأخبرني عمر بن قيس، عن إبراهيم بن','23','0','0');
INSERT INTO [t30173] VALUES (23,'إذا ارتدت المرأة عن الإسلام قتلت وأخبرني الليث بن سعد , أن سعيد بن عبد العزيز التنوخي','24','0','0');
INSERT INTO [t30173] VALUES (24,'جندب , وما جندب , وما يدريك ما جندب , يضربه ضربة يفرق فيها بين الحق والباطل فلم يدر الناس','25','0','0');
INSERT INTO [t30173] VALUES (25,'سحر من أهل العهد أعليه قتل , قال: بلغنا أن رسول الله عليه السلام قد صنع له ذلك , فلم يقتل','26','0','0');
INSERT INTO [t30173] VALUES (26,'أتي برجل ضرب مملوكا له فقتله , فجلده رسول الله عليه السلام مائة، وأخبرني الحارث بن نبهان','27','0','0');
