INSERT INTO [t30242] VALUES (1,'إن المقيم بها ثلاثة أيام من غير رياء، كمن عبد الله سبعين سنة ما بين الروم','2','0','0');
INSERT INTO [t30242] VALUES (2,'المرابط بها من غير رياء كمن عبد الله سبعين سنة فيما بين الروم والعرب','3','0','0');
INSERT INTO [t30242] VALUES (3,'المقيم بها ثلاثة أيام من غير رياء بمنزلة من عبد الله تعالى سبعين سنة ما بين الروم','4','0','0');
INSERT INTO [t30242] VALUES (4,'إن الإسكندرية وعسقلان عروسان، والإسكندرية أعظمهما عروسا، وإنها لتأتي يوم القيامة تزف','5','0','0');
INSERT INTO [t30242] VALUES (5,'الإسكندرية وعسقلان عروسان، والإسكندرية أفضلهما، وإنها لتأتي يوم القيامة تزف بأهلها إلى','6','0','0');
INSERT INTO [t30242] VALUES (6,'مدينتان من مدائن القدم، وإنهما سيفتحان على أمتي، إحداهما من مدائن الروم يقال لها:','7','0','0');
INSERT INTO [t30242] VALUES (7,'رأيت أناسا من أمتي في أصلاب الرجال لم تحملهن النساء في أرحامهن، يبعثهم الله عز وجل من','14','0','0');
INSERT INTO [t30242] VALUES (8,'إن في الإسكندرية وقزوين بابين من أبواب الجنة فمن رابط إلى إحداهما ليلة وجبت له','16','0','0');
INSERT INTO [t30242] VALUES (9,'إنهما سيفتحان على أمتي، وإنهما بابان من أبواب الجنة، من رابط فيهما أو في أحدهما ليلة','17','0','0');
INSERT INTO [t30242] VALUES (10,'لأن أبيت ليلة بالإسكندرية على فراش وطيء، وطعام طيب، لا تدخل في رجلي شوكة، ولا ألقى عدوا','18','0','0');
INSERT INTO [t30242] VALUES (11,'أحب الرباط إلى الله تعالى بالإسكندرية لأنها تشرف على الخلائق يوم القيامة في صورة المدينة','19','0','0');
INSERT INTO [t30242] VALUES (12,'في خير أرض وأحبها إلى الله تعالى تعرف بالساهرة وهي أرض فلسطين، وغور الأردن، والإسكندرية،','20','0','0');
INSERT INTO [t30242] VALUES (13,'والذي نفسي بيده لرباط ثلاثة أيام بالإسكندرية أفضل عند الله تعالى من عبادة ألف عام، صيام','22','0','0');
INSERT INTO [t30242] VALUES (14,'سيفتح لكم الشام، ومصر، والإسكندرية، وليرابطن فيها، فليبلغن الشاهد منكم الغائب، إن الله','24','0','0');
INSERT INTO [t30242] VALUES (15,'أحب الرباط إلى الله تعالى الإسكندرية، إنها تشرف على الخلائق يوم القيامة في صورة مدينة من','25','0','0');
INSERT INTO [t30242] VALUES (16,'من صلى أربع ركعات والضحى بعسقلان، أو بقيسارية فلسطين، أو في الإسكندرية، كل يوم عليها','27','0','0');
INSERT INTO [t30242] VALUES (17,'أقمت بها ثلاثة فقصرت الصلاة، والمقصر فيها كمن أتم الصلاة سبعين سنة.','28','0','0');
INSERT INTO [t30242] VALUES (18,'كل ميت يختم على عمله إلا المرابط في سبيل الله تعالى، فإنه ينمو له عمله إلى يوم القيامة،','31','0','0');
INSERT INTO [t30242] VALUES (19,'من مات مرابطا في سبيل الله أجري عليه عمله الصالح الذي كان يعمله، وأجري عليه رزقه، وأمن من','32','0','0');
INSERT INTO [t30242] VALUES (20,'العلم والسكينة والحج والإيمان والفضل في المغرب، والجفاء والكفر في','33','0','0');
INSERT INTO [t30242] VALUES (21,'صليت ليلة أسري بي على الباب الثالث من أبواب الإسكندرية ومعي جبريل، عليه السلام في سبعين','35','0','0');
INSERT INTO [t30242] VALUES (22,'يا أبا هريرة طوبى لقوم من أمتي يموتون على ساحل البحر، يخرجون من قبورهم يوم القيامة، يشفع','36','0','0');
INSERT INTO [t30242] VALUES (23,'المرابط في سبيل الله تعالى على ساحل البحر في كل يوم له دعوة مستجابة','38','0','0');
INSERT INTO [t30242] VALUES (24,'من رابط بها ثلاثة أيام من غير رياء كمن عبد الله تعالى سبعين سنة فيما بين الروم','39','0','0');
INSERT INTO [t30242] VALUES (25,'ستكون لكم مشايخ، وخير مشايخكم في الغرب.','41','0','0');
INSERT INTO [t30242] VALUES (26,'من كبر بشاطئ بحر الروم تكبيرة ولا يريد بها إلا وجه الله تعالى والدار الآخرة، جعل الله في','44','0','0');
INSERT INTO [t30242] VALUES (27,'إني قد أمددتك بعبد الله بن مطيع في أربعة آلاف من أهل المدينة، وأمرت معن بن يزيد السلمي أن','47','0','0');
INSERT INTO [t30242] VALUES (28,'إني أخاف أن تخرب هذه المنازل إذا كنتم تتعاورونها، فلما كان عند الكريون، قال: سيروا على','48','0','0');
INSERT INTO [t30242] VALUES (29,'أنه افتتح الإسكندرية الفتح الأخير عنوة فسرى في خلافة عثمان بن عفان، رضي الله عنه بعد موت','49','0','0');
INSERT INTO [t30242] VALUES (30,'يخيرهم فإن دخلوا في الإسلام فذلك، وإن كرهوا فارددهم إلى قراهم','50','0','0');
INSERT INTO [t30242] VALUES (31,'المقيم بها ثلاثة أيام من غير رياء بمنزلة من عبد الله تعالى سبعين ألف سنة ما بين الروم','55','0','0');
INSERT INTO [t30242] VALUES (32,'إذا انتاط عدوكم، وكثرت العزائم، واستحلت الغنائم، فخير جهادكم الرباط','56','0','0');
INSERT INTO [t30242] VALUES (33,'من رابط حارسا وراء المسلمين كان له مثل أجر من خلفه ممن صام وصلى','57','0','0');
INSERT INTO [t30242] VALUES (34,'ما اغبرت قدما عبد في سبيل الله تعالى فتمسهما النار أبدا','58','0','0');
INSERT INTO [t30242] VALUES (35,'لا يزال الجهاد حلوا أخضر ما قطرت السماء وأنبتت الأرض، وسينتشئ نشء من قبل المشرق يقولون:','59','0','0');
INSERT INTO [t30242] VALUES (36,'الخيل معقود في نواصيها الخير إلى يوم القيامة، وأهلها معانون عليها كالماد يده بالصدقة لا','60','0','0');
INSERT INTO [t30242] VALUES (37,'كل ميت يختم على عمله، إلا المرابط فإنه يجري عليه عمله حتى يبعثه الله','61','0','0');
INSERT INTO [t30242] VALUES (38,'حرس ليلة في سبيل الله أفضل من ألف ليلة يصوم نهارها، ويقوم ليلها','62','0','0');
INSERT INTO [t30242] VALUES (39,'من رابط اثني عشر ليلة، أو قال: يوما، في سبيل الله تعالى فليجتهد عباد المساجد أن يدركوا','63','0','0');
INSERT INTO [t30242] VALUES (40,'رباط يوم وليلة في سبيل الله تعالى خير من صيام شهر وقيامه، فإن مات جرى عليه عمله الذي كان','64','0','0');
INSERT INTO [t30242] VALUES (41,'من تقلد سيفا ليلة جمعة في سبيل الله مرابطا لا يريد بذلك إلا وجه الله تعالى ثم مات بعد','65','0','0');
