INSERT INTO [t30266] VALUES (1,'كنت أنا وأبو بكر وعمر، وخرجت أنا وأبو بكر.','2','0','0');
INSERT INTO [t30266] VALUES (2,'ذهبت أنا وأبو بكر وعمر، وخرجت أنا وأبو بكر وعمر، وإن كنت لأظن أن يجعلك الله','3','0','0');
INSERT INTO [t30266] VALUES (3,'اللهم قتلا في سبيلك، ووفاة في بلد نبيك صلى الله عليه وسلم، قلت: وأنى يكون هذا؟ قال:','4','0','0');
INSERT INTO [t30266] VALUES (4,'مسست جلد عمر، فقلت: جلد لا تمسه النار أبدا، قال: فنظر إلي نظرة كنت أرثي له من تلك النظرة،','6','0','0');
INSERT INTO [t30266] VALUES (5,'، قال: فما زادنا على هؤلاء الكلمات، قال محمد بن جعفر، قال شعبة: ثم سأله بعد ذلك، فقال في','7','0','0');
INSERT INTO [t30266] VALUES (6,'اذهب إلى أم المؤمنين عائشة فقل: عمر يقرئك السلام، ولا تقل: أمير المؤمنين، فإني لست اليوم','9','0','0');
INSERT INTO [t30266] VALUES (7,'لعلكما حملتما الأرض ما لم تطق، وكان حذيفة على جوخى وعثمان على ما سقى الفرات، فقال حذيفة:','10','0','0');
INSERT INTO [t30266] VALUES (8,'اسمعوا لقول خليفة رسول الله صلى الله عليه وسلم، فجاء مولى لأبي بكر، يقال له: شديد،','12','0','0');
INSERT INTO [t30266] VALUES (9,'لا يشبع الرجل دون جاره','13','0','0');
INSERT INTO [t30266] VALUES (10,'إذا صلى صلاة جلس للناس، فمن كانت له حاجة كلمه، وإن لم يكن لأحد حاجة، قام فدخل، قال: فصلى','14','0','0');
INSERT INTO [t30266] VALUES (11,'جديد ثوبك هذا، أم غسيل؟ . فقال: بل غسيل، فقال: البس جديدا، وعش حميدا، ومت','15','0','0');
INSERT INTO [t30266] VALUES (12,'إن تؤمروا أبا بكر تجدوه أمينا زاهدا في الدنيا، راغبا في الآخرة، وإن تؤمروا عمر تجدوه قويا','16','0','0');
INSERT INTO [t30266] VALUES (13,'أما إن ربك يحب المدح وجعلت أنشده فاستأذن رجل طوال أصلع، فقال لي رسول الله صلى الله عليه','17','0','0');
INSERT INTO [t30266] VALUES (14,'هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين لا','18','0','0');
INSERT INTO [t30266] VALUES (15,'هذان سيدا كهول أهل الجنة من الأولين والآخرين، ثم قال: يا علي، لا','19','0','0');
INSERT INTO [t30266] VALUES (16,'هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين.','20','0','0');
INSERT INTO [t30266] VALUES (17,'إن لي وزيرين من أهل السماء، ووزيرين من أهل الأرض فأما وزيراي من أهل السماء فجبريل','21','0','0');
INSERT INTO [t30266] VALUES (18,'إن أهل الجنة ليتراءون أهل الدرجات العلى كما يتراءى أهل الدنيا الكوكب الدري في السماء، وإن','22','0','0');
INSERT INTO [t30266] VALUES (19,'إن أهل الدرجات العلى يراهم من هم أسفل منهم كالكوكب الدري، وإن أبا بكر وعمر منهم وأنعما','23','0','0');
INSERT INTO [t30266] VALUES (20,'إن الرجل من أهل الجنة ليشرف على أهل الجنة كأنه كوكب دري، وإن أبا بكر وعمر منهم','24','0','0');
INSERT INTO [t30266] VALUES (21,'إن أهل الدرجات العلى يراهم من هم أسفل منهم كما يرى الكوكب الدري في أفق السماء، وأبو بكر','25','0','0');
INSERT INTO [t30266] VALUES (22,'من أصبح صائما اليوم؟ فقال عمر: أنا، قال: فمن تصدق اليوم؟ قال عمر: أنا، قال: فمن عاد','26','0','0');
INSERT INTO [t30266] VALUES (23,'كيف أبعثهما وهما من الدين بمنزلة السمع والبصر','27','0','0');
INSERT INTO [t30266] VALUES (24,'اقتدوا باللذين من بعدي ـ وأشار إلى أبي بكر وعمر ـ واهتدوا بهدي عمار، وإذا حدثكم ابن أم','28','0','0');
INSERT INTO [t30266] VALUES (25,'اقتدوا باللذين من بعدي: أبو بكر، وعمر','29','0','0');
INSERT INTO [t30266] VALUES (26,'إنه مضى صاحبان لي ـ يعني: رسول الله صلى الله عليه وسلم وأبا بكر ـ عملا عملا وسلكا طريقا،','30','0','0');
INSERT INTO [t30266] VALUES (27,'قد علمت أنه ليس منكم إلا ناصح ولكني تركت صاحبي على جادة فإن تركت جادتهما لم أدركهما في','31','0','0');
INSERT INTO [t30266] VALUES (28,'إن أبا بكر في الملائكة مثل ميكائيل عليه السلام، ومثلك يا عمر في الملائكة مثل جبريل عليه','32','0','0');
INSERT INTO [t30266] VALUES (29,': هكذا نبعث يوم القيامة','33','0','0');
INSERT INTO [t30266] VALUES (30,'هكذا نبعث يوم القيامة','34','0','0');
INSERT INTO [t30266] VALUES (31,'أتاني جبريل عليه السلام آنفا، فقلت: يا جبريل , حدثني عن فضائل عمر بن الخطاب في السماء،','35','0','0');
INSERT INTO [t30266] VALUES (32,'أنا أول من تنشق عنه الأرض، ثم أبو بكر، ثم عمر، ثم يأتي أهل البقيع فيحشرون معي، ثم أنتظر','36','0','0');
INSERT INTO [t30266] VALUES (33,'يا عائشة , تعالي فانظري , فجئت فوضعت ذقني على منكب رسول الله صلى الله عليه وسلم، فجعلت','37','0','0');
INSERT INTO [t30266] VALUES (34,'إني لأنظر إلى شياطين الجن والإنس قد فرقوا من عمر.','38','0','0');
INSERT INTO [t30266] VALUES (35,'من شهد منكم جنازة؟ قال عمر: أنا، قال: فمن عاد منكم مريضا؟ قال عمر: أنا، قال: فمن تصدق؟','39','0','0');
INSERT INTO [t30266] VALUES (36,'عمر معي وأنا مع عمر والحق بعدي مع عمر حيث كان. قال ابن شاهين: وهذا حديث صحيح غريب لا أعلم','40','0','0');
INSERT INTO [t30266] VALUES (37,': اللهم أيد هذا الدين بأحب الرجلين إليك عمر بن الخطاب أو أبي جهل بن هشام، وقال: فكان أحب','41','0','0');
