INSERT INTO [t30294] VALUES (1,'توضئوا مما أنضجت النار.','2','0','0');
INSERT INTO [t30294] VALUES (2,'يفتتح الصلاة حين يرفع يديه حتى يجاوز منكبيه، وإذا أراد أن يركع، وبعدما يرفع من الركوع،','3','0','0');
INSERT INTO [t30294] VALUES (3,'من أتم الوضوء كما أمره الله عز وجل فالصلوات الخمس كفارات لما بينهن. هذا حديث صحيح أخرجه','4','0','0');
INSERT INTO [t30294] VALUES (4,'إن خلق أحدكم يجمع في بطن أمه خمسة وأربعين يوما، ثم يكون علقة مثل ذلك، ثم يكون مضغة مثل','5','0','0');
INSERT INTO [t30294] VALUES (5,'إن لكل غادر لواء يوم القيامة يعرف به.','6','0','0');
INSERT INTO [t30294] VALUES (6,'ويحك يابن سمية، تقتلك الفئة الباغية، ويحك يابن سمية، تقتلك الفئة الباغية،','7','0','0');
INSERT INTO [t30294] VALUES (7,'الشؤم في الفرس والمرأة والدار. صحيح من حديث محمد بن مسلم بن شهاب، أخرجه البخاري عن سعيد','8','0','0');
INSERT INTO [t30294] VALUES (8,'بلى. قال الرجل: ألم تقرئني كذا وكذا؟ قال: بلى. فقال النبي صلى الله عليه وسلم: أتاني جبريل','9','0','0');
INSERT INTO [t30294] VALUES (9,'أرخص للمهاجر ثلاثة أيام بعد الصدر. صحيح من حديث السائب بن يزيد، أخرجه البخاري عن إبراهيم','10','0','0');
INSERT INTO [t30294] VALUES (10,'أني أخفف الصلاة على الناس.','11','0','0');
INSERT INTO [t30294] VALUES (11,'ليس على المرء المسلم في فرسه ولا مملوكه صدقة. صحيح من حديث عراك بن مالك الغفاري، عن أبي','12','0','0');
INSERT INTO [t30294] VALUES (12,'مره فليراجعها، ثم يمسكها حتى تطهر، ثم تحيض، ثم تطهر، ثم إن شاء طلق. صحيح، أخرجه البخاري','13','0','0');
INSERT INTO [t30294] VALUES (13,'ما حق امرء مسلم - يعني: له شيء يوصي فيه - يبيت ليلتين إلا ووصيته مكتوبة عنده. سقط من نسخة','14','0','0');
INSERT INTO [t30294] VALUES (14,'لا إله إلا الله، ويل للعرب من شر قد اقترب، فتح اليوم من ردم يأجوج ومأجوج مثل هذا. وعقد','15','0','0');
INSERT INTO [t30294] VALUES (15,'لا إله إلا الله ـ ثلاث مرات ـ ويل للعرب من شر قد اقترب، فتح من ردم يأجوج ومأجوج مثل هذه،','16','0','0');
INSERT INTO [t30294] VALUES (16,'كان رسول الله صلى الله عليه وسلم يقبل وهو صائم، وكان أملككم لإربه','17','0','0');
INSERT INTO [t30294] VALUES (17,'كان النبي صلى الله عليه وسلم يقبل وهو صائم وكان أملككم لإربه. هذا حديث صحيح رواه جماعة عن','18','0','0');
INSERT INTO [t30294] VALUES (18,'نهى رسول الله صلى الله عليه وسلم عن كرى الأرض. صحيح، أخرجاه بألفاظ مختلفة، وأخرجه مسلم','19','0','0');
INSERT INTO [t30294] VALUES (19,'أشهد أن لا إله إلا الله، وأني رسول الله، من لقي الله عز وجل بهما غير شاك لم يحجب عن','20','0','0');
INSERT INTO [t30294] VALUES (20,'توضئوا مما مست النار. قال أبو هريرة: ما توضأت إلا من ثور أقط أكلته.','21','0','0');
INSERT INTO [t30294] VALUES (21,'أيما امرء مسلم أعتق امرأ مسلما استنقذ الله بكل عضو منه عضوا من النار. قال سعيد بن مرجانة:','22','0','0');
INSERT INTO [t30294] VALUES (22,'العائد في هبته كالكلب يقيء ثم يعود في قيئه. صحيح من حديث عبد الله بن عباس، من رواية سعيد','23','0','0');
INSERT INTO [t30294] VALUES (23,'من اشترى سلعة ثم أفلس فصاحبها أحق بها. هذا حديث صحيح من حديث أبي بكر بن عبد الرحمن بن','24','0','0');
INSERT INTO [t30294] VALUES (24,'أوكلكم يجد ثوبين؟ . صحيح أخرجاه في الصحيح من حديث محمد بن سيرين، عن أبي','25','0','0');
INSERT INTO [t30294] VALUES (25,'لقد كنت أفتل القلائد لرسول الله صلى الله عليه وسلم، فيبعث بالهدي إلى الكعبة، ثم ما يحرم','26','0','0');
INSERT INTO [t30294] VALUES (26,'إذا قلت لصاحبك أنصت، والإمام يخطب، فقد لغوت. هذا حديث صحيح من حديث محمد بن مسلم بن عبيد','27','0','0');
INSERT INTO [t30294] VALUES (27,'أن رهطا من عكل وعرينة أتوا النبي صلى الله عليه وسلم، فقالوا: يا رسول الله، إنا كنا أهل','28','0','0');
INSERT INTO [t30294] VALUES (28,'كان يقطع في ربع دينار فصاعدا. صحيح أخرجه البخاري، عن إسماعيل بن أبي أويس، عن ابن وهب، عن','29','0','0');
INSERT INTO [t30294] VALUES (29,'دخلت الجنة فرأيت قصرا من ذهب، أعجبني حسنه، قلت: لمن هذا؟ قيل: لعمر. فما منعني أن أدخله','30','0','0');
INSERT INTO [t30294] VALUES (30,'لا يدخل الجنة رجل في قلبه مثقال حبة من كبر، ولا يدخل النار رجل في قلبه مثقال حبة من خردل','31','0','0');
INSERT INTO [t30294] VALUES (31,'لو أن أحدنا رأى على امرأته فاحشة، كيف يصنع؟ إن تكلم تكلم بأمر عظيم، وإن سكت سكت على مثل','32','0','0');
INSERT INTO [t30294] VALUES (32,'من صلى على جنازة فله قيراط، ومن تبعها حتى يقضى قضاؤها فله قيراطان أحدهما مثل أحد. وقال:','33','0','0');
INSERT INTO [t30294] VALUES (33,'من شيع جنازة من أهلها حتى توضع فله قيراط، ومن تبعها حتى تدفن فله قيراطان، أدناهما أو','34','0','0');
INSERT INTO [t30294] VALUES (34,'ما حق امرئ مسلم - له مال يوصي فيه - يبيت ليلتين إلا ووصيته مكتوبة','35','0','0');
INSERT INTO [t30294] VALUES (35,'طيبت رسول الله صلى الله عليه وسلم، من أطيب شيء قدرت عليه بيدي لحله وإحرامه. روي هذا','36','0','0');
INSERT INTO [t30294] VALUES (36,'إنا لا نورث. الحديث صحيح من رواية أبي بكر الصديق، وعمر، رضي الله عنهما، من غير طريق. وروي','37','0','0');
INSERT INTO [t30294] VALUES (37,'رخص في العرية أن تؤخذ بمثل خرصها، يأكلها أهلها رطبا. صحيح أخرجاه من حديث نافع، عن عبد','38','0','0');
INSERT INTO [t30294] VALUES (38,'أن رجلا مات فدخل الجنة، فقيل له: ما كنت تعمل، فإما ذكر وإما ذكر، فقال: إني كنت أبايع','39','0','0');
INSERT INTO [t30294] VALUES (39,'حلف لا يدخل على بعض أهله شهرا. فلما بلغ تسعا وعشرين يوما غدا عليهن، أو راح، فقيل له: حلفت','40','0','0');
INSERT INTO [t30294] VALUES (40,'إنه لوقتها، لولا أن أشق على أمتي.','41','0','0');
INSERT INTO [t30294] VALUES (41,'يدخل الجنة أقوام أفئدتهم مثل أفئدة الطير.','42','0','0');
INSERT INTO [t30294] VALUES (42,'من زعم أن محمدا صلى الله عليه وسلم رأى ربه فقد أعظم الفرية على الله عز وجل، ولكنه رأى','43','0','0');
INSERT INTO [t30294] VALUES (43,'من أرادها بسوء ـ يريد المدينة ـ أذابه الله عز وجل ذوب الملح في الماء.','44','0','0');
INSERT INTO [t30294] VALUES (44,'إن أصحاب هذه الصور يعذبون، ويقال لهم: أحيوا ما خلقتم. هذا حديث صحيح من الأبدال العوالي،','45','0','0');
INSERT INTO [t30294] VALUES (45,'من كان اعتكف معي فليعتكف في العشر الأواخر، وقد رأيت هذه الليلة ثم أنسيتها، وقد رأيتني','46','0','0');
INSERT INTO [t30294] VALUES (46,'بايعنا رسول الله صلى الله عليه وسلم على السمع والطاعة في العسر واليسر، والمنشط والمكره،','47','0','0');
INSERT INTO [t30294] VALUES (47,'ناس من أمتي عرضوا علي غزاة في سبيل الله، يركبون ثبج هذا البحر ملوكا على الأسرة ـ أو مثل','48','0','0');
INSERT INTO [t30294] VALUES (48,'اعزل الأذى عن طريق المسلمين.','49','0','0');
INSERT INTO [t30294] VALUES (49,'أمير عشر يلي أمر المسلمين ولا يجهد لهم ولا ينصح إلا لم يدخل معهم','50','0','0');
INSERT INTO [t30294] VALUES (50,'من توضأ فأحسن الوضوء، ثم أتى الجمعة، فدنا، وأنصت، واستمع، غفر له من الجمعة إلى الجمعة','51','0','0');
INSERT INTO [t30294] VALUES (51,'لأن أقول: سبحان الله والحمد لله ولا إله إلا الله والله أكبر أحب إلي مما طلعت عليه','52','0','0');
INSERT INTO [t30294] VALUES (52,'إن أثقل الصلاة على المنافقين صلاة العشاء وصلاة الفجر، ولو يعلمون ما فيهما لأتوهما ولو','53','0','0');
INSERT INTO [t30294] VALUES (53,'لعن الله السارق يسرق الحبل فتقطع يده، ويسرق البيضة فتقطع يده.','54','0','0');
INSERT INTO [t30294] VALUES (54,'فضحك رسول الله صلى الله عليه وسلم حتى بدت نواجذه، فأنزل الله تعالى {وما قدروا الله حق','55','0','0');
INSERT INTO [t30294] VALUES (55,'صدقة تصدق الله بها عليكم فاقبلوا صدقته.','56','0','0');
INSERT INTO [t30294] VALUES (56,'ما مسست بيدي ديباجا ولا حريرا ولا شيئا كان ألين من كف رسول الله صلى الله عليه وسلم، ولا','57','0','0');
INSERT INTO [t30294] VALUES (57,'ولك. فقال القوم: استغفر لك رسول الله صلى الله عليه وسلم، قال: نعم ولكم، ثم تلا الآية','58','0','0');
INSERT INTO [t30294] VALUES (58,'أمر النبي صلى الله عليه وسلم بقتل الوزغ وسماه فويسقا.','59','0','0');
INSERT INTO [t30294] VALUES (59,'إن اسمي محمد الذي سماني به أهلي. فقال اليهودي: جئت أسألك، فقال رسول الله صلى الله عليه','60','0','0');
INSERT INTO [t30294] VALUES (60,'{ونفس وما سواها فألهمها فجورها وتقواها} .','61','0','0');
INSERT INTO [t30294] VALUES (61,'لو أن لابن آدم واديا من مال لأحب أن يكون له إليه مثله، ولا يملأ نفس ابن آدم إلا التراب،','62','0','0');
INSERT INTO [t30294] VALUES (62,'يا عبد الله بن قيس ألا أعلمك كلمة من كنوز الجنة، لا حول ولا قوة إلا','63','0','0');
INSERT INTO [t30294] VALUES (63,'يا عبد الله بن قيس، ألا أدلك على كلمة من كنز الجنة؟ قال: قلت: بلى، قال: لا حول ولا قوة','64','0','0');
INSERT INTO [t30294] VALUES (64,'إنما فاطمة بضعة مني فمن أغضبها فقد أغضبني. أخرجاه في الصحيحين بمعناه من حديث سفيان بن','65','0','0');
INSERT INTO [t30294] VALUES (65,'يا أبا ذر إني أراك ضعيفا وإني أحب لك ما أحب لنفسي، لا تأمرن على اثنين، ولا تولين مال','67','0','0');
INSERT INTO [t30294] VALUES (66,'اللهم إني أعوذ بك من زوال نعمتك، وتحول عافيتك، وفجاءة نقمتك، وجميع','68','0','0');
INSERT INTO [t30294] VALUES (67,'فيما استطعت.','69','0','0');
INSERT INTO [t30294] VALUES (68,'يتتبع الدباء من حروف الصحفة. قال: فلم أزل أحب الدباء من ذلك اليوم. أخرجه البخاري ومسلم','70','0','0');
INSERT INTO [t30294] VALUES (69,'نهى النبي صلى الله عليه وسلم عن الضرب في الوجه.','71','0','0');
INSERT INTO [t30294] VALUES (70,'إذا دخل أهل الجنة الجنة نودوا: يا أهل الجنة، إن لكم عند الله موعدا لم تروه، قالوا: وما','72','0','0');
INSERT INTO [t30294] VALUES (71,'إن الله لا ينظر إلى صوركم وأموالكم وإنما ينظر إلى قلوبكم وأعمالكم.','73','0','0');
INSERT INTO [t30294] VALUES (72,'لو كان مطعم بن عدي حيا فكلمني في هؤلاء النتنى لخليتهم له.','74','0','0');
INSERT INTO [t30294] VALUES (73,'كسب الحجام خبيث، ومهر البغي خبيث، وحلوان الكاهن خبيث.','75','0','0');
INSERT INTO [t30294] VALUES (74,'ما اسمك؟ قلت: حزن، قال: بل أنت سهل، قال: لا أغير اسما سمانيه أبي، قال ابن المسيب: ففينا','76','0','0');
INSERT INTO [t30294] VALUES (75,'نعم. قلت: فهل بعد الشر من خير؟ قال: نعم، وفيه دخن. قلت: وما دخنه؟ قال: قوم تعرف منهم','77','0','0');
INSERT INTO [t30294] VALUES (76,'ألا تريحني من ذي الخلصة، وكانت بيتا في خثعم يسمى كعبة اليمانية، فانطلقت في خمسين ومائة','78','0','0');
