INSERT INTO [t30340] VALUES (1,'الأيمن فالأيمن','2','0','0');
INSERT INTO [t30340] VALUES (2,'أصلى الناس؟ فقيل: لا، هم ينتظرونك يا رسول الله، والناس عكوف لصلاة العشاء الآخرة، قالت:','3','0','0');
INSERT INTO [t30340] VALUES (3,'هاؤم. قلنا: ويحك، أو ويلك، اغضض من صوتك فإنك قد نهيت عن ذلك، فقال: والله لا أغضض صوتي،','4','0','0');
INSERT INTO [t30340] VALUES (4,'إن رجلا كان على ترعة من ترع الجنة، ولقد خير عبد من عباد الله أن يعيش في الدنيا ما شاء وأن','5','0','0');
INSERT INTO [t30340] VALUES (5,'أيها الناس، توبوا إلى الله قبل أن تموتوا، وصلوا الذي بينكم وبين ربكم بكثرة الصوم والصلاة','6','0','0');
INSERT INTO [t30340] VALUES (6,'ما هذه؟ قال: هذه الجمعة، فضلت بها أنت وأمتك والناس لكم فيها تبع، اليهود والنصارى لكم فيها','7','0','0');
INSERT INTO [t30340] VALUES (7,'أن عمار بن ياسر، وأسيد بن حضير كانا عند النبي صلى الله عليه وسلم في ليلة مظلمة حندس فخرجا','8','0','0');
INSERT INTO [t30340] VALUES (8,'يا أيها الناس، إن أبا بكر لم يسؤني قط فاعرفوا ذلك له، يا أيها الناس، إني راض عن عمر','9','0','0');
INSERT INTO [t30340] VALUES (9,'من كنت مولاه فعلي مولاه، اللهم وال من والاه، وعاد من عاداه.','10','0','0');
INSERT INTO [t30340] VALUES (10,'يا أبا الدرداء، أتمشي أمام من هو خير منك في الدنيا والآخرة، ما طلعت شمس ولا غربت على أحد','11','0','0');
INSERT INTO [t30340] VALUES (11,'ارتدف رسول الله صلى الله عليه وسلم خلف أبي بكر رضي الله عنه فكان إذا مر بالملأ من قريش،','12','0','0');
INSERT INTO [t30340] VALUES (12,'ما هذا يا بلال؟ قال تمر أدخره، قال: ويحك يا بلال أو ما تخاف أن يكون له بخار في النار،','13','0','0');
INSERT INTO [t30340] VALUES (13,'أعطاه دينارا يشتري له شاة للضحية، فاشترى له شاتين، فباع إحداهما بدينار فأتى النبي صلى','14','0','0');
INSERT INTO [t30340] VALUES (14,'من جاءني زائرا لم تنزعه حاجة إلا زيارتي كان حقا علي أن أكون له شفيعا يوم','15','0','0');
INSERT INTO [t30340] VALUES (15,'لعهد إلي النبي صلى الله عليه وسلم الأمي ألا يحبني إلا مؤمن ولا يبغضني إلا','16','0','0');
INSERT INTO [t30340] VALUES (16,'هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين، لا تخبرهما يا','17','0','0');
INSERT INTO [t30340] VALUES (17,'يا أبا بكر، إنما يعرف الفضل لأهل الفضل ذووا الفضل','18','0','0');
INSERT INTO [t30340] VALUES (18,'ما من نبي إلا له نظير في أمته، وأبو بكر نظير إبراهيم، وعمر نظير موسى، وعثمان نظير هارون،','19','0','0');
INSERT INTO [t30340] VALUES (19,'أعيذكما بكلمات الله التامة من كل شيطان وهامة ومن كل عين لامة، ويقول: كان إبراهيم صلى الله','20','0','0');
INSERT INTO [t30340] VALUES (20,'في ليلة باردة؟ فقال النبي صلى الله عليه وسلم: ما جاء بها إلا حاجة أو أمر فخرج حتى أتى باب','21','0','0');
INSERT INTO [t30340] VALUES (21,'إن شئت تصدقت وإن شئت أمسكت أصله.','22','0','0');
INSERT INTO [t30340] VALUES (22,'لم؟ .','23','0','0');
INSERT INTO [t30340] VALUES (23,'رأيت دلوا أخذها ابن أبي قحافة فنزع دلوا أو دلوين، وفي نزعه ضعف ليغفر الله له، ثم قام عمر','24','0','0');
INSERT INTO [t30340] VALUES (24,'أفعل وايم الله لو أنكما تتفقان على أمر واحد ما عصيتكما في مشورة أبدا، ولقد ضرب لي ربي عز','25','0','0');
INSERT INTO [t30340] VALUES (25,'لكل أمة أمين، وأمين هذه الأمة أبو عبيدة بن الجراح','26','0','0');
INSERT INTO [t30340] VALUES (26,'يا أبا ذر، إن لكل شيء تحية، وتحية المسجد ركعتان، فقم فاركعهما. فقمت فركعتهما ثم أقبلت','27','0','0');
INSERT INTO [t30340] VALUES (27,'يا أبا بكر، هل بلغك ما طوبى؟ فقال: الله ورسوله أعلم، فقال: طوبى: شجرة في الجنة لا يعلم ما','28','0','0');
INSERT INTO [t30340] VALUES (28,'جعل الحق على لسان عمر وقلبه','29','0','0');
INSERT INTO [t30340] VALUES (29,'اللهم عثمان رضيت عنه فارض عنه','30','0','0');
INSERT INTO [t30340] VALUES (30,'أنت الذي يقول: لأصومن الدهر ولأقومن الليل؟ فقال: قد قلت ذلك يا رسول الله، فقال رسول الله','31','0','0');
INSERT INTO [t30340] VALUES (31,'إنه كان يبغض عثمان.','32','0','0');
INSERT INTO [t30340] VALUES (32,'نعم القوم الأزد نقية قلوبهم بارة أيمانهم طيبة أفواههم','33','0','0');
INSERT INTO [t30340] VALUES (33,'لقد سألت عظيما وإنه ليسير على من يسره الله عليه، تعبد الله لا تشرك به شيئا، وتقيم الصلاة','34','0','0');
INSERT INTO [t30340] VALUES (34,'من كذب علي متعمدا فليتبوأ مقعده من النار','35','0','0');
INSERT INTO [t30340] VALUES (35,'ما زالت الملائكة حافته بأجنحتها حتى رفع. قال جابر: وكان عليه دين فجاء الغرماء فجلسوا','36','0','0');
INSERT INTO [t30340] VALUES (36,'أما ترضون أن يذهب الناس بالدنيا، وتذهبون برسول الله صلى الله عليه وسلم؟','37','0','0');
INSERT INTO [t30340] VALUES (37,'أتاكم أهل اليمن هم أرق أفئدة، وألين قلوبا، الإيمان يمان، والحكمة يمانية، والفخر والخيلاء','38','0','0');
INSERT INTO [t30340] VALUES (38,'يدخل الجنة زمرة هم سبعون ألفا تضيء وجوههم إضاءة القمر ليلة البدر. قال أبو هريرة: فقام','39','0','0');
INSERT INTO [t30340] VALUES (39,'من يرد هوان قريش أهانه الله','40','0','0');
INSERT INTO [t30340] VALUES (40,'اللهم صل على أبي بكر فإنه يحبك ويحب رسولك، اللهم صل على عمر فإنه يحبك ويحب رسولك، اللهم','41','0','0');
INSERT INTO [t30340] VALUES (41,'إذا مات الرجل منكم ودفنتموه فليقم أحدكم عند رأسه، وليقل: يا فلان بن فلانة فإنه سيسمع،','42','0','0');
INSERT INTO [t30340] VALUES (42,'أبني إني واعظ ومؤدب ... فافهم فإن العاقل المتأدب واحفظ وصية والد متحنن ... يغذوك بالآداب','48','0','0');
