INSERT INTO [t30341] VALUES (1,'أنت تبين لأمتي ما اختلفوا فيه من بعدي. هذا على شرط خ، م.','1','0','0');
INSERT INTO [t30341] VALUES (2,'ادعوا لي سيد العرب.','2','0','0');
INSERT INTO [t30341] VALUES (3,'لأقتلن العمالقة في كتيبة، فقال له جبريل: أو علي؟ فقال: أو علي رضي الله','3','0','0');
INSERT INTO [t30341] VALUES (4,'أنا مدينة العلم، وعلي بابها، فمن أراد المدينة فليأت الباب. صحيح.','4','0','0');
INSERT INTO [t30341] VALUES (5,'أنا مدينة العلم، وعلي بابها الحديث. وتمامه: علي إمام البررة، وقاتل الفجرة، منصور من نصره،','5','0','0');
INSERT INTO [t30341] VALUES (6,'أنت سيد في الدنيا والآخرة، حبيبك حبيبي، وحبيبي حبيب الله، وعدوك عدوي، وعدوي عدو الله،','6','0','0');
INSERT INTO [t30341] VALUES (7,'من أراد أن يحيا حياتي، ويموت موتي، ويسكن جنة الخلد، فليتول عليا. هذا','7','0','0');
INSERT INTO [t30341] VALUES (8,'ألا إن مثل أهلي فيكم مثل سفينة نوح، فمن ركبها نجا، ومن تخلف عنها هلك.','8','0','0');
INSERT INTO [t30341] VALUES (9,'إن أول من يدخل الجنة أنا وفاطمة والحسن والحسين، قلت: يا رسول الله، فمحبونا؟ قال: من','9','0','0');
INSERT INTO [t30341] VALUES (10,'تبعث الأنبياء يوم القيامة، وأبعث على البراق، وتبعث فاطمة أمامي، ويبعث صالح على ناقته.','10','0','0');
INSERT INTO [t30341] VALUES (11,'إذا كان يوم القيامة نادى مناد من وراء الحجب: يا أهل الجمع غضوا أبصاركم حتى تمر فاطمة. هذا','11','0','0');
INSERT INTO [t30341] VALUES (12,'إن الله يغضب لغضبك، ويرضى لرضاك. هذا حديث صحيح.','12','0','0');
INSERT INTO [t30341] VALUES (13,'دخل على فاطمة، فقال: والله ما أحد من الناس بعد أبيك أحب إلي منك. هذا على. . .','13','0','0');
INSERT INTO [t30341] VALUES (14,'أما ترضين أن الله اطلع إلى أهل الأرض فاختار رجلين أحدهما أبوك، والآخر بعلك. هذا على شرط','14','0','0');
INSERT INTO [t30341] VALUES (15,'النبي صلى الله عليه المنذر وأنا الهادي. قلت هذا من وضع الأشقر محمد بن أحمد بن عياض، هو','15','0','0');
INSERT INTO [t30341] VALUES (16,'يا علي، طوبى لمن أحبك، وصدق فيك، وويل لمن أبغضك، وكذب فيك. هذا صحيح.','16','0','0');
