INSERT INTO [t30346] VALUES (1,'والله ما الدنيا في الآخرة إلا مثل ما يجعل أحدكم إصبعه هذه في اليم فلينظر بم','2','0','0');
INSERT INTO [t30346] VALUES (2,'من صام يوم الأربعاء والخميس والجمعة ثم تصدق يوم الجمعة بما قل من ماله أو كثر غفر له كل','3','0','0');
INSERT INTO [t30346] VALUES (3,'أطفال المسلمين في جبل في الجنة يكفلهم إبراهيم وسارة حتى يدفعوهم إلى آبائهم يوم','4','0','0');
INSERT INTO [t30346] VALUES (4,'سرور تدخله على مسلم، أو كربة تكشفها عنه في دين تقضيه عنه أو جوع تطرده','5','0','0');
INSERT INTO [t30346] VALUES (5,'الكذب يسود الوجه، والنميمة عذاب القبر','6','0','0');
INSERT INTO [t30346] VALUES (6,'لقد رأيت النبي صلى الله عليه وسلم يلتوي ما يجد ما يملأ بطنه من الدقل، وما ترضون إلا ألوان','7','0','0');
INSERT INTO [t30346] VALUES (7,'فنزل رسول الله صلى الله عليه وسلم فضمها إليه، فكانت تئن كما يئن الصبي الذي يسكت، كانت تئن','8','0','0');
INSERT INTO [t30346] VALUES (8,'حوسب رجل فلم توجد له حسنة عملها، فقيل: إنه كان ذا مال وكان يداين الناس، فقال لغلمانه: من','9','0','0');
INSERT INTO [t30346] VALUES (9,'إن الله عز وجل حيي كريم يستحيي أن يرفع العبد يديه أن يردهما صفرا حتى يضع فيهما','10','0','0');
INSERT INTO [t30346] VALUES (10,'أستودع الله دينك وأمانتك وخواتيم عملك','11','0','0');
INSERT INTO [t30346] VALUES (11,'اعلم أبا مسعود. قال: فجعلت لا ألتفت إليه من الغضب حتى غشيني، فإذا هو رسول الله صلى الله','12','0','0');
INSERT INTO [t30346] VALUES (12,'ما من أمتي من أحد إلا أنا أعرفه يوم القيامة.','13','0','0');
INSERT INTO [t30346] VALUES (13,'هؤلاء في الجنة ولا أبالي، وهؤلاء في النار ولا أبالي.','14','0','0');
INSERT INTO [t30346] VALUES (14,'جاء أعرابي إلى النبي صلى الله عليه وسلم فسأله عن الحوض وذكر الجنة، ثم قال الأعرابي: فيها','15','0','0');
INSERT INTO [t30346] VALUES (15,'ثلاثة أشياء رأيتهن من رسول الله صلى الله عليه وسلم، بينا نحن نسير معه إذ مر رنا ببعير','16','0','0');
INSERT INTO [t30346] VALUES (16,'إذا جمع الله الأولين والآخرين ليوم القيامة ليوم لا ريب فيه، ينادي مناد: من كان أشرك في','17','0','0');
INSERT INTO [t30346] VALUES (17,'إذا فرغ الرجل من صلاته فقال: رضيت بالله ربا، وبالإسلام دينا، وبالقرآن إماما، كان حقا على','18','0','0');
INSERT INTO [t30346] VALUES (18,'من تبع جنازة حتى يصلى عليها ويفرغ منها فله قيراطان، ومن تبعها حتى يفرغ منها فله قيراط،','19','0','0');
INSERT INTO [t30346] VALUES (19,'سمى لنا رسول الله صلى الله عليه وسلم نفسه أسماء فمنها ما حفظنا، قال: أنا محمد، وأنا أحمد،','20','0','0');
INSERT INTO [t30346] VALUES (20,'إن الله عز وجل خلق في الجنة ريحا بعد الريح بسبع سنين، ومن دونها باب مغلق، فإنما يأتيكم','21','0','0');
INSERT INTO [t30346] VALUES (21,'يأجوج أمة، ومأجوج أمة، كل أمة أربع مائة ألف أمة، لا يموت الرجل حتى ينظر إلى ألف ذكر بين','22','0','0');
INSERT INTO [t30346] VALUES (22,'إنه ليس من عبد يلقى الله عز وجل لا يشرك به شيئا لم يتند بدم حرام إلا دخل من أي أبواب','23','0','0');
INSERT INTO [t30346] VALUES (23,'خيار أمتي الذين يشهدون أن لا إله إلا الله وأني رسول الله، والذين إذا أحسنوا استبشروا وإذا','25','0','0');
INSERT INTO [t30346] VALUES (24,'يطلع الله عز وجل إلى خلقه ليلة النصف من شعبان فيغفر لجميع خلقه إلا لمشرك أو','26','0','0');
INSERT INTO [t30346] VALUES (25,'إذا كان النصف من شعبان غفر الله تعالى من الذنوب أكثر من عدد شعر غنم','27','0','0');
INSERT INTO [t30346] VALUES (26,'إن الله يلحظ إلى الكعبة في كل عام لحظة، فعند ذلك تحن قلوب المؤمنين','28','0','0');
INSERT INTO [t30346] VALUES (27,'إن جبريل عليه السلام أتاني آنفا، فبشرني أن الله عز وجل أعطاني الشفاعة، فقلنا: يا رسول','30','0','0');
INSERT INTO [t30346] VALUES (28,'إذا كان للمؤمن عش كعش الطير وماء وخبز وملح، فذلك من النعيم','33','0','0');
INSERT INTO [t30346] VALUES (29,'إن الله بنى جنة عدن بيده، وبناها لبنة من ذهب ولبنة من فضة، وجعل ملاطها المسك، وترابها','34','0','0');
INSERT INTO [t30346] VALUES (30,'كأني أنظر إلى تدافع أمتي بين الحوض والمقام، فيلقى الرجل الرجل فيقول: يا فلان أشربت؟','35','0','0');
INSERT INTO [t30346] VALUES (31,'يؤتى بابن آدم يوم القيامة فيوقف بين كفتي الميزان، ويوكل به ملك، فإن ثقل ميزانه نادى الملك','36','0','0');
INSERT INTO [t30346] VALUES (32,'ليلة النصف من شعبان يهبط الرحمن عز وجل إلى سماء الدنيا فينظر إلى أعمال العباد فيغفر','38','0','0');
INSERT INTO [t30346] VALUES (33,'يا علي من صلى مائة ركعة في ليلة النصف من شعبان يقول في كل ركعة بفاتحة الكتاب مرة وعشر','39','0','0');
INSERT INTO [t30346] VALUES (34,'{من جاء بالحسنة فله خير منها وهم من فزع يومئذ آمنون} [النمل: ٨٩] ، قال: هي لا إله إلا','40','0','0');
INSERT INTO [t30346] VALUES (35,'فيم كنتم تتفكرون؟ قالوا: نتفكر في خلق الله، قال: لا تفكروا في الله، وتفكروا في خلق الله،','43','0','0');
INSERT INTO [t30346] VALUES (36,'من شر الناس منزلة من أذهب آخرته بدنيا غيره','44','0','0');
INSERT INTO [t30346] VALUES (37,'{إن الذين قالوا ربنا الله ثم استقاموا} [فصلت: ٣٠] ، قال: استقاموا على شهادة أن لا إله إلا','47','0','0');
