INSERT INTO [t30358] VALUES (1,'وذكر بيان تحويل القبلة. وقع في نسخة أبي زيد المروزي: حدثنا عمر بن خالد، بضم العين، كذا','2','0','0');
INSERT INTO [t30358] VALUES (2,'ثلاثة لهم أجران. محمد في هذا الإسناد هو ابن سلام، وصالح. . . . . غير صالح بن صالح بن مسلم','3','0','0');
INSERT INTO [t30358] VALUES (3,'أي يوم هذا. . . . . . . . . . . . . . . . . . . . . . . . . . . . . وفي هذا الشأن قال. .','4','0','0');
INSERT INTO [t30358] VALUES (4,'لا، إلا ما في كتاب الله. قال أبو مسعود: يقال أن حديث وكيع عن سفيان هذا هو سفيان بن عيينة','5','0','0');
INSERT INTO [t30358] VALUES (5,'من لقي الله لا يشرك به شيئا دخل الجنة الحديث. سقط في نسخة أبي يزيد المروزي ذكر مسدد فجاء','6','0','0');
INSERT INTO [t30358] VALUES (6,'في المني يصيب الثوب. وقع يزيد في نسخة أبي زيد وأبي أحمد يزيد غير منسوب، قال أبو مسعود','7','0','0');
INSERT INTO [t30358] VALUES (7,'سأل عثمان بن عفان. وقع في نسخة أبي الحسن سعيد بن حفص وهو وهم، وصوابه سعد بسكون العين وهو','8','0','0');
INSERT INTO [t30358] VALUES (8,'عند جابر بن عبد الله هو وأبوه. سقط من هذا الإسناد عند أبي محمد الحموي من شيوخ البخاري','9','0','0');
INSERT INTO [t30358] VALUES (9,'كان يطوف على نسائه. في نسخة أبي محمد الأصيلي بدل سعيد شعبة بين ابن زريع وقتادة، والصواب','10','0','0');
INSERT INTO [t30358] VALUES (10,'ذكر عمر لرسول الله أنه يصيبه جنابة من الليل. الحديث. كذا رواه أبو زيد المروزي، وأسقط ابن','11','0','0');
INSERT INTO [t30358] VALUES (11,'وصلى النبي صلى الله عليه وسلم في بيته. وفي آخر الحديث قال ابن نبهان: ثم سألت الحصين بن','12','0','0');
INSERT INTO [t30358] VALUES (12,'عبدا خيره الله بين الدنيا وبين ما عنده. الحديث. هكذا هذا الإسناد عند أبي زيد المروزي،','13','0','0');
INSERT INTO [t30358] VALUES (13,'مر النبي صلى الله عليه وسلم برجل، وذكر الحديث، ثم أردفه بحديث شعبة، عن سعد بن إبراهيم، عن','14','0','0');
INSERT INTO [t30358] VALUES (14,'لما ثقل النبي صلى الله عليه وسلم جاء بلال يؤذنه بالصلاة. سقط في هذا الإسناد من أبي زيد','15','0','0');
INSERT INTO [t30358] VALUES (15,'كان جذع يقوم إليه النبي صلى الله عليه وسلم، فلما وضع له المنبر سمعنا للجذع مثل أصوات','16','0','0');
INSERT INTO [t30358] VALUES (16,'إذا كان يوم عيد خالف الطريق. تابعه يونس بن محمد، عن فليح، وحديث جابر أصح. هكذا هو في','17','0','0');
INSERT INTO [t30358] VALUES (17,'أما بعد. وقع لابن السكن وهم فزاد في إسناد هذا الحديث بين هشام وفاطمة عروة بن الزبير،','18','0','0');
INSERT INTO [t30358] VALUES (18,'كانت بي بواسير. أسقط أبو زيد في نسخته ذكر ابن المبارك في هذا الإسناد والصواب ذكره بعد','19','0','0');
INSERT INTO [t30358] VALUES (19,'الدائم. ثم قال: حدثنا محمد، حدثنا أبو الأحوص، عن الأشعث مثله. هكذا قال البخاري: حدثنا','20','0','0');
INSERT INTO [t30358] VALUES (20,'هل فيكم من لم يقارف الليلة. قال ابن المبارك: يعني الذنب، هكذا في النسخ. ابن المبارك وفي','21','0','0');
INSERT INTO [t30358] VALUES (21,'دفن مع أبي رجل آخر. الحديث. هكذا روي هذا الحديث بهذا الإسناد عن البخاري، ووقع لأبي علي بن','22','0','0');
INSERT INTO [t30358] VALUES (22,'أخبرني بعمل يدخلني الجنة. وذكر الحديث. ووقع في نسخة الأصيلي عن أبي أحمد الجرجاني في إسناد','23','0','0');
INSERT INTO [t30358] VALUES (23,'ماله أرب ماله. الحديث. قال البخاري: أخشى أن يكون محمد غير محفوظ وإنما هو عمرو بن عثمان،','24','0','0');
INSERT INTO [t30358] VALUES (24,'ليس فيما دون خمس ذود صدقة. الحديث. قال أبو مسعود الدمشقي: هكذا قال البخاري، عن الأوزاعي،','25','0','0');
INSERT INTO [t30358] VALUES (25,'إذا طاف الطواف الأول خب ثلاثا. وقع في نسخة أبي محمد الأصيلي بخطه. حدثنا محمد بن عبيد بن','28','0','0');
INSERT INTO [t30358] VALUES (26,'دفع مع النبي صلى الله عليه وسلم يوم عرفة. كتب الأصيلي بخطه في حاشية الكتاب: إبراهيم بن','29','0','0');
INSERT INTO [t30358] VALUES (27,'اللهم اغفر للمحلقين. هكذا هو عند أبي محمد الأصيلي عياش بشين معجمة وعند ابن السكن بسين','30','0','0');
INSERT INTO [t30358] VALUES (28,'سئل رسول الله: ما يلبس المحرم من الثياب؟ وقع هذا الحديث مرسلا في رواية أبي زيد المروزي،','31','0','0');
INSERT INTO [t30358] VALUES (29,'قدم رسول الله صلى الله عليه وسلم المدينة وهم يسلمون في الثمار. الحديث. كان أبو الحسن','32','0','0');
INSERT INTO [t30358] VALUES (30,'لم أعقل أبوي إلا وهما يدينان الدين. وقال أبو صالح: حدثني عبد الله بن يونس، عن الزهري، عن','33','0','0');
INSERT INTO [t30358] VALUES (31,'في غير حق مسلم، وليس لعرق ظالم حق. هكذا روي عن أبي زيد المروزي، وأبي أحمد الجرجاني، عن','34','0','0');
INSERT INTO [t30358] VALUES (32,'أتيت النبي صلى الله عليه وسلم في المسجد فقضاني وزادني. في رواية أبي زيد المروزي لم يذكر','35','0','0');
INSERT INTO [t30358] VALUES (33,'وفوقه عرش الرحمن. وقع في نسخة أبي الحسن القابسي: حدثنا محمد بن فليح، وهو وهم، والبخاري لم','37','0','0');
INSERT INTO [t30358] VALUES (34,'أنه ركب فرسا يقال له الجرادة. في نسخة أبي زيد المروزي: حدثنا محمد بن بكر بدل محمد بن أبي','38','0','0');
INSERT INTO [t30358] VALUES (35,'ناس من أمتي يركبون البحر الأخضر. هكذا هو في جميع الطرق عن البخاري، قال أبو مسعود الدمشقي:','39','0','0');
INSERT INTO [t30358] VALUES (36,'قلما يريد غزوة إلا ورى بغيرها. هكذا رواه البخاري في جامعه وتاريخه الكبير، عن أحمد بن محمد','40','0','0');
INSERT INTO [t30358] VALUES (37,'يوم الخميس وما يوم الخميس. هكذا روي في إسناد هذا الحديث عن أبي زيد المروزي، وأبي أحمد،','41','0','0');
INSERT INTO [t30358] VALUES (38,'تقاتلون اليهود. الحديث، وقد حدث البخاري عن محمد بن يحيى الذهلي، عن إسحاق بن محمد هذا','42','0','0');
INSERT INTO [t30358] VALUES (39,'ما ترك رسول الله صلى الله عليه وسلم إلا سلاحه. سقط من هذا الإسناد في نسخة أبي الحسن','43','0','0');
INSERT INTO [t30358] VALUES (40,'أن قدح النبي صلى الله عليه وسلم انكسر فاتخذ مكان الشعب سلسلة من فضة. كذا هذا الإسناد عند','44','0','0');
INSERT INTO [t30358] VALUES (41,'إنما تغيب عثمان عن بدر، وذكر الحديث. هكذا رواه ابن السكن، وأبو زيد المروزي، وغيرهما، وفي','45','0','0');
INSERT INTO [t30358] VALUES (42,'يا رسول الله كان علي اعتكاف يوم في الجاهلية. هكذا روي مرسلا عن ابن السكن، وأبي زيد، وعند','46','0','0');
INSERT INTO [t30358] VALUES (43,'من قتل معاهدا لم يرح رائحة الجنة. هكذا روي هذا الحديث لعبد الله بن عمرو بن العاص، وفي','47','0','0');
INSERT INTO [t30358] VALUES (44,'قام فينا رسول الله صلى الله عليه وسلم مقاما فأخبر عن بدء الخلق. الحديث. هكذا هو في النسخ','48','0','0');
INSERT INTO [t30358] VALUES (45,'يرحم الله أم إسماعيل لولا أنها عجلت لكان زمزم عينا معينا. قال أبو مسعود الدمشقي: رأيت','49','0','0');
INSERT INTO [t30358] VALUES (46,'فلما رأيت عيسى وموسى، وإبراهيم، فأما عيسى فأحمر جعد عريض الصدر، وأما موسى فآدم جسيم سبط','50','0','0');
INSERT INTO [t30358] VALUES (47,'إن رجلا حضره الموت إلى قوله: فذروه في يوم حار. ثم قال: حدثنا مسدد، عن أبي عوانة، عن عبد','51','0','0');
INSERT INTO [t30358] VALUES (48,'تسموا باسمي. هكذا الإسناد في رواية أبي زيد وأبي أحمد، وغيرهما، وعند ابن السكن، ثنا محمد','52','0','0');
INSERT INTO [t30358] VALUES (49,'لو كنت متخذا. الحديث. في نسخة أبي ذر عن أبي إسحاق المستملي وحده ثنا معلى بن أسد وموسى بن','54','0','0');
INSERT INTO [t30358] VALUES (50,'وضع عمر على سريره. الحديث. وقع في نسخة أبي الحسن القابسي عمر بن سعد بسكون العين وحذف','55','0','0');
INSERT INTO [t30358] VALUES (51,'قال النبي صلى الله عليه وسلم لأهل نجران. الحديث. نسخة أبي الحسن القابسي صلة بن حذيفة،','56','0','0');
INSERT INTO [t30358] VALUES (52,'كان الناس يتحرون بهداياهم يوم عائشة. في نسخة أبي زيد: حدثنا عبيد الله بن عبد الوهاب، وهو','57','0','0');
INSERT INTO [t30358] VALUES (53,'في رواية أبي زيد وأبي أحمد، حدثنا إسحاق بن نصر، وعند ابن السكن: ابن منصور، فمن قال ابن','58','0','0');
INSERT INTO [t30358] VALUES (54,'إن خير دور الأنصار. كذا رواه ابن السكن، وأبو زيد، والنسفي، وعند الأصيلي عباس بن سهل، عن','59','0','0');
INSERT INTO [t30358] VALUES (55,'استغفروا لأخيكم','60','0','0');
INSERT INTO [t30358] VALUES (56,'صلى بهم في المصلى فصلى وكبر عليه أربعا. هكذا روى أبو زيد وابن السكن في هذين الحديثين،','61','0','0');
INSERT INTO [t30358] VALUES (57,'سألت عبد الله بن عمرو بن العاص، قلت: أخبرني بأشد شيء صنعه المشركون بالنبي صلى الله عليه','62','0','0');
INSERT INTO [t30358] VALUES (58,'في يوم عاشوراء. هكذا عن ابن السكن، وأبي زيد، وأبي أحمد، حدثنا أحمد أو محمد على الشك، وفي','63','0','0');
INSERT INTO [t30358] VALUES (59,'بعث رسول الله عشرة عينا، وأمر عليهم عاصما. حديث قتل خبيب بن عدي. هكذا روي عن أبي زيد','64','0','0');
INSERT INTO [t30358] VALUES (60,'كانوا خمس عشرة مائة، قال البخاري تابع أبو داود، عن قرة، عن قتادة، قال أبو مسعود الدمشقي،','65','0','0');
INSERT INTO [t30358] VALUES (61,'أنه بايع النبي صلى الله عليه وسلم تحت الشجرة. الحديث. هكذا هو عن رواية الفربري، وكذلك قال','66','0','0');
INSERT INTO [t30358] VALUES (62,'هذا من أهل النار. الحديث إلى آخره، وقوله: إن الله يؤيد هذا الدين بالرجل الفاجر. ثم قال:','67','0','0');
INSERT INTO [t30358] VALUES (63,'نهى يوم خيبر عن أكل الثوم. وفي بعض النسخ في أول هذا الإسناد: حدثنا عبيد الله بن إسماعيل،','68','0','0');
INSERT INTO [t30358] VALUES (64,'أن رسول الله صلى الله عليه وسلم لما قدم مكة أبى أن يدخل البيت وفيه الآلهة. سقط في نسخة','69','0','0');
INSERT INTO [t30358] VALUES (65,'لما حاصر النبي صلى الله عليه وسلم الطائف. الحديث. هكذا أسند هذا الحديث ابن السكن، وأبو','70','0','0');
INSERT INTO [t30358] VALUES (66,'قدمت أنا وأخي من اليمن، سقط في نسخة أبي زيد عبد الله بن محمد وإسحاق بن نصر، وابتدأ','71','0','0');
INSERT INTO [t30358] VALUES (67,'دخل عليه الأشعث وهو يطعم. الحديث. في نسخة الأصيلي عن أبي أحمد: حدثنا محمد، عن عبد الله بن','72','0','0');
INSERT INTO [t30358] VALUES (68,'نسخت {وإن تبدوا ما في أنفسكم أو تخفوه} [البقرة: ٢٨٤] الآية، هكذا هي عند أكثر الرواة، ووقع','73','0','0');
INSERT INTO [t30358] VALUES (69,'{أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم} [النساء: ٥٩] ، وقع في رواية ابن السكن، عن','74','0','0');
INSERT INTO [t30358] VALUES (70,'لما رميت عائشة خرت مغشيا عليها. هكذا هذا الإسناد عند الجماعة، وفي نسخة أبي محمد، عن أبي','76','0','0');
INSERT INTO [t30358] VALUES (71,'لو كان الإيمان بالثريا. الحديث. قال أبو نصر الكلاباذي: عبد العزيز في هذا الإسناد هو ابن','77','0','0');
INSERT INTO [t30358] VALUES (72,'كنت في غزاة فسمعت عبد الله بن أبي، يقول: {لا تنفقوا على من عند رسول الله} [المنافقون: ٧]','78','0','0');
INSERT INTO [t30358] VALUES (73,'في الحرام شهر يكفرها. هكذا هذا الإسناد عند ابن السكن، وعند الأصيلي، عن أبي أحمد، وأبي','79','0','0');
INSERT INTO [t30358] VALUES (74,'صارت الأوثان التي كانت في قوم نوح في العرب تعبد، أما ود فكانت بدومة الجندل. وذكر القصة','80','0','0');
INSERT INTO [t30358] VALUES (75,'ليس أحد يحاسب إلا هلك. سقط من نسخة أبي زيد من إسناد هذا الحديث ابن أبي مليكة، ولا يسند','81','0','0');
INSERT INTO [t30358] VALUES (76,'إن الله أمرني أن أقرئك القرآن. الحديث. هكذا قال البخاري: أحمد بن أبي داود، وإنما اسمه','82','0','0');
INSERT INTO [t30358] VALUES (77,'والذي لا إله غيره. الحديث. في نسخة أبي محمد، عن أبي أحمد: حدثنا حفص بن عمر، ثنا أبي، وهو','83','0','0');
INSERT INTO [t30358] VALUES (78,'الرقية بفاتحة الكتاب. ثم قال في آخره: وقال أبو معمر: ثنا عبد الوارث، عن هشام، عن محمد بن','84','0','0');
INSERT INTO [t30358] VALUES (79,'من قرأ بالآيتين من آخر سورة البقرة في ليلة كفتاه. وفي نسخة أبي محمد، عن أبي أحمد، عن عبد','85','0','0');
INSERT INTO [t30358] VALUES (80,'أعتقها ثم أصدقها. وقع عند أبي زيد المروزي، عن أبي حصين، عن أبي بردة، عن أبيه، عن أبي','86','0','0');
INSERT INTO [t30358] VALUES (81,'ثم أردف بقوله: وقال سعيد بن مسلم، عن أبي سلمة. . . . . . بعثنا هكذا في رواية أبي زيد','87','0','0');
INSERT INTO [t30358] VALUES (82,'إذا دعا الرجل امرأته. الحديث. وهكذا الإسناد عند جماعة الرواة، عن محمد بن بشار، وفي نسخة','88','0','0');
INSERT INTO [t30358] VALUES (83,'لأحدثنكم حديثا سمعته من رسول الله صلى الله عليه وسلم، وذكر بقية الحديث، وقع في نسخة','89','0','0');
INSERT INTO [t30358] VALUES (84,'تزويج النبي صلى الله عليه وسلم أميمة بنت شراحيل، هكذا روي هذا الإسناد عن أبي علي بن','90','0','0');
INSERT INTO [t30358] VALUES (85,'الإيمان ها هنا. وأبو مسعود هذا هو البدري عقبة بن عمرو، ووقع في نسخة أبي الحسن القابسي عن','91','0','0');
INSERT INTO [t30358] VALUES (86,'ووقع في نسخة أبي محمد الأصيلي وأبي الحسن القابسي، عن أبي زيد المروزي، قال أبو جعفر مكنى:','92','0','0');
INSERT INTO [t30358] VALUES (87,'فضل عائشة على النساء. الحديث. وقع في نسخة أبي الحسن القابسي خالد بن عبد الله بن أبي','93','0','0');
INSERT INTO [t30358] VALUES (88,'كنت مع النبي صلى الله عليه وسلم فيما بين مكة والمدينة. الحديث. هكذا رواه ابن السكن وأبو','94','0','0');
INSERT INTO [t30358] VALUES (89,'كل شيء في البحر مذبوح. هكذا قال النسفي والفربري من رواية أبي زيد وأبي أحمد، ولم يكن في','95','0','0');
INSERT INTO [t30358] VALUES (90,'إنا نلقى العدو غدا وليس معنا مدى. هكذا جاء هذا الإسناد من طريق أبي الأحوص، عن عباية بن','96','0','0');
INSERT INTO [t30358] VALUES (91,'أخروه لا أذوقه حتى آتي أخي أبا قتادة، وكان أخاه لأمه. هكذا وقع في نسخة أبي محمد والقابسي','97','0','0');
INSERT INTO [t30358] VALUES (92,'مثل البخيل والمتصدق. الحديث. ثم قال: وقال جعفر، عن الأعرج. وفي كتاب الزكاة، وقال الليث،','98','0','0');
INSERT INTO [t30358] VALUES (93,'لما نزل برسول الله طفق. وذكر الحديث. وقع في نسخة أبي محمد، عن أبي أحمد في هذا الإسناد','99','0','0');
INSERT INTO [t30358] VALUES (94,'من لبس الحرير في الدنيا لم يلبسه في الآخرة. هكذا روي عن الفربري، عن أبي ذبيان، بذال معجمة','100','0','0');
INSERT INTO [t30358] VALUES (95,'كساني النبي صلى الله عليه وسلم حلة سيراء، فخرجت فيها، فرأيت في وجهه الغضب. الحديث. هكذا','101','0','0');
INSERT INTO [t30358] VALUES (96,'أن نعل رسول الله صلى الله عليه وسلم كان له قبالان. هكذا إسناد هذا الحديث: همام، عن قتادة،','102','0','0');
INSERT INTO [t30358] VALUES (97,'لعن الله الواصلة. قال البخاري: حدثنا يوسف بن موسى، عن الفضل بن زهير، عن صخر بن جويرة، عن','104','0','0');
INSERT INTO [t30358] VALUES (98,'أن رفاعة القرظي طلق امرأته. الحديث. وفيه قال: وأبو بكر جالس عند النبي صلى الله عليه وسلم،','105','0','0');
INSERT INTO [t30358] VALUES (99,'هكذا روي عن أبي علي وأبي زيد، وفي نسخة أبي محمد، عن أبي أحمد ثابت مولى عبد الرحمن بن يزيد','106','0','0');
INSERT INTO [t30358] VALUES (100,'دخل علي عجوزان من عجوز يهود. الحديث. هكذا إسناده، وفي نسخة أبي ذر عن أبي إسحاق المستملي:','107','0','0');
INSERT INTO [t30358] VALUES (101,'أتيت عثمان بطهوره. الحديث. هكذا رواه أبو زيد أن ابن أبان أخبره، وفي نسخة أبي محمد، عن أبي','108','0','0');
INSERT INTO [t30358] VALUES (102,'مستريح ومستراح منه. هكذا عند أبي زيد المروزي، وعند أبي ذر عن شيوخه بلا خلاف بينهم، وكان','109','0','0');
INSERT INTO [t30358] VALUES (103,'ما بين منكبي الكافر مسيرة ثلاثة أيام للراكب المسرع. هكذا هذا الإسناد عن أبي زيد، وأبي','110','0','0');
INSERT INTO [t30358] VALUES (104,'يرد على الحوض رجال من أمتي فيحلئون عنه. وذكر اختلاف أصحاب الزهري عليه في هذا الحديث في','111','0','0');
INSERT INTO [t30358] VALUES (105,'ما كنت لأقيم حدا على أحد فيموت، فآخذ في نفسي إلا صاحب الخمر. هكذا رواه ابن السكن، وأبو','112','0','0');
INSERT INTO [t30358] VALUES (106,'سبعة يظلهم الله في ظله. محمد هذا شيخ البخاري، نسبه ابن السكن والأصيلي: محمد بن مقاتل، وفي','113','0','0');
INSERT INTO [t30358] VALUES (107,'هكذا رواه ابن السكن، وأبو زيد المروزي، وعند الأصيلي عن أبي أحمد سليمان بن يسار، عن عبد','114','0','0');
INSERT INTO [t30358] VALUES (108,'كانوا يضربون على عهد رسول الله إذا اشتروا طعاما جزافا. الحديث. هكذا روي مسندا متصلا عن','115','0','0');
INSERT INTO [t30358] VALUES (109,'وذكر الحرورية. قال أبو محمد: قرأ لنا أبو زيد في عرصة بغداد عمرو بن محمد بزيادة واو في','116','0','0');
INSERT INTO [t30358] VALUES (110,'إن بين يدي الساعة. وقع في هذا الإسناد عند أبي زيد: حدثنا مسدد، ثنا عبد الله بن موسى، فزاد','118','0','0');
INSERT INTO [t30358] VALUES (111,'أحرم رسول الله صلى الله عليه وسلم المدينة؟ قال: نعم، ما بين كذا وكذا، لا يقطع شجرها، من','119','0','0');
INSERT INTO [t30358] VALUES (112,'بعث أخا بني عدي الأنصاري واستعمله على خيبر. الحديث. هكذا روى هذا الإسناد إبراهيم بن معقل','120','0','0');
INSERT INTO [t30358] VALUES (113,'إنكم لستم مثلي. الحديث. قال أبو مسعود: هكذا في كتاب البخاري، أردف حديث الليث، عن عبد','121','0','0');
INSERT INTO [t30358] VALUES (114,'فأكون أول من بعث، فإذا موسى آخذ بالعرش، قال أبو مسعود الدمشقي: هذا إنما يعرف عن الماجشون،','122','0','0');
INSERT INTO [t30358] VALUES (115,'بينا أنا نائم رأيتني على قليب. الحديث. قال أبو مسعود: هكذا في كتاب البخاري، إبراهيم بن','123','0','0');
INSERT INTO [t30358] VALUES (116,'أخبرنا نبينا عن رسالة ربنا أنه من قتل منا صار إلى الجنة. الحديث. في هذا الإسناد موضعان','124','0','0');
INSERT INTO [t30358] VALUES (117,'الصوم لي وأنا أجزي به. الحديث. هكذا إسناده عند جميع الرواة ما خلا ابن السكن فإنه قال فيه:','125','0','0');
INSERT INTO [t30358] VALUES (118,'قدم وفد عبد القيس. الحديث. هكذا إسناده عند جميع الرواة عن الفربري إلا أبا زيد، فإنه سقط','126','0','0');
