INSERT INTO [t30368] VALUES (1,'لا تحاسدوا، ولا تقاطعوا، ولا تدابروا، وكونوا عباد الله إخوانا، ولا يحل لمسلم أن يهجر أخاه','2','0','0');
INSERT INTO [t30368] VALUES (2,'دخل مكة في عمرة القضاء وعبد الله بن رواحة بين يديه.','3','0','0');
INSERT INTO [t30368] VALUES (3,'فرضت على رسول الله صلى الله عليه وسلم، ليلة أسري به الصلوات خمسين، ثم نقصت حتى جعلت خمسا،','4','0','0');
INSERT INTO [t30368] VALUES (4,'لو أعلم أن هذا ينتظرني حتى آتيه، لطعنت بالمدرى في عينيه، وهل جعل الاستئذان إلا من أجل','5','0','0');
INSERT INTO [t30368] VALUES (5,'لا صلاة لمن لم يقرأ بأم القرآن فصاعدا','6','0','0');
INSERT INTO [t30368] VALUES (6,'على رسلكما إنها صفية بنت حيي.','7','0','0');
INSERT INTO [t30368] VALUES (7,'ألا أرى هذا يعلم ما هاهنا، لا يدخلن عليكن هذا.','8','0','0');
INSERT INTO [t30368] VALUES (8,'كان رسول الله صلى الله عليه وسلم إذا اعتكف لم يدخل البيت إلا لحاجة','9','0','0');
INSERT INTO [t30368] VALUES (9,'من أنفق زوجين من ماله في سبيل الله عز وجل دعي من أبواب الجنة، وللجنة أبواب فمن كان من أهل','10','0','0');
INSERT INTO [t30368] VALUES (10,'لولا أن أشق على أمتي لأمرتهم بالسواك مع كل وضوء','11','0','0');
INSERT INTO [t30368] VALUES (11,'كل عمل ابن آدم له إلا الصيام، الصيام لي وأنا أجزي به، وخلوف الصائم عند الله أطيب من ريح','12','0','0');
INSERT INTO [t30368] VALUES (12,'إذا استيقظ أحدكم من الليل، فلا يدخل يده في الإناء حتى يفرغ عليها الماء مرتين أو ثلاثا،','13','0','0');
INSERT INTO [t30368] VALUES (13,'إذا قال الإمام {غير المغضوب عليهم ولا الضالين} [الفاتحة: ٧] فقولوا: آمين، فإن الملائكة','14','0','0');
INSERT INTO [t30368] VALUES (14,'إذا أقيمت الصلاة فلا تأتوها تسعون، ولكن ائتوها تمشون وعليكم السكينة، فما أدركتم فصلوا،','15','0','0');
INSERT INTO [t30368] VALUES (15,'لا يبيع حاضر لباد، ولا تناجشوا، ولا يبيع الرجل على بيع أخيه، ولا يخطب على خطبته، ولا تسأل','16','0','0');
INSERT INTO [t30368] VALUES (16,'من أكل هذه الشجرة يعني الثوم فلا يؤذينا في مسجدنا','17','0','0');
INSERT INTO [t30368] VALUES (17,'إن كان جامدا فألقوها وما حولها، وإن كان مائعا فلا تقربوه','18','0','0');
INSERT INTO [t30368] VALUES (18,'من كان يؤمن بالله واليوم الآخر فليكرم ضيفه، ومن كان يؤمن بالله واليوم الآخر فلا يؤذين','19','0','0');
INSERT INTO [t30368] VALUES (19,'أولكلكم ثوبان.','20','0','0');
INSERT INTO [t30368] VALUES (20,'إني لأستغفر الله عز وجل في اليوم وأتوب إليه سبعين مرة','21','0','0');
INSERT INTO [t30368] VALUES (21,'كل شراب أسكر حرام','22','0','0');
INSERT INTO [t30368] VALUES (22,'الرؤيا من الله عز وجل، والحلم من الشيطان، فإذا حلم أحدكم حلما يكرهه فليبصق ثلاث بصقات،','23','0','0');
INSERT INTO [t30368] VALUES (23,'أنا الرحمن، خلقت الرحم، وشققت لها اسما من اسمي، فمن وصلها وصلته، ومن قطعها','24','0','0');
INSERT INTO [t30368] VALUES (24,'سأل سعد بن عبادة رسول الله صلى الله عليه وسلم عن نذر كان على أمه فأمره','25','0','0');
INSERT INTO [t30368] VALUES (25,'جئت إلى النبي صلى الله عليه وسلم في حجة الوداع، أو قال: يوم الفتح، وهو يصلي وأنا والفضل','26','0','0');
INSERT INTO [t30368] VALUES (26,'لا تدخل الملائكة بيتا فيه كلب ولا صورة تماثيل','27','0','0');
INSERT INTO [t30368] VALUES (27,'خمس من الفطرة: الاستحداد، والختان، وقص الشارب، ونتف الإبط، وتقليم','28','0','0');
INSERT INTO [t30368] VALUES (28,'الإيمان بالله قال: ثم ماذا؟ قال: ثم الجهاد في سبيل الله قال: ثم ماذا؟ قال: ثم حج','29','0','0');
INSERT INTO [t30368] VALUES (29,'لا حسد إلا على اثنتين: رجل آتاه الله القرآن فهو يقوم به آناء الليل وآناء النهار، ورجل','30','0','0');
INSERT INTO [t30368] VALUES (30,'دعه فإن الحياء من الإيمان','31','0','0');
INSERT INTO [t30368] VALUES (31,'من أعتق شركا له في عبد أقيم عليه ما بقي في ماله','32','0','0');
INSERT INTO [t30368] VALUES (32,'كان إذا عجل به السير جمع بين المغرب والعشاء','33','0','0');
INSERT INTO [t30368] VALUES (33,'نعم، وتوضأ وضوءك للصلاة','34','0','0');
INSERT INTO [t30368] VALUES (34,'كان إذا افتتح الصلاة رفع يديه حذو منكبيه، وإذا أراد أن يركع فعل مثل ذلك، وإذا رفع رأسه من','35','0','0');
INSERT INTO [t30368] VALUES (35,'صلاة الليل مثنى، فإذا خفت الصبح فواحدة توتر بها','36','0','0');
INSERT INTO [t30368] VALUES (36,'لا تتركوا النار في بيوتكم حين تنامون','37','0','0');
INSERT INTO [t30368] VALUES (37,'خمس تجب للمسلم على أخيه: رد السلام، وتشميت العاطس، وعيادة المرضى، واتباع الجنائز، وإجابة','38','0','0');
INSERT INTO [t30368] VALUES (38,'لا ألفينكم ترجعون بعدي كفارا، يضرب بعضكم رقاب بعض، ومن صلى الصبح فهو في ذمة الله، ومن','39','0','0');
INSERT INTO [t30368] VALUES (39,'هذا كهذ الشعر، لكن رسول الله صلى الله عليه وسلم كان يقرأ النظائر عشرين سورة من المفصل','40','0','0');
INSERT INTO [t30368] VALUES (40,'أعطني نمرتك، وخذ نمرتي.','41','0','0');
INSERT INTO [t30368] VALUES (41,'رأيت رسول الله صلى الله عليه وسلم إذا سجد وضع ركبتيه قبل يديه، وإذا نهض رفع يديه قبل','42','0','0');
INSERT INTO [t30368] VALUES (42,'جاءت فاطمة إلى رسول الله صلى الله عليه وسلم تشتكي مجلا بيديها، فأمرها رسول الله صلى الله','43','0','0');
INSERT INTO [t30368] VALUES (43,'أي سعد ألم تسمع ما قال أبو حباب يريد عبد الله بن أبي قال كذا وكذا قال: اعف عنه يا رسول','44','0','0');
INSERT INTO [t30368] VALUES (44,'أسلمت على ما سلف لك من خير','45','0','0');
INSERT INTO [t30368] VALUES (45,'هل ترون ما أرى؟ قالوا: لا.','46','0','0');
INSERT INTO [t30368] VALUES (46,'خذوا من العمل ما تطيقون، فالله لا يسأم حتى تسأموا','47','0','0');
INSERT INTO [t30368] VALUES (47,'كان المنافقون يجلسون عند رسول الله صلى الله عليه وسلم، ثم يخرجون، فيقولون: ماذا قال آنفا،','48','0','0');
INSERT INTO [t30368] VALUES (48,'تزوج ميمونة وهو محرم','49','0','0');
INSERT INTO [t30368] VALUES (49,'هل في نفسك من أمر الجاهلية؟ قال: لا.','50','0','0');
INSERT INTO [t30368] VALUES (50,'فضل الصلاة بالسواك على الصلاة بغير السواك سبعون ضعفا','51','0','0');
INSERT INTO [t30368] VALUES (51,'لم أر رسول الله صلى الله عليه وسلم يصلي قاعدا، حتى كان قبل موته بعام أو اثنين، فكان يصلي','52','0','0');
INSERT INTO [t30368] VALUES (52,'صلى في بيته سبحة الضحى فقاموا وراءه فصلوا','53','0','0');
INSERT INTO [t30368] VALUES (53,'رأيت رسول الله صلى الله عليه وسلم يقسم لحما بالجعرانة، وأنا غلام شاب، فأقبلت امرأة، فلما','54','0','0');
INSERT INTO [t30368] VALUES (54,'نهى عن الزبيب والتمر','55','0','0');
INSERT INTO [t30368] VALUES (55,'مثل المجاهد في سبيل الله، والله أعلم بمن يجاهد في سبيله، أن يتوفاه فيدخله الجنة، أو يرجعه','56','0','0');
INSERT INTO [t30368] VALUES (56,'كان يعتكف العشر الأواخر من رمضان حتى قبضه الله عز وجل','57','0','0');
INSERT INTO [t30368] VALUES (57,'إن الرجل من أهل الجنة ليشرف على أهل الجنة كأنه كوكب دري، وإن أبا بكر وعمر منهم،','58','0','0');
INSERT INTO [t30368] VALUES (58,'بينما أنا نائم رأيت الناس يعرضون علي وعليهم قمص منها ما يبلغ الثدي، ومنها ما يبلغ دون','59','0','0');
INSERT INTO [t30368] VALUES (59,'وهل ترك لنا عقيل بن أبي طالب شيئا ثم قال: لا يرث المسلم الكافر، ولا الكافر المسلم ثم قال:','60','0','0');
INSERT INTO [t30368] VALUES (60,'ألا تصلون؟ فقلت: يا رسول الله، إنما أنفسنا بيد الله عز وجل، فإذا شاء أن يبعثنا بعثنا،','61','0','0');
INSERT INTO [t30368] VALUES (61,'هذه آخر سورة سمعت رسول الله صلى الله عليه وسلم يقرأ بها في المغرب','62','0','0');
INSERT INTO [t30368] VALUES (62,'إذا قلت لأخيك أنصت، والإمام يخطب فقد لغوت','63','0','0');
INSERT INTO [t30368] VALUES (63,'صلاة في مسجدي هذا خير من ألف صلاة في غيره من المساجد إلا المسجد الحرام','64','0','0');
INSERT INTO [t30368] VALUES (64,'كان أجود الناس، وأجود ما يكون في رمضان، حين يلقاه جبريل، وكان يلقاه في كل ليلة في رمضان،','65','0','0');
INSERT INTO [t30368] VALUES (65,'إن له دسما','66','0','0');
INSERT INTO [t30368] VALUES (66,'أقرأني جبريل فراجعته، فلم أستزيده، ويزيدني، فانتهى إلى سبعة قال الزهري: وإنما حده في','67','0','0');
INSERT INTO [t30368] VALUES (67,'خير نسائها مريم بنت عمران، وخير نسائها خديجة','68','0','0');
INSERT INTO [t30368] VALUES (68,'وعندك؟ قال: قلت: نعم، بنت حمزة.','69','0','0');
INSERT INTO [t30368] VALUES (69,'والذي فلق الحبة، وبرأ النسمة إنه لعهد النبي صلى الله عليه وسلم إلي أن لا يحبك إلا مؤمن،','70','0','0');
INSERT INTO [t30368] VALUES (70,'أرسله، اقرأ يا هشام فقرأ عليه القراءة التي سمعتها، فقال النبي صلى الله عليه وسلم: هكذا','71','0','0');
INSERT INTO [t30368] VALUES (71,'ما رأيت رسول الله صلى الله عليه وسلم يصلي في بيت أم سلمة في ثوب واحد واضعا طرفيه على','72','0','0');
INSERT INTO [t30368] VALUES (72,'في الجنة شجرة يسير الراكب في ظلها مائة سنة لا يقطعها','73','0','0');
INSERT INTO [t30368] VALUES (73,'لو تعلمون ما أعلم لضحكتم قليلا، ولبكيتم كثيرا','74','0','0');
INSERT INTO [t30368] VALUES (74,'لما قضى الله الخلق، كتب كتابا فهو تحت العرش إن رحمتي سبقت غضبي','75','0','0');
INSERT INTO [t30368] VALUES (75,'إن لله عز وجل تسعة وتسعين اسما، مائة غير واحد من أحصاها كلها دخل الجنة، إنه وتر يحب','76','0','0');
INSERT INTO [t30368] VALUES (76,'إذا نظر أحدكم إلى من فضل عليه في المال والخلق، فلينظر إلى من هو دونه ممن فضل هو','77','0','0');
INSERT INTO [t30368] VALUES (77,'إن الذي يجر ثوبه من الخيلاء لا ينظر الله إليه يوم القيامة','78','0','0');
INSERT INTO [t30368] VALUES (78,'كان يدخل مكة من الثنية العليا، ويخرج من الثنية السفلى','79','0','0');
INSERT INTO [t30368] VALUES (79,'فرض زكاة الفطر صاعا من تمر أو صاعا من شعير على كل عبد أو حر، صغير أو','80','0','0');
INSERT INTO [t30368] VALUES (80,'رأى النبي صلى الله عليه وسلم يصلي على ناقته، أو قال: على بعيره النوافل في كل','81','0','0');
INSERT INTO [t30368] VALUES (81,'أكل بنيك نحلت مثل هذا؟ قال: لا.','82','0','0');
INSERT INTO [t30368] VALUES (82,'صدق رؤياك، فسجد على جبهته','83','0','0');
INSERT INTO [t30368] VALUES (83,'صلى بهم، فسها في صلاته، فسجد سجدتي السهو ثم تشهد ثم سلم','84','0','0');
INSERT INTO [t30368] VALUES (84,'إذا سمعتم النداء فقولوا مثل ما يقول المنادي','85','0','0');
INSERT INTO [t30368] VALUES (85,'لا بل دعه قال: قلت: وإن قطع يدي؟ قال: وإن فعل فراجعته مرتين أو ثلاثا، فقال النبي صلى الله','86','0','0');
INSERT INTO [t30368] VALUES (86,'من صلى البردين دخل الجنة','87','0','0');
INSERT INTO [t30368] VALUES (87,'من وفى فأجره على الله، ومن أصاب من ذلك شيئا، فعوقب به في الدنيا، فهو طهرة له أو قال:','88','0','0');
INSERT INTO [t30368] VALUES (88,'ألا أخبركم بخير لكم من الصيام والصدقة، صلاح ذات البين، وإياكم والبغضة فإنها هي','89','0','0');
INSERT INTO [t30368] VALUES (89,'جاءت الجدة إلى أبي بكر رضي الله عنه، تسأله ميراثها فقال: ما لك في كتاب الله عز وجل شيء،','90','0','0');
INSERT INTO [t30368] VALUES (90,'أن النبي صلى الله عليه وسلم جاءه رجل فقال: يا رسول الله إن أخي مات، فقم فصل عليه، فقام','91','0','0');
INSERT INTO [t30368] VALUES (91,'توفي النبي صلى الله عليه وسلم وأنا ابن خمس عشرة، مختونا','92','0','0');
INSERT INTO [t30368] VALUES (92,'إن كنت لآتي البيت، وفيه المريض فما أسأل عنه إلا وأنا مارة وهي معتكفة، وإن كان رسول الله','93','0','0');
INSERT INTO [t30368] VALUES (93,'من صام رمضان ثم أتبعه بست من شوال كان كمن صام السنة','94','0','0');
INSERT INTO [t30368] VALUES (94,'نهى أن يدخل الرجل الماء إلا بإزار','95','0','0');
INSERT INTO [t30368] VALUES (95,'كان رسول الله صلى الله عليه وسلم يقرأ في الركعتين اللتين يوتر بعدهما بـ {سبح اسم ربك','96','0','0');
INSERT INTO [t30368] VALUES (96,'نهى الرجال والنساء عن دخول الحمامات، ثم رخص للرجال أن يدخلوها بالمآزر','99','0','0');
INSERT INTO [t30368] VALUES (97,'ادخلوا قالا: كلنا يا أم المؤمنين؟ قالت: نعم، ادخلوا كلكم ولا تعلم عائشة أن معهم ابن','100','0','0');
INSERT INTO [t30368] VALUES (98,'لا حمى إلا لله ولرسوله','102','0','0');
INSERT INTO [t30368] VALUES (99,'ليبعثن الله أقواما على منابر من نور، يغبطهم الناس، ليسوا بأنبياء ولا شهداء فقام أعرابي،','103','0','0');
INSERT INTO [t30368] VALUES (100,'ادعوا لي ابن أبي ربيعة فقال: هذا مالك، فبارك الله لك في مالك، إنما جزاء السلف الوفاء','104','0','0');
INSERT INTO [t30368] VALUES (101,'إذا سمعتم به في أرض فلا تقدموا عليه، وإذا وقع بأرض وأنتم بها، فلا تخرجوا فرارا منه قال:','105','0','0');
INSERT INTO [t30368] VALUES (102,'من أصاب دما أقيم عليه الحد في ذلك فهو كفارة','107','0','0');
INSERT INTO [t30368] VALUES (103,'أوليس قد ابتعته منك؟ قال الأعرابي: لا والله ما بعتكه، فقال النبي صلى الله عليه وسلم: بلى','108','0','0');
INSERT INTO [t30368] VALUES (104,'من الشيطان','111','0','0');
