INSERT INTO [t30384] VALUES (1,'أفضل الكلام أربع لا يضرك بأيهن بدأت: سبحان الله، والحمد لله، ولا إله إلا الله، والله','2','0','0');
INSERT INTO [t30384] VALUES (2,'إن شئت أصبح الصفا لهم ذهبا، فمن كفر منهم بعد ذلك عذبته عذابا لا أعذبه أحدا من العالمين،','3','0','0');
INSERT INTO [t30384] VALUES (3,'أصبحنا على فطرة الإسلام، وكلمة الإخلاص، ودين نبينا محمد صلى الله عليه وسلم، وملة أبينا','4','0','0');
INSERT INTO [t30384] VALUES (4,'من حدث بحديث، وهو يرى أنه كذب، فهو أحد الكاذبين','5','0','0');
INSERT INTO [t30384] VALUES (5,'فإن ذلك جبريل عليه السلام أمرني أن أخرج إلى بني قريظة','6','0','0');
INSERT INTO [t30384] VALUES (6,'غفر لرجل أخذ غصن شوك من طريق الناس ذنبه ما تقدم منه وما تأخر','7','0','0');
INSERT INTO [t30384] VALUES (7,'لا تصحب الملائكة رفقة فيها جرس ولا كلب.','8','0','0');
INSERT INTO [t30384] VALUES (8,'إذا قرب العشاء وحضرت الصلاة، فابدءوا به قبل صلاة المغرب','9','0','0');
INSERT INTO [t30384] VALUES (9,'أرضعيه، يذهب ما في وجه أبي حذيفة فقالت: والله ما عرفته في وجه أبي','10','0','0');
INSERT INTO [t30384] VALUES (10,'نهى عن صيام يومي الفطر والأضحى، وعن صلاتين: بعد العصر حتى تغرب الشمس، وبعد الصبح حتى','11','0','0');
INSERT INTO [t30384] VALUES (11,'من كذب علي متعمدا فليتبوأ مقعده من النار وإني أشهد أني سمعته يقول: من لبس الحرير حرمه أن','12','0','0');
INSERT INTO [t30384] VALUES (12,'من أنظر معسرا أو وضع له أظله الله عز وجل في ظله يوم القيامة','13','0','0');
INSERT INTO [t30384] VALUES (13,'إن أعظم خطيئة عند الله عز وجل بعد الكبائر التي نهى الله عنها أن يموت الرجل، وعليه دين في','14','0','0');
INSERT INTO [t30384] VALUES (14,'إن الله وملائكته يصلون على الذين يصلون الصفوف','15','0','0');
INSERT INTO [t30384] VALUES (15,'قلت لجبريل هل ترى ربك؟ قال: إن بيني وبينه سبعين حجابا من نور لو رأيت أدناها','16','0','0');
INSERT INTO [t30384] VALUES (16,'طهور إناء أحدكم إذا ولغ فيه الكلب أن يغسله سبع مرات أولاهن بالتراب','17','0','0');
INSERT INTO [t30384] VALUES (17,': من ابتاع مصراة فهو بالخيار، ثلاثة أيام، فإن ردها رد معها صاعا من تمر','18','0','0');
INSERT INTO [t30384] VALUES (18,'لا تلقوا الجلب، فمن تلقاه فاشترى منه شيئا فصاحبه إذا أتى السوق بالخيار','19','0','0');
INSERT INTO [t30384] VALUES (19,'إن الملائكة تلعن أحدكم، إذا أشار إلى أخيه بحديدة، وإن كان أخاه لأبيه','20','0','0');
INSERT INTO [t30384] VALUES (20,'لا تسبوا الدهر فإن الله هو الدهر','21','0','0');
INSERT INTO [t30384] VALUES (21,'لا تسموا العنب الكرم، إنما الكرم المسلم','22','0','0');
INSERT INTO [t30384] VALUES (22,'إن في الجمعة ساعة لا يوافقها عبد مسلم يصلي يسأل الله فيها خيرا إلا أعطاه','23','0','0');
INSERT INTO [t30384] VALUES (23,'من تاب قبل أن تطلع الشمس من مغربها تاب الله عليه','24','0','0');
INSERT INTO [t30384] VALUES (24,'إذا وقع الذباب في طعام أحدكم، أو في شرابه، فليغمسه فإن أحد جناحيه داء والآخر','25','0','0');
INSERT INTO [t30384] VALUES (25,'لا تزوج المرأة المرأة، ولا تزوج المرأة نفسها فإن الزانية هي التي تزوج','26','0','0');
INSERT INTO [t30384] VALUES (26,'بالحج.','27','0','0');
INSERT INTO [t30384] VALUES (27,'قرأ والنجم فسجد بها، وسجد من كان معه إلا شيخا كبيرا، فإنه أخذ كفا من تراب أو حصى، فرفعه','28','0','0');
INSERT INTO [t30384] VALUES (28,'لو لم أر رسول الله صلى الله عليه وسلم سجد لم أسجد','29','0','0');
INSERT INTO [t30384] VALUES (29,'سجد فيها خليلي صلى الله عليه وسلم فأسجد حتى ألقاه','30','0','0');
INSERT INTO [t30384] VALUES (30,'سجدنا مع رسول الله صلى الله عليه وسلم في إذا السماء انشقت','31','0','0');
INSERT INTO [t30384] VALUES (31,'الكيس من دان نفسه، وعمل لما بعد الموت، والعاجز من أتبع نفسه هواها، وتمنى على','32','0','0');
INSERT INTO [t30384] VALUES (32,'ما آمن بالقرآن من استحل محارمه','33','0','0');
INSERT INTO [t30384] VALUES (33,'اللهم استجب له إذا دعاك','34','0','0');
INSERT INTO [t30384] VALUES (34,'ولد لنوح ثلاثة سام وحام ويافث، فولد سام للعرب وفارس والروم، والخير فيهم، وولد يافث يأجوج','35','0','0');
INSERT INTO [t30384] VALUES (35,'القدرية مجوس هذه الأمة، إن مرضوا فلا تعودوهم، وإن ماتوا فلا تصلوا','36','0','0');
INSERT INTO [t30384] VALUES (36,'إن من الشعر حكمة','37','0','0');
INSERT INTO [t30384] VALUES (37,'سئل النبي صلى الله عليه وسلم: أي الأعمال أفضل؟ قال: إيمان بالله وجهاد لا غلول فيه، وحج','38','0','0');
INSERT INTO [t30384] VALUES (38,'إنه سيأتيكم أقوام يتعلمون منكم، فاستوصوا بهم خيرا قال: وكنا نسأله عن الحديث المرة','39','0','0');
INSERT INTO [t30384] VALUES (39,'لا نورث، ما تركنا صدقة؟ قالوا: نعم. ثم أقبل على علي والعباس، فقال: أنشدكما بالله الذي','40','0','0');
INSERT INTO [t30384] VALUES (40,'احتجم وهو محرم، واحتجم وهو صائم','42','0','0');
INSERT INTO [t30384] VALUES (41,'نزلت هذه الآية {إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا أولئك لا خلاق لهم في','43','0','0');
INSERT INTO [t30384] VALUES (42,'نهى أن يغتسل الرجل بفضل المرأة، والمرأة بفضل الرجل، ولكن يشتركان جميعا','44','0','0');
INSERT INTO [t30384] VALUES (43,'هل صليت معنا؟ قال: لا.','45','0','0');
INSERT INTO [t30384] VALUES (44,'نعم قالوا: فاكتب لنا عليك كتابا.','46','0','0');
INSERT INTO [t30384] VALUES (45,'هذا ديني، ورضيته لنفسي، ولن يصلحه إلا السخاء وحسن الخلق، فأكرموه بهما ما','47','0','0');
INSERT INTO [t30384] VALUES (46,': إذا جلس بين شعبها الأربع واجتهد، فقد وجب الغسل','48','0','0');
INSERT INTO [t30384] VALUES (47,'الإضرار في الوصية من الكبائر','49','0','0');
INSERT INTO [t30384] VALUES (48,'اكووه بالرضف.','50','0','0');
INSERT INTO [t30384] VALUES (49,'يمسح على الخفين وعلى العمامة','51','0','0');
INSERT INTO [t30384] VALUES (50,'إنه ليس بنا رد عليك، ولكنا حرم','52','0','0');
INSERT INTO [t30384] VALUES (51,'إذا حملتم فأخروا، فإن اليدين معلقة، والرجل موثقة','53','0','0');
INSERT INTO [t30384] VALUES (52,'إن الله عز وجل أدبر لهم الدنيا','54','0','0');
INSERT INTO [t30384] VALUES (53,'أم الفضل أرسلت إلى النبي صلى الله عليه وسلم يوم عرفة بلبن فشربه وهو يخطب','55','0','0');
INSERT INTO [t30384] VALUES (54,'دخل البيت، ولم يصل في البيت، ولكنه لما نزل خرج فصلى عند باب البيت','56','0','0');
INSERT INTO [t30384] VALUES (55,'رأى النبي صلى الله عليه وسلم خرج من الكعبة، فقام بحيال الركن الأسود يصلي ركعتين، والناس','57','0','0');
INSERT INTO [t30384] VALUES (56,'فتلت قلائد هدي رسول الله صلى الله عليه وسلم، ثم لم يعتزل شيئا، ولم يتركه إنا لا نعلم','58','0','0');
INSERT INTO [t30384] VALUES (57,'بعث على رأس أربعين، وقبض على رأس ستين، وما في رأسه ولحيته عشرون شعرة','59','0','0');
INSERT INTO [t30384] VALUES (58,'ادن فدنوت منه حتى كنت عند رجليه، فتوضأ ومسح على خفيه','71','0','0');
INSERT INTO [t30384] VALUES (59,'بال في الشعب ليلة المزدلفة ولم يقل إهراق الماء','72','0','0');
INSERT INTO [t30384] VALUES (60,'لا يسمع صوت المؤذن جن ولا إنس ولا حجر ولا شيء إلا شهد له يوم القيامة','73','0','0');
INSERT INTO [t30384] VALUES (61,'إن بلالا ينادي بليل، فكلوا واشربوا حتى ينادي ابن أم مكتوم','74','0','0');
INSERT INTO [t30384] VALUES (62,'إذا أقيمت الصلاة فلا تقوموا حتى تروني','75','0','0');
INSERT INTO [t30384] VALUES (63,'إذا قال المنادي مثل هذا','76','0','0');
INSERT INTO [t30384] VALUES (64,'سمعت النبي صلى الله عليه إذا سمع المنادي يقول مثل هذا','77','0','0');
INSERT INTO [t30384] VALUES (65,'ألا صلوا في رحالكم، ثم قال: إن رسول الله صلى الله عليه كان يأمر مناديه أن ينادي في الليلة','78','0','0');
INSERT INTO [t30384] VALUES (66,'يقرأ في المغرب ب الطور','79','0','0');
INSERT INTO [t30384] VALUES (67,'يقرأ في المغرب ب المرسلات عرفا','80','0','0');
INSERT INTO [t30384] VALUES (68,'سمعت النبي صلى الله عليه يقرأ في الصبح {والنخل باسقات لها طلع نضيد} [ق: ١٠]','81','0','0');
INSERT INTO [t30384] VALUES (69,'قدمت المدينة والنبي صلى الله عليه وسلم بخيبر، ورجل من غفار يؤم الناس في الصبح فقرأ في','82','0','0');
INSERT INTO [t30384] VALUES (70,'سمعت رسول الله صلى الله عليه وسلم، يقرأ في العتمة ب التين والزيتون','83','0','0');
INSERT INTO [t30384] VALUES (71,'يقرأ في العشاء ب التين والزيتون قال البراء: فلم أسمع أحدا أحسن قراءة','84','0','0');
INSERT INTO [t30384] VALUES (72,'هل كان رسول الله صلى الله عليه وسلم يقرأ في الظهر والعصر؟ قال: نعم.','85','0','0');
INSERT INTO [t30384] VALUES (73,'قل: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر، ولا حول ولا قوة إلا بالله فضم','86','0','0');
INSERT INTO [t30384] VALUES (74,'من أعمر عمرى له ولعقبه، فهي له ولعقبه، يرثها من يرثه من عقبه','87','0','0');
INSERT INTO [t30384] VALUES (75,'أينام أحدنا وهو جنب؟ فأمره رسول الله صلى الله عليه أن يغسل فرجه ويتوضأ','88','0','0');
INSERT INTO [t30384] VALUES (76,'لا ينكح المحرم ولا يخطب ولا ينكح','89','0','0');
INSERT INTO [t30384] VALUES (77,'إذا وضعت المائدة، فليأكل الرجل مما يليه، ولا يتناول مما بين يدي جليسه، ولا من ذروة','90','0','0');
INSERT INTO [t30384] VALUES (78,': من حمل علينا السلاح فليس منا','91','0','0');
INSERT INTO [t30384] VALUES (79,'صلى خمسا فلما انصرف، قال له بعض القوم: أزيد في الصلاة؟ قال: وما ذلك؟ قال: صليت خمسا، فسجد','92','0','0');
INSERT INTO [t30384] VALUES (80,'نهى رسول الله صلى الله عليه وسلم عن لبس الحرير','93','0','0');
INSERT INTO [t30384] VALUES (81,'ثلاثة يدعون الله فلا يستجيب لهم: رجل أعطى ماله سفيها، وقال الله عز وجل {ولا تؤتوا السفهاء','94','0','0');
INSERT INTO [t30384] VALUES (82,'يا ذا مخبر قال: قلت: لبيك وسعديك يا رسول الله، قال: خذ برأس ناقتي هذه، واقعد هاهنا، ولا','95','0','0');
INSERT INTO [t30384] VALUES (83,'مر بالقدور يوم خيبر، فأمر بها فأكفئت، قال: فكان قتادة إذا سئل عن لحم البغل، قال: نهى رسول','96','0','0');
INSERT INTO [t30384] VALUES (84,'إن الرجل ليكون من أهل الصلاة والزكاة والعمرة والحج، حتى ذكر سهام الخير، وما يجزى يوم','97','0','0');
INSERT INTO [t30384] VALUES (85,'إني وقعت على أهلي، وكان ذلك في شهر رمضان، فقال رسول الله صلى الله عليه وسلم: أعتق','98','0','0');
INSERT INTO [t30384] VALUES (86,'مر على قبر منبوذ فصلى عليه، وصلينا معه','99','0','0');
INSERT INTO [t30384] VALUES (87,'هو شر الثلاثة','100','0','0');
INSERT INTO [t30384] VALUES (88,'خير الصحابة أربعة، وخير السرايا أربع مائة، وخير الجيوش أربعة آلاف، ولن يؤتى اثنا عشر ألفا','101','0','0');
INSERT INTO [t30384] VALUES (89,'لا تقدموا رمضان بيوم ولا بيومين إلا أن يوافق ذلك صوما كان يصومه أحدكم، ولا تصوموا الجمعة','102','0','0');
INSERT INTO [t30384] VALUES (90,'تسحروا فإن في السحور بركة','103','0','0');
INSERT INTO [t30384] VALUES (91,'أبهذا أمرتم؟ أو بهذا أرسلت إليكم، إنما أهلك من كان قبلكم حيث تنازعوا في هذا الأمر، عزمت','104','0','0');
INSERT INTO [t30384] VALUES (92,'إذا أكل أحدكم وشرب ناسيا، وهو صائم فليمض في صومه، فإنما أطعمه الله','105','0','0');
INSERT INTO [t30384] VALUES (93,'لم يجعل لها سكنى ولا نفقة قال: فأخذ الأسود كفا من حصى، فحصبه به، فقال: ويحك تحدث بمثل هذا','106','0','0');
INSERT INTO [t30384] VALUES (94,'خيار ولد آدم خمسة: نوح، وإبراهيم، وموسى، وعيسى، ومحمد، وخيرهم محمد صلى الله عليه وسلم','107','0','0');
INSERT INTO [t30384] VALUES (95,'إذا تقارب الزمان لم تكد رؤيا المؤمن تكذب، وأصدقهم رؤيا أصدقهم حديثا','108','0','0');
INSERT INTO [t30384] VALUES (96,'من حسن إسلام المرء تركه ما لا يعنيه','109','0','0');
INSERT INTO [t30384] VALUES (97,'إنكم سترون ربكم كما ترون هذا القمر، لا تضامون في رؤيته','110','0','0');
INSERT INTO [t30384] VALUES (98,'كان رسول الله صلى الله عليه وسلم يصلي وأنا بين يديه معترضة كأني جنازة','111','0','0');
INSERT INTO [t30384] VALUES (99,': اغسلوه بماء وسدر، ولا تخمروا وجهه ورأسه، وكفنوه في ثوبين فإن الله عز وجل يبعثه يوم','112','0','0');
INSERT INTO [t30384] VALUES (100,'أن جارية بكرا أتت النبي صلى الله عليه وسلم فقالت: أن أباها زوجها وهي كارهة، فخيرها النبي','113','0','0');
INSERT INTO [t30384] VALUES (101,'إن لجهنم سبعة أبواب، باب منها لمن سل سيفه على أمتي أو قال: على أمة محمد صلى الله عليه','114','0','0');
INSERT INTO [t30384] VALUES (102,'رجلا أعتق ستة أعبد له عند موته، ليس له مال غيرهم، فجزأهم رسول الله صلى الله عليه أجزاء','115','0','0');
INSERT INTO [t30384] VALUES (103,'خفف القرآن على داود، فإن كان ليأمر بدابته لتسرج، فما يفرغ من إسراجها حتى يفرغ','116','0','0');
INSERT INTO [t30384] VALUES (104,'لم يمت رسول الله صلى الله عليه حتى أحل له أن يتزوج من النساء ما شاء إلا ذات محرم، وذلك','118','0','0');
