INSERT INTO [t30432] VALUES (1,'من ألقى جلباب الحياء فلا غيبة له','2','0','0');
INSERT INTO [t30432] VALUES (2,'خيركم في المائتين كل خفيف الحاذ.','3','0','0');
INSERT INTO [t30432] VALUES (3,'من صلى أربعا قبل الظهر وأربعا بعدها وجبت له الجنة','4','0','0');
INSERT INTO [t30432] VALUES (4,'طلب العلم فريضة على كل مسلم','5','0','0');
INSERT INTO [t30432] VALUES (5,'ما من صلاة مفروضة إلا وبين يديها ركعتان','6','0','0');
INSERT INTO [t30432] VALUES (6,'لوددت أن تبارك في صدر كل إنسان من أمتي','7','0','0');
INSERT INTO [t30432] VALUES (7,'صيام ثلاثة أيام من كل شهر: ثلاث عشرة، وأربع عشرة، وخمس عشرة، صوم الدهر','8','0','0');
INSERT INTO [t30432] VALUES (8,'عليكم بالأمير وأصحابه.','9','0','0');
INSERT INTO [t30432] VALUES (9,'إذا مررت ببلدة ليس فيها سلطان فلا تدخلها، إنما السلطان ظل الله، ورمحه في','10','0','0');
INSERT INTO [t30432] VALUES (10,'إذا استقر أهل الجنة في الجنة اشتاق الأخوان، فيسير سرير ذا إلى ذا حتى يلتقيان، فيتحدثان ما','11','0','0');
INSERT INTO [t30432] VALUES (11,'الغيبة تخرق الصوم، والاستغفار يرقعه، فمن استطاع منكم أن لا يجيء غدا بصومه مرقعا','12','0','0');
INSERT INTO [t30432] VALUES (12,'كنا نركعهما إذا قمنا، يعني: بين الأذان والإقامة من المغرب','13','0','0');
INSERT INTO [t30432] VALUES (13,'إن الرجل ليعمل الحسنة فيتكل عليها، ويعمل المحقرات حتى يأتي الله وقد أحطن به، وإن الرجل','14','0','0');
INSERT INTO [t30432] VALUES (14,'إنكم في ممر الليل والنهار في آجال منقوصة وأعمال محفوظة والموت يأتي بغتة، فمن زرع خيرا','15','0','0');
INSERT INTO [t30432] VALUES (15,'موقف ساعة في سبيل الله خير من قيام ليلة القدر عند الحجر الأسود','16','0','0');
INSERT INTO [t30432] VALUES (16,'من غسل مسلما فكتم عليه غفر الله له أربعين مرة، ومن حفر له فأجنه أجري عليه كأجر مسكن أسكنه','17','0','0');
INSERT INTO [t30432] VALUES (17,': نهى رسول الله صلى الله عليه وسلم عن البسر والتمر أن ينتبذا جميعا','18','0','0');
INSERT INTO [t30432] VALUES (18,'كان رسول الله صلى الله عليه وسلم يتبع الحرير من الثوب فينزعه','19','0','0');
INSERT INTO [t30432] VALUES (19,'إن الدنيا خضرة حلوة، وإن رجالا سيخوضون في مال الله عز وجل ورسوله بغير حق، لهم النار يوم','20','0','0');
INSERT INTO [t30432] VALUES (20,'من لم يطهره المسح على الخمار فلا طهره الله عز وجل','21','0','0');
INSERT INTO [t30432] VALUES (21,'إذا رأت المرأة ذلك فأنزلت فلتغتسل.','22','0','0');
INSERT INTO [t30432] VALUES (22,'هو صغير.','23','0','0');
INSERT INTO [t30432] VALUES (23,'صوموا لرؤيته، وأفطروا لرؤيته، فإن غم عليكم فأكملوا العدة ثلاثين','24','0','0');
INSERT INTO [t30432] VALUES (24,'من قتل دون ماله مظلوما فله الجنة','25','0','0');
INSERT INTO [t30432] VALUES (25,': نزلت هذه الآية: {يأيها النبي لم تحرم ما أحل الله لك} [التحريم: ١] .','26','0','0');
INSERT INTO [t30432] VALUES (26,'إن الله تعالى فضل محمدا صلى الله عليه وسلم على أهل السماء وعلى الأنبياء، قالوا: يابن عباس','27','0','0');
INSERT INTO [t30432] VALUES (27,'الهر من متاع البيت','28','0','0');
INSERT INTO [t30432] VALUES (28,'ألا تغسل هذا النتن عنك؟ أفلا تغسل هذا الرجس عنك. فأتيت بئرا فاغتسلت فيه حتى اصفر الماء،','29','0','0');
INSERT INTO [t30432] VALUES (29,'السائبة تضع ماله حيث شاء، قال شعبة: لم يسمع هذا من سلمة أحد غيري','30','0','0');
INSERT INTO [t30432] VALUES (30,'يا غلام، أو يا بني، ألا أعلمك كلمات ينفعك الله بهن.','31','0','0');
INSERT INTO [t30432] VALUES (31,'ما كنتم تذكرون؟ قلنا: العزل يا رسول الله، فقال: لا عليكم أن لا تفعلوا، فإنه كما قدر الله','34','0','0');
INSERT INTO [t30432] VALUES (32,'أهل اليمن أرق قلوبا، وأكثر أفئدة، وأنجع طاعة','35','0','0');
INSERT INTO [t30432] VALUES (33,'ما منكم أحد ينجيه عمله.','36','0','0');
INSERT INTO [t30432] VALUES (34,'الحرب خدعة','37','0','0');
INSERT INTO [t30432] VALUES (35,'لرأي شيخ أحب إلي من مشهد شاب','38','0','0');
INSERT INTO [t30432] VALUES (36,'قال لي أبو بكر: أي يوم توفي النبي صلى الله عليه وسلم؟ قلت: يوم الاثنين، قال: إني أرجو أن','39','0','0');
INSERT INTO [t30432] VALUES (37,'أن ملكا من الملوك خرج يسير في مملكته وهو مستخف من الناس، حتى نزل على رجل له بقرة، فراحت','40','0','0');
INSERT INTO [t30432] VALUES (38,'إن خير الصيام صيام داود، كان يصوم نصف الدهر، وخير الصلاة صلاة داود، كان يرقد نصف الليل','41','0','0');
INSERT INTO [t30432] VALUES (39,'لعلك أن تمر بقبري ومسجدي، قد بعثتك إلى قوم رقيقة قلوبهم يقاتلونك على الحق مرتين، فقاتل','43','0','0');
INSERT INTO [t30432] VALUES (40,'إن أحبكم إلي وأقربكم مني من لقيني بمثل الحال التي فارقني عليها','44','0','0');
INSERT INTO [t30432] VALUES (41,'اللهم اجعله هاديا مهديا، واهده واهد به','45','0','0');
INSERT INTO [t30432] VALUES (42,'أعطني نخلتك المائلة التي فرعها في دار فلان ولك بها نخلة في الجنة. فقال له الرجل: لقد','46','0','0');
INSERT INTO [t30432] VALUES (43,'أن يورث امرأة أشيم الضبابي من الدية','47','0','0');
INSERT INTO [t30432] VALUES (44,'أوتيت الكتاب وما يعدله، يعني: ومثله، يوشك شبعان على أريكته يقول: بيننا وبينكم هذا الكتاب،','48','0','0');
INSERT INTO [t30432] VALUES (45,'من أصحابي من لا يراني بعد أن يفارقني.','49','0','0');
INSERT INTO [t30432] VALUES (46,'أصلح لي هذا اللحم فأصلحته، فلم يزل يأكل منه حتى بلغ المدينة','50','0','0');
INSERT INTO [t30432] VALUES (47,'نعم، ويتوضأ','51','0','0');
INSERT INTO [t30432] VALUES (48,'من خير الناس؟ قال: ذاك أبو بكر بعد نبي الله صلى الله عليه وسلم، قال: ثم أتى أبو بكر بعد،','52','0','0');
INSERT INTO [t30432] VALUES (49,'عليكم بالسمع والطاعة لمن ولاه الله أمركم، وإن كان عبدا حبشيا، ألا وسيرى من بقي منكم بعدي','53','0','0');
INSERT INTO [t30432] VALUES (50,'كان ابن الزبير يصلي ركعتين بعد العصر، قال: فنهيته عن ذلك، قال: فأخذ بيدي حتى أقامني على','54','0','0');
INSERT INTO [t30432] VALUES (51,'مر رسول الله صلى الله عليه وسلم في يوم شديد الحر على بقيع الغرقد، فكان الناس يمشون خلفه،','55','0','0');
INSERT INTO [t30432] VALUES (52,'ما من أحد يسلم علي إلا رد الله إلي روحي حتى أرد عليه السلام','56','0','0');
INSERT INTO [t30432] VALUES (53,'من صام يوما ابتغاء وجه الله باعده الله من جهنم كبعد غراب طار وهو فرخ حتى مات','57','0','0');
INSERT INTO [t30432] VALUES (54,'إن من دعوة الأخ للأخ في الله تستجاب','58','0','0');
INSERT INTO [t30432] VALUES (55,'لا ترغبوا عن آبائكم، فمن رغب عن أبيه فإنه كفر','59','0','0');
INSERT INTO [t30432] VALUES (56,'إن هؤلاء كانوا عبادا لي يعبدوني ولا يشركوا بي شيئا، ويسد بهم الثغور، ويتقى بهم المكاره،','60','0','0');
INSERT INTO [t30432] VALUES (57,'فراش للرجل، وفراش لامرأته، والثالث للضيف، والرابع للشيطان','61','0','0');
INSERT INTO [t30432] VALUES (58,'لا تخيفوا أنفسكم. أو قال الأنفس بعد أمنها.','62','0','0');
INSERT INTO [t30432] VALUES (59,'إن كان في شيء شفاء ففي شرطة من حجم، أو شربة من عسل، أو كية بنار تصيب الماء، ولا أحب أن','63','0','0');
INSERT INTO [t30432] VALUES (60,'ما من غازية تغزو في سبيل الله فيصيبوا غنيمة إلا تعجلوا ثلثي أجرهم من الأجر، ويبقى لهم','64','0','0');
INSERT INTO [t30432] VALUES (61,'من عرض عليه طيب فلا يرده، فإنه خفيف المحمل طيب الرائحة','65','0','0');
INSERT INTO [t30432] VALUES (62,'من سمع رجلا ينشد في المسجد ضالة فليقل: لا أداها الله إليك، إن المساجد لم تبن','66','0','0');
INSERT INTO [t30432] VALUES (63,'كان رسول الله صلى الله عليه وسلم يمص اللسان','70','0','0');
INSERT INTO [t30432] VALUES (64,'ما من قلب إلا وهو بين إصبعين من أصابع الرحمن، إذا شاء أن يقيمه أقامه، وإذا شاء أن يزيغه','74','0','0');
INSERT INTO [t30432] VALUES (65,'يا خرقاء، تموتين بفلاة من الأرض، يدفنك خير مؤمن من أهل الأرض يومئذ، فقال له عمر: ومن أنت','76','0','0');
INSERT INTO [t30432] VALUES (66,'ألا أخبركم بخمس دنانير أفضلها وأخسها، أفضلها دينار أنفقته على والدتك، ودينار أنفقته على','77','0','0');
INSERT INTO [t30432] VALUES (67,'امكث حتى يأتي القوم.','78','0','0');
INSERT INTO [t30432] VALUES (68,'الإيمان بضع وستون، أو بضع وسبعون، أفضلها شهادة أن لا إله إلا الله، وأدناها إماطة الأذى عن','79','0','0');
INSERT INTO [t30432] VALUES (69,'لا يجزئ ولد والده إلا أن يجده مملوكا فيشتريه ويعتقه','80','0','0');
INSERT INTO [t30432] VALUES (70,'لا تذهب الأيام والليالي حتى تعود أرض العرب مروجا وأنهارا، ويحبس الفرات عن جبل من الذهب،','81','0','0');
INSERT INTO [t30432] VALUES (71,'إذا لقيتم المشركين في الطريق فلا تبدءوهم بالسلام، واضطروهم إلى أضيقها','82','0','0');
INSERT INTO [t30432] VALUES (72,'إنما الدين النصيحة.','83','0','0');
INSERT INTO [t30432] VALUES (73,'من قال حين يمسي: أعوذ بكلمات الله التامة من شر ما خلق، لم يضره لدغة عقرب حتى','84','0','0');
INSERT INTO [t30432] VALUES (74,'إذا قام الرجل من مجلسه ثم جاء فهو أحق به','85','0','0');
INSERT INTO [t30432] VALUES (75,'من قام من مجلسه يوم الجمعة ثم عاد فهو أحق به','86','0','0');
INSERT INTO [t30432] VALUES (76,'إن الله تعالى لا ينام ولا ينبغي له أن ينام يحفظ القسط ويرفعه، يرفع إليه عمل الليل قبل','87','0','0');
INSERT INTO [t30432] VALUES (77,'حين غربت الشمس تدري أين تذهب؟ قال: الله ورسوله أعلم، قال: إنها تذهب فتسجد تحت العرش،','88','0','0');
INSERT INTO [t30432] VALUES (78,'ما عاب رسول الله صلى الله عليه وسلم طعاما قط، إن اشتهى شيئا أكله، وإن لم يشته شيئا','89','0','0');
INSERT INTO [t30432] VALUES (79,'اتقوا النار ولو بشق تمرة، فإن لم يكن شق تمرة فكلمة طيبة','90','0','0');
INSERT INTO [t30432] VALUES (80,'اقرءوا القرآن من أربعة: من ابن أم عبد، فبدأ به، وأبي بن كعب، ومعاذ بن جبل، وسالم مولى أبي','91','0','0');
INSERT INTO [t30432] VALUES (81,'ما هلك أهل نبوة حتى يفشو فيهم الربا والزنا','92','0','0');
INSERT INTO [t30432] VALUES (82,'يا أبا ذر، تدري فيم ينتطحان؟ قلت: الله ورسوله أعلم، قال: لكن الله أعلم سيقضي','93','0','0');
INSERT INTO [t30432] VALUES (83,'كنا نرى الآية في زمن رسول الله صلى الله عليه وسلم بركة، وأنت ترون الآية','94','0','0');
INSERT INTO [t30432] VALUES (84,'أنا أحق أن أتجاوز عنه','95','0','0');
INSERT INTO [t30432] VALUES (85,'كفر بالله ادعاء إلى نسب لا يعرف، وكفر بالله تبرؤ من نسب وإن دق','96','0','0');
INSERT INTO [t30432] VALUES (86,'ما منكم أحد ينجيه عمله.','97','0','0');
INSERT INTO [t30432] VALUES (87,'يا مقلب القلوب ثبت قلوبنا على دينك. فقال له رجل من أصحابه: تخاف علينا وقد آمنا بك وبما','98','0','0');
INSERT INTO [t30432] VALUES (88,'لا يموتن أحد إلا وهو حسن الظن بالله عز وجل','99','0','0');
INSERT INTO [t30432] VALUES (89,'اهتز عرش الرحمن لموت سعد بن معاذ','100','0','0');
INSERT INTO [t30432] VALUES (90,'والله ما رقدت، ولكن أشياء حدثت بها نفسي حتى والله غمتني، فنظرت في الأشياء كلها، فإذا هي','101','0','0');
INSERT INTO [t30432] VALUES (91,'اللهم من آمن بي وصدقني وعلم أن ما جئت به الحق من عندك فأقل ماله وولده، وحبب إليه لقاءك،','102','0','0');
INSERT INTO [t30432] VALUES (92,'يا أرض ربي وربك الله، أعوذ بالله من شرك وشر ما فيك، وشر ما خلق فيك وشر ما دب عليك، أعوذ','103','0','0');
INSERT INTO [t30432] VALUES (93,'البر حسن الخلق، والإثم ما حاك في نفسك وكرهت أن يعلم الناس','104','0','0');
INSERT INTO [t30432] VALUES (94,'نزلت في المسافر تصيبه الجنابة فيتيمم ويصلي','105','0','0');
INSERT INTO [t30432] VALUES (95,'إن هذا الإحصاء شديد','106','0','0');
INSERT INTO [t30432] VALUES (96,'صليت بغير وضوء {ولم يصروا على ما فعلوا وهم يعلمون} [آل عمران: ١٣٥] فأتى المطهرة فتوضأ ثم','107','0','0');
INSERT INTO [t30432] VALUES (97,'يا عمر، قم فأطعمهم، فقال عمر: يا رسول الله، ما عندي إلا تمر هو قوت عيالي، قال: قم فأطعمهم','108','0','0');
INSERT INTO [t30432] VALUES (98,'إذا استقر أهل الجنة في الجنة اشتاق الإخوان إلى الإخوان، فيسير سرير ذا إلى ذا، فيلتقيان','109','0','0');
INSERT INTO [t30432] VALUES (99,'صلى بنا رسول الله صلى الله عليه وسلم في ثوب واحد متوشحا به','110','0','0');
INSERT INTO [t30432] VALUES (100,'كنا نصلي مع رسول الله صلى الله عليه وسلم الجمعة، ثم ننصرف وليس للحيطان فيء نستظل','111','0','0');
INSERT INTO [t30432] VALUES (101,'إن الله لم يفرض الزكاة إلا ليطيب ما بقي من أموالكم، إنما فرض المواريث في أموال من بقي','113','0','0');
INSERT INTO [t30432] VALUES (102,'لو كنت تقدمت في هذا لفعلت كذا وكذا. ثم كتب إلى أهل الأمصار وأهل الذمة في قرى أو نزل، شك','114','0','0');
INSERT INTO [t30432] VALUES (103,'لم يطف هو وأصحابه بين الصفا والمروة إلا طوافا واحدا لعمرتهم ولحجتهم','115','0','0');
INSERT INTO [t30432] VALUES (104,'ويحك استغفر الله وتب إليه. فرجع غير بعيد ثم جاء، فقال: يا رسول الله، طهرني، فقال رسول','116','0','0');
INSERT INTO [t30432] VALUES (105,'ناولني شيئا أستنجي به، قال: فأناوله العود والحجر، أو يأتي حائطا يتمسح، أو يمسه الأرض، ولم','117','0','0');
INSERT INTO [t30432] VALUES (106,'استقاموا على شهادة أن لا إله إلا الله','118','0','0');
INSERT INTO [t30432] VALUES (107,'وما بأس، إنما هو بضعة منه','119','0','0');
INSERT INTO [t30432] VALUES (108,'إذا اشتهى أحدكم أهله فليأتها ولو على تنور','120','0','0');
INSERT INTO [t30432] VALUES (109,'في قول الله تعالى: {ارجعون {٩٩} لعلي أعمل صالحا} [المؤمنون: ٩٩-١٠٠] .','125','0','0');
