INSERT INTO [t30515] VALUES (1,'نزل جبريل فأمنا فصليت معه , ثم نزل فأمنا فصليت معه حتى عد خمس صلوات','2','0','0');
INSERT INTO [t30515] VALUES (2,'تريدين أن ترجعي إلى رفاعة لا حتى يذوق عسيلتك وتذوقي عسيلته','3','0','0');
INSERT INTO [t30515] VALUES (3,'إنه عمك فائذني له','4','0','0');
INSERT INTO [t30515] VALUES (4,'إذا رأيتم الجنازة فقوموا حتى تخلفكم أو توضع','5','0','0');
INSERT INTO [t30515] VALUES (5,'إذا دعي إلى جنازة سأل عنها فإن أثني عليها خيرا صلى عليها , وإن أثني عليها غير ذلك قال','6','0','0');
INSERT INTO [t30515] VALUES (6,'أعليه دين؟ . فقالوا: نعم , ثمانية عشر درهما , فقال: صلوا على صاحبكم.','7','0','0');
INSERT INTO [t30515] VALUES (7,'على صاحبكم دين؟ . فقالوا: نعم , فقال رسول الله صلى الله عليه وسلم: صلوا على','8','0','0');
INSERT INTO [t30515] VALUES (8,'ما رأيناك تركت الصلاة إلا على هذا , فقال: إنه كان يبغض عثمان.','9','0','0');
INSERT INTO [t30515] VALUES (9,'ذنبان لا يغفران , ويعجل لصاحبهما العقوبة البغي وقطيعة الرحم','10','0','0');
INSERT INTO [t30515] VALUES (10,'ليأرز الإيمان إلى ما بين المسجدين مسجد مكة ومسجد المدينة , كما تأرز الحية إلى','11','0','0');
INSERT INTO [t30515] VALUES (11,'الملحمة العظمى وفتح القسطنطينية في سبعة أشهر','12','0','0');
INSERT INTO [t30515] VALUES (12,'نعم القوم الأزد نقية قلوبهم بارة أيمانهم طيبة أفواههم','13','0','0');
INSERT INTO [t30515] VALUES (13,'أحب الجهاد والهجرة , فقال له رسول الله صلى الله عليه وسلم: هل بقي من والديك أحد؟','14','0','0');
INSERT INTO [t30515] VALUES (14,'أظهر النبي صلى الله عليه وسلم الإسلام فأسلم أهل مكة كلهم قبل أن تفرض الصلاة , وإن كان','15','0','0');
INSERT INTO [t30515] VALUES (15,'بيع الثمر حتى يبدو صلاحها','16','0','0');
INSERT INTO [t30515] VALUES (16,'إذا كان يوم الفطر لم يخرج حتى يأكل تمرات يأكلهن فرادى','17','0','0');
INSERT INTO [t30515] VALUES (17,'لا تصوم المرأة يوما واحدا وزوجها شاهد إلا بإذنه','18','0','0');
INSERT INTO [t30515] VALUES (18,'من يضم أو يضيف هذا.','19','0','0');
INSERT INTO [t30515] VALUES (19,'يصلي في حجر الكعبة , إذا أقبل عقبة بن أبي معيط , فوضع ثوبه في عنقه فخنقه خنقا شديدا ,','20','0','0');
INSERT INTO [t30515] VALUES (20,'قد كان من قبلكم ليمشط بأمشاط الحديد ما دون عظامه من لحم أو عصب ما يصرفه ذلك عن دينه ,','21','0','0');
INSERT INTO [t30515] VALUES (21,'إذا أراد أن ينام , وهو جنب توضأ وضوءه للصلاة قبل أن ينام','22','0','0');
INSERT INTO [t30515] VALUES (22,'من رآني في المنام فسيراني في اليقظة.','23','0','0');
INSERT INTO [t30515] VALUES (23,'من رآني فقد رآني الحق','24','0','0');
INSERT INTO [t30515] VALUES (24,'ما من مولود إلا يولد على الفطرة.','25','0','0');
INSERT INTO [t30515] VALUES (25,'يقول في العشاء بالتين والزيتون , فما سمعت أحسن قراءة ولا صوتا منه','26','0','0');
INSERT INTO [t30515] VALUES (26,'عشاء الآخرة فقرأ فيها والتين والزيتون','27','0','0');
INSERT INTO [t30515] VALUES (27,'حسن الظن من حسن العبادة','28','0','0');
INSERT INTO [t30515] VALUES (28,'من حسن عبادة المرء حسن ظنه','29','0','0');
INSERT INTO [t30515] VALUES (29,'منزلا نزله النبي صلى الله عليه وسلم , ليكون أسمح لخروجه تعني الأبطح','30','0','0');
INSERT INTO [t30515] VALUES (30,'الله تبارك وتعالى , زوجك أم كلثوم على مثل صداق رقية , وعلى مثل صحبتها','31','0','0');
INSERT INTO [t30515] VALUES (31,'أنأكل على آبائنا وأبنائنا وأزواجنا , فما الذي يحل لنا من أموالهم؟ قال: الرطب تأكليه','32','0','0');
INSERT INTO [t30515] VALUES (32,'هل قرأ معي منكم أحد آنفا. قالوا: نعم يا رسول الله , قال رسول الله صلى الله عليه وسلم: إني','33','0','0');
INSERT INTO [t30515] VALUES (33,'فناء أمتي بالطعن والطاعون.','34','0','0');
INSERT INTO [t30515] VALUES (34,'فمن كنت مولاه فإن عليا مولاه','35','0','0');
INSERT INTO [t30515] VALUES (35,'إنك مني بمنزلة هارون من موسى , إلا أنه لا يوحى إليك','36','0','0');
INSERT INTO [t30515] VALUES (36,'أما ترضى أن تكون مني بمنزلة هارون من موسى إلا أنه لا نبي بعدي','37','0','0');
INSERT INTO [t30515] VALUES (37,'خير فرساننا أبو قتادة , وخير رجالتنا سلمة بن وهب بن الأكوع','38','0','0');
INSERT INTO [t30515] VALUES (38,'بعدي من أمتي قوما يقرءون القرآن لا يجاوز تراقيهم يخرجون من الدين , كما يخرج السهم من','39','0','0');
INSERT INTO [t30515] VALUES (39,'لو كنت متخذا خليلا لاتخذت أبا بكر خليلا','40','0','0');
INSERT INTO [t30515] VALUES (40,'أبرأ إلى كل خل من خله , فلو كنت متخذا خليلا لاتخذت أبا بكر خليلا إن صاحبكم خليل','41','0','0');
INSERT INTO [t30515] VALUES (41,'ما من عبد يذنب ذنبا فيتوضأ فيحسن الوضوء , ويصلي ركعتين ويستغفر الله إلا غفر','42','0','0');
INSERT INTO [t30515] VALUES (42,'عرضت علي أجور أمتي حتى القذاة يخرجها الرجل من المسجد , وعرضت علي ذنوب أمتي فلم أر ذنبا','43','0','0');
INSERT INTO [t30515] VALUES (43,'ما من عبد أذنب ذنبا , فقام فتوضأ وأحسن الوضوء , وقام فصلى , ثم استغفر الله تبارك وتعالى','44','0','0');
INSERT INTO [t30515] VALUES (44,'منكم قوما يتعبدون حتى يعجب الناس وتعجبهم أنفسهم يمرقون من الدين كما يمرق السهم من','45','0','0');
INSERT INTO [t30515] VALUES (45,'شر البرية عند الله منزلة يوم القيامة , من أذهب آخرته بدنيا غيره','46','0','0');
INSERT INTO [t30515] VALUES (46,'يسير الرياء شركا , وإن من عادى أولياء الله قد بارز الله بالمحاربة , إن الله يحب الأبرار','47','0','0');
INSERT INTO [t30515] VALUES (47,'الله تبارك وتعالى طيب , ولا يقبل إلا طيبا , وإن الله عز وجل أمر المؤمنين بما أمر به','48','0','0');
INSERT INTO [t30515] VALUES (48,'ما من مسلم يدخل على أخيه , فيلقي له وسادة إكراما له، إلا غفر الله له','49','0','0');
INSERT INTO [t30515] VALUES (49,'من توضأ كما أمر , وصلى كما أمر غفر له ما تقدم من ذنبه','50','0','0');
INSERT INTO [t30515] VALUES (50,'رضا الله في رضا الوالدين , وسخطه في سخطهما','51','0','0');
INSERT INTO [t30515] VALUES (51,'عليكم بالسنا والسنوت فإن فيهما شفاء من كل داء إلا السام. قالوا: يا رسول الله , وما السام؟','52','0','0');
INSERT INTO [t30515] VALUES (52,'من هجر أخاه سنة لقي الله بخطية قابيل بن آدم , لا يفكه شيء دون ولوج','53','0','0');
INSERT INTO [t30515] VALUES (53,'سقط في حجرها أو حجرتها ثلاثة أقمار , فذكرت ذلك لأبي بكر , رضي الله عنه , فقال:','54','0','0');
INSERT INTO [t30515] VALUES (54,'سيخرج أناس من النار أخبرنا أبو عبد الله شعيب بن عبد الله بن أحمد بن المنهال , قال: أخبرنا','55','0','0');
INSERT INTO [t30515] VALUES (55,'{للذين أحسنوا الحسنى وزيادة} [يونس: ٢٦] ، قال: هو النظر إلى وجه الله عز','56','0','0');
INSERT INTO [t30515] VALUES (56,'ما خلق الله من يوم ولا ليلة إلا وللعبد فيه رزق معلوم، بينه وبينه ستر، فإن هو أجمل في','57','0','0');
INSERT INTO [t30515] VALUES (57,'إنكم في ممر الليل والنهار في آجال منقوصة , وأعمال محفوظة , والموت يأتي بغتة , من زرع خيرا','58','0','0');
INSERT INTO [t30515] VALUES (58,'قرأ القرآن ثلاثة , رجل اتخذه بضاعة ينقله من مصر إلى مصر , يطلب به ما عند الناس , وقوم','59','0','0');
