INSERT INTO [t30552] VALUES (1,'لا يبع حاضر لباد وإن كان أخاه , أو أباه.','2','0','0');
INSERT INTO [t30552] VALUES (2,'خذوا الثيب بالثيب جلد مائة والرجم , والبكر بالبكر جلد مائة ونفي سنة','3','0','0');
INSERT INTO [t30552] VALUES (3,'المؤمن من أمنه الناس , والمسلم من سلم المسلمون من لسانه ويده , والمهاجر من هجر السوء ,','4','0','0');
INSERT INTO [t30552] VALUES (4,'لا تناجشوا , ولا تبايعوا الغرر , ولا تباغضوا , ولا يبيع حاضر لباد','5','0','0');
INSERT INTO [t30552] VALUES (5,'فلم يجهروا: بسم الله الرحمن الرحيم','6','0','0');
