INSERT INTO [t30552] VALUES (7,'الإقعاء في الصلاة','8','0','0');
INSERT INTO [t30552] VALUES (8,'من شرب الخمر فاجلدوه.','9','0','0');
INSERT INTO [t30552] VALUES (9,'صدقة المؤمن ظله يوم القيامة','10','0','0');
INSERT INTO [t30552] VALUES (10,'له ستة أعبد فأعتقهم عند موته , ولم يكن له مال غيرهم , قال: فرفع ذلك إلى نبي الله صلى الله','11','0','0');
INSERT INTO [t30552] VALUES (11,'يكبر كلما خفض ورفع , فلما صلى قال عمران بن حصين: لقد أذكرنا هذا الشيخ صلاة كنا نصليها مع','12','0','0');
INSERT INTO [t30552] VALUES (12,'جمع النبي صلى الله عليه وسلم بين حجة وعمرة','13','0','0');
INSERT INTO [t30552] VALUES (13,'خير تمراتكم البرني','14','0','0');
INSERT INTO [t30552] VALUES (14,'لولا أن الكلاب أمة من الأمم لأمرت بقتلها , فاقتلوا منها كل أسود بهيم','15','0','0');
INSERT INTO [t30552] VALUES (15,'لم يكن نبي قبلي إلا وقد حذر أمته الدجال , وإني أحذركموه وإنه كائن فيكم','16','0','0');
INSERT INTO [t30552] VALUES (16,'بات البارحة حتى أصبح , قال: بال الشيطان في أذنه','17','0','0');
INSERT INTO [t30552] VALUES (17,'من تركهن فهو عدوي حقا , ومن أخذهن فهو وليي حقا الصلاة والصوم والجنابة','18','0','0');
INSERT INTO [t30552] VALUES (18,'ما من عبد استرعاه الله رعية يموت يوم يموت , وهو لها غاش , إلا حرم الله عليه','19','0','0');
INSERT INTO [t30552] VALUES (19,'إذا زنت الأمة فاجلدوها , ولا تعيروها , وإن زنت فاجلدوها , وإن زنت فاجلدوها , وإن زنت','20','0','0');
INSERT INTO [t30552] VALUES (20,'الله يحب سمح البيع , سمح الشراء , سمح القضاء','21','0','0');
INSERT INTO [t30552] VALUES (21,'رجلا كان فيمن كان قبلكم خرج في بردين مختالا فيهما , فأمر الله الأرض فأخذته , فهو يتجلجل','22','0','0');
INSERT INTO [t30552] VALUES (22,'يبول الرجل في الماء الدائم ثم يتوضأ منه','23','0','0');
INSERT INTO [t30552] VALUES (23,'لا يسترعي الله عبدا رعية قلت أو كثرت إلا سأله الله عنها أقام فيهم أمر الله أم أضاعه , حتى','24','0','0');
INSERT INTO [t30552] VALUES (24,'قال لا إله إلا الله مخلصا , وأني رسول الله , يرجع ذلك إلى قلب مؤمن إلا غفر','25','0','0');
INSERT INTO [t30552] VALUES (25,'صلى في الآيات ركعتين','26','0','0');
INSERT INTO [t30552] VALUES (26,'المشركين يتسربلون ولا يتزرون , قال: فتسربلوا أنتم واتزروا.','27','0','0');
INSERT INTO [t30552] VALUES (27,'يؤمنا في الصلاة، فجهر وخافت , فجهرنا فيما جهر فيه , وخافتنا فيما خافت فيه، وسمعته يقول:','28','0','0');
INSERT INTO [t30552] VALUES (28,'إذا تغشى أحدكم المرأة وكان بين شعبها الأربع ثم اجتهد فقد حل عليه الغسل أنزل أو لم','29','0','0');
INSERT INTO [t30552] VALUES (29,'الله ليسأل العبد المملوك يوم القيامة، فيقول: يا رب، عبدتني عبدا من عبادك فكان يشغلني ,','30','0','0');
INSERT INTO [t30552] VALUES (30,'عبد أطاع الله , وأطاع مواليه يدخله الله الجنة قبل مواليه , فيقول السيد: رب هذا كان عبدي','31','0','0');
INSERT INTO [t30552] VALUES (31,'السؤال نصف العلم , والرفق نصف العيش , وما افتقر امرؤ فيه اقتصاد','32','0','0');
INSERT INTO [t30552] VALUES (32,'أفطر الحاجم والمحجوم','33','0','0');
