INSERT INTO [t30552] VALUES (79,'أردفه غداة جمع فلم يزل يلبي حتى رمى جمرة العقبة','80','0','0');
INSERT INTO [t30552] VALUES (80,'من لطم مملوكا له فكفارته عتقه؛ وإني كنت لطمته.','81','0','0');
INSERT INTO [t30552] VALUES (81,'من صلى على جنازة كتب له قيراط من الأجر , ومن تبعها كان له قيراطان. قال رجل لرسول الله:','82','0','0');
INSERT INTO [t30552] VALUES (82,'أخر الدفعة , فقال ابن عمر: ما ننتظر إلا فعل الجاهلية','83','0','0');
INSERT INTO [t30552] VALUES (83,'تغسل رسول الله صلى الله عليه وسلم، وهو معتكف يصغي رأسه إليها في حجرتها وهي حائض , لفظهما','84','0','0');
