INSERT INTO [t30552] VALUES (86,'{فلا رفث ولا فسوق} [البقرة: ١٩٧] قال: المعاصي والكذب , {ولا جدال في الحج} [البقرة: ١٩٧]','87','0','0');
INSERT INTO [t30552] VALUES (87,'إذا كانت فتنة بين المسلمين أن أتخذ سيفا من خشب.','88','0','0');
INSERT INTO [t30552] VALUES (88,'لا تبع ما ليس عندك','89','0','0');
INSERT INTO [t30552] VALUES (89,'القضاة ثلاثة قاضيان في النار , وقاض في الجنة , قاض قضى بغير حق، وهو يعلم فهو في النار ,','90','0','0');
INSERT INTO [t30552] VALUES (90,'أنسا رجل لبيب كاتب فاستعن به , قال: فبعثني على بعض الصدقات , قال: فرجعت وقبض أبو بكر ,','91','0','0');
INSERT INTO [t30552] VALUES (91,'عملان لا عمل أفضل منهما إلا مثلهما حجة مبرورة , وعمرة','92','0','0');
INSERT INTO [t30552] VALUES (92,'لا تنظروا إلى صيام أحد ولا صلاته ولكن انظروا إلى صدق حديثه إذا حدث , وأمانته إذا ائتمن ,','93','0','0');
INSERT INTO [t30552] VALUES (93,'ما أكل النبي صلى الله عليه وسلم على خوان قط , ولا في سكرجة , ولا خبز له مرقق , قال: قلت','94','0','0');
INSERT INTO [t30552] VALUES (94,'وضع طهوره , فجاءت هرة فأدناها من طهوره حتى ولغ فيها , ثم تطهر منه , وكان لا يرى بذلك','95','0','0');
